using System.Collections.Generic;

namespace Pathfinda
{
    public static partial class ImportedData
    { 
        public class Spell
        {
            public string Name { get; set; }
            public string Description { get; set; }
        }
        private static List<Spell> _spells = null;
        public static List<Spell> Spells
        {
            get
            {
                if (_spells == null)
                {
                    _spells = new List<Spell>()
                    {
new Spell(){ Name = "Acid Arrow", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Acid Arrow </p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [acid]; <b>Level </b>sorcerer/wizard 2, magus 2, bloodrager 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (rhubarb leaf and an adder's stomach), F (a dart)</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>one arrow of acid</h5><h5><b>Duration </b>1 round + 1 round per three levels</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>An arrow of acid springs from your hand and speeds to its target. You must succeed on a ranged touch attack to hit your target. The arrow deals 2d4 points of acid damage with no splash damage. For every three caster levels you possess, the acid, unless neutralized, lasts for another round (to a maximum of 6 additional rounds at 18th level), dealing another 2d4 points of damage in each round.</p></h4></div>"},
new Spell(){ Name = "Acid Fog", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Acid Fog </p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [acid]; <b>Level </b>sorcerer/wizard 6, magus 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (powdered peas and an animal hoof)</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>fog spreads in 20-ft. radius, 20 ft. high</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p><i>Acid fog</i> creates a billowing mass of misty vapors like the <i>solid fog</i> spell. In addition to slowing down creatures and obscuring sight, this spell's vapors are highly acidic. Each round on your turn, starting when you cast the spell, the fog deals 2d6 points of acid damage to each creature and object within it.</p></h4></div>"},
new Spell(){ Name = "Acid Splash", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Acid Splash </p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [acid]; <b>Level </b>sorcerer/wizard 0, summoner 0, inquisitor 0, magus 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one missile of acid</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You fire a small orb of acid at the target. You must succeed on a ranged touch attack to hit your target. The orb deals 1d3 points of acid damage. This acid disappears after 1 round.</p></h4></div>"},
new Spell(){ Name = "Aid", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Aid</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>cleric/oracle 2, alchemist 2, inquisitor 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> living creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p><i>Aid</i> grants the target a +1 morale bonus on attack rolls and saves against fear effects, plus temporary hit points equal to 1d8 + caster level (to a maximum of 1d8+10 temporary hit points at caster level 10th).</p></h4></div>"},
new Spell(){ Name = "Air Walk", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Air Walk </p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [air]; <b>Level </b>cleric/oracle 4, druid 4, alchemist 4, occultist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature (Gargantuan or smaller) touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject can tread on air as if walking on solid ground. Moving upward is similar to walking up a hill. The maximum upward or downward angle possible is 45 degrees, at a rate equal to half the air walker's normal speed.</p><p>A strong wind (21+ miles per hour) can push the subject along or hold it back. At the end of a creature's turn each round, the wind blows the air walker 5 feet for each 5 miles per hour of wind speed. The creature may be subject to additional penalties in exceptionally strong or turbulent winds, such as loss of control over movement or physical damage from being buffeted about.</p><p>Should the spell duration expire while the subject is still aloft, the magic fails slowly. The subject floats downward 60 feet per round for 1d6 rounds. If it reaches the ground in that amount of time, it lands safely. If not, it falls the rest of the distance, taking 1d6 points of damage per 10 feet of fall. Since dispelling a spell effectively ends it, the subject also descends in this way if the <i>air walk</i> spell is dispelled, but not if it is negated by an <i>antimagic field</i>.</p><p>You can cast <i>air walk</i> on a specially trained mount so it can be ridden through the air. You can train a mount to move with the aid of <i>air walk</i> (counts as a trick; see Handle Animal skill) with 1 week of work and a DC 25 Handle Animal check.</p></h4></div>"},
new Spell(){ Name = "Align Weapon", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Align Weapon </p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [see text]; <b>Level </b>cleric/oracle 2, inquisitor 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> weapon touched or 50 projectiles (all of which must be together at the time of casting)</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><div><h4><p><i>Align weapon</i> makes a weapon chaotic, evil, good, or lawful, as you choose. A weapon that is aligned can bypass the damage reduction of certain creatures. This spell has no effect on a weapon that already has an alignment.</p><p>You can't cast this spell on a natural weapon, such as an unarmed strike. When you make a weapon chaotic, evil, good, or lawful, <i>align weapon</i> is a chaotic, evil, good, or lawful spell, respectively.</p></h4></div>"},
new Spell(){ Name = "Alter Self", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Alter Self </p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>bard 2, sorcerer/wizard 2, alchemist 2, summoner 2, witch 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>When you cast this spell, you can assume the form of any Small or Medium creature of the humanoid type. If the form you assume has any of the following abilities, you gain the listed ability: darkvision 60 feet, low-light vision, scent, and swim 30 feet.</p><p><i>Small creature</i>: If the form you take is that of a Small humanoid, you gain a +2 size bonus to your Dexterity.</p><p><i>Medium creature</i>: If the form you take is that of a Medium humanoid, you gain a +2 size bonus to your Strength.</p></h4></div>"},
new Spell(){ Name = "Alarm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Alarm </p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 1, ranger 1, sorcerer/wizard 1, summoner 1, inquisitor 1, occultist 1, psychic 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a tiny bell and a piece of very fine silver wire)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>20-ft.-radius emanation centered on a point in space</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p><i>Alarm</i> creates a subtle ward on an area you select. Once the spell effect is in place, it thereafter sounds a mental or audible alarm each time a creature of Tiny or larger size enters the warded area or touches it. A creature that speaks the password (determined by you at the time of casting) does not set off the <i>alarm</i>. You decide at the time of casting whether the <i>alarm</i> will be mental or audible in nature.</p><p><i>Mental Alarm</i>: A mental <i>alarm</i> alerts you (and only you) so long as you remain within 1 mile of the warded area. You note a single mental ""ping"" that awakens you from normal sleep but does not otherwise disturb concentration. A silence spell has no effect on a mental <i>alarm</i>.</p><p><i>Audible Alarm</i>: An audible <i>alarm</i> produces the sound of a hand bell, and anyone within 60 feet of the warded area can hear it clearly. Reduce the distance by 10 feet for each interposing closed door and by 20 feet for each substantial interposing wall.</p><p>In quiet conditions, the ringing can be heard faintly as far as 180 feet away. The sound lasts for 1 round. Creatures within a <i>silence</i> spell cannot hear the ringing.  Ethereal or astral creatures do not trigger the <i>alarm</i>.</p><p><i>Alarm</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Analyze Dweomer", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Analyze Dweomer </p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 6, sorcerer/wizard 6, alchemist 6, witch 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a ruby and gold lens worth 1,500 gp)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one object or creature per caster level</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none or Will negates; see text; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can observe magical auras. Each round, you may examine a single creature or object that you can see as a free action. In the case of a magic item, you learn its functions (including any curse effects), how to activate its functions (if appropriate), and how many charges are left (if it uses charges). In the case of an object or creature with active spells cast upon it, you learn each spell, its effect, and its caster level.</p><p>An attended object may attempt a Will save to resist this effect if its holder so desires. If the save succeeds, you learn nothing about the object except what you can discern by looking at it. An object that makes its save cannot be affected by any other <i>analyze dweomer</i> spells for 24 hours.</p><p><i>Analyze dweomer</i> does not function when used on an artifact.</p></h4></div>"},
new Spell(){ Name = "Animal Growth", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animal Growth </p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 5, ranger 4, sorcerer/wizard 5, shaman 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b> one animal (Gargantuan or smaller)</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>The target animal grows to twice its normal size and eight times its normal weight. This alteration changes the animal's size category to the next largest, grants it a +8 size bonus to Strength and a +4 size bonus to Constitution (and thus an extra 2 hit points per HD), and imposes a -2 size penalty to Dexterity. The creature's existing natural armor bonus increases by 2. The size change also affects the animal's modifier to AC, attack rolls, and its base damage. The animal's space and reach change as appropriate to the new size, but its speed does not change. If insufficient room is available for the desired growth, the creature attains the maximum possible size and may make a Strength check (using its increased Strength) to burst any enclosures in the process. If it fails, it is constrained without harm by the materials enclosing it--the spell cannot be used to crush a creature by increasing its size.</p><p>All equipment worn or carried by the animal is similarly enlarged by the spell, though this change has no effect on the magical properties of any such equipment.</p><p>Any enlarged item that leaves the enlarged creature's possession instantly returns to its normal size.</p><p>The spell gives no means of command over an enlarged animal.</p><p>Multiple magical effects that increase size do not stack.</p></h4></div>"},
new Spell(){ Name = "Animal Messenger", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animal Messenger </p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 2, druid 2, ranger 1, shaman 2, psychic 2, mesmerist 2</h5><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (a morsel of food the animal likes)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one Tiny animal</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You compel a Tiny animal to go to a spot you designate. The most common use for this spell is to get an animal to carry a message to your allies. The animal cannot be one tamed or trained by someone else, including such creatures as familiars and animal companions.</p><p>Using some type of food desirable to the animal as a lure, you call the animal to you. It advances and awaits your bidding. You can mentally impress on the animal a certain place well known to you or an obvious landmark. The directions must be simple, because the animal depends on your knowledge and can't find a destination on its own. You can attach a small item or note to the messenger. The animal then goes to the designated location and waits there until the duration of the spell expires, whereupon it resumes its normal activities.</p><p>During this period of waiting, the messenger allows others to approach it and remove any scroll or token it carries. The intended recipient gains no special ability to communicate with the animal or read any attached message (if it's written in a language he doesn't know, for example).</p></h4></div>"},
new Spell(){ Name = "Animal Shapes", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animal Shapes</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>druid 8, shaman 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> up to one willing creature per level, all within 30 ft. of each other.</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>As <i>beast shape III</i>, except you change the form of up to one willing creature per caster level into an animal of your choice; the spell has no effect on unwilling creatures. All creatures must take the same kind of animal form. Recipients remain in the animal form until the spell expires or until you dismiss it for all recipients. In addition, an individual subject may choose to resume its normal form as a full-round action; doing so ends the spell for that subject alone.</p></h4></div>"},
new Spell(){ Name = "Animal Trance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animal Trance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, sonic]; <b>Level </b>bard 2, druid 2, psychic 2, mesmerist 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> animals or magical beasts with Intelligence 1 or 2</h5><h5><b>Duration </b>concentration</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>Your swaying motions and music (or singing, or chanting) compel animals and magical beasts to do nothing but watch you. Only a creature with an Intelligence score of 1 or 2 can be fascinated by this spell. Roll 2d6 to determine the total number of HD worth of creatures that you fascinate. The closest targets are selected first until no more targets within range can be affected.</p></h4></div>"},
new Spell(){ Name = "Animate Dead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animate Dead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [evil]; <b>Level </b>cleric/oracle 3, sorcerer/wizard 4, antipaladin 3, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (an onyx gem worth at least 25 gp per Hit Die of the undead)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one or more corpses touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell turns corpses into undead skeletons or zombies (see the <i>Pathfinder RPG</i> Bestiary) that obey your spoken commands.</p><p>The undead can be made to follow you, or they can be made to remain in an area and attack any creature (or just a specific kind of creature) entering the place. They remain animated until they are destroyed. A destroyed skeleton or zombie can't be animated again.</p><p>Regardless of the type of undead you create with this spell, you can't create more HD of undead than twice your caster level with a single casting of <i>animate dead</i>. The <i>desecrate</i> spell doubles this limit.</p><p>The undead you create remain under your control indefinitely.</p><p>No matter how many times you use this spell, however, you can control only 4 HD worth of undead creatures per caster level. If you exceed this number, all the newly created creatures fall under your control, and any excess undead from previous castings become uncontrolled. You choose which creatures are released. Undead you control through the Command Undead feat do not count toward this limit.</p><p><i>Skeletons</i>: A skeleton can be created only from a mostly intact corpse or skeleton. The corpse must have bones. If a skeleton is made from a corpse, the flesh falls off the bones.</p><p><i>Zombies</i>: A zombie can be created only from a mostly intact corpse.</p><p>The corpse must be that of a creature with a physical anatomy.</p></h4><h5><b>Mythic: </b>Add your tier to your caster level when determining how many Hit Dice of undead you can animate with a single casting of this spell. This doesn't increase the total number of Hit Dice worth of undead you can control. By expending a second use of mythic power, you can ignore the spell's material component cost.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, any skeletons or zombies you create gain either the agile (see page 224) or savage (see page 224) mythic template. This template lasts for a number of days equal to your tier. Alternatively, if you're 8th tier and expend 10 uses of mythic power, any skeletons you create permanently gain the mythic skeleton template (see page 216).</h5></div>"},
new Spell(){ Name = "Animate Objects", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animate Objects</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 6, cleric/oracle 6, witch 6, occultist 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one Small object per caster level; see text</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You imbue inanimate objects with mobility and a semblance of life. Each such animated object then immediately attacks whomever or whatever you initially designate.</p><p>An animated object can be of any nonmagical material. You may animate one Small or smaller object or a corresponding number of larger objects as follows: A Medium object counts as two Small or smaller objects, a Large object as four, a Huge object as eight, a Gargantuan object as 16, and a Colossal object as 32. You can change the designated target or targets as a move action, as if directing an active spell. See the <i>Pathfinder RPG Bestiary</i> for the statistics of animated objects.</p><p>This spell cannot affect objects carried or worn by a creature.</p><p><i>Animate objects</i> can be made permanent with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>Animated objects you create with this spell get the maximum number of hit points per level, gain a +4 bonus to their Strength ability scores, and have 1-1/2 times the normal number of construction points.</h5></div>"},
new Spell(){ Name = "Animate Plants", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animate Plants</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 7, shaman 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one Large plant per three caster levels or all plants within  range; see text</h5><h5><b>Duration </b>1 round/level or 1 hour/level; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You imbue inanimate plants with mobility and a semblance of life. Each animated plant then immediately attacks whomever or whatever you initially designate as though it were an animated object of the appropriate size category. You may animate one Large or smaller plant, or a number of larger plants as follows: a Huge plant counts as two Large or smaller plants, a Gargantuan plant as four, and a Colossal plant as eight. You can change the designated target or targets as a move action, as if directing an active spell.</p><p>Use the statistics for animated objects (see <i>Pathfinder RPG</i> Bestiary), except that plants smaller than Large don't have hardness.</p><p><i>Animate plants</i> cannot affect plant creatures, nor does it affect nonliving vegetable material.</p><p><i>Entangle</i>: Alternatively, you may imbue all plants within range with a degree of mobility, which allows them to entwine around creatures in the area. This usage of the spell duplicates the effect of an <i>entangle</i> spell. Spell resistance does not keep creatures from being <i>entangle</i>d. This effect lasts 1 hour per caster level.</p></h4><h5><b>Mythic: </b>Animated plants you create with this spell get the maximum number of hit points per level, gain a +4 bonus to their Strength ability scores, and have 1-1/2 times the normal number of construction points.</h5></div>"},
new Spell(){ Name = "Animate Rope", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Animate Rope </p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 1, sorcerer/wizard 1, psychic 1, mesmerist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b> one rope-like object, length up to 50 ft. + 5 ft./level; see text</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can animate a nonliving rope-like object. The maximum length assumes a rope with a 1-inch diameter. Reduce the maximum length by 50% for every additional inch of thickness, and increase it by 50% for each reduction of the rope's diameter by half.</p><p>The possible commands are ""coil"" (form a neat, coiled stack), ""coil and knot,"" ""loop,"" ""loop and knot,"" ""tie and knot,"" and the opposites of all of the above (""uncoil,"" and so forth). You can give one command each round as a move action, as if directing an active spell.</p><p>The rope can enwrap only a creature or an object within 1 foot of it - it does not snake outward - so it must be thrown near the intended target. Doing so requires a successful ranged touch attack roll (range increment 10 feet). A typical 1-inch-diameter hemp rope has 2 hit points, AC 10, and requires a DC 23 Strength check to burst it. The rope does not deal damage, but it can be used as a trip line or to cause a single opponent that fails a Reflex saving throw to become entangled. A creature capable of spellcasting that is bound by this spell must make a concentration check with a DC of 15 + the spell's level to cast a spell. An entangled creature can slip free with a DC 20 Escape Artist check.</p><p>The rope itself and any knots tied in it are not magical.</p><p>The spell cannot affect objects carried or worn by a creature.</p></h4></div>"},
new Spell(){ Name = "Antilife Shell", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Antilife Shell</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 6, druid 6, shaman 6, psychic 6</h5><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>10 ft.</h5><h5><b>Area </b>10-ft.-radius emanation, centered on you</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You bring into being a mobile, hemispherical energy field that prevents the entrance of most types of living creatures.</p><p>The effect hedges out animals, aberrations, dragons, fey, giants, humanoids, magical beasts, monstrous humanoids, oozes, plants, and vermin, but not constructs, elementals, outsiders, or undead.</p><p>This spell may be used only defensively, not aggressively. Forcing an abjuration barrier against creatures that the spell keeps at bay collapses the barrier.</p></h4></div>"},
new Spell(){ Name = "Antimagic Field", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Antimagic Field</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 8, sorcerer/wizard 6, occultist 6, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (pinch of powdered iron or iron filings)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Area </b>10-ft.-radius emanation, centered on you</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An invisible barrier surrounds you and moves with you. The space within this barrier is impervious to most magical effects, including spells, spell-like abilities, and supernatural abilities.</p><p>Likewise, it prevents the functioning of any magic items or spells within its confines.</p><p>An <i>antimagic field</i> suppresses any spell or magical effect used within, brought into, or cast into the area, but does not dispel it.</p><p>Time spent within an <i>antimagic field</i> counts against the suppressed spell's duration.</p><p>Summoned creatures of any type wink out if they enter an <i>antimagic field</i>. They reappear in the same spot once the field goes away. Time spent winked out counts normally against the duration of the conjuration that is maintaining the creature. If you cast <i>antimagic field</i> in an area occupied by a summoned creature that has spell resistance, you must make a caster level check (1d20 + caster level) against the creature's spell resistance to make it wink out.</p><p>(The effects of instantaneous conjurations are not affected by an <i>antimagic field</i> because the conjuration itself is no longer in effect, only its result.) A normal creature can enter the area, as can normal missiles.</p><p>Furthermore, while a magic sword does not function magically within the area, it is still a sword (and a masterwork sword at that). The spell has no effect on golems and other constructs that are imbued with magic during their creation process and are thereafter self-supporting (unless they have been summoned, in which case they are treated like any other summoned creatures). Elementals, undead, and outsiders are likewise unaffected unless summoned. These creatures' spell-like or supernatural abilities may be temporarily nullified by the field.</p><p><i>Dispel magic</i> does not remove the field.</p><p>Two or more <i>antimagic field</i>s sharing any of the same space have no effect on each other. Certain spells, such as <i>wall of force</i>, <i>prismatic sphere</i>, and <i>prismatic wall</i>, remain unaffected by <i>antimagic field</i>.</p><p>Artifacts and deities are unaffected by mortal magic such as this.</p><p>Should a creature be larger than the area enclosed by the barrier, any part of it that lies outside the barrier is unaffected by the field.</p></h4><h5><b>Mythic: </b>Select a number of spell schools equal to half your tier. Spells and effects of the chosen schools are unaffected by the antimagic field.</h5></div>"},
new Spell(){ Name = "Antipathy", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Antipathy</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, emotion]; <b>Level </b>druid 9, sorcerer/wizard 8, summoner 6, witch 8</h5><h5><b>Casting Time </b>1 hour</h5><h5><b>Components </b>V, S, M/DF (a lump of alum soaked in vinegar)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one location (up to a 10-ft. cube/level) or one object</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You cause an object or location to emanate magical vibrations that repel either a specific kind of intelligent creature or creatures of a particular alignment, as defined by you. The kind of creature to be affected must be named specifically. A creature subtype is not specific enough. Likewise, the specific alignment to be repelled must be named.</p><p>Creatures of the designated kind or alignment feel an urge to leave the area or to avoid the affected item.</p><p>A compulsion forces them to abandon the area or item, shunning it and never willingly returning to it while the spell is in effect. A creature that makes a successful saving throw can stay in the area or touch the item but feels uncomfortable doing so. This distracting discomfort reduces the creature's Dexterity score by 4 points.</p><p><i>Antipathy</i> counters and dispels <i>sympathy</i>.</p></h4></div>"},
new Spell(){ Name = "Antiplant Shell", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Antiplant Shell</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>druid 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>10 ft.</h5><h5><b>Area </b>10-ft.-radius emanation, centered on you</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>The <i>antiplant shell</i> spell creates an invisible, mobile barrier that keeps all creatures within the shell protected from attacks by plant creatures or animated plants. As with many abjuration spells, forcing the barrier against creatures that the spell keeps at bay strains and collapses the field.</p></h4></div>"},
new Spell(){ Name = "Arcane Eye", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Arcane Eye </p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination (scrying); <b>Level </b>sorcerer/wizard 4, alchemist 4, witch 4</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (a bit of bat fur)</h5><h5><b>Range </b>unlimited</h5><h5><b>Effect </b>magical sensor</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You create an invisible magical sensor that sends you visual information. You can create the <i>arcane eye</i> at any point you can see, but it can then travel outside your line of sight without hindrance. An <i>arcane eye</i> travels at 30 feet per round (300 feet per minute) if viewing an area ahead as a human would (primarily looking at the floor) or 10 feet per round (100 feet per minute) if examining the ceiling and walls as well as the floor ahead. It sees exactly as you would see if you were there.</p><p>The eye can travel in any direction as long as the spell lasts. Solid barriers block its passage, but it can pass through a hole or space as small as 1 inch in diameter. The eye can't enter another plane of existence, even through a <i>gate</i> or similar magical portal.</p><p>You must concentrate to use an <i>arcane eye</i>. If you do not concentrate, the eye is inert until you again concentrate.</p></h4></div>"},
new Spell(){ Name = "Arcane Lock", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Arcane Lock</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 2, occultist 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (gold dust worth 25 gp)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> door, chest, or portal touched, up to 30 sq. ft./level in size</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>An <i>arcane lock</i> spell cast upon a door, chest, or portal magically locks it. You can freely pass your own arcane lock without affecting it. If the locked object has a lock, the DC to open that lock increases by 10 while it remains attached to the object. If the object does not have a lock, this spell creates one that can only be opened with a DC 20 Disable Device skill check. A door or object secured with this spell can be opened only by breaking in or with a successful <i>dispel magic</i> or <i>knock</i> spell. Add 10 to the normal DC to break open a door or portal affected by this spell. A <i>knock</i> spell does not remove an <i>arcane lock</i>; it only suppresses the effect for 10 minutes.</p></h4></div>"},
new Spell(){ Name = "Arcane Mark", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Arcane Mark</p><div style=""clear: both;""></div></div><div><h5><b>School </b>universal; <b>Level </b>sorcerer/wizard 0, summoner 0, magus 0, shaman 0, psychic 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>touch</h5><h5><b>Effect </b>one personal rune or mark, all of which must fit within 1 sq. ft.</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell allows you to inscribe your personal rune or mark, which can consist of no more than six characters. The writing can be visible or invisible. An <i>arcane mark</i> spell enables you to etch the rune upon any substance without harm to the material upon which it is placed. If an invisible mark is made, a <i>detect magic</i> spell causes it to glow and be visible, though not necessarily understandable.</p><p><i>See invisibility</i>, <i>true seeing</i>, a <i>gem of seeing</i>, or a <i>robe of eyes</i> likewise allows the user to see an invisible <i>arcane mark</i>. A <i>read magic</i> spell reveals the words, if any. The mark cannot be dispelled, but it can be removed by the caster or by an <i>erase</i> spell.</p><p>If an <i>arcane mark</i> is placed on a living being, the effect gradually fades in about a month.</p><p><i>Arcane mark</i> must be cast on an object prior to casting <i>instant summons</i> on the same object (see that spell description for details).</p></h4></div>"},
new Spell(){ Name = "Arcane Sight", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Arcane Sight </p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>sorcerer/wizard 3, alchemist 3, witch 3, inquisitor 3</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>This spell makes your eyes glow blue and allows you to see magical auras within 120 feet of you. The effect is similar to that of a <i>detect magic</i> spell, but <i>arcane sight</i> does not require concentration and discerns aura location and power more quickly.</p><p>You know the location and power of all magical auras within your sight. An aura's power depends on a spell's functioning level or an item's caster level, as noted in the description of the <i>detect magic</i> spell. If the items or creatures bearing the auras are in line of sight, you can make Spellcraft skill checks to determine the school of magic involved in each. (Make one check per aura; DC 15 + spell level, or 15 + half caster level for a nonspell effect.) If you concentrate on a specific creature within 120 feet of you as a standard action, you can determine whether it has any spellcasting or spell-like abilities, whether these are arcane or divine (spell-like abilities register as arcane), and the strength of the most powerful spell or spell-like ability the creature currently has available for use.</p><p>As with <i>detect magic</i>, you can use this spell to identify the properties of magic items, but not artifacts.</p><p><i>Arcane sight</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Arcane Sight, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Arcane Sight, Greater </p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>sorcerer/wizard 7, witch 7, psychic 7</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>This spell functions like <i>arcane sight</i>, except that you automatically know which spells or magical effects are active upon any individual or object you see.</p><p>Unlike <i>arcane sight</i>, this spell cannot be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Astral Projection", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Astral Projection</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 9, sorcerer/wizard 9, witch 9, psychic 9</h5><h5><b>Casting Time </b>30 minutes</h5><h5><b>Components </b>V, S, M (1,000 gp jacinth)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> you plus one additional willing creature touched per two caster levels</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>By freeing your spirit from your physical body, this spell allows you to project an astral body onto another plane altogether. You can bring the astral forms of other willing creatures with you, provided that these subjects are linked in a circle with you at the time of the casting. These fellow travelers are dependent upon you and must accompany you at all times. If something happens to you during the journey, your companions are stranded wherever you left them.</p><p>You project your astral self onto the Astral Plane, leaving your physical body behind on the Material Plane in a state of suspended animation. The spell projects an astral copy of you and all you wear or carry onto the Astral Plane. Since the Astral Plane touches upon other planes, you can travel astrally to any of these other planes as you will. To enter one, you leave the Astral Plane, forming a new physical body (and equipment) on the plane of existence you have chosen to enter.</p><p>While you are on the Astral Plane, your astral body is connected at all times to your physical body by an incorporeal silver cord. If the cord is broken, you are killed, astrally and physically. Luckily, very few things can destroy a silver cord. When a second body is formed on a different plane, the silver cord remains invisibly attached to the new body. If the second body or the astral form is slain, the cord simply returns to your body where it rests on the Material Plane, thereby reviving it from its state of suspended animation. This is a traumatic affair, however, and you gain two permanent negative levels if your second body or astral form is slain. Although astral projections are able to function on the Astral Plane, their actions affect only creatures existing on the Astral Plane; a physical body must be materialized on other planes.</p><p>You and your companions may travel through the Astral Plane indefinitely. Your bodies simply wait behind in a state of suspended animation until you choose to return your spirits to them. The spell lasts until you desire to end it, or until it is terminated by some outside means, such as <i>dispel magic</i> cast upon either the physical body or the astral form, the breaking of the silver cord, or the destruction of your body back on the Material Plane (which kills you).</p><p>When this spell ends, your astral body and all of its gear, vanishes.</p></h4></div>"},
new Spell(){ Name = "Atonement", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Atonement</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 5, druid 5, inquisitor 5</h5><h5><b>Casting Time </b>1 hour</h5><h5><b>Components </b>V, S, M (burning incense), F (a set of prayer beads or other prayer device worth at least 500 gp), DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> living creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell removes the burden of misdeeds from the subject. The creature seeking atonement must be truly repentant and desirous of setting right its misdeeds. If the atoning creature committed the evil act unwittingly or under some form of compulsion, <i>atonement</i> operates normally at no cost to you. However, in the case of a creature atoning for deliberate misdeeds, you must intercede with your deity (requiring you to expend 2,500 gp in rare incense and offerings). <i>Atonement</i> may be cast for one of several purposes, depending on the version selected.</p><p><i>Reverse Magical Alignment Change</i>: If a creature has had its alignment magically changed, atonement returns its alignment to its original status at no additional cost.</p><p><i>Restore Class</i>: A paladin, or other class, who has lost her class features due to violating the alignment restrictions of her class may have her class features restored by this spell.</p><p><i>Restore Cleric or Druid Spell Powers</i>: A cleric or druid who has lost the ability to cast spells by incurring the anger of her deity may regain that ability by seeking <i>atonement</i> from another cleric of the same deity or another druid. If the transgression was intentional, the casting cleric must expend 2,500 gp in rare incense and offerings for her god's intercession.</p><p><i>Redemption or Temptation</i>: You may cast this spell upon a creature of an opposing alignment in order to offer it a chance to change its alignment to match yours. The prospective subject must be present for the entire casting process. Upon completion of the spell, the subject freely chooses whether it retains its original alignment or acquiesces to your offer and changes to your alignment. No duress, compulsion, or magical influence can force the subject to take advantage of the opportunity offered if it is unwilling to abandon its old alignment. This use of the spell does not work on outsiders or any creature incapable of changing its alignment naturally.</p><p>Though the spell descriptionrefers to evil acts, <i>atonement</i> can be used on any creature that has performed acts against its alignment, regardless of the actual alignment in question.</p><p>Note: Normally, changing alignment is up to the player. This use of <i>atonement</i> offers a method for a character to change his or her alignment drastically, suddenly, and definitively.</p></h4></div>"},
new Spell(){ Name = "Augury", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Augury</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 2, witch 2, shaman 2, occultist 2, psychic 2, spiritualist 2</h5><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (incense worth at least 25 gp), F (a set of marked sticks or bones worth at least 25 gp)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>instantaneous</h5></div><div><h4><p>An <i>augury</i> can tell you whether a particular action will bring good or bad results for you in the immediate future.</p><p>The base chance for receiving a meaningful reply is 70% + 1% per caster level, to a maximum of 90%; this roll is made secretly. A question may be so straightforward that a successful result is automatic, or so vague as to have no chance of success. If the <i>augury</i> succeeds, you get one of four results: <ul><li>Weal (if the action will probably bring good results).  <li>Woe (for bad results).  <li>Weal and woe (for both).  <li>Nothing (for actions that don't have especially good or bad results).</ul></p><p>If the spell fails, you get the ""nothing"" result. A cleric who gets the ""nothing"" result has no way to tell whether it was the consequence of a failed or successful <i>augury</i>.</p><p>The <i>augury</i> can see into the future only about half an hour, so anything that might happen after that does not affect the result. Thus, the result might not take into account the long-term consequences of a contemplated action. All <i>auguries</i> cast by the same person about the same topic use the same die result as the first casting.</p></h4></div>"},
new Spell(){ Name = "Awaken", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Awaken</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 5, shaman 6</h5><h5><b>Casting Time </b>24 hours</h5><h5><b>Components </b>V, S, M (herbs and oils worth 2,000 gp), DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> animal or tree touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You awaken a tree or animal to human-like sentience. To succeed, you must make a Will save (DC 10 + the animal's current HD, or the HD the tree will have once awakened). The awakened animal or tree is friendly toward you. You have no special empathy or connection with a creature you awaken, although it serves you in specific tasks or endeavors if you communicate your desires to it. If you cast <i>awaken</i> again, any previously awakened creatures remain friendly to you, but they no longer undertake tasks for you unless it is in their best interests.</p><p>An awakened tree has characteristics as if it were an animated object, except that it gains the plant type and its Intelligence, Wisdom, and Charisma scores are each 3d6. An awakened plant gains the ability to move its limbs, roots, vines, creepers, and so forth, and it has senses similar to a human's.</p><p>An awakened animal gets 3d6 Intelligence, +1d3 Charisma, and +2 HD. Its type becomes magical beast (augmented animal). An awakened animal can't serve as an animal companion, familiar, or special mount.</p><p>An awakened tree or animal can speak one language that you know, plus one additional language that you know per point of Intelligence bonus (if any). This spell does not function on an animal or plant with an Intelligence greater than 2.</p></h4></div>"},
new Spell(){ Name = "Bane", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bane</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [fear, mind-affecting]; <b>Level </b>antipaladin 1, cleric/oracle 1, inquisitor 1, shaman 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>50 ft.</h5><h5><b>Area </b>50-ft.-radius burst, centered on you</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Bane</i> fills your enemies with fear and doubt. Each affected creature takes a -1 penalty on attack rolls and a -1 penalty on saving throws against fear effects. <i>Bane</i> counters and dispels <i>bless</i>.</p></h4><h5><b>Mythic: </b>The -1 penalty applies on attack rolls, weapon damage rolls, and all saving throws. Each affected creature must roll its next attack roll or saving throw twice and take the lower result.</h5></div>"},
new Spell(){ Name = "Banishment", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Banishment </p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 6, sorcerer/wizard 7, summoner 5, inquisitor 5, shaman 6, psychic 7, spiritualist 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (see text)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one or more extraplanar creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>A <i>banishment</i> spell is a more powerful version of the <i>dismissal</i> spell. It enables you to force extraplanar creatures out of your home plane. As many as 2 Hit Dice of creatures per caster level can be banished.</p><p>You can improve the spell's chance of success by presenting at least one object or substance that the target hates, fears, or otherwise opposes. For each such object or substance, you gain a +1 bonus on your caster level check to overcome the target's  Spell Resistance (if any), and the saving throw DC increases by 2.</p><p>Certain rare items might work twice as well as a normal item for the purpose of the bonuses (each providing a +2 bonus on the caster level check against Spell Resistance and increasing the save DC by 4).</p></h4></div>"},
new Spell(){ Name = "Barkskin", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Barkskin</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 2, ranger 2, summoner 2, alchemist 2, shaman 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Barkskin</i> toughens a creature's skin. The effect grants a +2 enhancement bonus to the creature's existing natural armor bonus. This enhancement bonus increases by 1 for every three caster levels above 3rd, to a maximum of +5 at 12th level.</p><p>The enhancement bonus provided by <i>barkskin</i> stacks with the target's natural armor bonus, but not with other enhancement bonuses to natural armor. A creature without natural armor has an effective natural armor bonus of +0.</p></h4><h5><b>Mythic: </b>The target also gains DR/magic equal to the double the enhancement bonus the spell provides to the target's natural armor bonus.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the spell grants DR/epic rather than DR/magic.</h5></div>"},
new Spell(){ Name = "Bear's Endurance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bear's Endurance </p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 2, druid 2, ranger 2, sorcerer/wizard 2, alchemist 2, summoner 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a few hairs, or a pinch of dung, from a bear)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><div><h4><p>The affected creature gains greater vitality and stamina. The spell grants the subject a +4 enhancement bonus to Constitution, which adds the usual benefits to hit points, Fortitude saves, Constitution checks, and so forth. Hit points gained by a temporary increase in Constitution score are not temporary hit points. They go away when the subject's Constitution drops back to normal. They are not lost first as temporary hit points are.</p></h4></div>"},
new Spell(){ Name = "Bear's Endurance, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bear's Endurance, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 6, druid 6, sorcerer/wizard 6, summoner 4, magus 6, shaman 6, psychic 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a few hairs, or a pinch of dung, from a bear)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> creature one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><div><h4><p><i>Mass bear's endurance</i> works like <i>bear's endurance</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Beast Shape I", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Beast Shape I</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 3, alchemist 3, magus 3, bloodrager 3</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>When you cast this spell, you can assume the form of any Small or Medium creature of the animal type. If the form you assume has any of the following abilities, you gain the listed ability: climb 30 feet, fly 30 feet (average maneuverability), swim 30 feet, darkvision 60 feet, low-light vision, and scent.</p><p><i>Small animal</i>: If the form you take is that of a Small animal, you gain a +2 size bonus to your Dexterity and a +1 natural armor bonus.</p><p><i>Medium animal</i>: If the form you take is that of a Medium animal, you gain a +2 size bonus to your Strength and a +2 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Beast Shape II", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Beast Shape II</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 4, alchemist 4, magus 4, bloodrager 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>This spell functions as beast shape I, except that it also allows you to assume the form of a Tiny or Large creature of the animal type. If the form you assume has any of the following abilities, you gain the listed ability: climb 60 feet, fly 60 feet (good maneuverability), swim 60 feet, darkvision 60 feet, low-light vision, scent, grab, pounce, and trip.</p><p><i>Tiny</i> animal: If the form you take is that of a Tiny animal, you gain a +4 size bonus to your Dexterity, a -2 penalty to your Strength, and a +1 natural armor bonus.</p><p><i>Small animal</i>: If the form you take is that of a Small animal, you gain a +2 size bonus to your Dexterity and a +1 natural armor bonus.</p><p><i>Medium animal</i>: If the form you take is that of a Medium animal, you gain a +2 size bonus to your Strength and a +2 natural armor bonus.</p><p><i>Large animal</i>: If the form you take is that of a Large animal, you gain a +4 size bonus to your Strength, a -2 penalty to your Dexterity, and a +4 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Beast Shape III", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Beast Shape III</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 5, alchemist 5, magus 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>This spell functions as <i>beast shape II</i>, except that it also allows you to assume the form of a Diminutive or Huge creature of the animal type. This spell also allows you to take on the form of a Small or Medium creature of the magical beast type. If the form you assume has any of the following abilities, you gain the listed ability: burrow 30 feet, climb 90 feet, fly 90 feet (good maneuverability), swim 90 feet, blindsense 30 feet, darkvision 60 feet, low-light vision, scent, constrict, ferocity, grab, jet, poison, pounce, rake, trample, trip, and web.</p><p><i>Diminutive animal</i>: If the form you take is that of a Diminutive animal, you gain a +6 size bonus to your Dexterity, a -4 penalty to your Strength, and a +1 natural armor bonus.</p><p><i>Tiny animal</i>: If the form you take is that of a Tiny animal, you gain a +4 size bonus to your Dexterity, a -2 penalty to your Strength, and a +1 natural armor bonus.</p><p><i>Small animal</i>: If the form you take is that of a Small animal, you gain a +2 size bonus to your Dexterity and a +1 natural armor bonus.</p><p><i>Medium animal</i>: If the form you take is that of a Medium animal, you gain a +2 size bonus to your Strength and a +2 natural armor bonus.</p><p><i>Large animal</i>: If the form you take is that of a Large animal, you gain a +4 size bonus to your Strength, a -2 penalty to your Dexterity, and a +4 natural armor bonus.</p><p><i>Huge animal</i>: If the form you take is that of a Huge animal, you gain a +6 size bonus to your Strength, a -4 penalty to your Dexterity, and a +6 natural armor bonus.</p><p><i>Small magical beast</i>: If the form you take is that of a Small magical beast, you gain a +4 size bonus to your Dexterity, and a +2 natural armor bonus.</p><p><i>Medium magical beast</i>: If the form you take is that of a Medium magical beast, you gain a +4 size bonus to your Strength, and a +4 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Beast Shape IV", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Beast Shape IV</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 6, alchemist 6, magus 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><div><h4><p>This spell functions as <i>beast shape III</i> except that it also allows you to assume the form of a Tiny or Large creature of the magical beast type. If the form you assume has any of the following abilities, you gain the listed ability: burrow 60 feet, climb 90 feet, fly 120 feet (good maneuverability), swim 120 feet, blindsense 60 feet, darkvision 90 feet, low-light vision, scent, tremorsense 60 feet, breath weapon, constrict, ferocity, grab, jet, poison, pounce, rake, rend, roar, spikes, trample, trip, and web. If the creature has immunity or resistance to any elements, you gain resistance 20 to those elements. If the creature has vulnerability to an element, you gain that vulnerability.</p><p><i>Diminutive animal</i>: If the form you take is that of a Diminutive animal, you gain a +6 size bonus to your Dexterity, a -4 penalty to your Strength, and a +1 natural armor bonus.</p><p><i>Tiny animal</i>: If the form you take is that of a Tiny animal, you gain a +4 size bonus to your Dexterity, a -2 penalty to your Strength, and a +1 natural armor bonus.</p><p><i>Small animal</i>: If the form you take is that of a Small animal, you gain a +2 size bonus to your Dexterity and a +1 natural armor bonus.</p><p><i>Medium animal</i>: If the form you take is that of a Medium animal, you gain a +2 size bonus to your Strength and a +2 natural armor bonus.</p><p><i>Large animal</i>: If the form you take is that of a Large animal, you gain a +4 size bonus to your Strength, a -2 penalty to your Dexterity, and a +4 natural armor bonus.</p><p><i>Huge animal</i>: If the form you take is that of a Huge animal, you gain a +6 size bonus to your Strength, a -4 penalty to your Dexterity, and a +6 natural armor bonus.</p><p><i>Tiny magical beast</i>: If the form you take is that of a Tiny magical beast, you gain a -2 penalty to your Strength, a +8 size bonus to your Dexterity, and a +3 natural armor bonus.</p><p><i>Small magical beast</i>: If the form you take is that of a Small magical beast, you gain a +4 size bonus to your Dexterity, and a +2 natural armor bonus.</p><p><i>Medium magical beast</i>: If the form you take is that of a Medium magical beast, you gain a +4 size bonus to your Strength, and a +4 natural armor bonus.</p><p><i>Large magical beast</i>: If the form you take is that of a Large magical beast, you gain a +6 size bonus to your Strength, a -2 penalty on your Dexterity, a +2 size bonus to your Constitution, and a +6 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Bestow Curse", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bestow Curse </p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [curse]; <b>Level </b>cleric/oracle 3, sorcerer/wizard 4, witch 3, antipaladin 3, bloodrager 4, shaman 3, occultist 3, mesmerist 3, spiritualist 3</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You place a curse on the subject. Choose one of the following.</p><p><ul><li>-6 decrease to an ability score (minimum 1).  <li>-4 penalty on attack rolls, saves, ability checks, and skill checks.  <li>Each turn, the target has a 50% chance to act normally; otherwise, it takes no action.</p><p></ul>You may also invent your own curse, but it should be no more powerful than those described above.</p><p>The curse bestowed by this spell cannot be dispelled, but it can be removed with a <i>break enchantment</i>, <i>limited wish</i>, <i>miracle</i>, <i>remove curse</i>, or <i>wish</i> spell.</p><p><i>Bestow curse</i> counters <i>remove curse</i>.</p></h4></div>"},
new Spell(){ Name = "Binding", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Binding</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 8, summoner 6, psychic 8</h5><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (opals worth 500 gp per HD of the target creature, plus other components as specified below)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one living creature</h5><h5><b>Duration </b>see text (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><div><h4><p>A <i>binding</i> spell creates a magical restraint to hold a creature. The target gets an initial saving throw only if its Hit Dice equal at least half your caster level.</p><p>You may have as many as six assistants help you with the spell. For each assistant who casts <i>suggestion</i>, your caster level for this casting of <i>binding</i> increases by 1. For each assistant who casts <i>dominate animal</i>, <i>dominate person</i>, or <i>dominate monster</i>, your caster level for this casting of binding increases by a number equal to a third of that assistant's level, provided that the spell's target is appropriate for a <i>binding</i> spell. Since the assistants' spells are cast simply to improve your caster level for the purpose of the <i>binding</i> spell, saving throws and spell resistance against the assistants' spells are irrelevant. Your caster level determines whether the target gets an initial Will saving throw and how long the <i>binding</i> lasts. All <i>binding</i> spells are dismissible.</p><p>Regardless of the version of <i>binding</i> you cast, you can specify triggering conditions that end the spell and release the creature whenever they occur. These triggers can be as simple or elaborate as you desire, but the condition must be reasonable and have a likelihood of coming to pass. The conditions can be based on a creature's name, identity, or alignment, but otherwise must be based on observable actions or qualities. Intangibles such as level, class, Hit Dice, or hit points don't qualify. Once the spell is cast, its triggering conditions cannot be changed. Setting a release condition increases the save DC (assuming a saving throw is allowed) by 2.</p><p>If you cast any of the first three versions of <i>binding</i> (those with limited durations), you may cast additional <i>binding</i> spells to prolong the effect, overlapping the durations. If you do so, the target gets a saving throw at the end of the first spell's duration, even if your caster level was high enough to disallow an initial saving throw. If the creature's save succeeds, all <i>binding</i> spells it has received are broken.</p><p>The <i>binding</i> spell has six versions. Choose one of the following versions when you cast the spell.</p><p><i>Chaining</i>: The subject is confined by restraints that generate an <i>antipathy</i> spell affecting all creatures who approach the subject, except you. The duration is 1 year per caster level. The subject of this form of <i>binding</i> is confined to the spot it occupied when it received the spell. Casting this version requires a chain that is long enough to wrap around the creature three times.</p><p><i>Slumber</i>: This version causes the subject to become comatose for as long as 1 year per caster level. The subject does not need to eat or drink while slumbering, nor does it age. This form of <i>binding</i> is slightly easier to resist. Reduce the spell's save DC by 1. Casting this version requires a jar of sand or rose petals. This is a <i>sleep</i> effect.</p><p><i>Bound Slumber</i>: This combination of chaining and slumber lasts for as long as 1 month per caster level. Reduce the save DC by 2. Casting this version requires both a long chain and a jar of sand or rose petals. This is a <i>sleep</i> effect.</p><p><i>Hedged Prison</i>: The subject is transported to or otherwise brought within a confined area from which it cannot wander by any means. This effect is permanent. Reduce the save DC by 3. Casting this version requires a tiny golden cage worth 100 gp that is consumed when the spell is cast.</p><p><i>Metamorphosis</i>: The subject assumes <i>gaseous form</i>, except for its head or face. It is held harmless in a jar or other container, which may be transparent if you so choose. The creature remains aware of its surroundings and can speak, but it cannot leave the container, attack, or use any of its powers or abilities. The <i>binding</i> is permanent. The subject does not need to breathe, eat, or drink while metamorphosed, nor does it age. Reduce the save DC by 4.</p><p><i>Minimus Containment</i>: The subject is shrunk to a height of 1 inch or less and held within some gem, jar, or similar object. The <i>binding</i> is permanent. The subject does not need to breathe, eat, or drink while contained, nor does it age. Reduce the save DC by 4.</p><p>You can't dispel a <i>binding</i> spell with <i>dispel magic</i> or a similar effect, though an <i>antimagic field</i> or <i>mage's disjunction</i> affects it normally. A bound extraplanar creature cannot be sent back to its home plane by <i>dismissal</i>, <i>banishment</i>, or a similar effect.</p></h4></div>"},
new Spell(){ Name = "Black Tentacles", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Black Tentacles</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 4, witch 4, summoner 3, magus 4, bloodrager 4, psychic 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (octopus or squid tentacle)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>20-ft.-radius spread</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes a field of rubbery <i>black tentacles</i> to appear, burrowing up from the floor and reaching for any creature in the area.</p><p>Every creature within the area of the spell is the target of a combat maneuver check made to grapple each round at the beginning of your turn, including the round that <i>black tentacles</i> is cast. Creatures that enter the area of effect are also automatically attacked. The tentacles do not provoke attacks of opportunity. When determining the tentacles' CMB, the tentacles use your caster level as their base attack bonus and receive a +4 bonus due to their Strength and a +1 size bonus. Roll only once for the entire spell effect each round and apply the result to all creatures in the area of effect.</p><p>If the tentacles succeed in grappling a foe, that foe takes 1d6+4 points of damage and gains the grappled condition. Grappled opponents cannot move without first breaking the grapple. All other movement is prohibited unless the creature breaks the grapple first. The <i>black tentacles</i> spell receives a +5 bonus on grapple checks made against opponents it is already grappling, but cannot move foes or pin foes. Each round that <i>black tentacles</i> succeeds on a grapple check, it deals an additional 1d6+4 points of damage. The CMD of <i>black tentacles</i>, for the purposes of escaping the grapple, is equal to 10 + its CMB.</p><p>The tentacles created by this spell cannot be damaged, but they can be dispelled as normal. The entire area of effect is considered difficult terrain while the tentacles last.</p></h4><h5><b>Mythic: </b>Add your tier to the base attack bonus of the tentacles. The tentacles also deal an additional 2d6 points of acid damage with a successful grapple.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the spell creates twice as many tentacles in the same area, meaning each creature in the area is attacked twice per round. The tentacles can grapple creatures that are immune to grappling if that immunity is from a non-mythic source, but combat maneuver checks to grapple such creatures take a -5 penalty.</h5></div>"},
new Spell(){ Name = "Blade Barrier", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Blade Barrier</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>cleric/oracle 6, inquisitor 6, occultist 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>wall of whirling blades up to 20 ft. long/level, or a ringed wall of whirling blades with a radius of up to 5 ft. per two levels; either form is 20 ft. high</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Reflex half or Reflex negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An immobile, vertical curtain of whirling blades shaped of pure force springs into existence. Any creature passing through the wall takes 1d6 points of damage per caster level (maximum 15d6), with a Reflex save for half damage.</p><p>If you evoke the barrier so that it appears where creatures are, each creature takes damage as if passing through the wall.</p><p>Each such creature can avoid the wall (ending up on the side of its choice) and thus take no damage by making a successful Reflex save.</p><p>A <i>blade barrier</i> provides cover (+4 bonus to AC, +2 bonus on Reflex saves) against attacks made through it.</p></h4><h5><b>Mythic: </b>You may cast this spell as an immediate action. The barrier's damage increases to 1d8 per caster level (maximum 20d8).</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, you can spend a move action to move the barrier up to 10 feet in any direction. The shape (flat or ring) and orientation (vertical or horizontal) of the wall must remain the same. If you move the barrier through a creature's space, it can attempt a saving throw to avoid the wall, as if you had created it where the creature is.</h5></div>"},
new Spell(){ Name = "Blasphemy", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Blasphemy</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [evil, sonic]; <b>Level </b>cleric/oracle 7, inquisitor 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>40 ft.</h5><h5><b>Area </b>nonevil creatures in a 40-ft.-radius spread centered on you</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any nonevil creature within the area of a <i>blasphemy</i> spell suffers the following ill effects.   <table border ='1'><tr><th>HD</th><th>Effect</th></tr><tr><td>Equal to caster level</td><td>Dazed</td></tr><tr><td>Up to caster level -1</td><td>Weakened, dazed</td></tr><tr><td>Up to caster level -5</td><td>Paralyzed, weakened, dazed</td></tr><tr><td>Up to caster level -10</td><td>Killed, paralyzed, weakened, dazed</td></tr></table>   The effects are cumulative and concurrent. A successful Will save reduces or eliminates these effects. Creatures affected by multiple effects make only one save and apply the result to all the effects.</p><p><i>Dazed</i>: The creature can take no actions for 1 round, though it defends itself normally. Save negates.</p><p><i>Weakened</i>: The creature's Strength score decreases by 2d6 points for 2d4 rounds. Save for half.</p><p><i>Paralyzed</i>: The creature is paralyzed and helpless for 1d10 minutes. Save reduces the paralyzed effect to 1 round.</p><p><i>Killed</i>: Living creatures die. Undead creatures are destroyed. Save negates. If the save is successful, the creature instead takes 3d6 points of damage + 1 point per caster level (maximum +25).</p><p>Furthermore, if you are on your home plane when you cast this spell, nonevil extraplanar creatures within the area are instantly banished back to their home planes. Creatures so banished cannot return for at least 24 hours. This effect takes place regardless of whether the creatures hear the <i>blasphemy</i> or not. The banishment effect allows a Will save (at a -4 penalty) to negate.</p><p>Creatures whose Hit Dice exceed your caster level are unaffected by <i>blasphemy</i>.</p></h4><h5><b>Mythic: </b>When determining the spell's effect on non-mythic creatures, add your tier to your caster level. Nonevil creatures that fail the save take a -4 penalty on attack rolls and saving throws, and their spell resistance decreases by 5 for as long as the spell's other effects last.</h5></div>"},
new Spell(){ Name = "Bleed", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bleed</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 0, inquisitor 0, sorcerer/wizard 0, witch 0, shaman 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You cause a living creature that is below 0 hit points but stabilized to resume dying. Upon casting this spell, you target a living creature that has -1 or fewer hit points. That creature begins dying, taking 1 point of damage per round. The creature can be stabilized later normally. This spell causes a creature that is dying to take 1 point of damage.</p></h4></div>"},
new Spell(){ Name = "Bless", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bless</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>cleric/oracle 1, paladin 1, inquisitor 1, shaman 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>50 ft.</h5><h5><b>Area </b>The caster and all allies within a 50-ft. burst, centered on the caster</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Bless</i> fills your allies with courage. Each ally gains a +1 morale bonus on attack rolls and on saving throws against fear effects.</p><p><i>Bless</i> counters and dispels <i>bane</i>.</p></h4><h5><b>Mythic: </b>The +1 morale bonus applies on attack rolls, weapon damage rolls, and all saving throws. Once during the spell's duration, an affected creature can roll an attack roll or saving throw twice and take the higher result. The target must decide to use this ability before the first roll is attempted.</h5></div>"},
new Spell(){ Name = "Bless Water", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bless Water</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [good]; <b>Level </b>cleric/oracle 1, paladin 1, inquisitor 1</h5><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (5 pounds of powdered silver worth 25 gp)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> flask of water touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><div><h4><p>This transmutation imbues a flask (1 pint) of water with positive energy, turning it into holy water.</p></h4></div>"},
new Spell(){ Name = "Bless Weapon", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bless Weapon</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>paladin 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> weapon touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This transmutation makes a weapon strike true against evil foes. The weapon is treated as having a +1 enhancement bonus for the purpose of bypassing the DR of evil creatures or striking evil incorporeal creatures (though the spell doesn't grant an actual enhancement bonus). The weapon also becomes good-aligned, which means it can bypass the DR of certain creatures. (This effect overrides and suppresses any other alignment the weapon might have.) Individual arrows or bolts can be transmuted, but affected projectile weapons (such as bows) don't confer the benefit to the projectiles they shoot.</p><p>In addition, all critical hit rolls against evil foes are automatically successful, so every threat is a critical hit. This last effect does not apply to any weapon that already has a magical effect related to critical hits, such as a <i>keen</i> weapon or a <i>vorpal sword</i>.</p></h4></div>"},
new Spell(){ Name = "Blight", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Blight</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>druid 4, sorcerer/wizard 5, witch 5, shaman 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude half; see text; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell withers a single plant of any size. An affected plant creature takes 1d6 points of damage per level (maximum 15d6) and may attempt a Fortitude saving throw for half damage. A plant that isn't a creature doesn't receive a save and immediately withers and dies.</p><p>This spell has no effect on the soil or surrounding plant life.</p></h4></div>"},
new Spell(){ Name = "Blindness/Deafness", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Blindness/Deafness</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>antipaladin 2, bard 2, cleric/oracle 3, sorcerer/wizard 2, witch 2, bloodrager 2, shaman 3, psychic 2, mesmerist 2, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>permanent (D)</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You call upon the powers of unlife to render the subject blinded or deafened, as you choose.</p></h4><h5><b>Mythic: </b>You can cause the target to be both blinded and deafened at the same time. The target must attempt a saving throw against each effect separately.</h5></div>"},
new Spell(){ Name = "Blink", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Blink</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 3, sorcerer/wizard 3, magus 3, psychic 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 round/level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You ""blink"" quickly back and forth between the Material Plane and the Ethereal Plane and look as though you're winking in and out of reality at random. <i>Blink</i> has several effects, as follows.</p><p>Physical attacks against you have a 50% miss chance, and the Blind-Fight feat doesn't help opponents, since you're ethereal and not merely invisible. If the attack is capable of striking ethereal creatures, the miss chance is only 20% (for concealment).</p><p>If the attacker can see invisible creatures, the miss chance is also only 20%. (For an attacker who can both see and strike ethereal creatures, there is no miss chance.) Likewise, your own attacks have a 20% miss chance, since you sometimes go ethereal just as you are about to strike.</p><p>Any individually targeted spell has a 50% chance to fail against you while you're blinking unless your attacker can target invisible, ethereal creatures. Your own spells have a 20% chance to activate just as you go ethereal, in which case they typically do not affect the Material Plane (but they might affect targets on the Ethereal Plane).</p><p>While blinking, you take only half damage from area attacks (but full damage from those that extend onto the Ethereal Plane).</p><p>Although you are only partially visible, you are not considered invisible and targets retain their Dexterity bonus to AC against your attacks. You do receive a +2 bonus on attack rolls made against enemies that cannot see invisible creatures.</p><p>You take only half damage from falling, since you fall only while you are material.</p><p>While blinking, you can step through (but not see through) solid objects. For each 5 feet of solid material you walk through, there is a 50% chance that you become material. If this occurs, you are shunted off to the nearest open space and take 1d6 points of damage per 5 feet so traveled.</p><p>Since you spend about half your time on the Ethereal Plane, you can see and even attack ethereal creatures. You interact with ethereal creatures roughly the same way you interact with material ones.</p><p>An ethereal creature is invisible, incorporeal, and capable of moving in any direction, even up or down. As an incorporeal creature, you can move through solid objects, including living creatures.</p><p>An ethereal creature can see and hear the Material Plane, but everything looks gray and insubstantial. Sight and hearing on the Material Plane are limited to 60 feet.</p><p>Force effects and abjurations affect you normally. Their effects extend onto the Ethereal Plane from the Material Plane, but not vice versa. An ethereal creature can't attack material creatures, and spells you cast while ethereal affect only other ethereal things.</p><p>Certain material creatures or objects have attacks or effects that work on the Ethereal Plane. Treat other ethereal creatures and objects as material.</p></h4><h5><b>Mythic: </b>You may spend a move action to remain corporeal or incorporeal until the end of your turn (you automatically resume blinking at the end of your turn).</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, you can spend either a swift action or a move action to remain corporeal or incorporeal until the end of your turn.</h5></div>"},
new Spell(){ Name = "Blur", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Blur</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 2, sorcerer/wizard 2, alchemist 2, summoner 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject's outline appears blurred, shifting, and wavering. This distortion grants the subject concealment (20% miss chance).</p><p>A <i>see invisibility</i> spell does not counteract the <i>blur</i> effect, but a <i>true seeing</i> spell does.</p><p>Opponents that cannot see the subject ignore the spell's effect (though fighting an unseen opponent carries penalties of its own).</p></h4></div>"},
new Spell(){ Name = "Break Enchantment", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Break Enchantment</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 4, cleric/oracle 5, paladin 4, sorcerer/wizard 5, witch 5, inquisitor 5, shaman 5, occultist 4, psychic 4, mesmerist 4, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>up to one creature per level, all within 30 ft. of each other</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell frees victims from enchantments, transmutations, and curses. <i>Break enchantment</i> can reverse even an instantaneous effect. For each such effect, you make a caster level check (1d20 + caster level, maximum +15) against a DC of 11 + caster level of the effect. Success means that the creature is free of the spell, curse, or effect. For a cursed magic item, the DC is equal to the DC of the curse.</p><p>If the spell is one that cannot be dispelled by <i>dispel magic</i> or <i>stone to flesh</i>, <i>break enchantment</i> works only if that spell is 5th level or lower.</p><p>If the effect comes from a permanent magic item, <i>break enchantment</i> does not remove the curse from the item, but it does free the victim from the item's effects.</p></h4><h5><b>Mythic: </b>Against non-mythic effects, the spell automatically succeeds without requiring a caster level check. Against mythic effects, add your tier to your caster level check. The maximum level of spell this spell can remove is 5 + half your tier.</h5><h5><b>Augmented (7th)</b>: If you expend two uses of mythic power, you can apply a removed enchantment, transmutation, or curse to the creature that originally cast it. The creature must be a valid target of the spell and on the same plane as you, and it gets a saving throw at the effect's original DC.</h5></div>"},
new Spell(){ Name = "Breath Of Life", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Breath Of Life</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 5, shaman 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless) or Will half, see text; <b>Spell Resistance </b>yes (harmless) or yes, see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell cures 5d8 points of damage + 1 point per caster level (maximum +25).</p><p>Unlike other spells that heal damage, <i>breath of life</i> can bring recently slain creatures back to life. If cast upon a creature that has died within 1 round, apply the healing from this spell to the creature. If the healed creature's hit point total is at a negative amount less than its Constitution score, it comes back to life and stabilizes at its new hit point total. If the creature's hit point total is at a negative amount equal to or greater than its Constitution score, the creature remains dead. Creatures brought back to life through <i>breath of life</i> gain a temporary negative level that lasts for 1 day.</p><p>Creatures slain by death effects cannot be saved by <i>breath of life</i>.</p><p>Like cure spells, <i>breath of life</i> deals damage to undead creatures rather than curing them, and cannot bring them back to life.</p></h4><h5><b>Mythic: </b>The spell heals 5d12 points of damage + 1 point per caster level (maximum +25). It can revive a creature that died of hp damage within the past 2 rounds. If the target was slain by a death effect that allows a saving throw, this spell allows the creature to attempt another save against that effect. If this save is successful, the creature is revived with -10 hp plus the amount healed by this spell.</h5><h5><b>Augmented (9th)</b>: If you expend two uses of mythic power, you can target up to one creature per caster level within 30 feet of you. Alternatively, you can target one creature that has been dead for a number of rounds up to your caster level.</h5></div>"},
new Spell(){ Name = "Bull's Strength", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bull's Strength</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 2, druid 2, paladin 2, sorcerer/wizard 2, summoner 2, alchemist 2, antipaladin 2, magus 2, bloodrager 2, shaman 2, psychic 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a few hairs, or a pinch of dung, from a bull)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject becomes stronger. The spell grants a +4 enhancement bonus to Strength, adding the usual benefits to melee attack rolls, melee damage rolls, and other uses of the Strength modifier.</p></h4></div>"},
new Spell(){ Name = "Bull's Strength, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Bull's Strength, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 6, druid 6, sorcerer/wizard 6, summoner 4, magus 6, shaman 6, psychic 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a few hairs, or a pinch of dung, from a bull)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>This spell functions like <i>bull's strength</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Burning Hands", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Burning Hands</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>sorcerer/wizard 1, witch 1, magus 1, bloodrager 1, shaman 1, occultist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>15 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A cone of searing flame shoots from your fingertips. Any creature in the area of the flames takes 1d4 points of fire damage per caster level (maximum 5d4). Flammable materials burn if the flames touch them. A character can extinguish burning items as a full-round action.</p></h4><h5><b>Mythic: </b>The range increases to 20 feet, and the damage dealt increases to 1d6 points of fire damage per caster level (maximum 5d6).</h5></div>"},
new Spell(){ Name = "Call Lightning", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Call Lightning</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [electricity]; <b>Level </b>druid 3, shaman 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>one or more 30-ft.-long vertical lines of lightning</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Immediately upon completion of the spell, and once per round thereafter, you may call down a 5-foot-wide, 30-foot-long, vertical bolt of lightning that deals 3d6 points of electricity damage. The bolt of lightning flashes down in a vertical stroke at whatever target point you choose within the spell's range (measured from your position at the time). Any creature in the target square or in the path of the bolt is affected.</p><p>You need not call a bolt of lightning immediately; other actions, even spellcasting, can be performed first. Each round after the first you may use a standard action (concentrating on the spell) to call a bolt. You may call a total number of bolts equal to your caster level (maximum 10 bolts).</p><p>If you are outdoors and in a stormy area-a rain shower, clouds and wind, hot and cloudy conditions, or even a tornado (including a whirlwind formed by a djinni or an air elemental of at least Large size)-each bolt deals 3d10 points of electricity damage instead of 3d6.</p><p>This spell functions indoors or underground but not underwater.</p></h4><h5><b>Mythic: </b>Each lightning bolt's damage increases to 3d10 points of damage (or 5d10 points outdoors in stormy weather). Half of this damage is electricity damage, and the other half is sonic. Creatures that succeed at their saving throws are dazzled and deafened for 1 round. Those that fail at their saving throws are dazzled and deafened for 1 minute.</h5></div>"},
new Spell(){ Name = "Call Lightning Storm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Call Lightning Storm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [electricity]; <b>Level </b>druid 5, shaman 5, occultist 5</h5><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>one or more 30-ft.-long vertical lines of lightning</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>call lightning</i>, except that each bolt deals 5d6 points of electricity damage (or 5d10 if created outdoors in a stormy area), and you may call a maximum of 15 bolts.</p></h4></div>"},
new Spell(){ Name = "Calm Animals", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Calm Animals</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, emotion]; <b>Level </b>druid 1, ranger 1, shaman 1, psychic 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> animals within 30 ft. of each other</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell soothes and quiets animals, rendering them docile and harmless. Only ordinary animals (those with Intelligence scores of 1 or 2) can be affected by this spell. All the subjects must be of the same kind, and no two may be more than 30 feet apart. The maximum number of HD of animals you can affect is equal to 2d4 + caster level.</p><p>The affected creatures remain where they are and do not attack or flee. They are not helpless and defend themselves normally if attacked. Any threat breaks the spell on the threatened creatures.</p></h4></div>"},
new Spell(){ Name = "Calm Emotions", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Calm Emotions</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, emotion]; <b>Level </b>bard 2, cleric/oracle 2, inquisitor 2, shaman 2, occultist 2, psychic 2, mesmerist 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>creatures in a 20-ft.-radius spread</h5><h5><b>Duration </b>concentration, up to 1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell calms agitated creatures. You have no control over the affected creatures, but <i>calm emotions</i> can stop raging creatures from fighting or joyous ones from reveling. Creatures so affected cannot take violent actions (although they can defend themselves) or do anything destructive. Any aggressive action against or damage dealt to a calmed creature immediately breaks the spell on all calmed creatures.</p><p>This spell automatically suppresses (but does not dispel) any morale bonuses granted by spells such as <i>bless</i>, <i>good hope</i>, and <i>rage</i>, and also negates a bard's ability to inspire courage or a barbarian's rage ability. It also suppresses any fear effects and removes the confused condition from all targets. While the spell lasts, a suppressed spell, condition, or effect has no effect. When the <i>calm emotions spell</i> ends, the original spell or effect takes hold of the creature again, provided that its duration has not expired in the meantime.</p></h4></div>"},
new Spell(){ Name = "Cat's Grace", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cat's Grace</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 2, druid 2, ranger 2, sorcerer/wizard 2, alchemist 2, summoner 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (pinch of cat fur)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><div><h4><p>The transmuted creature becomes more graceful, agile, and coordinated. The spell grants a +4 enhancement bonus to Dexterity, adding the usual benefits to AC, Reflex saves, and other uses of the Dexterity modifier.</p></h4></div>"},
new Spell(){ Name = "Cat's Grace, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cat's Grace, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 6, druid 6, sorcerer/wizard 6, , summoner 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (pinch of cat fur)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>cat's grace</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Cause Fear", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cause Fear</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [emotion, fear, mind-affecting]; <b>Level </b>antipaladin 1, bard 1, cleric/oracle 1, inquisitor 1, sorcerer/wizard 1, witch 1, bloodrager 1, shaman 1, occultist 1, psychic 1, mesmerist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature with 5 or fewer HD</h5><h5><b>Duration </b>1d4 rounds or 1 round; see text</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The affected creature becomes frightened. If the subject succeeds on a Will save, it is shaken for 1 round. Creatures with 6 or more HD are immune to this effect. Cause fear counters and dispels remove fear.</p></h4></div>"},
new Spell(){ Name = "Chain Lightning", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Chain Lightning</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [electricity]; <b>Level </b>sorcerer/wizard 6, witch 7, magus 6, occultist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a bit of fur; a piece of amber, glass, or a crystal rod; plus one silver pin per caster level)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Targets </b>one primary target, plus one secondary target/level (each of which must be within 30 ft. of the primary target)</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates an electrical discharge that begins as a single stroke commencing from your fingertips. Unlike <i>lightning bolt</i>, <i>chain lightning</i> strikes one object or creature initially, then arcs to other targets.</p><p>The bolt deals 1d6 points of electricity damage per caster level (maximum 20d6) to the primary target. After it strikes, lightning can arc to a number of secondary targets equal to your caster level (maximum 20). The secondary bolts each strike one target and deal as much damage as the primary bolt.</p><p>Each target can attempt a Reflex saving throw for half damage.</p><p>The Reflex DC to halve the damage of the secondary bolts is 2 lower than the DC to halve the damage of the primary bolt. You choose secondary targets as you like, but they must all be within 30 feet of the primary target, and no target can be struck more than once. You can choose to affect fewer secondary targets than the maximum.</p></h4><h5><b>Mythic: </b>This spell deals 1d10 points of damage per caster level (maximum 20d10) and the save DC isn't reduced for secondary targets. Secondary targets have to be within 30 feet of any other target, not necessarily the primary target.</h5></div>"},
new Spell(){ Name = "Changestaff", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Changestaff</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, F (a quarterstaff that has been carved and polished for 28 days)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>your touched staff</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You change a specially prepared quarterstaff into a Huge treant-like creature (see the <i>Pathfinder RPG</i> Bestiary), about 24 feet tall.</p><p>When you plant the end of the staff in the ground and speak a special command to conclude the casting of the spell, your staff turns into a creature that looks and fights just like a treant.</p><p>The staff-treant defends you and obeys any spoken commands.</p><p>However, it is by no means a true treant; it cannot converse with actual treants or control trees. If the staff-treant is reduced to 0 or fewer hit points, it crumbles to powder and the staff is destroyed.</p><p>Otherwise, the staff returns to its normal form when the spell duration expires (or when the spell is dismissed), and it can be used as the focus for another casting of the spell. The staff-treant is always at full strength when created, despite any wounds it may have incurred the last time it appeared.</p></h4><h5><b>Mythic: </b>The treant-like creature created with this spell is considered a mythic creature, and it gains DR 10/epic and slashing instead of gaining DR 10/slashing.</h5><h5><b>Augmented (7th)</b>: If you expend two uses of mythic power, the creature gains the savage mythic template (see page 224).</h5></div>"},
new Spell(){ Name = "Chaos Hammer", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Chaos Hammer</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [chaotic]; <b>Level </b>cleric/oracle 4, inquisitor 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>20-ft.-radius burst</h5><h5><b>Duration </b>instantaneous (1d6 rounds); see text</h5><h5><b>Saving Throw </b>Will partial; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You unleash chaotic power to smite your enemies. The power takes the form of a multicolored explosion of leaping, ricocheting energy. Only lawful and neutral (not chaotic) creatures are harmed by the spell.</p><p>The spell deals 1d8 points of damage per two caster levels (maximum 5d8) to lawful creatures (or 1d6 points of damage per caster level, maximum 10d6, to lawful outsiders) and <i>slow</i>s them for 1d6 rounds (see the <i>slow</i> spell). A successful Will save reduces the damage by half and negates the <i>slow</i> effect.</p><p>The spell deals only half damage against creatures who are neither lawful nor chaotic, and they are not <i>slow</i>ed. Such a creature can reduce the damage by half again (down to one-quarter) with a successful Will save.</p></h4><h5><b>Mythic: </b>The damage dealt to lawful outsiders increases to 1d10 points of damage per caster level (maximum 10d10), and the damage dealt to other lawful creatures increases to 1d12 points of damage per 2 caster levels (maximum 5d12). The duration of slow increases to 2d6 rounds. Lawful creatures that succeed at their saves are slowed for 1 round.</h5></div>"},
new Spell(){ Name = "Charm Person", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Charm Person</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (charm) [mind-affecting]; <b>Level </b>bard 1, sorcerer/wizard 1, witch 1, shaman 1, occultist 1, psychic 1, mesmerist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one humanoid creature</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This charm makes a humanoid creature regard you as its trusted friend and ally (treat the target's attitude as friendly). If the creature is currently being threatened or attacked by you or your allies, however, it receives a +5 bonus on its saving throw.</p><p>The spell does not enable you to control the charmed person as if it were an automaton, but it perceives your words and actions in the most favorable way. You can try to give the subject orders, but you must win an opposed Charisma check to convince it to do anything it wouldn't ordinarily do. (Retries are not allowed.) An affected creature never obeys suicidal or obviously harmful orders, but it might be convinced that something very dangerous is worth doing. Any act by you or your apparent allies that threatens the charmed person breaks the spell. You must speak the person's language to communicate your commands, or else be good at pantomiming.</p></h4></div>"},
new Spell(){ Name = "Charm Animal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Charm Animal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (charm) [mind-affecting]; <b>Level </b>druid 1, ranger 1, shaman 1, psychic 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one animal</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>charm person</i>, except that it affects a creature of the animal type.</p></h4></div>"},
new Spell(){ Name = "Charm Monster", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Charm Monster</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (charm) [mind-affecting]; <b>Level </b>bard 3, sorcerer/wizard 4, summoner 3, witch 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one living creature</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>charm person</i>, except that the effect is not restricted by creature type or size.</p></h4></div>"},
new Spell(){ Name = "Charm Monster, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Charm Monster, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (charm) [mind-affecting]; <b>Level </b>bard 6, sorcerer/wizard 8, summoner 6, summoner 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> One or more creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>charm monster</i>, except that mass charm monster affects a number of creatures whose combined HD do not exceed twice your level, or at least one creature regardless of HD. If there are more potential targets than you can affect, you choose them one at a time until you must choose a creature with too many HD to affect.</p></h4></div>"},
new Spell(){ Name = "Chill Metal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Chill Metal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [cold]; <b>Level </b>druid 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>metal equipment of one creature per two levels, no two of which can be more than 30 ft. apart; or 25 lbs. of metal/level, none of which can be more than 30 ft. away from any of the rest</h5><h5><b>Duration </b>7 rounds</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Chill metal</i> makes metal extremely cold. Unattended, nonmagical metal gets no saving throw. Magical metal is allowed a saving throw against the spell. An item in a creature's possession uses the creature's saving throw bonus unless its own is higher.</p><p>A creature takes cold damage if its equipment is chilled. It takes full damage if its armor, shield, or weapon is affected. The creature takes minimum damage (1 point or 2 points; see the table) if it's not wearing or wielding such an item.</p><p>On the first round of the spell, the metal becomes chilly and uncomfortable to touch but deals no damage. The same effect also occurs on the last round of the spell's duration. During the second (and also the next-to-last) round, icy coldness causes pain and damage. In the third, fourth, and fifth rounds, the metal is freezing cold, and causes more damage, as shown on the table below. <table border ='1'><tr><th>Round</th><th>Metal Temperature Damage</th></tr><tr><td>1</td><td>Cold None</td></tr><tr><td>2</td><td>Icy 1d4 points</td></tr><tr><td>3-5</td><td>Freezing 2d4 points</td></tr><tr><td>6</td><td>Icy 1d4 points</td></tr><tr><td>7</td><td>Cold None</td></tr></table>  Any heat intense enough to damage the creature negates cold damage from the spell (and vice versa) on a point-for-point basis.</p><p>Underwater, <i>chill metal</i> deals no damage, but ice immediately forms around the affected metal, making it float if unattended.</p><p><i>Chill metal</i> counters and dispels <i>heat metal</i>.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d8 points of cold damage on rounds 2 and 6, and to 2d8 points of cold damage on rounds 3 through 5. Each round a creature takes cold damage from this spell, it also takes 1 point of Dexterity damage (Fortitude negates).</h5></div>"},
new Spell(){ Name = "Chill Touch", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Chill Touch</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 1, witch 1, magus 1, bloodrager 1, shaman 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature or creatures touched (up to one/level)</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial or Will negates; see text; <b>Spell Resistance </b>yes</h5></div><div><h4><p>A touch from your hand, which glows with blue energy, disrupts the life force of living creatures. Each touch channels negative energy that deals 1d6 points of damage. The touched creature also takes 1 point of Strength damage unless it makes a successful Fortitude saving throw. You can use this melee touch attack up to one time per level.</p><p>An undead creature you touch takes no damage of either sort, but it must make a successful Will saving throw or flee as if panicked for 1d4 rounds + 1 round per caster level.</p></h4></div>"},
new Spell(){ Name = "Circle Of Death", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Circle Of Death</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [death]; <b>Level </b>sorcerer/wizard 6, inquisitor 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a crushed black pearl worth 500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>several living creatures within a 40-ft.-radius burst</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Circle of death</i> snuffs out the life force of living creatures, killing them instantly. The spell slays 1d4 HD worth of living creatures per caster level (maximum 20d4). Creatures with the fewest HD are affected first; among creatures with equal HD, those who are closest to the burst's point of origin are affected first. No creature of 9 or more HD can be affected, and HD that are not sufficient to affect a creature are wasted.</p></h4><h5><b>Mythic: </b>The number of Hit Dice worth of creatures killed by the spell increases to 1d6 per caster level (maximum 20d6). Add your tier to the maximum HD of creatures that can be affected by the spell. (For example, at 3rd tier, a creature with 12 HD cannot be affected.)</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, creatures that succeed at their saves against this spell take 1d6 points of damage per tier.</h5></div>"},
new Spell(){ Name = "Clairaudience/Clairvoyance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Clairaudience/Clairvoyance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination (scrying); <b>Level </b>bard 3, sorcerer/wizard 3, witch 3, shaman 3, occultist 3, psychic 3, mesmerist 3, spiritualist 3, alchemist 2, witch 2, inquisitor 2, witch 8, witch 3</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, F/DF (a small horn or a glass eye)</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>magical sensor</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p><i>Clairaudience/clairvoyance</i> creates an invisible magical sensor at a specific location that enables you to hear or see (your choice) almost as if you were there. You don't need line of sight or line of effect, but the locale must be known--a place familiar to you, or an obvious one. Once you have selected the locale, the sensor doesn't move, but you can rotate it in all directions to view the area as desired. Unlike other scrying spells, this spell does not allow magically or supernaturally enhanced senses to work through it. If the chosen locale is magically dark, you see nothing. If it is naturally pitch black, you can see in a 10-foot radius around the center of the spell's effect. <i>Clairaudience/clairvoyance</i> functions only on the plane of existence you are currently occupying.</p></h4></div>"},
new Spell(){ Name = "Clenched Fist", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Clenched Fist</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 8, psychic 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a leather glove)</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>10-ft. hand</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>interposing hand</i>, except that the hand can also push or strike one opponent that you select. The floating hand can move as far as 60 feet and can attack in the same round. Since this hand is directed by you, its ability to notice or attack invisible or concealed creatures is no better than yours.</p><p>The hand attacks once per round, and its attack bonus equals your caster level + your Intelligence, Wisdom, or Charisma modifier (for a wizard, cleric, or sorcerer, respectively) + 11 for the hand's Strength score (33), -1 for being Large. The hand deals 1d8+11 points of damage on each attack, and any creature struck must make a Fortitude save (against this spell's save DC) or be stunned for 1 round. Directing the spell to a new target is a move action.</p><p>The <i>clenched fist</i> can also interpose itself as <i>interposing hand</i> does, or it can bull rush an opponent as <i>forceful hand</i> does. Its CMB for bull rush checks uses your caster level in place of its base attack bonus, with a +11 bonus for its Strength score and a +1 bonus for being Large.</p></h4></div>"},
new Spell(){ Name = "Cloak of Chaos", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cloak of Chaos</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [chaotic]; <b>Level </b>cleric/oracle 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a tiny reliquary worth 500 gp)</h5><h5><b>Range </b>20 ft.</h5><h5><b>Targets </b> one creature/level in a 20-ft.-radius burst centered on you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>A random pattern of color surrounds the subjects, protecting them from attacks, granting them resistance to spells cast by lawful creatures, and causing lawful creatures that strike the subjects to become confused. This abjuration has four effects.</p><p>First, each warded creature gains a +4 deflection bonus to AC and a +4 resistance bonus on saves. Unlike <i>protection from law</i>, the benefit of this spell applies against all attacks, not just against attacks by lawful creatures.</p><p>Second, each warded creature gains spell resistance 25 against lawful spells and spells cast by lawful creatures.</p><p>Third, the abjuration protects from possession and mental influence, just as <i>protection from law</i> does.</p><p>Finally, if a lawful creature succeeds on a melee attack against a warded creature, the offending attacker is confused for 1 round (Will save negates, as with the <i>confusion spell</i>, but against the save DC of <i>cloak of chaos</i>).</p></h4></div>"},
new Spell(){ Name = "Clone", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Clone</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 8, witch 8</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (laboratory supplies worth 1,000 gp), F (special laboratory equipment costing 500 gp)</h5><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>one clone</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell makes an inert duplicate of a creature. If the original individual has been slain, its soul immediately transfers to the clone, creating a replacement (provided that the soul is free and willing to return). The original's physical remains, should they still exist, become inert and cannot thereafter be restored to life. If the original creature has reached the end of its natural life span (that is, it has died of natural causes), any cloning attempt fails.</p><p>To create the duplicate, you must have a piece of flesh (not hair, nails, scales, or the like) with a volume of at least 1 cubic inch that was taken from the original creature's living body. The piece of flesh need not be fresh, but it must be kept from rotting. Once the spell is cast, the duplicate must be grown in a laboratory for 2d4 months.</p><p>When the clone is completed, the original's soul enters it immediately, if that creature is already dead. The clone is physically identical to the original and possesses the same personality and memories as the original. In other respects, treat the clone as if it were the original character raised from the dead, including its gaining of two permanent negative levels, just as if it had been hit by an energy-draining creature. If the subject is 1st level, it takes 2 points of Constitution drain instead (if this would reduce its Con to 0 or less, it can't be cloned). If the original creature gained permanent negative levels since the flesh sample was taken, the clone gains these negative levels as well.</p><p>The spell duplicates only the original's body and mind, not its equipment. A duplicate can be grown while the original still lives, or when the original soul is unavailable, but the resulting body is merely a soulless bit of inert flesh which rots if not preserved.</p></h4></div>"},
new Spell(){ Name = "Cloudkill", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cloudkill</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 5, witch 5, magus 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>cloud spreads in 20-ft. radius, 20 ft. high</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Fortitude partial; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell generates a bank of fog, similar to a <i>fog cloud</i>, except that its vapors are yellowish green and poisonous. These vapors automatically kill any living creature with 3 or fewer HD (no save).</p><p>A living creature with 4 to 6 HD is slain unless it succeeds on a Fortitude save (in which case it takes 1d4 points of Constitution damage on your turn each round while in the cloud).</p><p>A living creature with more than 6 HD takes 1d4 points of Constitution damage on your turn each round while in the cloud (a successful Fortitude save halves this damage). Holding one's breath doesn't help, but creatures immune to poison are unaffected by the spell.</p><p>Unlike a <i>fog cloud</i>, the <i>cloudkill</i> moves away from you at 10 feet per round, rolling along the surface of the ground.</p><p>Figure out the cloud's new spread each round based on its new point of origin, which is 10 feet farther away from the point of origin where you cast the spell.</p><p>Because the vapors are heavier than air, they sink to the lowest level of the land, even pouring down den or sinkhole openings. It cannot penetrate liquids, nor can it be cast underwater.</p></h4><h5><b>Mythic: </b>As a move action, you can move the cloud 10 feet in any direction. Add your tier to the spell's level and Hit Dice categories to determine its effect on creatures. (For example, at 3rd tier a creature with 6 or fewer HD gets no save, one with 7-9 HD must save or die, and one with 10 or more HD must save or take 1d4 points of Con damage.)</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the spell bypasses the poison immunity of living creatures.</h5></div>"},
new Spell(){ Name = "Color Spray", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Color Spray</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (pattern) [mind-affecting]; <b>Level </b>sorcerer/wizard 1, magus 1, bloodrager 1, psychic 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (red, yellow, and blue powder or colored sand)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>15 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>instantaneous; see text</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A vivid cone of clashing colors springs forth from your hand, causing creatures to become stunned, perhaps also blinded, and possibly knocking them unconscious. Each creature within the cone is affected according to its HD.</p><p>2 HD or less: The creature is unconscious, blinded, and stunned for 2d4 rounds, then blinded and stunned for 1d4 rounds, and then stunned for 1 round. (Only living creatures are knocked unconscious.) 3 or 4 HD: The creature is blinded and stunned for 1d4 rounds, then stunned for 1 round.</p><p>5 or more HD: The creature is stunned for 1 round.</p><p>Sightless creatures are not affected by <i>color spray</i>.</p></h4><h5><b>Mythic: </b>The range increases to 30 feet. Add half your tier to the spell's Hit Dice categories to determine its effects on creatures. (At 3rd tier a creature with 3 or fewer HD is unconscious, blinded, and stunned; one with 4 or 5 HD is blinded and stunned, and one with 6 or more HD is stunned.) Creatures that succeed at their saves are dazzled for 1 minute.</h5></div>"},
new Spell(){ Name = "Command", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Command</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [language-dependent, mind-affecting]; <b>Level </b>antipaladin 1, cleric/oracle 1, inquisitor 1, witch 1, occultist 1, psychic 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 round</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You give the subject a single command, which it obeys to the best of its ability at its earliest opportunity. You may select from the following options.</p><p><i>Approach</i>: On its turn, the subject moves toward you as quickly and directly as possible for 1 round. The creature may do nothing but move during its turn, and it provokes attacks of opportunity for this movement as normal.</p><p><i>Drop</i>: On its turn, the subject drops whatever it is holding. It can't pick up any dropped item until its next turn.</p><p><i>Fall</i>: On its turn, the subject falls to the ground and remains prone for 1 round. It may act normally while prone but takes any appropriate penalties.</p><p><i>Flee</i>: On its turn, the subject moves away from you as quickly as possible for 1 round. It may do nothing but move during its turn, and it provokes attacks of opportunity for this movement as normal.</p><p><i>Halt</i>: The subject stands in place for 1 round. It may not take any actions but is not considered helpless.</p><p>If the subject can't carry out your command on its next turn, the spell automatically fails.</p></h4><h5><b>Mythic: </b>You can target up to one creature per level, each of which must receive the same command. Creatures that succeed at their saving throws are staggered for 1 round (this is a mind-affecting effect).</h5></div>"},
new Spell(){ Name = "Command, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Command, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [language-dependent, mind-affecting]; <b>Level </b>cleric/oracle 5, inquisitor 5, occultist 5, psychic 5, mesmerist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>command</i>, except that up to one creature per level may be affected, and the activities continue beyond 1 round. At the start of each commanded creature's action after the first, it gets another Will save to attempt to break free from the spell. Each creature must receive the same command.</p></h4></div>"},
new Spell(){ Name = "Command Plants", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Command Plants</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 4, ranger 3, shaman 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> up to 2 HD/level of plant creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell allows you some degree of control over one or more plant creatures. Affected plant creatures can understand you, and they perceive your words and actions in the most favorable way (treat their attitude as friendly). They will not attack you while the spell lasts. You can try to give a subject orders, but you must win an opposed Charisma check to convince it to do anything it wouldn't ordinarily do. (Retries are not allowed.) A commanded plant never obeys suicidal or obviously harmful orders, but it might be convinced that something very dangerous is worth doing.</p><p>You can affect a number of plant creatures whose combined level or HD do not exceed twice your level.</p></h4></div>"},
new Spell(){ Name = "Command Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Command Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a shred of raw meat and a splinter of bone)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one undead creature</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell allows you a degree of control over an undead creature. If the subject is intelligent, it perceives your words and actions favorably (treat its attitude as friendly). It will not attack you while the spell lasts. You can give the subject orders, but you must win an opposed Charisma check to convince it to do anything it wouldn't ordinarily do. Retries are not allowed. An intelligent commanded undead never obeys suicidal or obviously harmful orders, but it might be convinced that something very dangerous is worth doing.</p><p>A nonintelligent undead creature gets no saving throw against this spell. When you control a mindless being, you can communicate only basic commands, such as ""come here,"" ""go there,"" ""fight,"" ""stand still,"" and so on. Nonintelligent undead won't resist suicidal or obviously harmful orders.</p><p>Any act by you or your apparent allies that threatens the commanded undead (regardless of its Intelligence) breaks the spell.</p><p>Your commands are not telepathic. The undead creature must be able to hear you.</p></h4></div>"},
new Spell(){ Name = "Commune", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Commune</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 5, inquisitor 5, shaman 5, occultist 5</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (holy or unholy water and incense worth 500 gp), DF</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>1 round/level</h5></div><div><h4><p>You contact your deity--or agents thereof--and ask questions that can be answered by a simple yes or no. (A cleric of no particular deity contacts a philosophically allied deity.) You are allowed one such question per caster level. The answers given are correct within the limits of the entity's knowledge. ""Unclear"" is a legitimate answer, because powerful beings of the Outer Planes are not necessarily omniscient. In cases where a one-word answer would be misleading or contrary to the deity's interests, a short phrase (five words or less) may be given as an answer instead.</p><p>The spell, at best, provides information to aid character decisions. The entities contacted structure their answers to further their own purposes. If you lag, discuss the answers, or go off to do anything else, the spell ends.</p></h4></div>"},
new Spell(){ Name = "Commune with Nature", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Commune with Nature</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>druid 5, ranger 4, shaman 5, psychic 5</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>instantaneous</h5></div><div><h4><p>You become one with nature, attaining knowledge of the surrounding territory. You instantly gain knowledge of as many as three facts from among the following subjects: the ground or terrain, plants, minerals, bodies of water, people, general animal population, presence of woodland creatures, presence of powerful unnatural creatures, or even the general state of the natural setting.</p><p>In outdoor settings, the spell operates in a radius of 1 mile per caster level. In natural underground settings--caves, caverns, and the like--the spell is less powerful, and its radius is limited to 100 feet per caster level. The spell does not function where nature has been replaced by construction or settlement, such as in dungeons and towns.</p></h4></div>"},
new Spell(){ Name = "Comprehend Languages", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Comprehend Languages</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 1, cleric/oracle 1, sorcerer/wizard 1, alchemist 1, witch 1, inquisitor 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (pinch of soot and salt)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>10 min./level</h5></div><div><h4><p>You can understand the spoken words of creatures or read otherwise incomprehensible written messages. The ability to read does not necessarily impart insight into the material, merely its literal meaning. The spell enables you to understand or read an unknown language, not speak or write it.</p><p>Written material can be read at the rate of one page (250 words) per minute. Magical writing cannot be read, though the spell reveals that it is magical. This spell can be foiled by certain warding magic (such as the <i>secret page</i> and <i>illusory script</i> spells). It does not decipher codes or reveal messages concealed in otherwise normal text.</p><p><i>Comprehend languages</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Cone of Cold", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cone of Cold</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [cold]; <b>Level </b>sorcerer/wizard 5, witch 6, magus 5, shaman 6, occultist 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a small crystal or glass cone)</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><div><h4><p><i>Cone of cold</i> creates an area of extreme cold, originating at your hand and extending outward in a cone. It drains heat, dealing 1d6 points of cold damage per caster level (maximum 15d6).</p></h4></div>"},
new Spell(){ Name = "Confusion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Confusion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 3, sorcerer/wizard 4, witch 4, bloodrager 4, occultist 4, psychic 4, mesmerist 3, spiritualist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (three nutshells)</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b> all creatures in a 15-ft.-radius burst</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell causes confusion in the targets, making them unable to determine their actions. Roll on the following table at the start of each subject's turn each round to see what it does in that round.</p><p><table><tr><th>d%</th><th>Behavior</th></tr> <tr><td>01-25</td><td>Act normally</td></tr> <tr><td>26-50</td><td>Do nothing but babble incoherently</td></tr> <tr><td>51-75</td><td>Deal 1d8 points of damage + Str modifier to self with item in hand</td></tr> <tr><td>76-100</td><td>Attack nearest creature (for this purpose, a familiar counts as part of the subject's self)</td></tr></table>  A confused character who can't carry out the indicated action does nothing but babble incoherently. Attackers are not at any special advantage when attacking a confused character. Any confused character who is attacked automatically attacks its attackers on its next turn, as long as it is still confused when its turn comes. Note that a confused character will not make attacks of opportunity against any creature that it is not already devoted to attacking (either because of its most recent action or because it has just been attacked).</p></h4></div>"},
new Spell(){ Name = "Confusion, Lesser", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Confusion, Lesser</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 1, psychic 1, mesmerist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one living creature</h5><h5><b>Duration </b>1 round</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell causes a single creature to become confused for 1 round.</p></h4></div>"},
new Spell(){ Name = "Consecrate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Consecrate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [good]; <b>Level </b>cleric/oracle 2, inquisitor 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a vial of holy water and 25 gp worth of silver dust), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>20-ft.-radius emanation</h5><h5><b>Duration </b>2 hours/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell blesses an area with positive energy. The DC to resist positive channeled energy within this area gains a +3 sacred bonus. Every undead creature entering a <i>consecrate</i>d area suffers minor disruption, suffering a -1 penalty on attack rolls, damage rolls, and saves. Undead cannot be created within or summoned into a <i>consecrate</i>d area. If the <i>consecrate</i>d area contains an altar, shrine, or other permanent fixture dedicated to your deity, pantheon, or aligned higher power, the modifiers given above are doubled (+6 sacred bonus to positive channeled energy DCs, -2 penalties for undead in the area).</p><p>You cannot <i>consecrate</i> an area with a similar fixture of a deity other than your own patron. Instead, the <i>consecrate</i> spell curses the area, cutting off its connection with the associated deity or power.</p><p>This secondary function, if used, does not also grant the bonuses and penalties relating to undead, as given above.</p><p><i>Consecrate</i> counters and dispels <i>desecrate</i>.</p></h4><h5><b>Mythic: </b>The black pudding has fast healing equal to your tier, as do any puddings that split off from it. The black puddings from this spell never attack you, though they're otherwise uncontrolled and might attack your allies if there are no other obvious opponents.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the pudding gains the savage mythic template (see page 224). Puddings that split off from the giant pudding don't gain the template.</h5></div>"},
new Spell(){ Name = "Contact Other Plane", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Contact Other Plane</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>sorcerer/wizard 5, alchemist 5, summoner 4, witch 5</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>concentration</h5></div><div><h4><p>You send your mind to another plane of existence (an Elemental Plane or some plane farther removed) in order to receive advice and information from powers there. See the accompanying table for possible consequences and results of the attempt. The powers reply in a language you understand, but they resent such contact and give only brief answers to your questions. All questions are answered with ""yes,"" ""no,"" ""maybe,"" ""never,"" ""irrelevant,"" or some other one-word answer.</p><p>You must concentrate on maintaining the spell (a standard action) in order to ask questions at the rate of one per round. A question is answered by the power during the same round. You may ask one question for every two caster levels.</p><p>Contact with minds far removed from your home plane increases the probability that you will incur a decrease in Intelligence and Charisma due to your brain being overwhelmed, but also increases the chance of the power knowing the answer and answering correctly. Once the Outer Planes are reached, the power of the deity contacted determines the effects. (Random results obtained from the table are subject to the personalities of individual deities.) On rare occasions, this divination may be blocked by an act of certain deities or forces.</p><p><i>Avoid Int/Cha Decrease</i>: You must succeed on an Intelligence check against this DC to avoid a decrease in Intelligence and Charisma. If the check fails, your Intelligence and Charisma scores each fall to 8 for the stated duration, and you become unable to cast arcane spells. If you lose Intelligence and Charisma, the effect strikes as soon as the first question is asked, and no answer is received. If a successful contact is made, roll d% to determine the type of answer you gain.</p><p><i>True Answer</i>: You get a true, one-word answer. Questions that cannot be answered in this way are answered randomly.</p><p><i>Don't Know</i>: The entity tells you that it doesn't know.</p><p><i>Lie</i>: The entity intentionally lies to you.</p><p><i>Random Answer</i>: The entity tries to lie but doesn't know the answer, so it makes one up.</p><p> <table> <tr> <td>Contact Other Plane</td> </tr> <tr> <th>Plane Contacted</th> <th>Avoid Int/Cha Decrease</th>  <th>True Answer</th> <th>Don't Know</th> <th>Lie</th> <th>Random Answer</th> </tr> <tr> <td>Elemental Plane</td> <td>DC 7/1 week</td> <td>01-34</td>  <td>35-62</td> <td>63-83</td> <td>84-100</td> </tr> <tr> <td>Positive/Negative Energy Plane</td> <td>DC 8/1 week</td> <td>01-39</td> <td>40-65</td> <td>66-86</td>  <td>87-100</td> </tr> <tr> <td>Astral Plane</td> <td>DC 9/1 week</td> <td>01-44</td> <td>45-67</td> <td>68-88</td> <td>89-100</td> </tr>  <tr> <td>Outer Plane, demigod</td> <td>DC 10/2 weeks</td> <td>01-49</td> <td>50-70</td> <td>71-91</td> <td>92-100</td> </tr> <tr> <td>Outer Plane, lesser deity</td>  <td>DC 12/3 weeks</td> <td>01-60</td> <td>61-75</td> <td>76-95</td> <td>96-100</td> </tr> <tr> <td>Outer Plane, intermediate deity</td> <td>DC 14/4 weeks</td> <td>01-73</td>  <td>74-81</td> <td>82-98</td> <td>99-100</td> </tr> <tr> <td>Outer Plane, greater deity</td> <td>DC 16/5 weeks</td> <td>01-88</td> <td>89-90</td> <td>91-99</td>  <td>100</td> </tr> </table></p></h4></div>"},
new Spell(){ Name = "Contagion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Contagion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [evil]; <b>Level </b>cleric/oracle 3, druid 3, sorcerer/wizard 4, antipaladin 3, bloodrager 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject contracts one of the following diseases: blinding sickness, bubonic plague, cackle fever, filth fever, leprosy, mindfire, red ache, shakes, or slimy doom. The disease is contracted immediately (the onset period does not apply). Use the disease's listed frequency and save DC to determine further effects. For more information on these diseases, see page 557.</p></h4><h5><b>Mythic: </b>The affected target is highly contagious. Any creature it touches or that touches it with natural weapons or unarmed strikes must save or contract the disease. The save DC for these targets is equal to the spell's DC - 4. The target can't spread this disease to you.</h5><h5><b>Augmented (7th)</b>: If you expend five uses of mythic power, the spell targets every living creature within a 1-mile radius. You can select one creature per caster level within your line of sight; these creatures are unaffected by the spell.</h5></div>"},
new Spell(){ Name = "Contingency", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Contingency</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>sorcerer/wizard 6, occultist 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>at least 10 minutes; see text</h5><h5><b>Components </b>V, S, M (quicksilver and an eyelash of a spell-using creature), F (ivory statuette of you worth 1,500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 day/level (D) or until discharged</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can place another spell upon your person so that it comes into effect under some condition you dictate when casting <i>contingency</i>.</p><p>The <i>contingency</i> spell and the companion spell are cast at the same time. The 10-minute casting time is the minimum total for both castings; if the companion spell has a casting time longer than 10 minutes, use that instead. You must pay any costs associated with the companion spell when you cast <i>contingency</i>.</p><p>The spell to be brought into effect by the <i>contingency</i> must be one that affects your person and be of a spell level no higher than one-third your caster level (rounded down, maximum 6th level).</p><p>The conditions needed to bring the spell into effect must be clear, although they can be general. In all cases, the <i>contingency</i> immediately brings into effect the companion spell, the latter being ""cast"" instantaneously when the prescribed circumstances occur.</p><p>If complicated or convoluted conditions are prescribed, the whole spell combination (<i>contingency</i> and the companion magic) may fail when triggered. The companion spell occurs based solely on the stated conditions, regardless of whether you want it to.</p><p>You can use only one <i>contingency</i> spell at a time; if a second is cast, the first one (if still active) is dispelled.</p></h4><h5><b>Mythic: </b>You can cast this spell on yourself or another willing creature as if the spell had a range of touch. A companion spell placed on another creature must be a spell from you, not from the creature, and affects that creature when triggered. The target can have only one contingency spell upon it at a time unless it also knows mythic contingency. The number of companion spells you can have on yourself is equal to 1 + half your tier.</h5><h5><b>Augmented (5th)</b>: If you expend two uses of mythic power, the casting time changes to 1 full round plus the casting time of the companion spell, but the duration of mythic contingency decreases to 1 hour per level or until discharged.</h5></div>"},
new Spell(){ Name = "Continual Flame", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Continual Flame</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [light]; <b>Level </b>cleric/oracle 3, sorcerer/wizard 2, inquisitor 3, occultist 2, psychic 3</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (ruby dust worth 50 gp)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> object touched Effect magical, heatless flame</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>A flame, equivalent in brightness to a torch, springs forth from an object that you touch. The effect looks like a regular flame, but it creates no heat and doesn't use oxygen. A <i>continual flame</i> can be covered and hidden but not smothered or quenched.</p><p>Light spells counter and dispel <i>darkness</i> spells of an equal or lower level.</p></h4></div>"},
new Spell(){ Name = "Control Plants", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Control Plants</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> up to 2 HD/level of plant creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell enables you to control the actions of one or more plant creatures for a short period of time. You command the creatures by voice and they understand you, no matter what language you speak. Even if vocal communication is impossible, the controlled plants do not attack you. At the end of the spell, the subjects revert to their normal behavior.</p><p>Suicidal or self-destructive commands are simply ignored.</p></h4></div>"},
new Spell(){ Name = "Control Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Control Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 7</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of bone and a piece of raw meat)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> up to 2 HD/level of undead creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell enables you to control undead creatures for a short period of time. You command them by voice and they understand you, no matter what language you speak. Even if vocal communication is impossible, the controlled undead do not attack you. At the end of the spell, the subjects revert to their normal behavior.</p><p>Intelligent undead creatures remember that you controlled them, and they may seek revenge after the spell's effects end.</p></h4></div>"},
new Spell(){ Name = "Control Water", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Control Water</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [water]; <b>Level </b>cleric/oracle 4, druid 4, sorcerer/wizard 6, shaman 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a pinch of dust for lower water or a drop of water for raise water)</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>water in a volume of 10 ft./level by 10 ft./level by 2 ft./level  (S)</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell has two different applications, both of which control water in different ways. The first version of this spell causes water in the area to swiftly evaporate or to sink into the ground below, lowering the water's depth. The second version causes the water to surge and rise, increasing its overall depth and possibly flooding nearby areas.</p><p><i>Lower Water</i>: This causes water or similar liquid to reduce its depth by as much as 2 feet per caster level (to a minimum depth of 1 inch). The water is lowered within a squarish depression whose sides are up to caster level x 10 feet long. In extremely large and deep bodies of water, such as a deep ocean, the spell creates a whirlpool that sweeps ships and similar craft downward, putting them at risk and rendering them unable to leave by normal movement for the duration of the spell. When cast on water elementals and other water-based creatures, this spell acts as a <i>slow</i> spell (Will negates). The spell has no effect on other creatures.</p><p><i>Raise Water</i>: This causes water or similar liquid to rise in height, just as the <i>lower water</i> version causes it to lower. Boats raised in this way slide down the sides of the hump that the spell creates. If the area affected by the spell includes riverbanks, a beach, or other land nearby, the water can spill over onto dry land.</p><p>With either version of this spell, you may reduce one horizontal dimension by half and double the other horizontal dimension to change the overall area of effect.</p></h4></div>"},
new Spell(){ Name = "Control Weather", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Control Weather</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 7, druid 7, sorcerer/wizard 7, witch 7, shaman 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes; see text</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>2 miles</h5><h5><b>Area </b>2-mile-radius circle, centered on you; see text</h5><h5><b>Duration </b>4d12 hours; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You change the weather in the local area. It takes 10 minutes to cast the spell and an additional 10 minutes for the effects to manifest. You can call forth weather appropriate to the climate and season of the area you are in. You can also use this spell to cause the weather in the area to become calm and normal for the season.</p>   <table border ='1'><tr><th>Season</th><th>Possible Weather</th></tr><tr><td>Spring</td><td>Tornado, thunderstorm, sleet storm, or hot weather</td></tr><tr><td>Summer</td><td>Torrential rain, heat wave, or hailstorm</td></tr><tr><td>Autumn</td><td>Hot or cold weather, fog, or sleet</td></tr><tr><td>Winter</td><td>Frigid cold, blizzard, or thaw</td></tr><tr><td>Late winter or early spring</td><td>Hurricane-force winds</td></tr></table>   You control the general tendencies of the weather, such as the direction and intensity of the wind. You cannot control specific applications of the weather-where lightning strikes, for example, or the exact path of a tornado. The weather continues as you left it for the duration, or until you use a standard action to designate a new kind of weather (which fully manifests itself 10 minutes later). Contradictory conditions are not possible simultaneously.</p><p><i>Control weather</i> can do away with atmospheric phenomena (naturally occurring or otherwise) as well as create them.</p><p>A druid casting this spell doubles the duration and affects a circle with a 3-mile radius.</p></h4><h5><b>Mythic: </b>The casting time changes to 1 standard action, and the duration doubles. The weather changes after a number of rounds equal to 11 - your tier.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, you can create weather of any type regardless of the season, and the weather changes after 1 round.</h5></div>"},
new Spell(){ Name = "Control Winds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Control Winds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [air]; <b>Level </b>druid 5, shaman 5, occultist 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>40 ft./level</h5><h5><b>Area </b>40 ft./level radius cylinder 40 ft. high</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>no</h5></div><div><h4><p>You alter wind force in the area surrounding you. You can make the wind blow in a certain direction or manner, increase its strength, or decrease its strength. The new wind direction and strength persist until the spell ends or until you choose to alter your handiwork, which requires concentration. You may create an ""eye"" of calm air up to 80 feet in diameter at the center of the area if you so desire, and you may choose to limit the area to any cylindrical area less than your full limit.</p><p><i>Wind Direction</i>: You may choose one of four basic wind patterns to function over the spell's area.</p><p><ul>     <li> A downdraft blows from the center outward in equal strength in all directions.</p><p>    <li> An updraft blows from the outer edges in toward the center in equal strength from all directions, veering upward before impinging on the eye in the center.</p><p>    <li> Rotation causes the winds to circle the center in clockwise or counterclockwise fashion.</p><p>    <li> A blast simply causes the winds to blow in one direction across the entire area from one side to the other.</ul></p><p> <i>Wind Strength</i>: For every three caster levels, you can increase or decrease wind strength by one level. Each round on your turn, a creature in the wind must make a Fortitude save or suffer the effect of being in the windy area. See Environment for more details.</p><p>Strong winds (21+ mph) make sailing difficult.</p><p>A severe wind (31+ mph) causes minor ship and building damage.</p><p>A windstorm (51+ mph) drives most flying creatures from the skies, uproots small trees, knocks down light wooden structures, tears off roofs, and endangers ships.</p><p>Hurricane force winds (75+ mph) destroy wooden buildings, uproot large trees, and cause most ships to founder.</p><p>A tornado (175+ mph) destroys all nonfortified buildings and often uproots large trees.</p></h4></div>"},
new Spell(){ Name = "Create Food and Water", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Create Food and Water</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>cleric/oracle 3, shaman 3, occultist 3</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>food and water to sustain three humans or one horse/level for 24 hours</h5><h5><b>Duration </b>24 hours; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>The food that this spell creates is simple fare of your choice--highly nourishing, if rather bland. Food so created decays and becomes inedible after 24 hours, although it can be kept fresh for another 24 hours by casting a <i>purify food and water</i> spell on it. The water created by this spell is just like clean rain water, and it doesn't go bad as the food does.</p></h4></div>"},
new Spell(){ Name = "Create Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Create Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [evil]; <b>Level </b>cleric/oracle 6, sorcerer/wizard 6, shaman 6, spiritualist 6</h5><h5><b>Casting Time </b>1 hour</h5><h5><b>Components </b>V, S, M (a clay pot filled with grave dirt and an onyx gem worth at least 50 gp per HD of the undead to be created)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one corpse</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>A much more potent spell than  <i>animate dead </i>, this evil spell allows you to infuse a dead body with negative energy to create more powerful sorts of undead: ghouls, ghasts, mummies, and mohrgs. The type or types of undead you can create are based on your caster level, as shown on the table below.</p><p><table><thead><tr><th>Caster Level</th><th>Undead Created</th></tr> <tbody><tr><td>11th or lower</td><td>Ghoul</td></tr> <tr><td>12th-14th</td><td>Ghast</td></tr> <tr><td>15th-17th</td><td>Mummy</td></tr> <tr><td>18th or higher</td><td>Mohrg</td></tr></tbody></table>  You may create less powerful undead than your level would allow if you choose. Created undead are not automatically under the control of their animator. If you are capable of commanding undead, you may attempt to command the undead creature as it forms.</p><p>This spell must be cast at night.</p></h4></div>"},
new Spell(){ Name = "Create Greater Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Create Greater Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [evil]; <b>Level </b>cleric/oracle 8, sorcerer/wizard 8, shaman 8</h5><h5><b>Casting Time </b>1 hour</h5><h5><b>Components </b>V, S, M (a clay pot filled with grave dirt and an onyx gem worth at least 50 gp per HD of the undead to be created)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one corpse</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell functions like  <i>create undead </i>, except that you can create more powerful and intelligent sorts of undead: shadows, wraiths, spectres, and devourers. The type or types of undead created is based on caster level, as shown below.</p><p><table><tr><th>Caster Level</th><th>Undead Created</th></tr> <tr><td>15th or lower</td><td>Shadow</td></tr> <tr><td>16th-17th</td><td>Wraith</td></tr> <tr><td>18th-19th</td><td>Spectre</td></tr> <tr><td>20th or higher</td><td>Devourer</td></tr></table></p></h4></div>"},
new Spell(){ Name = "Create Water", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Create Water</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [water]; <b>Level </b>cleric/oracle 0, druid 0, paladin 1, inquisitor 0, shaman 0, occultist 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>up to 2 gallons of water/level</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell generates wholesome, drinkable water, just like clean rain water. Water can be created in an area as small as will actually contain the liquid, or in an area three times as large--possibly creating a downpour or filling many small receptacles. This water disappears after 1 day if not consumed.</p><p>Note: Conjuration spells can't create substances or objects within a creature. Water weighs about 8 pounds per gallon. One cubic foot of water contains roughly 8 gallons and weighs about 60 pounds.</p></h4></div>"},
new Spell(){ Name = "Creeping Doom", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Creeping Doom</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>druid 7, summoner 5, shaman 7</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)/100 ft.; see text</h5><h5><b>Effect </b>four swarms of insects</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Fortitude partial; see text; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell summons four massive swarms of biting and stinging insects. These swarms appear adjacent to one another, but can be directed to move independently. Treat these swarms as centipede swarms with the following adjustments. The swarms have 60 hit points each and deal 4d6 points of damage with their swarm attack. The save to resist their poison and distraction effects is equal to the save DC of this spell. Creatures caught in multiple swarms only take damage and make saves once.</p><p>You may summon the swarms so that they share the area of other creatures. As a standard action, you can command any number of the swarms to move toward any target within 100 feet of you. You cannot command any swarm to move more than 100 feet away from you, and if you move more than 100 feet from any swarm, that swarm remains stationary, attacking any creatures in its area (but can be commanded again if you move within 100 feet).</p></h4></div>"},
new Spell(){ Name = "Crushing Despair", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Crushing Despair</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, emotion]; <b>Level </b>bard 3, sorcerer/wizard 4, witch 4, bloodrager 4, psychic 4, mesmerist 3, spiritualist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M</h5><h5><b>Range </b>30 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>An invisible cone of despair causes great sadness in the subjects. Each affected creature takes a -2 penalty on attack rolls, saving throws, ability checks, skill checks, and weapon damage rolls.</p><p> <i>Crushing despair </i> counters and dispels  <i>good hope </i>.</p></h4></div>"},
new Spell(){ Name = "Crushing Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Crushing Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 9, psychic 9</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a soft glove)</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>10-ft. hand</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions as <i>interposing hand</i>, except that it can also grapple one opponent as <i>grasping hand</i>. Its CMB and CMD for grapple checks use your caster level in place of its base attack bonus, with a +12 bonus for its Strength score (35) and a +1 bonus for being Large (its Dexterity is 10, granting no bonus to the CMD). A <i>crushing hand</i> deals 2d6+12 points of damage on each successful grapple check against an opponent. The <i>crushing hand</i> can instead be directed to bull rush a target (as <i>forceful hand</i>), using the same bonuses outlined above, or it can be directed to interpose itself, as <i>interposing hand</i> does.</p></h4></div>"},
new Spell(){ Name = "Cure Light Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Light Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 1, cleric/oracle 1, druid 1, paladin 1, ranger 2, witch 1, inquisitor 1, alchemist 1, shaman 1, occultist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless); see text; <b>Spell Resistance </b>yes (harmless); see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When laying your hand upon a living creature, you channel positive energy that cures 1d8 points of damage + 1 point per caster level (maximum +5). Since undead are powered by negative energy, this spell deals damage to them instead of curing their wounds. An undead creature can apply spell resistance, and can attempt a Will save to take half damage.</p></h4><h5><b>Mythic: </b>The damage cured increases to 2d8 points of damage + 2 points per caster level (maximum +10). The spell cures up to 1 point of ability damage if the target is a living creature. The target chooses what type of ability damage is cured.</h5></div>"},
new Spell(){ Name = "Cure Critical Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Critical Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 4, cleric/oracle 4, druid 5, witch 5, inquisitor 4, alchemist 4, shaman 4, occultist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless); see text; <b>Spell Resistance </b>yes (harmless); see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>cure light wounds</i>, except that it cures 4d8 points of damage + 1 point per caster level (maximum +20).</p></h4><h5><b>Mythic: </b>The damage cured increases to 8d8 points of damage + 2 points per caster level (maximum +40). The spell cures up to 4 points of ability damage if the target is a living creature. The target chooses what types of ability damage are cured.</h5></div>"},
new Spell(){ Name = "Cure Light Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Light Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 5, cleric/oracle 5, druid 6, witch 6, inquisitor 5, shaman 5, occultist 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless) or Will half; see text; <b>Spell Resistance </b>yes (harmless) or yes; see text</h5></div><div><h4><p>You channel positive energy to cure 1d8 points of damage + 1 point per caster level (maximum +25) on each selected creature. Like other cure spells, <i>mass cure light wounds</i> deals damage to undead in its area rather than curing them. Each affected undead may attempt a Will save for half damage.</p></h4></div>"},
new Spell(){ Name = "Cure Critical Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Critical Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 8, druid 9, witch 9, shaman 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless) or Will half; see text; <b>Spell Resistance </b>yes (harmless) or yes; see text</h5></div><div><h4><p>This spell functions like <i>mass cure light wounds</i>, except that it cures 4d8 points of damage + 1 point per caster level (maximum +40).</p></h4></div>"},
new Spell(){ Name = "Cure Moderate Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Moderate Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 2, cleric/oracle 2, druid 3, paladin 3, ranger 3, witch 2, inquisitor 2, alchemist 2, shaman 2, occultist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless); see text; <b>Spell Resistance </b>yes (harmless); see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>cure light wounds</i>, except that it cures 2d8 points of damage + 1 point per caster level (maximum +10).</p></h4><h5><b>Mythic: </b>The damage cured increases to 4d8 points of damage + 2 points per caster level (maximum +20). The spell cures up to 2 points of ability damage if the target is a living creature. The target chooses what types of ability damage are cured.</h5></div>"},
new Spell(){ Name = "Cure Moderate Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Moderate Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 6, cleric/oracle 6, druid 7, witch 7, inquisitor 6, shaman 6, occultist 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless) or Will half; see text; <b>Spell Resistance </b>yes (harmless) or yes; see text</h5></div><div><h4><p>This spell functions like <i>mass cure light wounds</i>, except that it cures 2d8 points of damage + 1 point per caster level (maximum +30).</p></h4></div>"},
new Spell(){ Name = "Cure Serious Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Serious Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 3, cleric/oracle 3, druid 4, paladin 4, ranger 4, witch 4, inquisitor 3, alchemist 3, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless); see text; <b>Spell Resistance </b>yes (harmless); see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>cure light wounds</i>, except that it cures 3d8 points of damage + 1 point per caster level (maximum +15).</p></h4><h5><b>Mythic: </b>The damage cured increases to 6d8 points of damage + 2 points per caster level (maximum +30). The spell cures up to 3 points of ability damage if the target is a living creature. The target chooses what types of ability damage are cured.</h5></div>"},
new Spell(){ Name = "Cure Serious Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Cure Serious Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 7, druid 8, witch 8, shaman 7</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half (harmless) or Will half; see text; <b>Spell Resistance </b>yes (harmless) or yes; see text</h5></div><div><h4><p>This spell functions like <i>mass cure light wounds</i>, except that it cures 3d8 points of damage + 1 point per caster level (maximum +35).</p></h4></div>"},
new Spell(){ Name = "Curse Water", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Curse Water</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [evil]; <b>Level </b>cleric/oracle 1, inquisitor 1, antipaladin 1</h5><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (5 lbs. of powdered silver worth 25 gp)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> flask of water touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><div><h4><p>This spell imbues a flask (1 pint) of water with negative energy, turning it into unholy water (see Equipment). Unholy water damages good outsiders the way holy water damages undead and evil outsiders.</p></h4></div>"},
new Spell(){ Name = "Dancing Lights", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dancing Lights</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [light]; <b>Level </b>bard 0, sorcerer/wizard 0, witch 0, magus 0, shaman 0, occultist 0, psychic 0, mesmerist 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>Up to four lights, all within a 10-ft.-radius area</h5><h5><b>Duration </b>1 minute (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>Depending on the version selected, you create up to four lights that resemble lanterns or torches (and cast that amount of light), or up to four glowing spheres of light (which look like will-o'-wisps), or one faintly glowing, vaguely humanoid shape. The <i>dancing lights</i> must stay within a 10-foot-radius area in relation to each other but otherwise move as you desire (no concentration required): forward or back, up or down, straight or turning corners, or the like. The lights can move up to 100 feet per round. A light winks out if the distance between you and it exceeds the spell's range.</p><p>You can only have one <i>dancing lights</i> spell active at any one time. If you cast this spell while another casting is still in effect, the previous casting is dispelled. If you make this spell permanent, it does not count against this limit.</p><p><i>Dancing lights</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Darkness", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Darkness</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [darkness]; <b>Level </b>bard 2, cleric/oracle 2, sorcerer/wizard 2, inquisitor 2, antipaladin 2, magus 2, shaman 2, occultist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, M/DF (bat fur and a piece of coal)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes an object to radiate <i>darkness</i> out to a 20-foot radius. This <i>darkness</i> causes the illumination level in the area to drop one step, from bright light to normal light, from normal light to dim light, or from dim light to <i>darkness</i>. This spell has no effect in an area that is already dark. Creatures with light vulnerability or sensitivity take no penalties in normal light. All creatures gain concealment (20% miss chance) in dim light. All creatures gain total concealment (50% miss chance) in <i>darkness</i>.</p><p>Creatures with darkvision can see in an area of dim light or <i>darkness</i> without penalty. Nonmagical sources of light, such as torches and lanterns, do not increase the light level in an area of <i>darkness</i>. Magical light sources only increase the light level in an area if they are of a higher spell level than <i>darkness</i>.</p><p>If <i>darkness</i> is cast on a small object that is then placed inside or under a lightproof covering, the spell's effect is blocked until the covering is removed.</p><p>This spell does not stack with itself. <i>Darkness</i> can be used to counter or dispel any light spell of equal or lower spell level.</p></h4><h5><b>Mythic: </b>The illumination level in the area drops to darkness regardless of the existing lighting conditions. Non-mythic spells and effects can't increase the light level in the area. Creatures with darkvision and the power to see in darkness have difficulty seeing through this darkness, and can see as well as a human can in dim light. Creatures in the area of darkness take a -2 penalty on saves against fear.</h5></div>"},
new Spell(){ Name = "Darkvision", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Darkvision</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>ranger 3, sorcerer/wizard 2, alchemist 2, antipaladin 2, occultist 2, psychic 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (either a pinch of dried carrot or an agate)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject gains the ability to see 60 feet even in total darkness. Darkvision is black and white only but otherwise like normal sight.</p><p><i>Darkvision</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Daylight", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Daylight</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [light]; <b>Level </b>bard 3, cleric/oracle 3, druid 3, paladin 3, sorcerer/wizard 3, inquisitor 3, magus 3, shaman 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You touch an object when you cast this spell, causing the object to shed bright light in a 60-foot radius. This illumination increases the light level for an additional 60 feet by one step (<i>darkness</i> becomes dim light, dim light becomes normal light, and normal light becomes bright light). Creatures that take penalties in bright light take them while within the 60-foot radius of this magical light. Despite its name, this spell is not the equivalent of <i>daylight</i> for the purposes of creatures that are damaged or destroyed by such light.</p><p>If <i>daylight</i> is cast on a small object that is then placed inside or under a light-proof covering, the spell's effects are blocked until the covering is removed.</p><p><i>Daylight</i> brought into an area of magical <i>darkness</i> (or vice versa) is temporarily negated, so that the otherwise prevailing light conditions exist in the overlapping areas of effect.</p><p><i>Daylight</i> counters or dispels any <i>darkness</i> spell of equal or lower level, such as <i>darkness</i>.</p></h4><h5><b>Mythic: </b>The illumination in the additional 60 foot radius increases to at least normal light regardless of the existing lighting conditions. Creatures that take penalties in bright light double those penalties while in the area of bright light produced by this spell. All other creatures in the area of bright light gain a +2 circumstance bonus on Perception checks and saves to resist fear.</h5></div>"},
new Spell(){ Name = "Daze", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Daze</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 0, sorcerer/wizard 0, summoner 0, witch 0, inquisitor 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a pinch of wool or similar substance)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one humanoid creature of 4 HD or less</h5><h5><b>Duration </b>1 round</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell clouds the mind of a humanoid creature with 4 or fewer Hit Dice so that it takes no actions. Humanoids of 5 or more HD are not affected. A dazed subject is not stunned, so attackers get no special advantage against it. After a creature has been dazed by this spell, it is immune to the effects of this spell for 1 minute.</p></h4></div>"},
new Spell(){ Name = "Daze Monster", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Daze Monster</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 2, sorcerer/wizard 2, witch 2, summoner 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a pinch of wool or similar substance)</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b> one living creature of 6 HD or less</h5><h5><b>Duration </b>1 round</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>daze</i>, but it can affect any one living creature of any type. Creatures of 7 or more HD are not affected.</p></h4></div>"},
new Spell(){ Name = "Death Knell", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Death Knell</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [death, evil]; <b>Level </b>antipaladin 1, cleric/oracle 2, inquisitor 2, witch 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>instantaneous/10 minutes per HD of subject; see text</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You draw forth the ebbing life force of a creature and use it to fuel your own power. Upon casting this spell, you touch a living creature that has -1 or fewer hit points. If the subject fails its saving throw, it dies, and you gain 1d8 temporary hit points and a +2 enhancement bonus to Strength. Additionally, your effective caster level goes up by +1, improving spell effects dependent on caster level. This increase in effective caster level does not grant you access to more spells. These effects last for 10 minutes per HD of the subject creature.</p></h4><h5><b>Mythic: </b>Increase the spell's range to close (25 feet + 5 feet per 2 caster levels). Increase the temporary hp gained from the spell to 2d8 and the enhancement bonuses to Strength to +4, and gain a +4 enhancement bonus to Dexterity.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the casting time decreases to 1 immediate action, and you may target a creature that you killed on your last turn as if it were still alive; the dead target gets no save.</h5></div>"},
new Spell(){ Name = "Death Ward", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Death Ward</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 4, druid 5, paladin 4, witch 4, inquisitor 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> living creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject gains a +4 morale bonus on saves against all death spells and magical death effects. The subject is granted a save to negate such effects even if one is not normally allowed. The subject is immune to energy drain and any negative energy effects, including channeled negative energy.</p><p>This spell does not remove negative levels that the subject has already gained, but it does remove the penalties from negative levels for the duration of its effect.</p><p><i>Death ward</i> does not protect against other sorts of attacks, even if those attacks might be lethal.</p></h4></div>"},
new Spell(){ Name = "Deathwatch", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Deathwatch</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>30 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>Using the powers of necromancy, you can determine the condition of creatures near death within the spell's range. You instantly know whether each creature within the area is dead, fragile (alive and wounded, with 3 or fewer hit points left), fighting off death (alive with 4 or more hit points), healthy, undead, or neither alive nor dead (such as a construct). <i>Deathwatch</i> sees through any spell or ability that allows creatures to feign death.</p></h4></div>"},
new Spell(){ Name = "Deeper Darkness", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Deeper Darkness</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [darkness]; <b>Level </b>cleric/oracle 3, shaman 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, M/DF (bat fur and a piece of coal)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>darkness</i>, except that objects radiate <i>darkness</i> in a 60-foot radius and the light level is lowered by two steps. Bright light becomes dim light and normal light becomes <i>darkness</i>. Areas of dim light and <i>darkness</i> become supernaturally dark. This functions like <i>darkness</i>, but even creatures with darkvision cannot see within the spell's confines.</p><p>This spell does not stack with itself. <i>Deeper darkness</i> can be used to counter or dispel any light spell of equal or lower spell level.</p></h4></div>"},
new Spell(){ Name = "Deep Slumber", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Deep Slumber</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 3, sorcerer/wizard 3, witch 3, shaman 3, occultist 3, psychic 3, mesmerist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (fine sand, rose petals, or a live cricket)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>one or more living creatures within a 10-ft.-radius burst</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>sleep</i>, except that it affects 10 HD of targets.</p></h4><h5><b>Mythic: </b>This spell works like mythic sleep, except that it affects up to 20 Hit Dice of creatures. Alternatively, you may target one creature rather than an area, increasing the duration to 1 hour per level.</h5><h5><b>Augmented (5th)</b>: If you expend two uses of mythic power, creatures affected by the spell can be awakened only by mythic effects. If you're 5th tier and expend three uses of mythic power, the duration increases to 10 years per tier. If you're 5th tier and expend four uses of mythic power, the spell has both augmented effects. Creatures sleeping for years because of this spell age and breathe as normal, but don't need to eat or drink while the spell lasts.</h5></div>"},
new Spell(){ Name = "Delayed Blast Fireball", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Delayed Blast Fireball</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>sorcerer/wizard 7</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a ball of bat guano and sulfur)</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>20-ft.-radius spread</h5><h5><b>Duration </b>5 rounds or less; see text</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>fireball</i>, except that it is more powerful and can detonate up to 5 rounds after the spell is cast. The burst of flame deals 1d6 points of fire damage per caster level (maximum 20d6). The glowing bead created by <i>delayed blast fireball</i> can detonate immediately if you desire, or you can choose to delay the burst for as many as 5 rounds. You select the amount of delay upon completing the spell, and that time cannot change once it has been set unless someone touches the bead. If you choose a delay, the glowing bead sits at its destination until it detonates. A creature can pick up and hurl the bead as a thrown weapon (range increment 10 feet). If a creature handles and moves the bead within 1 round of its detonation, there is a 25% chance that the bead detonates while being handled.</p></h4></div>"},
new Spell(){ Name = "Delay Poison", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Delay Poison</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 2, cleric/oracle 2, druid 2, paladin 2, ranger 1, alchemist 2, witch 2, inquisitor 2, shaman 2, occultist 2, mesmerist 2, alchemist 2, witch 2, inquisitor 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b> creature touched</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Fortitude negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject becomes temporarily immune to poison. Any poison in its system or any poison to which it is exposed during the spell's duration does not affect the subject until the spell's duration has expired. <i>Delay poison</i> does not cure any damage that poison may have already done.</p></h4></div>"},
new Spell(){ Name = "Demand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Demand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 8, witch 8, psychic 8, mesmerist 6</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M/DF (fine copper wire)</h5><h5><b>Range </b>see text</h5><h5><b>Targets </b> one creature</h5><h5><b>Duration </b>1 round; see text</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>sending</i>, but the message can also contain a <i>suggestion</i> (see the <i>suggestion</i> spell), which the subject does its best to carry out. A successful Will save negates the <i>suggestion</i> effect but not the contact itself. The <i>demand</i>, if received, is understood even if the subject's Intelligence score is as low as 1. If the message is impossible or meaningless according to the circumstances that exist for the subject at the time the <i>demand</i> is issued, the message is understood but the <i>suggestion</i> is ineffective.</p><p>The <i>demand's</i> message to the creature must be 25 words or less, including the <i>suggestion</i>. The creature can also give a short reply immediately.</p></h4></div>"},
new Spell(){ Name = "Desecrate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Desecrate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [evil]; <b>Level </b>cleric/oracle 2, inquisitor 2, antipaladin 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a vial of unholy water and 25 gp worth (5 pounds) of silver dust, all of which must be sprinkled around the area), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>20-ft.-radius emanation</h5><h5><b>Duration </b>2 hours/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell imbues an area with negative energy. The DC to resist negative channeled energy within this area gains a +3 profane bonus. Every undead creature entering a <i><i>desecrate</i>d</i> area gains a +1 profane bonus on all attack rolls, damage rolls, and saving throws.</p><p>An undead creature created within or summoned into such an area gains +1 hit points per HD. If the <i><i>desecrate</i>d</i> area contains an altar, shrine, or other permanent fixture dedicated to your deity or aligned higher power, the modifiers given above are doubled (+6 profane bonus to negative channeled energy DCs, +2 profane bonus and +2 hit points per HD for undead created in the area).</p><p>Furthermore, anyone who casts <i>animate dead</i> within this area may create as many as double the normal amount of undead (that is, 4 HD per caster level rather than 2 HD per caster level).</p><p>If the area contains an altar, shrine, or other permanent fixture of a deity, pantheon, or higher power other than your patron, the <i>desecrate</i> spell instead curses the area, cutting off its connection with the associated deity or power. This secondary function, if used, does not also grant the bonuses and penalties relating to undead, as given above.</p><p><i>Desecrate</i> counters and dispels <i>consecrate</i>.</p></h4><h5><b>Mythic: </b>Select one alignment component that you have (chaotic, evil, or lawful). Outsiders with the opposite alignment subtype of the chosen alignment component take a -1 penalty on attack rolls, damage rolls, and saves in the area.</h5></div>"},
new Spell(){ Name = "Destruction", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Destruction</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [death]; <b>Level </b>cleric/oracle 7, witch 8, shaman 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (holy or unholy symbol costing 500 gp)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell instantly delivers 10 points of damage per caster level. If the spell slays the target, it consumes the remains utterly in holy (or unholy) fire (but not its equipment or possessions). If the target's Fortitude saving throw succeeds, it instead takes 10d6 points of damage. The only way to restore life to a character who has failed to save against this spell (and was slain) is to use <i>true resurrection</i>, a carefully worded <i>wish</i> spell followed by <i>resurrection</i>, or <i>miracle</i>.</p></h4></div>"},
new Spell(){ Name = "Detect Animals or Plants", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Animals or Plants</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>druid 1, ranger 1, shaman 1, psychic 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 10 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can detect a particular kind of animal or plant in a cone emanating out from you in whatever direction you face. You must think of a kind of animal or plant when using the spell, but you can change the animal or plant kind each round. The amount of information revealed depends on how long you search a particular area or focus on a specific kind of animal or plant.</p><p><i>1st Round</i>: Presence or absence of that kind of animal or plant in the area.</p><p><i>2nd Round</i>: Number of individuals of the specified kind in the area and the condition of the healthiest specimen.</p><p><i>3rd Round</i>: The condition (see below) and location of each individual present. If an animal or a plant is outside your line of sight, then you discern its direction but not its exact location.</p><p><i>Conditions</i>: For purposes of this spell, the categories of condition are as follows: Normal: Has at least 90% of full normal hit points, free of disease.</p><p>Fair: 30% to 90% of full normal hit points remaining.</p><p>Poor: Less than 30% of full normal hit points remaining, afflicted with a disease, or suffering from a debilitating injury.</p><p>Weak: 0 or fewer hit points remaining, afflicted with a disease that has reduced an ability score to 5 or less, or crippled.</p><p>If a creature falls into more than one category, the spell indicates the weaker of the two.</p><p>Each round you can turn to detect a kind of animal or plant in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p></h4></div>"},
new Spell(){ Name = "Detect Chaos", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Chaos</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 1, inquisitor 1, shaman 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 10 min./ level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell functions like <i>detect evil</i>, except that it detects the auras of chaotic creatures, clerics of chaotic deities, chaotic spells, and chaotic magic items, and you are vulnerable to an overwhelming chaotic aura if you are lawful.</p></h4></div>"},
new Spell(){ Name = "Detect Evil", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Evil</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 1, inquisitor 1, shaman 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 10 min./ level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can sense the presence of evil. The amount of information revealed depends on how long you study a particular area or subject.</p><p><i>1st Round</i>: Presence or absence of evil.</p><p><i>2nd Round</i>: Number of evil auras (creatures, objects, or spells) in the area and the power of the most potent evil aura present.</p><p>If you are of good alignment, and the strongest evil aura's power is overwhelming (see below), and the HD or level of the aura's source is at least twice your character level, you are stunned for 1 round and the spell ends.</p><p><i>3rd Round</i>: The power and location of each aura. If an aura is outside your line of sight, then you discern its direction but not its exact location.</p><p><i>Aura Power</i>: An evil aura's power depends on the type of evil creature or object that you're detecting and its HD, caster level, or (in the case of a cleric) class level; see the table below. If an aura falls into more than one strength category, the spell indicates the stronger of the two.</p><p><i>Lingering Aura</i>: An evil aura lingers after its original source dissipates (in the case of a spell) or is destroyed (in the case of a creature or magic item). If <i>detect evil</i> is cast and directed at such a location, the spell indicates an aura strength of dim (even weaker than a faint aura). How long the aura lingers at this dim level depends on its original power: <table><tr><th>Original Strength</th><th>Duration of Lingering Aura</th></tr> <tr><td>Faint</td><td>1d6 rounds</td></tr> <tr><td>Moderate</td><td>1d6 minutes</td></tr> <tr><td>Strong</td><td>1d6x10 minutes</td></tr> <tr><td>Overwhelming</td><td>1d6 days</td></tr></table>  Animals, traps, poisons, and other potential perils are not evil, and as such this spell does not detect them. Creatures with actively evil intents count as evil creatures for the purpose of this spell.</p><p> Each round, you can turn to <i>detect evil</i> in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p><p><table><caption>Detect Chaos/Evil/Good/Law</caption> <tr><th rowspan=""2"">Creature/Object</th><th colspan=""5"">Aura Power</th></tr> <tr><th style=""text-align:center"">None</th><th style=""text-align:center"">Faint</th><th style=""text-align:center"">Moderate</th><th style=""text-align:center"">Strong</th><th style=""text-align:center"">Overwhelming</th></tr> <tr><td>Aligned creature<sup>1</sup> (HD)</td><td style=""text-align:center"">5 or lower</td><td style=""text-align:center"">5-10</td><td style=""text-align:center"">11-25</td><td style=""text-align:center"">26-50</td><td style=""text-align:center"">51 or higher</td></tr> <tr><td>Aligned Undead (HD)</td><td style=""text-align:center"">--<br /></td><td style=""text-align:center"">2 or lower</td><td style=""text-align:center"">3-8</td><td style=""text-align:center"">9-20</td><td style=""text-align:center"">21 or higher</td></tr>  <tr><td>Aligned outsider (HD)</td><td style=""text-align:center"">--<br /></td><td style=""text-align:center"">1 or lower</td><td style=""text-align:center"">2-4</td><td style=""text-align:center"">5-10</td><td style=""text-align:center"">11 or higher</td></tr> <tr><td>Cleric or paladin of an aligned deity<sup>2</sup> (class levels)</td><td style=""text-align:center"">--<br /></td><td style=""text-align:center"">1</td><td style=""text-align:center"">2-4</td><td style=""text-align:center"">5-10</td><td style=""text-align:center"">11 or higher</td></tr>  <tr><td>Aligned magic item or spell (caster level)</td><td>5th or lower</td><td style=""text-align:center"">6th-10th</td><td style=""text-align:center"">11th-15th</td><td style=""text-align:center"">16th-20th</td><td style=""text-align:center"">21st or higher</td></tr> </tbody><tfoot><tr><td colspan=""6"">1 Except for undead and outsiders, which have their own entries on the table.</td></tr> <tr><td colspan=""6"">2 Some characters who are not clerics may radiate an aura of equivalent power. The class description will indicate whether this applies.</td></tr></tfoot></table></div></td></tr></table></p></h4></div>"},
new Spell(){ Name = "Detect Good", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Good</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 1, inquisitor 1, shaman 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 10 min./ level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell functions like <i>detect evil</i>, except that it detects the auras of good creatures, clerics or paladins of good deities, good spells, and good magic items, and you are vulnerable to an overwhelming good aura if you are evil.</p></h4></div>"},
new Spell(){ Name = "Detect Law", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Law</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 1, inquisitor 1, shaman 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 10 min./ level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell functions like <i>detect evil</i>, except that it detects the auras of lawful creatures, clerics of lawful deities, lawful spells, and lawful magic items, and you are vulnerable to an overwhelming lawful aura if you are chaotic.</p></h4></div>"},
new Spell(){ Name = "Detect Magic", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Magic</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 0, cleric/oracle 0, druid 0, sorcerer/wizard 0, summoner 0, witch 0, inquisitor 0, magus 0, shaman 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0, summoner 0, witch 0, inquisitor 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You detect magical auras. The amount of information revealed depends on how long you study a particular area or subject.</p><p><i>1st Round</i>: Presence or absence of magical auras.</p><p><i>2nd Round</i>: Number of different magical auras and the power of the most potent aura.</p><p><i>3rd Round</i>: The strength and location of each aura. If the items or creatures bearing the auras are in line of sight, you can make Knowledge (arcana) skill checks to determine the school of magic involved in each. (Make one check per aura: DC 15 + spell level, or 15 + 1/2 caster level for a nonspell effect.) If the aura emanates from a magic item, you can attempt to identify its properties (see Spellcraft).</p><p>Magical areas, multiple types of magic, or strong local magical emanations may distort or conceal weaker auras.</p><p><i>Aura Strength</i>: An aura's power depends on a spell's functioning spell level or an item's caster level; see the accompanying table. If an aura falls into more than one category, detect magic indicates the stronger of the two.</p><p><table><tr><th rowspan=""2"">Spell or Object</th><th colspan=""4"" style=""text-align:center"">Aura Power</th></tr> <tr><th>Faint</th><th>Moderate</th><th>Strong</th><th>Overwhelming</th></tr> <tr><td>Functioning spell (spell level)</td><td>3rd or lower</td><td>4th-6th</td><td>7th-9th</td><td>10th+ (deity-level)</td></tr> <tr><td>Magic item (caster level)</td><td>5th or lower</td><td>6th-11th</td><td>12th-20th</td><td>21st+ (artifact)</td></tr></table>  <i>Lingering Aura</i>: A magical aura lingers after its original source dissipates (in the case of a spell) or is destroyed (in the case of a magic item). If <i>detect magic</i> is cast and directed at such a location, the spell indicates an aura strength of dim (even weaker than a faint aura). How long the aura lingers at this dim level depends on its original power: <table><tr><th>Original Strength</th><th>Duration of Lingering Aura</th></tr> <tr><td>Faint</td><td>1d6 rounds</td></tr> <tr><td>Moderate</td><td>1d6 minutes</td></tr> <tr><td>Strong</td><td>1d6x10 minutes</td></tr> <tr><td>Overwhelming</td><td>1d6 days</td></tr></table>  Outsiders and elementals are not magical in themselves, but if they are summoned, the conjuration spell registers. Each round, you can turn to detect magic in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p><p><i>Detect magic</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Detect Poison", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Poison</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 0, druid 0, paladin 1, ranger 1, sorcerer/wizard 0, shaman 0, occultist 0, psychic 0, mesmerist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Target or Area </b>one creature, one object, or a 5-ft. cube<h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You determine whether a creature, object, or area has been poisoned or is poisonous. You can determine the exact type of poison with a DC 20 Wisdom check. A character with the Craft (alchemy) skill may try a DC 20 Craft (alchemy) check if the Wisdom check fails, or may try the Craft (alchemy) check prior to the Wisdom check. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p></h4></div>"},
new Spell(){ Name = "Detect Scrying", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Scrying</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 4, sorcerer/wizard 4, witch 4, inquisitor 4, shaman 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of mirror and a miniature brass hearing trumpet)</h5><h5><b>Range </b>40 ft.</h5><h5><b>Area </b>40-ft.-radius emanation centered on you</h5><h5><b>Duration </b>24 hours</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You immediately become aware of any attempt to observe you by means of a divination (scrying) spell or effect. The spell's area radiates from you and moves as you move. You know the location of every magical sensor within the spell's area.</p><p>If the scrying attempt originates within the area, you also know its location; otherwise, you and the scrier immediately make opposed caster level checks (1d20 + caster level). If you at least match the scrier's result, you get a visual image of the scrier and an accurate sense of his direction and distance from you.</p></h4></div>"},
new Spell(){ Name = "Detect Secret Doors", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Secret Doors</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 1, sorcerer/wizard 1, witch 1, alchemist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can detect secret doors, compartments, caches, and so forth. Only passages, doors, or openings that have been specifically constructed to escape detection are detected by this spell. The amount of information revealed depends on how long you study a particular area or subject.</p><p><i>1st Round</i>: Presence or absence of secret doors.</p><p><i>2nd Round</i>: Number of secret doors and the location of each. If an aura is outside your line of sight, then you discern its direction but not its exact location.</p><p><i>Each Additional Round</i>: The mechanism or trigger for one particular secret portal closely examined by you. Each round, you can turn to detect secret doors in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p></h4></div>"},
new Spell(){ Name = "Detect Snares and Pits", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Snares and Pits</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>druid 1, ranger 1, occultist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 10 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can detect simple pits, deadfalls, and snares as well as mechanical traps constructed of natural materials. The spell does not detect complex traps, including trapdoor traps.</p><p><i>Detect snares and pits</i> does detect certain natural hazards--quicksand (a snare), a sinkhole (a pit), or unsafe walls of natural rock (a deadfall). It does not reveal other potentially dangerous conditions. The spell does not detect magic traps (except those that operate by pit, deadfall, or snaring; see the spell snare), nor mechanically complex ones, nor those that have been rendered safe or inactive.</p><p>The amount of information revealed depends on how long you study a particular area.</p><p><i>1st Round</i>: Presence or absence of hazards.</p><p><i>2nd Round</i>: Number of hazards and the location of each. If a hazard is outside your line of sight, then you discern its direction but not its exact location.</p><p><i>Each Additional Round</i>: The general type and trigger for one particular hazard closely examined by you.</p><p>Each round, you can turn to examine a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p></h4></div>"},
new Spell(){ Name = "Detect Thoughts", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Thoughts</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination [mind-affecting]; <b>Level </b>bard 2, sorcerer/wizard 2, alchemist 2, summoner 2, witch 2, inquisitor 2, psychic 1, mesmerist 2, spiritualist 2, alchemist 2, summoner 2, witch 2, inquisitor 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a copper piece)</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>no</h5></div><div><h4><p>You detect surface thoughts. The amount of information revealed depends on how long you study a particular area or subject.</p><p><i>1st Round</i>: Presence or absence of thoughts (from conscious creatures with Intelligence scores of 1 or higher).</p><p><i>2nd Round</i>: Number of thinking minds and the Intelligence score of each. If the highest Intelligence is 26 or higher (and at least 10 points higher than your own Intelligence score), you are stunned for 1 round and the spell ends. This spell does not let you determine the location of the thinking minds if you can't see the creatures whose thoughts you are detecting.</p><p><i>3rd Round</i>: Surface thoughts of any mind in the area. A target's Will save prevents you from reading its thoughts, and you must cast <i>detect thoughts</i> again to have another chance. Creatures of animal intelligence (Int 1 or 2) have simple, instinctual thoughts.</p><p>Each round, you can turn to detect thoughts in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p></h4></div>"},
new Spell(){ Name = "Detect Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Detect Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 1, paladin 1, sorcerer/wizard 1, alchemist 1, inquisitor 1, alchemist 1, inquisitor 1, shaman 1, psychic 1, mesmerist 1, spiritualist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (earth from a grave)</h5><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>concentration, up to 1 minute/ level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>You can detect the aura that surrounds undead creatures. The amount of information revealed depends on how long you study a particular area.</p><p><i>1st Round</i>: Presence or absence of undead auras.</p><p><i>2nd Round</i>: Number of undead auras in the area and the strength of the strongest undead aura present. If you are of good alignment, and the strongest undead aura's strength is overwhelming (see below), and the creature has HD of at least twice your character level, you are stunned for 1 round and the spell ends.</p><p><i>3rd Round</i>: The strength and location of each undead aura. If an aura is outside your line of sight, then you discern its direction but not its exact location.</p><p><i>Aura Strength</i>: The strength of an undead aura is determined by the HD of the undead creature, as given on the table below.</p><p><i>Lingering Aura</i>: An undead aura lingers after its original source is destroyed. If detect undead is cast and directed at such a location, the spell indicates an aura strength of dim (even weaker than a faint aura). How long the aura lingers at this dim level depends on its original power, as given on the table below.</p><p><table><tr><th>HD</th><th>Strength</th><th>Lingering Aura Duration</th></tr> <tr><td>1 or lower</td><td>Faint</td><td>1d6 rounds</td></tr> <tr><td>2-4</td><td>Moderate</td><td>1d6 minutes</td></tr> <tr><td>5-10</td><td>Strong</td><td>1d6 x 10 minutes</td></tr> <tr><td>11 or higher</td><td>Overwhelming</td><td>1d6 days</td></tr></table>  Each round, you can turn to detect undead in a new area. The spell can penetrate barriers, but 1 foot of stone, 1 inch of common metal, a thin sheet of lead, or 3 feet of wood or dirt blocks it.</p></h4></div>"},
new Spell(){ Name = "Dictum", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dictum</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [lawful, sonic]; <b>Level </b>cleric/oracle 7, inquisitor 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>40 ft.</h5><h5><b>Area </b>nonlawful creatures in a 40-ft.-radius spread centered on you</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none or Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any nonlawful creature within the area of a <i>dictum</i> spell suffers the following ill effects.</p>   <table border ='1'><tr><th>HD</th><th>Effect</th></tr><tr><td>Equal to caster level</td><td>Deafened</td></tr><tr><td>Up to caster level -1</td><td>Staggered, deafened</td></tr><tr><td>Up to caster level -5</td><td>Paralyzed, staggered, deafened</td></tr><tr><td>Up to caster level -10</td><td>Killed, paralyzed, staggered, deafened</td></tr></table>   The effects are cumulative and concurrent. A successful Will save reduces or eliminates these effects. Creatures affected by multiple effects make only one save and apply the result to all the effects.</p><p><i>Deafened</i>: The creature is deafened for 1d4 rounds. Save negates.</p><p><i>Staggered</i>: The creature is staggered for 2d4 rounds. Save reduces the staggered effect to 1d4 rounds.</p><p><i>Paralyzed</i>: The creature is paralyzed and helpless for 1d10 minutes. Save reduces the paralyzed effect to 1 round.</p><p><i>Killed</i>: Living creatures die. Undead creatures are destroyed. Save negates. If the save is successful, the creature instead takes 3d6 points of damage + 1 point per caster level (maximum +25).</p><p>Furthermore, if you are on your home plane when you cast this spell, nonlawful extraplanar creatures within the area are instantly banished back to their home planes. Creatures so banished cannot return for at least 24 hours. This effect takes place regardless of whether the creatures hear the <i>dictum</i> or not. The banishment allows a Will save (at a -4 penalty) to negate.</p><p>Creatures whose Hit Dice exceed your caster level are unaffected by <i>dictum</i>.</p></h4><h5><b>Mythic: </b>When determining the spell's effect on non-mythic creatures, add your tier to your caster level. Nonlawful creatures that fail their saves against the spell also take a -4 penalty on attack rolls and saving throws, and their spell resistance decreases by 5 for as long as the spell's other effects last.</h5></div>"},
new Spell(){ Name = "Dimensional Anchor", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dimensional Anchor</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 4, sorcerer/wizard 4, summoner 3, inquisitor 3, occultist 4, psychic 4, spiritualist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes (object)</h5></div><div><h4><p>A green ray springs from your hand. You must make a ranged touch attack to hit the target. Any creature or object struck by the ray is covered with a shimmering emerald field that completely blocks extradimensional travel. Forms of movement barred by a <i>dimensional anchor</i> include <i>astral projection</i>, <i>blink</i>, <i>dimension door</i>, <i>ethereal jaunt</i>, <i>etherealness</i>, <i>gate</i>, <i>maze</i>, <i>plane shift</i>, <i>shadow walk</i>, <i>teleport</i>, and similar spell-like abilities. The spell also prevents the use of a <i>gate</i> or <i>teleportation circle</i> for the duration of the spell.</p><p>A <i>dimensional anchor</i> does not interfere with the movement of creatures already in ethereal or astral form when the spell is cast, nor does it block extradimensional perception or attack forms. Also, <i>dimensional anchor</i> does not prevent summoned creatures from disappearing at the end of a summoning spell.</p></h4></div>"},
new Spell(){ Name = "Dimensional Lock", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dimensional Lock</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 8, sorcerer/wizard 8, summoner 6, psychic 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>20-ft.-radius emanation centered on a point in space</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You create a shimmering emerald barrier that completely blocks extradimensional travel. Forms of movement barred include <i>astral projection</i>, <i>dimension door</i>, <i>ethereal jaunt</i>, <i>etherealness</i>, <i>gate</i>, <i>maze</i>, <i>plane shift</i>, <i>shadow walk</i>, <i>teleport</i>, and similar spell-like abilities. Once <i>dimensional lock</i> is in place, extradimensional travel into or out of the area is not possible.</p><p>A <i>dimensional lock</i> does not interfere with the movement of creatures already in ethereal or astral form when the spell is cast, nor does it block extradimensional perception or attack forms. Also, the spell does not prevent summoned creatures from disappearing at the end of a summoning spell.</p></h4></div>"},
new Spell(){ Name = "Dimension Door", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dimension Door</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (teleportation); <b>Level </b>bard 4, sorcerer/wizard 4, summoner 3, witch 4, magus 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Targets </b> you and touched objects or other touched willing creatures</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none and Will negates (object); <b>Spell Resistance </b>no and yes (object)</h5></div><div><h4><p>You instantly transfer yourself from your current location to any other spot within range. You always arrive at exactly the spot desired--whether by simply visualizing the area or by stating direction. After using this spell, you can't take any other actions until your next turn. You can bring along objects as long as their weight doesn't exceed your maximum load. You may also bring one additional willing Medium or smaller creature (carrying gear or objects up to its maximum load) or its equivalent per three caster levels. A Large creature counts as two Medium creatures, a Huge creature counts as two Large creatures, and so forth. All creatures to be transported must be in contact with one another, and at least one of those creatures must be in contact with you.</p><p>If you arrive in a place that is already occupied by a solid body, you and each creature traveling with you take 1d6 points of damage and are shunted to a random open space on a suitable surface within 100 feet of the intended location.</p><p>If there is no free space within 100 feet, you and each creature traveling with you take an additional 2d6 points of damage and are shunted to a free space within 1,000 feet. If there is no free space within 1,000 feet, you and each creature travelling with you take an additional 4d6 points of damage and the spell simply fails.</p></h4></div>"},
new Spell(){ Name = "Diminish Plants", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Diminish Plants</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 3, ranger 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Area </b>see text</h5><h5><b>Targets </b>see text</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell has two versions.</p><p><i>Prune Growth</i>: This version of the spell causes normal vegetation within long range (400 feet + 40 feet per level) to shrink to about one-third normal size, becoming untangled and less bushy. The affected vegetation appears to have been carefully pruned and trimmed. This version of <i>diminish plants</i> automatically dispels any spells or effects that enhance plants, such as <i>entangle</i>, <i>plant growth</i>, and <i>wall of thorns</i>.</p><p>At your option, the area can be a 100-foot-radius circle, a 150-foot-radius semicircle, or a 200-foot-radius quarter-circle. You may also designate portions of the area that are not affected.</p><p><i>Stunt Growth</i>: This version of the spell targets all normal plants within a range of 1/2 mile, reducing their potential productivity over the course of the following year to half normal.</p><p>This spell has no effect on plant creatures.</p></h4></div>"},
new Spell(){ Name = "Discern Lies", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Discern Lies</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 4, paladin 3, alchemist 4, witch 4, inquisitor 4, alchemist 4, witch 4, inquisitor 4, psychic 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>concentration, up to 1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no</h5></div><div><h4><p>Each round, you concentrate on one target, who must be within range. You know if the target deliberately and knowingly speaks a lie by discerning disturbances in its aura caused by lying. The spell does not reveal the truth, uncover unintentional inaccuracies, or necessarily reveal evasions.</p><p>Each round, you may concentrate on a different target.</p></h4></div>"},
new Spell(){ Name = "Discern Location", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Discern Location</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 8, sorcerer/wizard 8, summoner 6, witch 8, shaman 8, psychic 8</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>unlimited</h5><h5><b>Targets </b> one creature or object</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>A <i>discern location</i> spell is among the most powerful means of locating creatures or objects. Nothing short of a <i>mind blank</i> spell or the direct intervention of a deity keeps you from learning the exact location of a single individual or object. <i>Discern location</i> circumvents normal means of protection from scrying or location. The spell reveals the name of the creature or object's location (place, name, business name, building name, or the like), community, county (or similar political division), country, continent, and the plane of existence where the target lies.</p><p>To find a creature with the spell, you must have seen the creature or have some item that once belonged to it. To find an object, you must have touched it at least once.</p></h4></div>"},
new Spell(){ Name = "Disguise Self", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Disguise Self</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 1, sorcerer/wizard 1, alchemist 1, inquisitor 1, antipaladin 1, occultist 1, psychic 1, mesmerist 1</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b> you</h5><h5><b>Duration </b>10 min./level (D)</h5></div><div><h4><p>You make yourself--including clothing, armor, weapons, and equipmen--look different. You can seem 1 foot shorter or taller, thin, fat, or in between. You cannot change your creature type (although you can appear as another subtype). Otherwise, the extent of the apparent change is up to you. You could add or obscure a minor feature or look like an entirely different person or gender.</p><p>The spell does not provide the abilities or mannerisms of the chosen form, nor does it alter the perceived tactile (touch) or audible (sound) properties of you or your equipment. If you use this spell to create a disguise, you get a +10 bonus on the Disguise check. A creature that interacts with the glamer gets a Will save to recognize it as an illusion.</p></h4></div>"},
new Spell(){ Name = "Disintegrate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Disintegrate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 6, magus 6, occultist 6, psychic 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a lodestone and a pinch of dust)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial (object); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A thin, green ray springs from your pointing finger. You must make a successful ranged touch attack to hit. Any creature struck by the ray takes 2d6 points of damage per caster level (to a maximum of 40d6). Any creature reduced to 0 or fewer hit points by this spell is entirely disintegrated, leaving behind only a trace of fine dust. A disintegrated creature's equipment is unaffected.</p><p>When used against an object, the ray simply disintegrates as much as a 10-foot cube of nonliving matter. Thus, the spell disintegrates only part of any very large object or structure targeted.</p><p>The ray affects even objects constructed entirely of force, such as <i>forceful hand</i> or a <i>wall of force</i>, but not magical effects such as a <i>globe of invulnerability</i> or an <i>antimagic field</i>.</p><p>A creature or object that makes a successful Fortitude save is partially affected, taking only 5d6 points of damage. If this damage reduces the creature or object to 0 or fewer hit points, it is entirely disintegrated.</p><p>Only the first creature or object struck can be affected; that is, the ray affects only one target per casting.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 3d6 points of damage per caster level (maximum 60d6) plus 1d4 points of Constitution damage. A creature that succeeds at its saving throw takes 5d8 points of damage plus 1 point of Constitution damage. A creature whose Constitution score is reduced to 0 by this spell is entirely disintegrated.</h5><h5><b>Augmented (7th)</b>: If you expend two uses of mythic power, you fire two rays. Alternatively, you may fire only one ray, but if the target is a non-mythic creature and it fails its saving throw, it's automatically disintegrated regardless of its current hp.</h5></div>"},
new Spell(){ Name = "Dismissal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dismissal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 4, sorcerer/wizard 5, summoner 4, inquisitor 4, shaman 4, occultist 4, psychic 5, spiritualist 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b> one extraplanar creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell forces an extraplanar creature back to its proper plane if it fails a Will save. If the spell is successful, the creature is instantly whisked away, but there is a 20% chance of actually sending the subject to a plane other than its own.</p></h4></div>"},
new Spell(){ Name = "Dispel Chaos", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dispel Chaos</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [lawful]; <b>Level </b>cleric/oracle 5, paladin 4, inquisitor 5, shaman 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>you and a touched chaotic creature from another plane, or you and an enchantment or chaotic spell on a touched creature or object</h5><h5><b>Duration </b>1 round/level or until discharged, whichever comes first</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><div><h4><p>This spell functions like <i>dispel evil</i>, except that you are surrounded by constant, blue lawful energy, and the spell affects chaotic creatures and spells rather than evil ones.</p></h4></div>"},
new Spell(){ Name = "Dispel Evil", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dispel Evil</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [good]; <b>Level </b>cleric/oracle 5, paladin 4, inquisitor 5, shaman 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>you and a touched evil creature from another plane, or you and an enchantment or evil spell on a touched creature or object</h5><h5><b>Duration </b>1 round/level or until discharged, whichever comes first</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><div><h4><p>Shimmering, white holy energy surrounds you. This energy has three effects.</p><p>First, you gain a +4 deflection bonus to AC against attacks by evil creatures.</p><p>Second, on making a successful melee touch attack against an evil creature from another plane, you can choose to drive that creature back to its home plane. The creature can negate the effects with a successful Will save (spell resistance applies). This use discharges and ends the spell.</p><p>Third, with a touch you can automatically dispel any one enchantment spell cast by an evil creature or any one evil spell. Spells that can't be dispelled by <i>dispel magic</i> also can't be dispelled by <i>dispel evil</i>. Saving throws and spell resistance do not apply to this effect. This use discharges and ends the spell.</p></h4></div>"},
new Spell(){ Name = "Dispel Good", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dispel Good</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [evil]; <b>Level </b>cleric/oracle 5, inquisitor 5, antipaladin 4, shaman 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>you and a touched good creature from another plane, or you and an enchantment or goodl spell on a touched creature or object</h5><h5><b>Duration </b>1 round/level or until discharged, whichever comes first</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><div><h4><p>This spell functions like <i>dispel evil</i>, except that you are surrounded by dark, wavering unholy energy, and the spell affects good creatures and spells rather than evil ones.</p></h4></div>"},
new Spell(){ Name = "Dispel Law", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dispel Law</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [chaotic]; <b>Level </b>cleric/oracle 5, inquisitor 5, antipaladin 4, shaman 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>you and a touched lawful creature from another plane, or you and an enchantment or lawful spell on a touched creature or object</h5><h5><b>Duration </b>1 round/level or until discharged, whichever comes first</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><div><h4><p>This spell functions like <i>dispel evil</i>, except that you are surrounded by flickering, yellow chaotic energy, and the spell affects lawful creatures and spells rather than evil ones.</p></h4></div>"},
new Spell(){ Name = "Dispel Magic", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dispel Magic</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 3, cleric/oracle 3, druid 4, paladin 3, sorcerer/wizard 3, summoner 3, witch 3, inquisitor 3, antipaladin 3, magus 3, shaman 3, occultist 3, psychic 3, mesmerist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Target or Area </b>one spellcaster, creature, or object<h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can use <i>dispel magic</i> to end one ongoing spell that has been cast on a creature or object, to temporarily suppress the magical abilities of a magic item, or to counter another spellcaster's spell. A dispelled spell ends as if its duration had expired. Some spells, as detailed in their descriptions, can't be defeated by <i>dispel magic</i>. <i><i>Dispel</i> magic</i> can dispel (but not counter) spell-like effects just as it does spells. The of a spell with an instantaneous duration can't be dispelled, because the magical effect is already over before the <i>dispel magic</i> can take effect.</p><p>You choose to use <i>dispel magic</i> in one of two ways: a targeted dispel or a counterspell.</p><p><i>Targeted</i> <i>Dispel</i>: One object, creature, or spell is the target of the <i>dispel magic</i> spell. You make one dispel check (1d20 + your caster level) and compare that to the spell with highest caster level (DC = 11 + the spell's caster level). If successful, that spell ends.</p><p>If not, compare the same result to the spell with the next highest caster level. Repeat this process until you have dispelled one spell affecting the target, or you have failed to dispel every spell.</p><p>For example, a 7th-level caster casts <i>dispel magic</i>, targeting a creature affected by <i>stoneskin</i> (caster level 12th) and <i>fly</i> (caster level 6th). The caster level check results in a 19. This check is not high enough to end the <i>stoneskin</i> (which would have required a 23 or higher), but it is high enough to end the <i>fly</i> (which only required a 17). Had the dispel check resulted in a 23 or higher, the <i>stoneskin</i> would have been dispelled, leaving the <i>fly</i> intact. Had the dispel check been a 16 or less, no spells would have been affected.</p><p>You can also use a targeted dispel to specifically end one spell affecting the target or one spell affecting an area (such as a <i>wall of</i> fire). You must name the specific spell effect to be targeted in this way. If your caster level check is equal to or higher than the DC of that spell, it ends. No other spells or effects on the target are dispelled if your check is not high enough to end the targeted effect.</p><p>If you target an object or creature that is the effect of an ongoing spell (such as a monster <i>summon</i>ed by <i>summon</i> monster), you make a dispel check to end the spell that conjured the object or creature.</p><p>If the object that you target is a magic item, you make a dispel check against the item's caster level (DC = 11 + the item's caster level). If you succeed, all the item's magical properties are suppressed for 1d4 rounds, after which the item recovers its magical properties. A suppressed item becomes nonmagical for the duration of the effect. An interdimensional opening (such as a <i>bag of</i> holding) is temporarily closed. A magic item's physical properties are unchanged: A suppressed magic sword is still a sword (a masterwork sword, in fact). Artifacts and deities are unaffected by mortal magic such as this.</p><p>You automatically succeed on your dispel check against any spell that you cast yourself.</p><p><i>Counterspell</i>: When <i>dispel magic</i> is used in this way, the spell targets a spellcaster and is cast as a counterspell. Unlike a true counterspell, however, <i>dispel magic</i> may not work; you must make a dispel check to counter the other spellcaster's spell.</p></h4><h5><b>Mythic: </b>When used as a targeted dispel, this spell can end two spells affecting the target instead of just one. If the targeted dispel successfully dispels at least one spell, you heal 1d4 points of damage for every spell level of the dispelled spell. If you dispel two spells, this healing applies only to the highest-level spell dispelled. If you use this spell to counterspell, roll your dispel check twice and take the higher result.</h5></div>"},
new Spell(){ Name = "Dispel Magic, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dispel Magic, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 5, cleric/oracle 6, druid 6, sorcerer/wizard 6, witch 6, inquisitor 6, summoner 5, magus 6, shaman 6, occultist 5, psychic 6, spiritualist 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>20-ft.-radius burst</h5><h5><b>Targets </b>one spellcaster, creature, or object</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell functions like <i>dispel magic</i>, except that it can end more than one spell on a target and it can be used to target multiple creatures.</p><p>You choose to use <i>greater dispel magic</i> in one of three ways: a targeted dispel, area dispel, or a counterspell: <i>Targeted Dispel</i>: This functions as a targeted <i>dispel magic</i>, but it can dispel one spell for every four caster levels you possess, starting with the highest level spells and proceeding to lower level spells.</p><p>Additionally, <i>greater dispel magic</i> has a chance to dispel any effect that <i>remove curse</i> can remove, even if <i>dispel magic</i> can't dispel that effect. The DC of this check is equal to the curse's DC.</p><p><i>Area Dispel</i>: When <i>greater dispel magic</i> is used in this way, the spell affects everything within a 20-foot-radius burst. Roll one dispel check and apply that check to each creature in the area, as if targeted by <i>dispel magic</i>. For each object within the area that is the target of one or more spells, apply the dispel check as with creatures. Magic items are not affected by an area dispel.</p><p>For each ongoing area or effect spell whose point of origin is within the area of the <i>greater dispel magic</i> spell, apply the dispel check to dispel the spell. For each ongoing spell whose area overlaps that of the <i>greater dispel magic</i> spell, apply the dispel check to end the effect, but only within the overlapping area.</p><p>If an object or creature that is the effect of an ongoing spell (such as a monster summoned by <i>summon monster</i>) is in the area, apply the dispel check to end the spell that conjured that object or creature (returning it whence it came) in addition to attempting to dispel one spell targeting the creature or object.</p><p>You may choose to automatically succeed on dispel checks against any spell that you have cast.</p><p><i>Counterspell</i>: This functions as <i>dispel magic</i>, but you receive a +4 bonus on your dispel check to counter the other spellcaster's spell.</p></h4></div>"},
new Spell(){ Name = "Displacement", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Displacement</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 3, sorcerer/wizard 3, alchemist 3, summoner 3, magus 3, occultist 3, psychic 3, mesmerist 3, spiritualist 3, witch 3, alchemist 2, witch 2, inquisitor 2, witch 8</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, M (a small loop of leather)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><div><h4><p>The subject of this spell appears to be about 2 feet away from its true location. The creature benefits from a 50% miss chance as if it had total concealment. Unlike actual total concealment, <i>displacement</i> does not prevent enemies from targeting the creature normally. <i>True seeing</i> reveals its true location and negates the miss chance.</p></h4></div>"},
new Spell(){ Name = "Disrupting Weapon", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Disrupting Weapon</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 5, inquisitor 5, spiritualist 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>one melee weapon</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); see text; <b>Spell Resistance </b>yes (harmless, object)</h5></div><div><h4><p>This spell makes a melee weapon deadly to undead. Any undead creature with HD equal to or less than your caster level must succeed on a Will save or be destroyed utterly if struck in combat with this weapon. Spell resistance does not apply against the destruction effect.</p></h4></div>"},
new Spell(){ Name = "Disrupt Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Disrupt Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 0, inquisitor 0, magus 0</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You direct a ray of positive energy. You must make a ranged touch attack to hit, and if the ray hits an undead creature, it deals 1d6 points of damage to it.</p></h4></div>"},
new Spell(){ Name = "Divination", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Divination</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 4, witch 4, inquisitor 4, shaman 4, psychic 4, spiritualist 4</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (incense and an appropriate offering worth 25 gp)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>instantaneous</h5></div><div><h4><p>Similar to <i>augury</i> but more powerful, a <i>divination</i> spell can provide you with a useful piece of advice in reply to a question concerning a specific goal, event, or activity that is to occur within 1 week. The advice granted by the spell can be as simple as a short phrase, or it might take the form of a cryptic rhyme or omen. If your party doesn't act on the information, the conditions may change so that the information is no longer useful. The base chance for a correct <i>divination</i> is 70% + 1% per caster level, to a maximum of 90%. If the die roll fails, you know the spell failed, unless specific magic yielding false information is at work.</p><p>As with <i>augury</i>, multiple <i>divinations</i> about the same topic by the same caster use the same dice result as the first <i>divination</i> spell and yield the same answer each time.</p></h4></div>"},
new Spell(){ Name = "Divine Favor", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Divine Favor</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 1, paladin 1, inquisitor 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 minute</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Calling upon the strength and wisdom of a deity, you gain a +1 luck bonus on attack and weapon damage rolls for every three caster levels you have (at least +1, maximum +3). The bonus doesn't apply to spell damage.</p></h4><h5><b>Mythic: </b>You also gain the luck bonus on saving throws and skill checks. Alternatively, you can cast this spell on another willing creature, changing the range to touch and target to living creature touched.</h5></div>"},
new Spell(){ Name = "Divine Power", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Divine Power</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 4, inquisitor 4, shaman 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 round/level</h5></div><div><h4><p>Calling upon the divine power of your patron, you imbue yourself with strength and skill in combat. You gain a +1 luck bonus on attack rolls, weapon damage rolls, Strength checks, and Strength-based skill checks for every three caster levels you have (maximum +6). You also gain 1 temporary hit point per caster level. Whenever you make a full-attack action, you can make an additional attack at your full base attack bonus, plus any appropriate modifiers. This additional attack is not cumulative with similar effects, such as <i>haste</i> or weapons with the speed special ability.</p></h4></div>"},
new Spell(){ Name = "Dominate Animal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dominate Animal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>druid 3, shaman 3, psychic 4, mesmerist 3</h5><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one animal</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell allows you to enchant the targeted animal and direct it with simple commands such as ""Attack,"" ""Run,"" and ""Fetch."" Suicidal or self-destructive commands (including an order to attack a creature two or more size categories larger than the dominated animal) are simply ignored.</p><p><i>Dominate animal</i> establishes a mental link between you and the subject creature. The animal can be directed by silent mental command as long as it remains in range. You need not see the creature to control it. You do not receive direct sensory input from the creature, but you know what it is experiencing. Because you are directing the animal with your own intelligence, it may be able to undertake actions normally beyond its own comprehension. You need not concentrate exclusively on controlling the creature unless you are trying to direct it to do something it normally couldn't do. Changing your instructions or giving a dominated creature a new command is the equivalent of redirecting a spell, so it is a move action.</p></h4></div>"},
new Spell(){ Name = "Dominate Monster", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dominate Monster</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 9, summoner 6, witch 9, psychic 9</h5><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>dominate person</i>, except that the spell is not restricted by creature type.</p></h4></div>"},
new Spell(){ Name = "Dominate Person", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dominate Person</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 4, sorcerer/wizard 5, witch 5, shaman 5, occultist 5, psychic 5, mesmerist 4, spiritualist 5</h5><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one humanoid</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><div><h4><p>You can control the actions of any humanoid creature through a telepathic link that you establish with the subject's mind.</p><p>If you and the subject have a common language, you can generally force the subject to perform as you desire, within the limits of its abilities. If no common language exists, you can communicate only basic commands, such as ""Come here,"" ""Go there,"" ""Fight,"" and ""Stand still."" You know what the subject is experiencing, but you do not receive direct sensory input from it, nor can it communicate with you telepathically.</p><p>Once you have given a dominated creature a command, it continues to attempt to carry out that command to the exclusion of all other activities except those necessary for day-to-day survival (such as sleeping, eating, and so forth). Because of this limited range of activity, a Sense Motive check against DC 15 (rather than DC 25) can determine that the subject's behavior is being influenced by an enchantment effect (see the Sense Motive skill description).</p><p>Changing your orders or giving a dominated creature a new command is a move action.</p><p>By concentrating fully on the spell (a standard action), you can receive full sensory input as interpreted by the mind of the subject, though it still can't communicate with you. You can't actually see through the subject's eyes, so it's not as good as being there yourself, but you still get a good idea of what's going on.</p><p>Subjects resist this control, and any subject forced to take actions against its nature receives a new saving throw with a +2 bonus. Obviously self-destructive orders are not carried out. Once control is established, the range at which it can be exercised is unlimited, as long as you and the subject are on the same plane. You need not see the subject to control it.</p><p>If you don't spend at least 1 round concentrating on the spell each day, the subject receives a new saving throw to throw off the domination.</p><p><i>Protection from evil</i> or a similar spell can prevent you from exercising control or using the telepathic link while the subject is so warded, but such an effect does not automatically dispel it.</p></h4></div>"},
new Spell(){ Name = "Doom", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Doom</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [emotion, fear, mind-affecting]; <b>Level </b>antipaladin 1, cleric/oracle 1, inquisitor 1, shaman 1, mesmerist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell fills a single subject with a feeling of horrible dread that causes it to become shaken.</p></h4></div>"},
new Spell(){ Name = "Dream", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Dream</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (phantasm) [mind-affecting]; <b>Level </b>bard 5, sorcerer/wizard 5, alchemist 5, psychic 4, mesmerist 4, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>unlimited</h5><h5><b>Targets </b>one living creature touched</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You, or a messenger you touch, send a message to others in the form of a <i>dream</i>. At the beginning of the spell, you must name the recipient or identify him or her by some title that leaves no doubt as to identity. The messenger then enters a trance, appears in the intended recipient's <i>dream</i>, and delivers the message. The message can be of any length, and the recipient remembers it perfectly upon waking. The communication is one-way. The recipient cannot ask questions or offer information, nor can the messenger gain any information by observing the <i>dream</i>s of the recipient.</p><p>Once the message is delivered, the messenger's mind returns instantly to its body. The duration of the spell is the time required for the messenger to enter the recipient's <i>dream</i> and deliver the message.</p><p>If the recipient is awake when the spell begins, the messenger can choose to wake up (ending the spell) or remain in the trance.</p><p>The messenger can remain in the trance until the recipient goes to sleep, then enter the recipient's <i>dream</i> and deliver the message as normal. A messenger that is disturbed during the trance comes awake, ending the spell.</p><p>Creatures who don't sleep or don't <i>dream</i> cannot be contacted by this spell.</p><p>The messenger is unaware of its own surroundings or of the activities around it while in the trance. It is defenseless both physically and mentally (always failing any saving throw) while in the trance.</p></h4><h5><b>Mythic: </b>You or the messenger may gain information from the dreamer you contact by asking questions as if using speak with dead to speak with a corpse. An unwilling dreamer can attempt to resist this probing with a Will save (as speak with dead, using the DC dream would have if it allowed a saving throw). Answers to these questions are still generally brief and cryptic.</h5></div>"},
new Spell(){ Name = "Eagle's Splendor", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Eagle's Splendor</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 2, cleric/oracle 2, paladin 2, sorcerer/wizard 2, alchemist 2, summoner 2, antipaladin 2, bloodrager 2, shaman 2, psychic 2, mesmerist 2</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (feathers or droppings from an eagle)</h5><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><div><h4><p>The transmuted creature becomes more poised, articulate, and personally forceful. The spell grants a +4 enhancement bonus to Charisma, adding the usual benefits to Charisma-based skill checks and other uses of the Charisma modifier. Bards, paladins, and sorcerers (and other spellcasters who rely on Charisma) affected by this spell do not gain any additional bonus spells for the increased Charisma, but the save DCs for spells they cast while under this spell's effect do increase.</p></h4></div>"},
new Spell(){ Name = "Eagle's Splendor, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Eagle's Splendor, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 6, cleric/oracle 6, sorcerer/wizard 6, summoner 4, shaman 6, psychic 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (feathers or droppings from an eagle)</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>One creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>eagle's splendor</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Earthquake", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Earthquake</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [earth]; <b>Level </b>cleric/oracle 8, druid 8, shaman 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>80-ft.-radius spread (S)</h5><h5><b>Duration </b>1 round</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you cast <i>earthquake</i>, an intense but highly localized tremor rips the ground. The powerful shockwave created by this spell knocks creatures down, collapses structures, opens cracks in the ground, and more. The effect lasts for 1 round, during which time creatures on the ground can't move or attack. A spellcaster on the ground must make a concentration check (DC 20 + spell level) or lose any spell he or she tries to cast. The <i>earthquake</i> affects all terrain, vegetation, structures, and creatures in the area. The specific effect of an <i>earthquake</i> spell depends on the nature of the terrain where it is cast.</p><p><i>Cave</i>, <i>Cave</i>rn, <i>or Tunnel</i>: The roof collapses, dealing 8d6 points of damage to any creature caught under the cave-in (Reflex DC 15 half) and pinning that creature beneath the rubble (see below). An <i>earthquake</i> cast on the roof of a very large cavern could also endanger those outside the actual area but below the falling debris and rubble.</p><p><i>Cliffs</i>: <i>Earthquake</i> causes a cliff to crumble, creating a landslide that travels horizontally as far as it falls vertically. Any creature in the path takes 8d6 points of bludgeoning damage (Reflex DC 15 half ) and is pinned beneath the rubble (see below).</p><p><i>Open Ground</i>: Each creature standing in the area must make a DC 15 Reflex save or fall down. Fissures open in the earth, and every creature on the ground has a 25% chance to fall into one (Reflex DC 20 to avoid a fissure). The fissures are 40 feet deep. At the end of the spell, all fissures grind shut. Treat all trapped creatures as if they were in the bury zone of an avalanche, trapped without air (see Chapter 13 for more details).</p><p><i>Structure</i>: Any structure standing on open ground takes 100 points of damage, enough to collapse a typical wooden or masonry building, but not a structure built of stone or reinforced masonry.</p><p>Hardness does not reduce this damage, nor is it halved as damage dealt to objects normally is. Any creature caught inside a collapsing structure takes 8d6 points of bludgeoning damage (Reflex DC 15 half ) and is pinned beneath the rubble (see below).</p><p><i>River</i>, <i>Lake</i>, <i>or Marsh</i>: Fissures open under the water, draining away the water from that area and forming muddy ground. Soggy marsh or swampland becomes quicksand for the duration of the spell, sucking down creatures and structures. Each creature in the area must make a DC 15 Reflex save or sink down in the mud and quicksand. At the end of the spell, the rest of the body of water rushes in to replace the drained water, possibly drowning those caught in the mud.</p><p><i>Pinned Beneath Rubble</i>: Any creature pinned beneath rubble takes 1d6 points of nonlethal damage per minute while pinned. If a pinned character falls unconscious, he or she must make a DC 15 Constitution check or take 1d6 points of lethal damage each minute thereafter until freed or dead.</p></h4><h5><b>Mythic: </b>Increase the damage dealt to structures by 10 points of damage per tier. Increase the damage dealt by cave-ins and landslides to 10d8 points of damage. Increase the damage dealt to those pinned beneath rubble to 1d10 points per minute. Add your tier to the DC of concentration checks and Constitution checks attempted as a consequence of this spell.</h5><h5><b>Augmented (8th)</b>: If you expend two uses of mythic power, the spell lasts for an additional round and the spread's radius increases by 20 feet. Each round this spell is in effect, on your turn you may expend one use of mythic power to continue its duration for 1 round and increase its radius by another 20 feet.</h5></div>"},
new Spell(){ Name = "Elemental Body I", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Elemental Body I</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 4, alchemist 4, magus 4, bloodrager 4</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (the element you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min/level (D)</h5></div><div><h4><p>When you cast this spell, you can assume the form of a Small air, earth, fire, or water elemental. The abilities you gain depend upon the type of elemental into which you change. Elemental abilities based on size, such as burn, vortex, and whirlwind, use the size of the elemental you transform into to determine their effect.</p><p><i>Air elemental</i>: If the form you take is that of a Small air elemental, you gain a +2 size bonus to your Dexterity and a +2 natural armor bonus. You also gain fly 60 feet (perfect), darkvision 60 feet, and the ability to create a whirlwind.</p><p><i>Earth elemental</i>: If the form you take is that of a Small earth elemental, you gain a +2 size bonus to your Strength and a +4 natural armor bonus. You also gain darkvision 60 feet, the push ability, and the ability to earth glide.</p><p><i>Fire elemental</i>: If the form you take is that of a Small fire elemental, you gain a +2 size bonus to your Dexterity and a +2 natural armor bonus. You gain darkvision 60 feet, resist fire 20, vulnerability to cold, and the burn ability.</p><p><i>Water elemental</i>: If the form you take is that of a Small water elemental, you gain a +2 size bonus to your Constitution and a +4 natural armor bonus. You also gain swim 60 feet, darkvision 60 feet, the ability to create a vortex, and the ability to breathe water.</p></h4></div>"},
new Spell(){ Name = "Elemental Body II", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Elemental Body II</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 5, alchemist 5, magus 5</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (the element you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min/level (D)</h5></div><div><h4><p>This spell functions as <i>elemental body I</i>, except that it also allows you to assume the form of a Medium air, earth, fire, or water elemental. The abilities you gain depend upon the elemental.</p><p><i>Air elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Dexterity and a +3 natural armor bonus.</p><p><i>Earth elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Strength and a +5 natural armor bonus.</p><p><i>Fire elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Dexterity and a +3 natural armor bonus.</p><p><i>Water elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Constitution and a +5 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Elemental Body III", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Elemental Body III</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 6, alchemist 6, magus 6</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (the element you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min/level (D)</h5></div><div><h4><p>This spell functions as <i>elemental body II</i>, except that it also allows you to assume the form of a Large air, earth, fire, or water elemental. The abilities you gain depend upon the type of elemental into which you change. You are also immune to critical hits and sneak attacks while in elemental form.</p><p><i>Air elemental</i>: As <i>elemental body I</i> except that you gain a +2 size bonus to your Strength, +4 size bonus to your Dexterity, and a +4 natural armor bonus.</p><p><i>Earth elemental</i>: As <i>elemental body I</i> except that you gain a +6 size bonus to your Strength, a -2 penalty on your Dexterity, a +2 size bonus to your Constitution, and a +6 natural armor bonus.</p><p><i>Fire elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Dexterity, a +2 size bonus to your Constitution, and a +4 natural armor bonus.</p><p><i>Water elemental</i>: As <i>elemental body I</i> except that you gain a +2 size bonus to your Strength, a -2 penalty on your Dexterity, a +6 size bonus to your Constitution, and a +6 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Elemental Body IV", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Elemental Body IV</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 7</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (the element you plan to assume)</h5><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min/level (D)</h5></div><div><h4><p>This spell functions as <i>elemental body III</i>, except that it also allows you to assume the form of a Huge air, earth, fire, or water elemental. The abilities you gain depend upon the type of elemental into which you change. You are also immune to critical hits and sneak attacks while in elemental form and gain DR 5/-.</p><p><i>Air elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Strength, +6 size bonus to your Dexterity, and a +4 natural armor bonus. You also gain fly 120 feet (perfect).</p><p><i>Earth elemental</i>: As <i>elemental body I</i> except that you gain a +8 size bonus to your Strength, a -2 penalty on your Dexterity, a +4 size bonus to your Constitution, and a +6 natural armor bonus.</p><p><i>Fire elemental</i>: As <i>elemental body I</i> except that you gain a +6 size bonus to your Dexterity, a +4 size bonus to your Constitution, and a +4 natural armor bonus.</p><p><i>Water elemental</i>: As <i>elemental body I</i> except that you gain a +4 size bonus to your Strength, a -2 penalty on your Dexterity, a +8 size bonus to your Constitution, and a +6 natural armor bonus. You also gain swim 120 feet.</p></h4></div>"},
new Spell(){ Name = "Elemental Swarm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Elemental Swarm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning) [see text]; <b>Level </b>druid 9, witch 9, shaman 9</h5><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>two or more summoned creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><div><h4><p>This spell opens a portal to an Elemental Plane and summons elementals from it. A druid can choose any plane (Air, Earth, Fire, or Water); a cleric opens a portal to the plane matching his domain.</p><p>When the spell is complete, 2d4 Large elementals appear. Ten minutes later, 1d4 Huge elementals appear. Ten minutes after that, one greater elemental appears. Each elemental has maximum hit points per HD. Once these creatures appear, they serve you for the duration of the spell.</p><p>The elementals obey you explicitly and never attack you, even if someone else manages to gain control over them. You do not need to concentrate to maintain control over the elementals. You can dismiss them singly or in groups at any time.</p><p>When you use a summoning spell to summon an air, earth, fire, or water creature, it is a spell of that type.</p></h4></div>"},
new Spell(){ Name = "Endure Elements", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Endure Elements</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 1, druid 1, paladin 1, ranger 1, sorcerer/wizard 1, alchemist 1, summoner 1, bloodrager 1, shaman 1, psychic 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>24 hours</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A creature protected by <i>endure elements</i> suffers no harm from being in a hot or cold environment. It can exist comfortably in conditions between -50 and 140 degrees Fahrenheit without having to make Fortitude saves. The creature's equipment is likewise protected.</p><p><i>Endure elements</i> doesn't provide any protection from fire or cold damage, nor does it protect against other environmental hazards such as smoke, lack of air, and so forth.</p></h4><h5><b>Mythic: </b>The target changes to one creature touched per level. Affected creatures gain cold resistance 5 and fire resistance 5. They're not slowed by snow, and ignore penalties on Perception checks and ranged weapon attacks from hail, rain, sleet, and snow. They treat wind as one category weaker.</h5></div>"},
new Spell(){ Name = "Energy Drain", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Energy Drain</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 9, sorcerer/wizard 9, shaman 9</h5><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>ray of negative energy</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial; see text for enervation; <b>Spell Resistance </b>yes</h5></div><div><h4><p>This spell functions like <i>enervation</i>, except that the creature struck gains 2d4 temporary negative levels. Twenty-four hours after gaining them, the subject must make a Fortitude saving throw (DC = <i>energy drain</i> spell's save DC) for each negative level. If the save succeeds, that negative level is removed. If it fails, that negative level becomes permanent.</p><p>An undead creature struck by the ray gains 2d4x5 temporary hit points for 1 hour.</p></h4></div>"},
new Spell(){ Name = "Enervation", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Enervation</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 4, witch 4, bloodrager 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>ray of negative energy</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You point your finger and fire a black ray of negative energy that suppresses the life force of any living creature it strikes. You must make a ranged touch attack to hit. If you hit, the subject gains 1d4 temporary negative levels (see Appendix 1). Negative levels stack.</p><p>Assuming the subject survives, it regains lost levels after a number of hours equal to your caster level (maximum 15 hours).</p><p>Usually, negative levels have a chance of becoming permanent, but the negative levels from <i>enervation</i> don't last long enough to do so.</p><p>An undead creature struck by the ray gains 1d4 x 5 temporary hit points for 1 hour.</p></h4><h5><b>Mythic: </b>The number of negative levels inflicted increases to 1d6, and the target is sickened for 24 hours. An undead creature struck by the ray gains 1d6 x 5 temporary hit points for a number of hours equal to your caster level.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, any creature attempting to remove the target's negative levels must succeed at a caster level check (DC 10 + your caster level + your tier).</h5></div>"},
new Spell(){ Name = "Enlarge Person", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Enlarge Person</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 1, alchemist 1, summoner 1, witch 1, magus 1, bloodrager 1, occultist 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (powdered iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one humanoid creature</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes instant growth of a humanoid creature, doubling its height and multiplying its weight by 8. This increase changes the creature's size category to the next larger one. The target gains a +2 size bonus to Strength, a -2 size penalty to Dexterity (to a minimum of 1), and a -1 penalty on attack rolls and AC due to its increased size.</p><p>A humanoid creature whose size increases to Large has a space of 10 feet and a natural reach of 10 feet. This spell does not change the target's speed.</p><p>If insufficient room is available for the desired growth, the creature attains the maximum possible size and may make a Strength check (using its increased Strength) to burst any enclosures in the process. If it fails, it is constrained without harm by the materials enclosing it-the spell cannot be used to crush a creature by increasing its size.</p><p>All equipment worn or carried by a creature is similarly <i>enlarged</i> by the spell. Melee weapons affected by this spell deal more damage (see page 145). Other magical properties are not affected by this spell. Any <i>enlarged</i> item that leaves an <i>enlarged</i> creature's possession (including a projectile or thrown weapon) instantly returns to its normal size. This means that thrown and projectile weapons deal their normal damage. Magical properties of <i>enlarged</i> items are not increased by this spell.</p><p>Multiple magical effects that increase size do not stack.</p><p><i>Enlarge person</i> counters and dispels <i>reduce person</i>.</p><p><i>Enlarge person</i> can be made permanent with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>You can increase the target's size by up to two size categories, to a maximum of Huge. If the target grows two sizes, its weight increases by a factor of 25, it gains a +4 size bonus to Strength, and takes a -4 size penalty to Dexterity and a -2 penalty on attack rolls and to AC because of its increased size. A Medium humanoid creature whose size increases to Huge has a space of 15 feet and a natural reach of 10 feet. Mythic enlarge person counters and dispels mythic reduce person.</h5></div>"},
new Spell(){ Name = "Enlarge Person, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Enlarge Person, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 4, summoner 3, bloodrager 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (powdered iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>One humanoid creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>enlarge person</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Entangle", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Entangle</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1, ranger 1, shaman 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>plants in a 40-ft.-radius spread</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Reflex partial; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes tall grass, weeds, and other plants to wrap around creatures in the area of effect or those that enter the area. Creatures that fail their save gain the <i>entangle</i>d condition. Creatures that make their save can move as normal, but those that remain in the area must save again at the end of your turn. Creatures that move into the area must save immediately. Those that fail must end their movement and gain the <i>entangle</i>d condition. Entangled creatures can attempt to break free as a move action, making a Strength or Escape Artist check. The DC for this check is equal to the DC of the spell. The entire area of effect is considered difficult terrain while the effect lasts.</p><p>If the plants in the area are covered in thorns, those in the area take 1 point of damage each time they fail a save against the <i>entangle</i> or fail a check made to break free. Other effects, depending on the local plants, might be possible at GM discretion.</p></h4><h5><b>Mythic: </b>The spell functions in areas without any vegetation, causing plants to spring up out of the ground. These plants can reach creatures that are up to 10 feet in the air. Plants affected or created by the spell sprout thorns that deal 1d6 points of damage each round to creatures in the area.</h5></div>"},
new Spell(){ Name = "Enthrall", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Enthrall</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (charm) [language-dependent, mind-affecting, sonic]; <b>Level </b>bard 2, cleric/oracle 2, witch 2, inquisitor 2, shaman 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>any number of creatures</h5><h5><b>Duration </b>1 hour or less</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>If you have the attention of a group of creatures, you can use this spell to hold them enthralled. To cast the spell, you must speak or sing without interruption for 1 full round. Thereafter, those affected give you their undivided attention, ignoring their surroundings. They are considered to have an attitude of friendly while under the effect of the spell. Any potentially affected creature of a race or religion unfriendly to yours gets a +4 bonus on the saving throw. A target with 4 or more HD or with a Wisdom score of 16 or higher remains aware of its surroundings and has an attitude of indifferent. It gains a new saving throw if it witnesses actions that it opposes. The effect lasts as long as you speak or sing, to a maximum of 1 hour. Those enthralled by your words take no action while you speak or sing and for 1d3 rounds thereafter while they discuss the topic or performance. Those entering the area during the performance must also successfully save or become enthralled. The speech ends (but the 1d3-round delay still applies) if you lose concentration or do anything other than speak or sing. If those not enthralled have unfriendly or hostile attitudes toward you, they can collectively make a Charisma check to try to end the spell by jeering and heckling. For this check, use the Charisma bonus of the creature with the highest Charisma in the group; others may make Charisma checks to assist. The heckling ends the spell if this check result beats your Charisma check result. Only one such challenge is allowed per use of the spell. If any member of the audience is attacked or subjected to some other overtly hostile act, the spell ends and the previously enthralled members become immediately unfriendly toward you. Each creature with 4 or more HD or with a Wisdom score of 16 or higher becomes hostile.</p></h4></div>"},
new Spell(){ Name = "Entropic Shield", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Entropic Shield</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A magical field appears around you, glowing with a chaotic blast of multicolored hues. This field deflects incoming arrows, rays, and other ranged attacks. Each ranged attack directed at you for which the attacker must make an attack roll has a 20% miss chance (similar to the effects of concealment). Other attacks that simply work at a distance are not affected.</p></h4><h5><b>Mythic: </b>When a ranged attack misses because of this spell, there is a 50% chance that the attack is redirected back at the attacker rather than just being deflected harmlessly. The attack uses the same result as the attack roll the attacker made against you.</h5></div>"},
new Spell(){ Name = "Erase", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Erase</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 1, sorcerer/wizard 1, occultist 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one scroll or two pages</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Erase</i> removes writings of either magical or mundane nature from a scroll or from one or two pages of paper, parchment, or similar surfaces. With this spell, you can remove <i><i>explosive runes</i>,</i> a <i><i>glyph of warding</i>, a <i>sepia snake sigil</i>,</i> or an <i>arcane mark</i>, but not <i>illusory script</i> or a <i>symbol</i> spell. Nonmagical writing is automatically erased if you touch it and no one else is holding it. Otherwise, the chance of erasing nonmagical writing is 90%. Magic writing must be touched to be erased, and you also must succeed on a caster level check (1d20 + caster level) against DC 15. A natural 1 is always a failure on this check. If you fail to erase <i><i>explosive runes</i>,</i> a <i>glyph of warding</i>, or a <i>sepia snake sigil</i>, you accidentally activate that writing instead.</p></h4></div>"},
new Spell(){ Name = "Ethereal Jaunt", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ethereal Jaunt</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 7, sorcerer/wizard 7, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 round/level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You become ethereal, along with your equipment. For the duration of the spell, you are in the Ethereal Plane, which overlaps the Material Plane. When the spell expires, you return to material existence. An ethereal creature is invisible, insubstantial, and capable of moving in any direction, even up or down, albeit at half normal speed. As an insubstantial creature, you can move through solid objects, including living creatures. An ethereal creature can see and hear on the Material Plane, but everything looks gray and ephemeral. Sight and hearing onto the Material Plane are limited to 60 feet. Force effects and abjurations affect an ethereal creature normally. Their effects extend onto the Ethereal Plane from the Material Plane, but not vice versa. An ethereal creature can't attack material creatures, and spells you cast while ethereal affect only other ethereal things. Certain material creatures or objects have attacks or effects that work on the Ethereal Plane. Treat other ethereal creatures and ethereal objects as if they were material. If you end the spell and become material while inside a material object (such as a solid wall), you are shunted off to the nearest open space and take 1d6 points of damage per 5 feet that you so travel.</p></h4></div>"},
new Spell(){ Name = "Etherealness", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Etherealness</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 9, sorcerer/wizard 9, shaman 9, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch; see text</h5><h5><b>Targets </b>you and one other touched creature per three levels</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>ethereal jaunt,</i> except that you and other willing creatures joined by linked hands (along with their equipment) become ethereal. Besides yourself, you can bring one creature per three caster levels to the Ethereal Plane. Once ethereal, the subjects need not stay together. When the spell expires, all affected creatures on the Ethereal Plane return to material existence.</p></h4></div>"},
new Spell(){ Name = "Expeditious Retreat", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Expeditious Retreat</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 1, sorcerer/wizard 1, alchemist 1, summoner 1, inquisitor 1, magus 1, bloodrager 1, occultist 1, psychic 1, mesmerist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell increases your base land speed by 30 feet. This adjustment is treated as an enhancement bonus. There is no effect on other modes of movement, such as burrow, climb, fly, or swim. As with any effect that increases your speed, this spell affects your jumping distance (see the Acrobatics skill).</p></h4><h5><b>Mythic: </b>The speed bonus from the spell increases to 40 feet. Add your tier to Acrobatics checks to avoid attacks of opportunity from movement, and when your movement provokes an attack of opportunity, add your tier to your AC against that attack.</h5></div>"},
new Spell(){ Name = "Explosive Runes", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Explosive Runes</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [force]; <b>Level </b>sorcerer/wizard 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched object weighing no more than 10 lbs.</h5><h5><b>Duration </b>permanent until discharged (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You trace mystic runes upon a book, map, scroll, or similar object bearing written information. The <i>explosive runes</i> detonate when read, dealing 6d6 points of force damage. Anyone next to the <i>explosive runes</i> (close enough to read them) takes the full damage with no saving throw; any other creature within 10 feet of the <i>explosive runes</i> is entitled to a Reflex save for half damage. The object on which the <i>explosive runes</i> were written also takes full damage (no saving throw). You and any characters you specifically instruct can read the protected writing without triggering the <i>explosive runes</i>. Likewise, you can remove the <i>explosive runes</i> whenever desired. Another creature can remove them with a successful <i>dispel magic</i> or <i>erase</i> spell, but attempting to dispel or <i>erase</i> the <i>explosive runes</i> and failing to do so triggers the explosion. Magic traps such as <i>explosive runes</i> are hard to detect and disable. A character with the trapfinding class feature (only) can use Disable Device to thwart <i>explosive runes</i>. The DC to find magic traps using Perception and to disable them is 25 + spell level, or 28 for <i>explosive runes</i>.</p></h4></div>"},
new Spell(){ Name = "Eyebite", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Eyebite</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [emotion, pain]; <b>Level </b>bard 6, sorcerer/wizard 6, alchemist 6, witch 6, mesmerist 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Each round, you can target a single living creature, striking it with waves of power. Depending on the target's HD, this attack has as many as three effects. <table><tr><th>hd</th><th>Effect</th></tr><tr><td>10 or more</td><td>Sickened</td></tr><tr><td>5-9</td><td>Panicked, sickened</td></tr><tr><td>4 or less</td><td>Comatose, panicked, sickened</td></tr></table>  The effects are cumulative and concurrent. <i>Sickened:</i> Sudden pain and fever sweeps over the subject's body. A creature affected by this spell remains sickened for 10 minutes per caster level. The effects cannot be negated by a <i>remove disease</i> or <i>heal</i> spell, but a <i>remove curse</i> is effective. <i>Panicked:</i> The subject becomes panicked for 1d4 rounds. Even after the panic ends, the creature remains shaken for 10 minutes per caster level, and it automatically becomes panicked again if it comes within sight of you during that time. This is a fear effect. <i>Comatose:</i> The subject falls into a catatonic coma for 10 minutes per caster level. During this time, it cannot be awakened by any means short of dispelling the effect. This is not a <i>sleep</i> effect, and thus elves are not immune to it. You must spend a swift action each round after the first to target a foe.</p></h4></div>"},
new Spell(){ Name = "Fabricate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fabricate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 5, occultist 5, psychic 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>see text</h5><h5><b>Components </b>V, S, M (the original material, which costs the same amount as the raw materials required to craft the item to be created)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>up to 10 cu. ft./level; see text</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You convert material of one sort into a product that is of the same material. Creatures or magic items cannot be created or transmuted by the <i>fabricate</i> spell. The quality of items made by this spell is commensurate with the quality of material used as the basis for the new fabrication. If you work with a mineral, the target is reduced to 1 cubic foot per level instead of 10 cubic feet. You must make an appropriate Craft check to <i>fabricate</i> articles requiring a high degree of craftsmanship. Casting requires 1 round per 10 cubic feet of material to be affected by the spell.</p></h4></div>"},
new Spell(){ Name = "Faerie Fire", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Faerie Fire</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [light]; <b>Level </b>druid 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>creatures and objects within a 5-ft.-radius burst</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A pale glow surrounds and outlines the subjects. Outlined subjects shed light as candles. Creatures outlined by <i>faerie fire</i> take a -20 penalty on all Stealth checks. Outlined creatures do not benefit from the concealment normally provided by <i>darkness</i> (though a 2nd-level or higher magical <i>darkness</i> effect functions normally), <i>blur</i>, <i>displacement</i>, <i>invisibility</i>, or similar effects. The light is too dim to have any special effect on undead or dark-dwelling creatures vulnerable to light. The <i>faerie fire</i> can be blue, green, or violet, according to your choice at the time of casting. The <i>faerie fire</i> does not cause any harm to the objects or creatures thus outlined.</p></h4><h5><b>Mythic: </b>Outlined creatures are dazzled. The spell outlines figment illusions in the burst, revealing them as such. This effect applies only to figments with a caster level equal to or less than your tier.</h5><h5><b>Augmented (2nd)</b>: If you expend two uses of mythic power, each creature in the burst must succeed at a Fortitude save or be blinded for 1 round.</h5></div>"},
new Spell(){ Name = "False Life", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">False Life</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 2, alchemist 2, witch 2, bloodrager 2, shaman 2, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a drop of blood)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 hour/level or until discharged; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You harness the power of unlife to grant yourself a limited ability to avoid death. While this spell is in effect, you gain temporary hit points equal to 1d10 + 1 per caster level (maximum +10).</p></h4></div>"},
new Spell(){ Name = "False Vision", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">False Vision</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 5, sorcerer/wizard 5, occultist 5, psychic 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (crushed jade worth 250 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Area </b>40-ft.-radius emanation</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates a subtle illusion, causing any divination (scrying) spell used to view anything within the area of this spell to instead receive a false image (as the <i>major image</i> spell), as defined by you at the time of casting. As long as the duration lasts, you can concentrate to change the image as desired. While you aren't concentrating, the image remains static.</p></h4></div>"},
new Spell(){ Name = "Feather Fall", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Feather Fall</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 1, sorcerer/wizard 1, summoner 1, magus 1, bloodrager 1, occultist 1, psychic 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 immediate action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one Medium or smaller freefalling object or creature/level, no two of which may be more than 20 ft. apart</h5><h5><b>Duration </b>until landing or 1 round/level</h5><h5><b>Saving Throw </b>Will negates (harmless) or Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The affected creatures or objects fall slowly. <i>Feather fall</i> instantly changes the rate at which the targets fall to a mere 60 feet per round (equivalent to the end of a fall from a few feet), and the subjects take no damage upon landing while the spell is in effect. When the spell duration expires, a normal rate of falling resumes.</p><p>The spell affects one or more Medium or smaller creatures (including gear and carried objects up to each creature's maximum load) or objects, or the equivalent in larger creatures: a Large creature or object counts as two Medium creatures or objects, a Huge creature or object counts as four Medium creatures or objects, and so forth.</p><p>This spell has no special effect on ranged weapons unless they are falling quite a distance. If the spell is cast on a falling item, the object does half normal damage based on its weight, with no bonus for the height of the drop.</p><p><i>Feather fall</i> works only upon free-falling objects. It does not affect a sword blow or a charging or flying creature.</p></h4><h5><b>Mythic: </b>The spell affects one additional target per level. The targets don't have to be within 20 feet of each other.</h5><h5><b>Augmented (4th)</b>: If you expend two uses of mythic power, the spell absorbs the targets' velocity and transforms it into a concussive blast. Targets fall at the normal rate (not slowed) but land safely. When a target lands, it creates a 10-foot-radius burst of force that deals 1d6 points of damage per caster level (maximum 5d6, Reflex half, DC equal to the DC of feather fall). The targets of this spell are unaffected by these concussive blasts.</h5></div>"},
new Spell(){ Name = "Feeblemind", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Feeblemind</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 5, witch 5, psychic 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a handful of clay, crystal, or glass spheres)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Target creature's Intelligence and Charisma scores each drop to 1. The affected creature is unable to use Intelligence- or Charismabased skills, cast spells, understand language, or communicate coherently. Still, it knows who its friends are and can follow them and even protect them. The subject remains in this state until a heal, limited <i>wish</i>, miracle, or <i>wish</i> spell is used to cancel the of the <i>feeblemind.</i> A creature that can cast arcane spells, such as a sorcerer or a wizard, takes a -4 penalty on its saving throw.</p></h4></div>"},
new Spell(){ Name = "Find the Path", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Find the Path</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 6, cleric/oracle 6, druid 6, witch 6, inquisitor 6, shaman 6, occultist 6, psychic 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>3 rounds</h5><h5><b>Components </b>V, S, F (a set of divination counters)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal or touch</h5><h5><b>Targets </b>you or creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none or Will negates (harmless); <b>Spell Resistance </b>no or yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The recipient of this spell can find the shortest, most direct physical route to a prominent specified destination, such as a city, keep, lake, or dungeon. The locale can be outdoors or underground, as long as it is prominent. For example, a hunter's cabin is not prominent enough, but a logging camp is. <i>Find the path</i> works with respect to locations, not objects or creatures at a locale. The location must be on the same plane as the subject at the time of casting. The spell enables the subject to sense the correct direction that will eventually lead it to its destination, indicating at appropriate times the exact path to follow or physical actions to take. For example, the spell enables the subject to sense what cavern corridor to take when a choice presents itself. The spell ends when the destination is reached or the duration expires, whichever comes first. <i>Find the path</i> can be used to remove the subject and its companions from the effect of a <i>maze</i> spell in a single round, specifying the destination as ""outside the <i>maze</i>."" This divination is keyed to the recipient, not its companions, and its effect does not predict or allow for the actions of creatures (including guardians) who might take action to oppose the caster as he follows the path revealed by this spell.</p></h4></div>"},
new Spell(){ Name = "Find Traps", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Find Traps</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 2, witch 2, inquisitor 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You gain intuitive insight into the workings of traps. You gain an insight bonus equal to 1/2 your caster level (maximum +10) on Perception checks made to <i>find traps</i> while the spell is in effect. You receive a check to notice traps within 10 feet of you, even if you are not actively searching for them. Note that <i>find traps</i> grants no ability to disable the traps that you may find.</p></h4></div>"},
new Spell(){ Name = "Finger Of Death", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Finger Of Death</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [death]; <b>Level </b>druid 8, sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell instantly delivers 10 points of damage per caster level. If the target's Fortitude saving throw succeeds, it instead takes 3d6 points of damage + 1 point per caster level. The subject might die from damage even if it succeeds on its saving throw.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 15 points of damage per caster level plus 1d8 points of Constitution damage. A creature that succeeds at its saving throw takes 3d8 points of damage + 1 point per caster level, takes 1d4 points of Constitution damage, and is staggered for 1 round.</h5></div>"},
new Spell(){ Name = "Fire Shield", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fire Shield</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire or cold]; <b>Level </b>sorcerer/wizard 4, alchemist 4, summoner 3, magus 4, bloodrager 4, occultist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (phosphorus for the warm shield; a firefly or glowworm for the chill shield)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 round/level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell wreathes you in flame and causes damage to each creature that attacks you in melee. The flames also protect you from either cold-based or fire-based attacks, depending on if you choose cool or <i>warm</i> flames for your <i>fire shield</i>.</p><p>Any creature striking you with its body or a handheld weapon deals normal damage, but at the same time the attacker takes 1d6 points of damage + 1 point per caster level (maximum +15).</p><p>This damage is either cold damage (if you choose a <i><i>chill</i> shield)</i> or fire damage (if you choose a <i><i>warm</i> shield</i>). If the attacker has spell resistance, it applies to this effect. Creatures wielding melee weapons with reach are not subject to this damage if they attack you.</p><p>When casting this spell, you appear to immolate yourself, but the flames are thin and wispy, increasing the light level within 10 feet by one step, up to normal light. The color of the flames is blue or green if the <i>chill</i> shield is cast, violet or red if the <i><i>warm</i> shield</i> is employed. The special powers of each version are as follows.</p><p><i>Chill Shield</i>: The flames are cool to the touch. You take only half damage from fire-based attacks. If such an attack allows a Reflex save for half damage, you take no damage on a successful saving throw.</p><p><i>Warm Shield</i>: The flames are <i>warm</i> to the touch. You take only half damage from cold-based attacks. If such an attack allows a Reflex save for half damage, you take no damage on a successful saving throw.</p></h4><h5><b>Mythic: </b>The damage dealt to your attackers increases to 1d8 points of damage + 1 point per caster level (maximum +15). A chill shield gives you fire immunity. A warm shield gives you cold immunity.</h5></div>"},
new Spell(){ Name = "Fire Seeds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fire Seeds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [fire]; <b>Level </b>druid 6, shaman 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (acorns or holly berries)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>up to four acorns or up to eight holly berries</h5><h5><b>Duration </b>10 min./level or until used</h5><h5><b>Saving Throw </b>none or Reflex half; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Depending on the version of <i>fire seeds</i> you choose, you turn acorns into splash weapons that you or another character can throw, or you turn holly berries into bombs that you can detonate on command.</p><p><i>Acorn Grenades</i>: As many as four acorns turn into special thrown splash weapons. An acorn grenade has a range increment of 20 feet. A ranged touch attack roll is required to strike the intended target. Together, the acorns are capable of dealing 1d4 points of fire damage per caster level (maximum 20d4) divided among the acorns as you wish. No acorn can deal more than 10d4 points of damage.</p><p>Each acorn grenade explodes upon striking any hard surface.</p><p>In addition to its regular fire damage, all creatures adjacent to the explosion take 1 point of fire damage per die of the explosion.</p><p>This explosion of fire ignites any combustible materials adjacent to the target.</p><p><i>Holly Berry Bombs</i>: You turn as many as eight holly berries into special bombs. The holly berries are usually placed by hand, since they are too light to make effective thrown weapons (they can be tossed only 5 feet). If you are within 200 feet and speak a word of command, each berry instantly bursts into flame, causing 1d8 points of fire damage + 1 point per caster level to every creature in a 5-foot-radius burst and igniting any combustible materials within 5 feet. A creature in the area that makes a successful Reflex saving throw takes only half damage.</p></h4><h5><b>Mythic: </b>Each acorn's damage increases to 1d6 points of fire damage per caster level (maximum 20d6 for all acorns, no more than 10d6 for any particular acorn). Each holly berry's damage increases to 2d6 points of fire damage + 2 points per caster level. Any creature that fails its Reflex save against an acorn or holly berry catches on fire (Core Rulebook 444), taking 2d6 points of fire damage each round until the fire is extinguished. Attempts to extinguish this fire use the spell's save DC.</h5></div>"},
new Spell(){ Name = "Fire Storm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fire Storm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>cleric/oracle 8, druid 7, shaman 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>two 10-ft. cubes per level (S)</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When a <i>fire storm</i> spell is cast, the whole area is shot through with sheets of roaring flame. The raging flames do not harm natural vegetation, ground cover, or any plant creatures in the area that you wish to exclude from damage. Any other creature within the area takes 1d6 points of fire damage per caster level (maximum 20d6). Creatures that fail their Reflex save catch on fire, taking 4d6 points of fire damage each round until the flames are extinguished. Extinguishing the flames is a full-round action that requires a DC 20 Reflex save.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d8 points of damage per caster level (maximum 20d8). Half of this damage is fire damage, and the other half is divine energy that bypasses fire resistance and fire immunity. You can exclude any number of animals from damage just as you can plant creatures.</h5><h5><b>Augmented (8th)</b>: If you expend two uses of mythic power, choose one creature type (and subtype, if humanoid or outsider) to exclude from the effects of the spell. For each additional use of mythic power you expend when casting the spell, exclude another creature type. For example, if you expend two uses of mythic power and choose magical beasts, you can exclude from damage any number of magical beasts in the spell's area.</h5></div>"},
new Spell(){ Name = "Fire Trap", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fire Trap</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [fire]; <b>Level </b>druid 2, sorcerer/wizard 4, occultist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (gold dust worth 25 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>permanent until discharged (D)</h5><h5><b>Saving Throw </b>Reflex half; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Fire trap</i> creates a fiery explosion when an intruder opens the item that the trap protects. A <i>fire trap</i> spell can ward any object that can be opened and closed.</p><p>When casting <i>fire trap</i>, you select a point on the object as the spell's center. When someone other than you opens the object, a fiery explosion fills the area within a 5-foot radius around the spell's center. The flames deal 1d4 points of fire damage + 1 point per caster level (maximum +20). The item protected by the trap is not harmed by this explosion.</p><p>A fire-trapped item cannot have a second closure or warding spell placed on it. A <i>knock</i> spell does not bypass a <i>fire trap</i>. An unsuccessful <i>dispel magic</i> spell does not detonate the spell.</p><p>Underwater, this ward deals half damage and creates a large cloud of steam.</p><p>You can use the fire-trapped object without discharging it, as can any individual to whom the object was specifically attuned when cast. Attuning a fire-trapped object to an individual usually involves setting a password that you can share with friends.</p><p>Magic traps such as <i>fire trap</i> are hard to detect and disable. A character with trapfinding can use the Perception skill to find a <i>fire trap</i> and Disable Device to thwart it. The DC in each case is 25 + spell level (DC 27 for a druid's <i>fire trap</i> or DC 29 for the arcane version).</p></h4></div>"},
new Spell(){ Name = "Fireball", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fireball</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>sorcerer/wizard 3, magus 3, bloodrager 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a ball of bat guano and sulfur)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>20-ft.-radius spread</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>fireball</i> spell generates a searing explosion of flame that detonates with a low roar and deals 1d6 points of fire damage per caster level (maximum 10d6) to every creature within the area.</p><p>Unattended objects also take this damage. The explosion creates almost no pressure.</p><p>You point your finger and determine the range (distance and height) at which the <i>fireball</i> is to burst. A glowing, pea-sized bead streaks from the pointing digit and, unless it impacts upon a material body or solid barrier prior to attaining the prescribed range, blossoms into the <i>fireball</i> at that point. An early impact results in an early detonation. If you attempt to send the bead through a narrow passage, such as through an arrow slit, you must ""hit"" the opening with a ranged touch attack, or else the bead strikes the barrier and detonates prematurely.</p><p>The <i>fireball</i> sets fire to combustibles and damages objects in the area. It can melt metals with low melting points, such as lead, gold, copper, silver, and bronze. If the damage caused to an interposing barrier shatters or breaks through it, the <i>fireball</i> may continue beyond the barrier if the area permits; otherwise it stops at the barrier just as any other spell effect does.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d10 points of fire damage per caster level (maximum 10d10). Any creature that fails its Reflex saving throw catches on fire (Core Rulebook 444), taking 2d6 points of fire damage each round until the fire is extinguished. Attempts to extinguish this fire use the spell's save DC.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the maximum damage increases to 20d10, the area increases to a 40-foot radius spread, and any fire damage dealt by the spell bypasses fire resistance and fire immunity.</h5></div>"},
new Spell(){ Name = "Flame Arrow", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Flame Arrow</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [fire]; <b>Level </b>sorcerer/wizard 3, magus 3, bloodrager 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a drop of oil and a small piece of flint)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>fifty projectiles, all of which must be together at the time of casting</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell allows you to turn ammunition (such as arrows, crossbow bolts, shuriken, and sling stones) into fiery projectiles. Each piece of ammunition deals an extra 1d6 points of fire damage to any target it hits. A flaming projectile can easily ignite a flammable object or structure, but it won't ignite a creature it strikes.</p></h4></div>"},
new Spell(){ Name = "Flame Blade", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Flame Blade</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>druid 2, shaman 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>sword-like beam</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A 3-foot-long, blazing beam of red-hot fire springs forth from your hand. You wield this blade-like beam as if it were a scimitar. Attacks with the <i>flame blade</i> are melee touch attacks.</p><p>The blade deals 1d8 points of fire damage + 1 point per two caster levels (maximum +10). Since the blade is immaterial, your Strength modifier does not apply to the damage. A <i>flame blade</i> can ignite combustible materials such as parchment, straw, dry sticks, and cloth.</p></h4><h5><b>Mythic: </b>The blade's damage increases to 2d6 points of fire damage + 1 point per caster level (maximum +20). The blade threatens a critical hit on a natural 18-20 as if it were an actual scimitar.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the blade threatens a critical hit on a natural 15-20, and bypasses hardness, fire resistance, and fire immunity.</h5></div>"},
new Spell(){ Name = "Flame Strike", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Flame Strike</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>cleric/oracle 5, druid 4, inquisitor 5, shaman 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>cylinder (10-ft. radius, 40-ft. high)</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>flame strike</i> evokes a vertical column of divine fire. The spell deals 1d6 points of damage per caster level (maximum 15d6). Half the damage is fire damage, but the other half results directly from divine power and is therefore not subject to being reduced by resistance to fire-based attacks.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d8 per caster level (maximum 15d8). The area increases to a 20-foot-radius, 40-foot-high cylinder. You may have the divine half of the damage affect only one specific creature type (as the bane weapon quality).</h5></div>"},
new Spell(){ Name = "Flaming Sphere", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Flaming Sphere</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>druid 2, sorcerer/wizard 2, magus 2, bloodrager 2, occultist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (tallow, brimstone, and powdered iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>5-ft.-diameter sphere</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Reflex negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A burning globe of fire rolls in whichever direction you point and burns those it strikes. It moves 30 feet per round. As part of this movement, it can ascend or jump up to 30 feet to strike a target. If it enters a space with a creature, it stops moving for the round and deals 3d6 points of fire damage to that creature, though a successful Reflex save negates that damage. A <i>flaming sphere</i> rolls over barriers less than 4 feet tall. It ignites flammable substances it touches and illuminates the same area as a torch would.</p><p>The sphere moves as long as you actively direct it (a move action for you); otherwise, it merely stays at rest and burns. It can be extinguished by any means that would put out a normal fire of its size. The surface of the sphere has a spongy, yielding consistency and so does not cause damage except by its flame. It cannot push aside unwilling creatures or batter down large obstacles. A <i>flaming sphere</i> winks out if it exceeds the spell's range.</p></h4></div>"},
new Spell(){ Name = "Flare", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Flare</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [light]; <b>Level </b>bard 0, druid 0, sorcerer/wizard 0, magus 0, occultist 0, psychic 0, mesmerist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>burst of light</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This cantrip creates a burst of light. If you cause the light to burst in front of a single creature, that creature is dazzled for 1 minute unless it makes a successful Fortitude save. Sightless creatures, as well as creatures already dazzled, are not affected by flare.</p></h4></div>"},
new Spell(){ Name = "Flesh to Stone", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Flesh to Stone</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 6, witch 6, magus 6, shaman 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (lime, water, and earth)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject, along with all its carried gear, turns into a mindless, inert statue. If the statue resulting from this spell is broken or damaged, the subject (if ever returned to its original state) has similar damage or deformities. The creature is not dead, but it does not seem to be alive either when viewed with spells such as <i>deathwatch.</i></p><p>Only creatures made of flesh are affected by this spell.</p></h4></div>"},
new Spell(){ Name = "Floating Disk", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Floating Disk</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 1, magus 1, occultist 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a drop of mercury)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>3-ft.-diameter disk of force</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You create a slightly concave, circular plane of force that follows you about and carries loads for you. The disk is 3 feet in diameter and 1 inch deep at its center. It can hold 100 pounds of weight per caster level. If used to transport a liquid, its capacity is 2 gallons. The disk floats approximately 3 feet above the ground at all times and remains level. It floats along horizontally within spell range and will accompany you at a rate of no more than your normal speed each round. If not otherwise directed, it maintains a constant interval of 5 feet between itself and you.</p><p>The disk winks out of existence when the spell duration expires.</p><p>The disk also winks out if you move beyond its range or try to take the disk more than 3 feet away from the surface beneath it.</p><p>When the disk winks out, whatever it was supporting falls to the surface beneath it.</p></h4></div>"},
new Spell(){ Name = "Fly", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fly</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 3, alchemist 3, summoner 3, witch 3, magus 3, bloodrager 3, shaman 3, occultist 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a wing feather)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject can <i>fly</i> at a speed of 60 feet (or 40 feet if it wears medium or heavy armor, or if it carries a medium or heavy load).</p><p>It can ascend at half speed and descend at double speed, and its maneuverability is good. Using a <i>fly</i> spell requires only as much concentration as walking, so the subject can attack or cast spells normally. The subject of a <i>fly</i> spell can charge but not run, and it cannot carry aloft more weight than its maximum load, plus any armor it wears. The subject gains a bonus on Fly skill checks equal to 1/2 your caster level.</p><p>Should the spell duration expire while the subject is still aloft, the magic fails slowly. The subject floats downward 60 feet per round for 1d6 rounds. If it reaches the ground in that amount of time, it lands safely. If not, it falls the rest of the distance, taking 1d6 points of damage per 10 feet of fall. Since dispelling a spell effectively ends it, the subject also descends safely in this way if the <i>fly</i> spell is dispelled, but not if it is negated by an <i>antimagic field</i>.</p></h4><h5><b>Mythic: </b>The fly speed changes to 120 feet (or 80 feet if the target is wearing medium or heavy armor, or carrying a medium or heavy load) with perfect maneuverability. When the duration expires, the target is protected by feather fall for a number of rounds equal to your caster level.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the target adds your tier on Reflex saves and as a dodge bonus to AC. It gains these bonuses only while flying.</h5></div>"},
new Spell(){ Name = "Fog Cloud", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fog Cloud</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>druid 2, magus 2, sorcerer/wizard 2, witch 2, shaman 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft. level)</h5><h5><b>Effect </b>fog spreads in 20-ft. radius</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A bank of fog billows out from the point you designate. The fog obscures all sight, including darkvision, beyond 5 feet. A creature within 5 feet has concealment (attacks have a 20% miss chance).</p><p>Creatures farther away have total concealment (50% miss chance, and the attacker can't use sight to locate the target).</p><p>A moderate wind (11+ mph) disperses the fog in 4 rounds; a strong wind (21+ mph) disperses the fog in 1 round.</p><p>The spell does not function underwater.</p></h4><h5><b>Mythic: </b>The radius of the fog increases to 50 feet.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the fog's radius changes to 500 feet, and its height changes to 100 feet. Winds can't dissipate the fog unless the area of the wind affects the entire area of the augmented mythic fog cloud. If you're at least 6th tier and instead expend three uses of mythic power, the range increases to 1 mile, the fog's radius changes to 1 mile, and its height changes to 500 feet. The cloud is as resistant to wind as the 3rd-tier augmented version.</h5></div>"},
new Spell(){ Name = "Forbiddance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Forbiddance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 6, inquisitor 6, occultist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>6 rounds</h5><h5><b>Components </b>V, S, M (holy water and incense worth 1,500 gp, plus 1,500 gp per 60-foot cube), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>60-ft. cube/level  (S)</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Forbiddance</i> seals an area against all planar travel into or within it. This includes all teleportation spells (such as <i>dimension door</i> and <i>teleport),</i> plane shifting, astral travel, ethereal travel, and all summoning spells. Such effects simply fail automatically.</p><p>In addition, it damages entering creatures whose alignments are different from yours. The effect on those attempting to enter the warded area is based on their alignment relative to yours (see below). A creature inside the area when the spell is cast takes no damage unless it exits the area and attempts to reenter, at which time it is affected as normal.</p><p><i>Alignments identical</i>: No effect. The creature may enter the area freely (although not by planar travel).</p><p><i>Alignments different with respect to either law/chaos or good/evil</i>: The creature takes 6d6 points of damage. A successful Will save halves the damage, and spell resistance applies.</p><p><i>Alignments different with respect to both law/chaos and good/evil</i>: The creature takes 12d6 points of damage. A successful Will save halves the damage, and spell resistance applies.</p><p>At your option, the abjuration can include a password, in which case creatures of alignments different from yours can avoid the damage by speaking the password as they enter the area. You must select this option (and the password) at the time of casting. Adding a password requires the burning of additional rare incenses worth at least 1,000 gp, plus 1,000 gp per 60-foot cube.</p><p><i>Dispel magic</i> does not dispel a <i>forbiddance</i> effect unless the dispeller's level is at least as high as your caster level.</p><p>You can't have multiple overlapping <i>forbiddance</i> effects. In such a case, the more recent effect stops at the boundary of the older effect.</p></h4></div>"},
new Spell(){ Name = "Forcecage", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Forcecage</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (ruby dust worth 500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>barred cage (20-ft. cube) or windowless cell (10-ft. cube)</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Reflex negates; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates an immobile, invisible cubical prison composed of either bars of force or solid walls of force (your choice).</p><p>Creatures within the area are caught and contained unless they are too big to fit inside, in which case the spell automatically fails.</p><p>Teleportation and other forms of astral travel provide a means of escape, but the force walls or bars extend into the Ethereal Plane, blocking ethereal travel.</p><p>Like a <i>wall of force</i>, a <i>forcecage</i> resists <i>dispel magic,</i> although a mage's disjunction still functions. The walls of a <i>forcecage</i> can be damaged by spells as normal, except for <i>disintegrate,</i> which automatically destroys it. The walls of a <i>forcecage</i> can be damaged by weapons and supernatural abilities, but they have a Hardness of 30 and a number of hit points equal to 20 per caster level. Contact with a <i>sphere of annihilation</i> or <i>rod of cancellation</i> instantly destroys a <i>forcecage</i>.</p><p>Barred Cage: This version of the spell produces a 20-foot cube made of bands of force (similar to a <i>wall of force</i> spell) for bars. The bands are a half-inch wide, with half-inch gaps between them. Any creature capable of passing through such a small space can escape; others are confined within the barred cage. You can't attack a creature in a barred cage with a weapon unless the weapon can fit between the gaps. Even against such weapons (including arrows and similar ranged attacks), a creature in the barred cage has cover. All spells and breath weapons can pass through the gaps in the bars.</p><p><i>Windowless Cell</i>: This version of the spell produces a 10-foot cube with no way in and no way out. Solid walls of force form its six sides.</p></h4></div>"},
new Spell(){ Name = "Forceful Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Forceful Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 6, magus 6, occultist 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a soft glove)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>10-ft. hand</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>interposing hand</i>, except that it can also pursue and bull rush one opponent you select. The <i><i>forceful</i> hand</i> gets one bull rush attack per round. This attack does not provoke an attack of opportunity. Its CMB for bull rush checks uses your caster level in place of its base attack bonus, with a +8 bonus for its Strength score (27), and a +1 bonus for being Large. The hand always moves with the opponent to push them back as far as possible. It has no movement limit for this purpose. Directing the spell to a new target is a move action. <i>Forceful hand</i> prevents the opponent from moving closer to you without first succeeding on a bull rush attack, moving both the <i><i>forceful</i> hand</i> and the target closer to you. The <i><i>forceful</i> hand</i> can instead be directed to interpose itself, as <i>interposing hand</i> does.</p></h4></div>"},
new Spell(){ Name = "Foresight", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Foresight</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>druid 9, sorcerer/wizard 9, witch 9, shaman 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a hummingbird's feather)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal or touch</h5><h5><b>Targets </b>see text</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none or Will negates (harmless); <b>Spell Resistance </b>no or yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell grants you a powerful sixth sense in relation to yourself or another. Once <i>foresight</i> is cast, you receive instantaneous warnings of impending danger or harm to the subject of the spell. You are never surprised or flat-footed. In addition, the spell gives you a general idea of what action you might take to best protect yourself and gives you a +2 insight bonus to AC and on Reflex saves. This insight bonus is lost whenever you would lose a Dexterity bonus to AC.</p><p>When another creature is the subject of the spell, you receive warnings about that creature. You must communicate what you learn to the other creature for the warning to be useful, and the creature can be caught unprepared in the absence of such a warning. Shouting a warning, yanking a person back, and even telepathically communicating (via an appropriate spell) can all be accomplished before some danger befalls the subject, provided you act on the warning without delay. The subject, however, does not gain the insight bonus to AC and Reflex saves.</p></h4><h5><b>Mythic: </b>The spell's insight bonus increases to +3. If you cast the spell on another creature, you and the target are simultaneously warned by the spell, and the target gains the spell's insight bonuses.</h5></div>"},
new Spell(){ Name = "Form of the Dragon I", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Form of the Dragon I</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 6, alchemist 6, magus 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a scale of the dragon type you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>see below; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You become a Medium chromatic or metallic dragon (see the <i>Pathfinder RPG Bestiary</i>). You gain a +4 size bonus to Strength, a +2 size bonus to Constitution, a +4 natural armor bonus, fly 60 feet (poor), darkvision 60 feet, a breath weapon, and resistance to one element. You also gain one bite (1d8), two claws (1d6), and two wing attacks (1d4). Your breath weapon and resistance depend on the type of dragon. You can only use the breath weapon once per casting of this spell. All breath weapons deal 6d8 points of damage and allow a Reflex save for half damage. In addition, some of the dragon types grant additional abilities, as noted below.</p><p><br><i>Black dragon</i>: 60-foot line of acid, resist acid 20, swim 60 feet <br><i>Blue dragon</i>: 60-foot line of electricity, resist electricity 20, burrow 20 feet <br><i>Green dragon</i>: 30-foot cone of acid, resist acid 20, swim 40 feet <br><i>Red dragon</i>: 30-foot cone of fire, resist fire 30, vulnerability to cold <br><i>White dragon</i>: 30-foot cone of cold, resist cold 20, swim 60 feet, vulnerability to fire <br><i>Brass dragon</i>: 60-foot line of fire, resist fire 20, burrow 30 feet, vulnerability to cold <br><i>Bronze dragon</i>: 60-foot line of electricity, resist electricity 20, swim 60 feet <br><i>Copper dragon</i>: 60-foot line of acid, resist acid 20, <i>spider climb</i> (always active) <br><i>Gold dragon</i>: 30-foot cone of fire, resist fire 20, swim 60 feet <br><i>Silver dragon</i>: 30-foot cone of cold, resist cold 30, vulnerability to fire</p></h4></div>"},
new Spell(){ Name = "Form of the Dragon II", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Form of the Dragon II</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a scale of the dragon type you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>see below; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>form of the dragon I</i> except that it also allows you to assume the form of a Large chromatic or metallic dragon.</p><p>You gain the following abilities: a +6 size bonus to Strength, a +4 size bonus to Constitution, a +6 natural armor bonus, fly 90 feet (poor), darkvision 60 feet, a breath weapon, DR 5/magic, and resistance to one element. You also gain one bite (2d6), two claws (1d8), two wing attacks (1d6), and one tail slap attack (1d8). You can only use the breath weapon twice per casting of this spell, and you must wait 1d4 rounds between uses. All breath weapons deal 8d8 points of damage and allow a Reflex save for half damage. Line breath weapons increase to 80-foot lines and cones increase to 40-foot cones.</p></h4></div>"},
new Spell(){ Name = "Form of the Dragon III", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Form of the Dragon III</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a scale of the dragon type you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>see below; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i><i>form of the dragon I</i>I</i> save that it also allows you to take the form of a Huge chromatic or metallic dragon. You gain the following abilities: a +10 size bonus to Strength, a +8 size bonus to Constitution, a +8 natural armor bonus, fly 120 feet (poor), blindsense 60 feet, darkvision 120 feet, a breath weapon, DR 10/magic, frightful presence (DC equal to the DC for this spell), and immunity to one element (of the same type <i>form of the dragon I</i> grants resistance to).</p><p>You also gain one bite (2d8), two claws (2d6), two wing attacks (1d8), and one tail slap attack (2d6). You can use the breath weapon as often as you like, but you must wait 1d4 rounds between uses. All breath weapons deal 12d8 points of damage and allow a Reflex save for half damage. Line breath weapons increase to 100-foot lines and cones increase to 50-foot cones.</p></h4></div>"},
new Spell(){ Name = "Fox's Cunning", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fox's Cunning</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 2, sorcerer/wizard 2, alchemist 2, summoner 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (hairs or dung from a fox)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The target becomes smarter. The spell grants a +4 enhancement bonus to Intelligence, adding the usual benefits to Intelligencebased skill checks and other uses of the Intelligence modifier.</p><p>Wizards (and other spellcasters who rely on Intelligence) affected by this spell do not gain any additional bonus spells for the increased Intelligence, but the save DCs for spells they cast while under this spell's effect do increase. This spell doesn't grant extra skill ranks.</p></h4></div>"},
new Spell(){ Name = "Fox's Cunning, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Fox's Cunning, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 6, sorcerer/wizard 6, summoner 4, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (hairs or dung from a fox)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like fox's cunning, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Freedom", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Freedom</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels) or see text</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject is freed from spells and effects that restrict movement, including <i>binding, entangle</i>, grappling, <i><i>imprisonment</i>, maze</i>, paralysis, petrification, pinning, <i>sleep, slow</i>, stunning, <i>temporal stasis</i>, and <i>web.</i> To free a creature from <i>imprisonment</i> or <i>maze,</i> you must know its name and background, and you must cast this spell at the spot where it was entombed or banished into the maze.</p></h4></div>"},
new Spell(){ Name = "Freedom of Movement", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Freedom of Movement</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 4, cleric/oracle 4, druid 4, ranger 4, alchemist 4, inquisitor 4, alchemist 4, inquisitor 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a leather strip bound to the target), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal or touch</h5><h5><b>Targets </b>you or creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell enables you or a creature you touch to move and attack normally for the duration of the spell, even under the influence of magic that usually impedes movement, such as paralysis, solid fog, slow, and <i>web.</i> All combat maneuver checks made to grapple the target automatically fail. The subject automatically succeeds on any combat maneuver checks and Escape Artist checks made to escape a grapple or a pin.</p><p>The spell also allows the subject to move and attack normally while underwater, even with slashing weapons such as axes and swords or with bludgeoning weapons such as flails, hammers, and maces, provided that the weapon is wielded in the hand rather than hurled. The <i>freedom of movement</i> spell does not, however, grant water breathing.</p></h4></div>"},
new Spell(){ Name = "Freezing Sphere", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Freezing Sphere</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [cold]; <b>Level </b>sorcerer/wizard 6, magus 6, occultist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a small crystal sphere)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Targets </b>see text</h5><h5><b>Duration </b>instantaneous or 1 round/level; see text</h5><h5><b>Saving Throw </b>Reflex half; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Freezing sphere</i> creates a frigid globe of cold energy that streaks from your fingertips to the location you select, where it explodes in a 40-foot-radius burst, dealing 1d6 points of cold damage per caster level (maximum 15d6) to each creature in the area. A creature of the water subtype instead takes 1d8 points of cold damage per caster level (maximum 15d8) and is staggered for 1d4 rounds.</p><p>If the <i>freezing sphere</i> strikes a body of water or a liquid that is principally water (not including water-based creatures), it freezes the liquid to a depth of 6 inches in a 40-foot radius. This ice lasts for 1 round per caster level. Creatures that were swimming on the surface of a targeted body of water become trapped in the ice. Attempting to break free is a full-round action. A trapped creature must make a DC 25 Strength check or a DC 25 Escape Artist check to do so.</p><p>You can refrain from firing the globe after completing the spell, if you wish. Treat this as a touch spell for which you are holding the charge. You can hold the charge for as long as 1 round per level, at the end of which time the <i>freezing sphere</i> bursts centered on you (and you receive no saving throw to resist its effect). Firing the globe in a later round is a standard action.</p></h4></div>"},
new Spell(){ Name = "Gaseous Form", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Gaseous Form</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 3, sorcerer/wizard 3, alchemist 3, magus 3, occultist 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>S, M/DF (a bit of gauze and a wisp of smoke)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>willing corporeal creature touched</h5><h5><b>Duration </b>2 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject and all its gear become insubstantial, misty, and translucent. Its material armor (including natural armor) becomes worthless, though its size, Dexterity, deflection bonuses, and armor bonuses from force effects still apply. The subject gains DR 10/ magic and becomes immune to poison, sneak attacks, and critical hits. It can't attack or cast spells with verbal, somatic, material, or focus components while in <i>gaseous form</i>. This does not rule out the use of certain spells that the subject may have prepared using the feats Silent Spell, Still Spell, and Eschew Materials. The subject also loses supernatural abilities while in <i>gaseous form</i>. If it has a touch spell ready to use, that spell is discharged harmlessly when the <i>gaseous form</i> spell takes effect.</p><p>A gaseous creature can't run, but it can fly at a speed of 10 feet and automatically succeeds on all Fly skill checks. It can pass through small holes or narrow openings, even mere cracks, with all it was wearing or holding in its hands, as long as the spell persists. The creature is subject to the effects of wind, and it can't enter water or other liquid. It also can't manipulate objects or activate items, even those carried along with its <i>gaseous form</i>. Continuously active items remain active, though in some cases their effects may be moot.</p></h4><h5><b>Mythic: </b>The damage reduction changes to DR 10/epic and magic, and the fly speed increases to 30 feet. The target can expand itself as a free action, filling an area equal to double its normal space and height (a Medium creature fills a 10-foot-by-10-foot space to a height of about 10 feet) and blocking vision as obscuring mist. Returning from expanded to normal size is also a free action.</h5><h5><b>Augmented</b>: If you expend two uses of mythic power, the target can shift into or out of gaseous form as a move action.</h5></div>"},
new Spell(){ Name = "Gate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Gate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation or calling); <b>Level </b>cleric/oracle 9, sorcerer/wizard 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (see text)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>see text</h5><h5><b>Duration </b>instantaneous or concentration (up to 1 round/level); see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Casting a <i>gate</i> spell has two effects. First, it creates an interdimensional connection between your plane of existence and a plane you specify, allowing travel between those two planes in either direction.</p><p>Second, you may then call a particular individual or kind of being through the <i>gate</i>.</p><p>The <i>gate</i> itself is a circular hoop or disk from 5 to 20 feet in diameter (caster's choice) oriented in the direction you desire when it comes into existence (typically vertical and facing you). It is a two-dimensional window looking into the plane you specified when casting the spell, and anyone or anything that moves through is shunted instantly to the other side.</p><p>A <i>gate</i> has a front and a back. Creatures moving through the <i>gate</i> from the front are transported to the other plane; creatures moving through it from the back are not.</p><p><i>Planar Travel</i>: As a mode of planar travel, a <i>gate</i> spell functions much like a <i>plane shift</i> spell, except that the <i>gate</i> opens precisely at the point you desire (a creation effect). Deities and other beings who rule a planar realm can prevent a <i>gate</i> from opening in their presence or personal demesnes if they so desire. Travelers need not join hands with you-anyone who chooses to step through the portal is transported. A <i>gate</i> cannot be opened to another point on the same plane; the spell works only for interplanar travel.</p><p>You may hold the <i>gate</i> open only for a brief time (no more than 1 round per caster level), and you must concentrate on doing so, or else the interplanar connection is severed.</p><p><i>Calling Creatures</i>: The second effect of the <i>gate</i> spell is to call an extraplanar creature to your aid (a calling effect). By naming a particular being or kind of being as you cast the spell, you cause the <i>gate</i> to open in the immediate vicinity of the desired creature and pull the subject through, willing or unwilling. Deities and unique beings are under no compulsion to come through the <i>gate</i>, although they may choose to do so of their own accord. This use of the spell creates a <i>gate</i> that remains open just long enough to transport the called creatures. This use of the spell has a material cost of 10,000 gp in rare incense and offerings. This cost is in addition to any cost that must be paid to the called creatures.</p><p>If you choose to call a kind of creature instead of a known individual, you may call either a single creature or several creatures.</p><p>In either case, their total HD cannot exceed twice your caster level. In the case of a single creature, you can control it if its HD does not exceed your caster level. A creature with more HD than your caster level can't be controlled. Deities and unique beings cannot be controlled in any event. An uncontrolled being acts as it pleases, making the calling of such creatures rather dangerous. An uncontrolled being may return to its home plane at any time.</p><p>If you choose to exact a longer or more involved form of service from a called creature, you must offer some fair trade in return for that service. The service exacted must be reasonable with respect to the promised favor or reward; see the <i>lesser planar ally</i> spell for appropriate rewards. Some creatures may want their payment in ""livestock"" rather than in coin, which could involve complications.</p><p>Immediately upon completion of the service, the being is transported to your vicinity, and you must then and there turn over the promised reward. After this is done, the creature is instantly freed to return to its own plane.</p><p>Failure to fulfill the promise to the letter results in your being subjected to service by the creature or by its liege and master, at the very least. At worst, the creature or its kin may attack you.</p><p><i>Note:</i> When you use a calling spell such as <i>gate</i> to call an air, chaotic, earth, evil, fire, good, lawful, or water creature, it becomes a spell of that type.</p></h4></div>"},
new Spell(){ Name = "Geas/Quest", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Geas /Quest</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [language-dependent, mind-affecting, curse]; <b>Level </b>bard 6, cleric/oracle 6, sorcerer/wizard 6, inquisitor 5, witch 6, psychic 6, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 day/level or until discharged (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions similarly to <i>lesser <i>geas,</i></i> except that it affects a creature of any HD and allows no saving throw.</p><p>If the subject is prevented from obeying the <i>geas/quest</i> for 24 hours, it takes a -3 penalty to each of its ability scores. Each day, another -3 penalty accumulates, up to a total of -12. No ability score can be reduced to less than 1 by this effect. The ability score penalties are removed 24 hours after the subject resumes obeying the <i>geas/quest</i>.</p><p>A remove curse spell ends a <i>geas/quest</i> spell only if its caster level is at least two higher than your caster level. <i>Break enchantment</i> does not end a <i>geas/quest</i>, but limited <i>wish</i>, miracle, and <i>wish</i> do.</p><p>Bards, sorcerers, and wizards usually refer to this spell as <i>geas,</i> while clerics call the same spell quest.</p></h4></div>"},
new Spell(){ Name = "Geas, Lesser", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Geas, Lesser</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [language-dependent, mind-affecting, curse]; <b>Level </b>bard 3, sorcerer/wizard 4, witch 4, inquisitor 4, psychic 4, mesmerist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature with 7 HD or less</h5><h5><b>Duration </b>1 day/level or until discharged (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>lesser <i>geas</i></i> places a magical command on a creature to carry out some service or to refrain from some action or course of activity, as desired by you. The creature must have 7 or fewer HD and be able to understand you. While a <i>geas</i> cannot compel a creature to kill itself or perform acts that would result in certain death, it can cause almost any other course of activity.</p><p>The <i>geas</i>ed creature must follow the given instructions until the <i>geas</i> is completed, no matter how long it takes.</p><p>If the instructions involve some open-ended task that the recipient cannot complete through his own actions, the spell remains in effect for a maximum of 1 day per caster level. A clever recipient can subvert some instructions.</p><p>If the subject is prevented from obeying the <i>lesser <i>geas</i></i> for 24 hours, it takes a -2 penalty to each of its ability scores. Each day, another -2 penalty accumulates, up to a total of -8. No ability score can be reduced to less than 1 by this effect. The ability score penalties are removed 24 hours after the subject resumes obeying the <i>lesser <i>geas</i></i>.</p><p>A <i>lesser <i>geas</i></i> (and all ability score penalties) can be ended by break enchantment, limited wish, remove curse, miracle, or <i>wish. Dispel magic</i> does not affect a <i>lesser <i>geas</i></i>.</p></h4></div>"},
new Spell(){ Name = "Gentle Repose", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Gentle Repose</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 2, sorcerer/wizard 3, witch 2, shaman 2, occultist 3, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (salt and a copper piece for each of the corpse's eyes)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>corpse touched</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You preserve the remains of a dead creature so that they do not decay. Doing so effectively extends the time limit on raising that creature from the dead (see <i>raise dead</i>). Days spent under the influence of this spell don't count against the time limit.</p><p>Additionally, this spell makes transporting a slain (and thus decaying) comrade less unpleasant.</p><p>The spell also works on severed body parts and the like.</p></h4></div>"},
new Spell(){ Name = "Ghost Sound", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ghost Sound</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 0, sorcerer/wizard 0, magus 0, occultist 0, psychic 0, mesmerist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a bit of wool or a small lump of wax)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>illusory sounds</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will disbelief; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Ghost sound</i> allows you to create a volume of sound that rises, recedes, approaches, or remains at a fixed place. You choose what type of sound <i>ghost sound</i> creates when casting it and cannot thereafter change the sound's basic character.</p><p>The volume of sound created depends on your level. You can produce as much noise as four normal humans per caster level (maximum 40 humans). Thus, talking, singing, shouting, walking, marching, or running sounds can be created. The noise a <i>ghost sound</i> spell produces can be virtually any type of sound within the volume limit. A horde of rats running and squeaking is about the same volume as eight humans running and shouting. A roaring lion is equal to the noise from 16 humans, while a roaring dragon is equal to the noise from 32 humans. Anyone who hears a <i>ghost sound</i> receives a Will save to disbelieve.</p><p><i>Ghost sound</i> can enhance the effectiveness of a <i>silent image</i> spell.</p><p><i>Ghost sound</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Ghoul touch", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ghoul touch</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 2, bloodrager 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (cloth from a ghoul or earth from a ghoul's lair)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living humanoid touched</h5><h5><b>Duration </b>1d6+2 rounds</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Imbuing you with negative energy, this spell allows you to paralyze a single living humanoid for the duration of the spell with a successful melee touch attack.</p><p>A paralyzed subject exudes a carrion stench that causes all living creatures (except you) in a 10-foot-radius spread to become sickened (Fortitude negates). A <i>neutralize poison</i> spell removes the effect from a sickened creature, and creatures immune to poison are unaffected by the stench. This is a poison effect.</p></h4></div>"},
new Spell(){ Name = "Giant Form I", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Giant Form I</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 7, alchemist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you cast this spell you can assume the form of any Large humanoid creature of the giant subtype (see the <i>Pathfinder RPG Bestiary</i>). Once you assume your new form, you gain the following abilities: a +6 size bonus to Strength, a -2 penalty to Dexterity, a +4 size bonus to Constitution, a +4 natural armor bonus, and low-light vision. If the form you assume has any of the following abilities, you gain the listed ability: darkvision 60 feet, rend (2d6 damage), regeneration 5, rock catching, and rock throwing (range 60 feet, 2d6 damage). If the creature has immunity or resistance to any elements, you gain resistance 20 to those elements. If the creature has vulnerability to an element, you gain that vulnerability.</p></h4></div>"},
new Spell(){ Name = "Giant Form II", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Giant Form II</p><div style=""clear: both;""></div></div><div><h5><b>School </b>Transmutation (polymorph); <b>Level </b>sorcerer/wizard 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>giant form I</i> except that it also allows you to assume the form of any Huge creature of the giant type. You gain the following abilities: a +8 size bonus to Strength, a -2 penalty to Dexterity, a +6 size bonus to Constitution, a +6 natural armor bonus, low-light vision, and a +10 foot enhancement bonus to your speed. If the form you assume has any of the following abilities, you gain the listed ability: swim 60 feet, darkvision 60 feet, rend (2d8 damage), regeneration 5, rock catching, and rock throwing (range 120 feet, 2d10 damage). If the creature has immunity or resistance to one element, you gain that immunity or resistance. If the creature has vulnerability to an element, you gain that vulnerability.</p></h4></div>"},
new Spell(){ Name = "Giant Vermin", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Giant Vermin</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 4, druid 4, shaman 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>1 or more vermin, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You turn a number of normal-sized centipedes, scorpions, or spiders into their giant counterparts (see the <i>Pathfinder RPG Bestiary</i>). Only one type of vermin can be transmuted (so a single casting cannot affect both a centipede and a spider). The number of vermin which can be affected by this spell depends on your caster level, as noted on the table below.</p><p>Giant vermin created by this spell do not attempt to harm you, but your control of such creatures is limited to simple commands (""Attack,"" ""Defend,"" ""Stop,"" and so forth). Orders to attack a certain creature when it appears or guard against a particular occurrence are too complex for the vermin to understand. Unless commanded to do otherwise, the giant vermin attack whomever or whatever is near them.</p><p> <table><tr><th>Caster Level</th><th>Centipedes</th><th>Scorpions</th><th>Spiders</th></tr><tr><td>9th or lower</td><td>3</td><td>1</td><td>2</td></tr><tr><td>10th-13th</td><td>4</td><td>2</td><td>3</td></tr><tr><td>14th-17th</td><td>6</td><td>3</td><td>4</td></tr><tr><td>18th-19th</td><td>8</td><td>4</td><td>5</td></tr><tr><td>20th or higher</td><td>12</td><td>6</td><td>8</td></tr></table> </p></h4></div>"},
new Spell(){ Name = "Glibness", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Glibness</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 3, mesmerist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>10 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Your speech becomes fluent and more believable, causing those who hear you to believe every word you say. You gain a +20 bonus on Bluff checks made to convince another of the truth of your words. This bonus doesn't apply to other uses of the Bluff skill, such as feinting in combat, creating a diversion to hide, or communicating a hidden message via innuendo.</p><p>If a magical effect is used against you that would detect your lies or force you to speak the truth, the user of the effect must succeed on a caster level check (1d20 + caster level) against a DC of 15 + your caster level to succeed. Failure means the effect does not detect your lies or force you to speak only the truth.</p></h4></div>"},
new Spell(){ Name = "Glitterdust", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Glitterdust</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>bard 2, sorcerer/wizard 2, summoner 2, witch 2, magus 2, bloodrager 2, occultist 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (ground mica)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>creatures and objects within 10-ft.-radius spread</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates (blinding only); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A cloud of golden particles covers everyone and everything in the area, causing creatures to become blinded and visibly outlining invisible things for the duration of the spell. All within the area are covered by the dust, which cannot be removed and continues to sparkle until it fades. Each round at the end of their turn blinded creatures may attempt new saving throws to end the blindness effect.</p><p>Any creature covered by the dust takes a -40 penalty on Stealth checks.</p></h4></div>"},
new Spell(){ Name = "Globe of Invulnerability, Lesser", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Globe of Invulnerability, Lesser</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 4, occultist 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a glass or crystal bead)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Area </b>10-ft.-radius spherical emanation, centered on you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An immobile, faintly shimmering magical sphere surrounds you and excludes all spell effects of 3rd level or lower. The area or effect of any such spells does not include the area of the <i>lesser globe of invulnerability</i>. Such spells fail to affect any target located within the globe. Excluded effects include spell-like abilities and spells or spell-like effects from items. Any type of spell, however, can be cast through or out of the magical globe.</p><p>Spells of 4th level and higher are not affected by the globe, nor are spells already in effect when the globe is cast. The globe can be brought down by a <i>dispel magic</i> spell. You can leave and return to the globe without penalty.</p><p>Note that spell effects are not disrupted unless their effects enter the globe, and even then they are merely suppressed, not dispelled.</p><p>If a given spell has more than one level depending on which character class is casting it, use the level appropriate to the caster to determine whether <i>lesser globe of invulnerability</i> stops it.</p></h4></div>"},
new Spell(){ Name = "Globe Of Invulnerability", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Globe Of Invulnerability</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 6, occultist 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a glass or crystal bead)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Area </b>10-ft.-radius spherical emanation, centered on you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser globe of invulnerability</i>, except that it also excludes 4th-level spells and spell-like effects.</p></h4><h5><b>Mythic: </b>Add half your tier to the level of non-mythic spells the globe excludes. The globe excludes mythic spells of 4th level or lower.</h5></div>"},
new Spell(){ Name = "Glyph Of Warding", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Glyph Of Warding</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (powdered diamond worth 200 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Target or Area </b>object touched or up to 5 sq. ft./level<h5><b>Duration </b>permanent until discharged (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no (object) and yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This powerful inscription harms those who enter, pass, or open the warded area or object. A <i>glyph of warding</i> can guard a bridge or passage, ward a portal, trap a chest or box, and so on.</p><p>You set all of the conditions of the ward. Typically, any creature entering the warded area or opening the warded object without speaking a password (which you set when casting the spell) is subject to the magic it stores. Alternatively or in addition to a password trigger, glyphs can be set according to physical characteristics (such as height or weight) or creature type, subtype, or kind. Glyphs can also be set with respect to good, evil, law, or chaos, or to pass those of your religion. They cannot be set according to class, HD, or level.</p><p>Glyphs respond to invisible creatures normally but are not triggered by those who travel past them ethereally. Multiple glyphs cannot be cast on the same area. However, if a cabinet has three different drawers, each can be separately warded.</p><p>When casting the spell, you weave a tracery of faintly glowing lines around the warding sigil. A glyph can be placed to conform to any shape up to the limitations of your total square footage. When the spell is completed, the glyph and tracery become nearly invisible.</p><p>Glyphs cannot be affected or bypassed by such means as physical or magical probing, though they can be dispelled. <i>Mislead</i>, <i>polymorph</i>, and <i>nondetection</i> (and similar magical effects) can fool a glyph, though nonmagical disguises and the like can't. <i>Read magic</i> allows you to identify a <i>glyph of warding</i> with a DC 13 Knowledge (arcana) check. Identifying the glyph does not discharge it and allows you to know the basic nature of the glyph (version, type of damage caused, what spell is stored).</p><p><i>Note</i>: Magic traps such as <i>glyph of warding</i> are hard to detect and disable. While any character can use Perception to find a glyph, only a character with the trapfinding class feature can use Disable Device to disarm it. The DC in each case is 25 + spell level, or 28 for <i>glyph of warding</i>.</p><p>Depending on the version selected, a glyph either blasts the intruder or activates a spell.</p><p><i>Blast Glyph</i>: A <i>blast glyph</i> deals 1d8 points of damage per two caster levels (maximum 5d8) to the intruder and to all within 5 feet of him or her. This damage is acid, cold, fire, electricity, or sonic (caster's choice, made at time of casting). Each creature affected can attempt a Reflex save to take half damage. Spell resistance applies against this effect.</p><p><i>Spell Glyph</i>: You can store any harmful spell of 3rd level or lower that you know. All level-dependent features of the spell are based on your caster level at the time of casting the glyph. If the spell has a target, it targets the intruder. If the spell has an area or an amorphous effect, the area or effect is centered on the intruder. If the spell summons creatures, they appear as close as possible to the intruder and attack.Saving Throws and spell resistance operate as normal, except that the DC is based on the level of the spell stored in the glyph.</p></h4><h5><b>Mythic: </b>Add your tier to the DC to detect or disable the glyph. Blast glyph damage increases to 1d6 points of damage per caster level (maximum 20d6). Add half your tier to the maximum spell level you can store in a spell glyph.</h5></div>"},
new Spell(){ Name = "Glyph of Warding, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Glyph of Warding, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (powdered diamond worth 200 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Target or Area </b>object touched or up to 5 sq. ft./level<h5><b>Duration </b>permanent until discharged (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no (object) and yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>glyph of warding</i>, except that a <i>greater blast glyph</i> deals up to 10d8 points of damage, and a <i>greater spell glyph</i> can store a spell of 6th level or lower.</p><p><i>Read magic</i> allows you to identify a greater <i>glyph of warding</i> with aDC 16 Spellcraft check.</p><p><i>Material Component</i>: You trace the glyph with incense, which must first be sprinkled with powdered diamond worth at least 400 gp.</p></h4></div>"},
new Spell(){ Name = "Goodberry", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Goodberry</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1, shaman 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>2d4 fresh berries touched</h5><h5><b>Duration </b>1 day/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Casting <i>goodberry</i> makes 2d4 freshly picked berries magical. You (as well as any other druid of 3rd or higher level) can immediately discern which berries are affected. Each transmuted berry provides nourishment as if it were a normal meal for a Medium creature.</p><p>The berry also cures 1 point of damage when eaten, subject to a maximum of 8 points of such curing in any 24-hour period.</p></h4></div>"},
new Spell(){ Name = "Good Hope", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Good Hope</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, emotion]; <b>Level </b>bard 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature/level, no two of which may be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell instills powerful hope in the subjects. Each affected creature gains a +2 morale bonus on saving throws, attack rolls, ability checks, skill checks, and weapon damage rolls.</p><p><i>Good hope</i> counters and dispels crushing despair.</p></h4></div>"},
new Spell(){ Name = "Grasping Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Grasping Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a soft glove)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>10-ft. hand</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>interposing hand</i>, except that it can also grapple one opponent you select. The <i>grasping hand</i> gets one grapple attack per round. This attack does not provoke an attack of opportunity. Its CMB and CMD for grapple checks use your caster level in place of its base attack bonus, with a +10 bonus for its Strength (31) score and a +1 bonus for being Large (its Dexterity is 10, granting no bonus on the Combat Maneuver Defense).</p><p>The hand holds but does not harm creatures that it grapples.</p><p>Directing the spell to a new target is a move action. The <i>grasping hand</i> can instead be directed to bull rush a target, using the same bonuses outlined above, or it can be directed to interpose itself, as <i>interposing hand</i> does.</p></h4></div>"},
new Spell(){ Name = "Grease", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Grease</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>bard 1, sorcerer/wizard 1, summoner 1, magus 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (butter)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one object or 10-ft. square</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>grease</i> spell covers a solid surface with a layer of slippery <i>grease</i>. Any creature in the area when the spell is cast must make a successful Reflex save or fall. A creature can walk within or through the area of <i>grease</i> at half normal speed with a DC 10 Acrobatics check. Failure means it can't move that round (and must then make a Reflex save or fall), while failure by 5 or more means it falls (see the Acrobatics skill for details). Creatures that do not move on their turn do not need to make this check and are not considered flat-footed.</p><p>The spell can also be used to create a greasy coating on an item.</p><p>Material objects not in use are always affected by this spell, while an object wielded or employed by a creature requires its bearer to make a Reflex saving throw to avoid the effect. If the initial saving throw fails, the creature immediately drops the item. A saving throw must be made in each round that the creature attempts to pick up or use the <i>grease</i>d item. A creature wearing <i>grease</i>d armor or clothing gains a +10 circumstance bonus on Escape Artist checks and combat maneuver checks made to escape a grapple, and to their CMD to avoid being grappled.</p></h4><h5><b>Mythic: </b>If you cast mythic grease on an area, add your tier to the Acrobatics DC to move within or through the area. If you cast it on a creature's armor or clothing, add your tier to its Escape Artist checks and combat maneuver checks attempted to escape a grapple and to its CMD to avoid being grappled.</h5><h5><b>Augmented</b>: You can expend two uses of mythic power to make the grease flammable. It ignites from any fire at least the size of a candle flame. If a greased area is ignited, any creature in the area on your turn takes 1d3 points of fire damage and might catch on fire (Core Rulebook 444). If a greased creature is ignited, it takes 2d6 points of fire damage, and might catch on fire; add your tier to the save DC.</h5></div>"},
new Spell(){ Name = "Guards and Wards", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Guards and Wards</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 6, witch 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>30 minutes</h5><h5><b>Components </b>V, S, M (burning incense, a small measure of brimstone and oil, a knotted string, and a small amount of blood), F (a small silver rod)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>anywhere within the area to be warded</h5><h5><b>Area </b>up to 200 sq. ft./level  (S)</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This powerful spell is primarily used to defend a stronghold or fortress by creating a number of magical wards and effects. The ward protects 200 square feet per caster level. The warded area can be as much as 20 feet high, and shaped as you desire. You can ward several stories of a stronghold by dividing the area among them; you must be somewhere within the area to be warded to cast the spell. The spell creates the following magical effects within the warded area.</p><p><i>Fog: Fog</i> fills all corridors, obscuring all sight, including darkvision, beyond 5 feet. A creature within 5 feet has concealment (attacks have a 20% miss chance). Creatures farther away have total concealment (50% miss chance, and the attacker cannot use sight to locate the target). Saving Throw: none. Spell Resistance: no.</p><p><i>Arcane Locks</i>: All doors in the warded area are <i>arcane locked.</i></p><p>Saving Throw: none. Spell Resistance: no.</p><p><i>Webs: Webs</i> fill all stairs from top to bottom. These strands are identical with those created by the <i>web</i> spell, except that they regrow in 10 minutes if they are burned or torn away while the <i>guards and wards</i> spell lasts. Saving Throw: Reflex negates; see text for <i>web</i>. Spell Resistance: no.</p><p><i>Confusion:</i> Where there are choices in direction-such as a corridor intersection or side passage-a minor <i>confusion-type</i> effect functions so as to make it 50% probable that intruders believe they are going in the opposite direction from the one they actually chose. This is a mind-affecting effect. Saving Throw: none. Spell Resistance: yes.</p><p><i>Lost Doors</i>: One door per caster level is covered by a <i>silent image</i> to appear as if it were a plain wall. Saving Throw: Will disbelief (if interacted with). Spell Resistance: no.</p><p>In addition, you can place your choice of one of the following five magical effects.</p><p>1. <i>Dancing lights</i> in four corridors. You can designate a simple program that causes the lights to repeat as long as the <i>guards and wards</i> spell lasts. Saving Throw: none. Spell Resistance: no.</p><p>2. A <i>magic mouth</i> in two places. Saving Throw: none. Spell Resistance: no.</p><p>3. A <i>stinking cloud</i> in two places. The vapors appear in the places you designate; they return within 10 minutes if dispersed by wind while the <i>guards and wards</i> spell lasts. Saving Throw: Fortitude negates; see text for <i>stinking cloud</i>. Spell Resistance: no.</p><p>4. A <i>gust of wind in</i> one corridor or room. Saving Throw: Fortitude negates. Spell Resistance: yes.</p><p>5. A <i>suggestion</i> in one place. You select an area of up to 5 feet square, and any creature who enters or passes through the area receives the <i>suggestion</i> mentally. Saving Throw: Will negates.</p><p>Spell Resistance: yes.</p><p>The whole warded area radiates strong magic of the abjuration school. A <i>dispel magic</i> cast on a specific effect, if successful, removes only that effect. A successful mage's disjunction destroys the entire <i>guards and wards</i> effect.</p></h4></div>"},
new Spell(){ Name = "Guidance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Guidance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 0, druid 0, summoner 0, witch 0, inquisitor 0, summoner 0, witch 0, inquisitor 0, shaman 0, occultist 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 minute or until discharged</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell imbues the subject with a touch of divine guidance.</p><p>The creature gets a +1 competence bonus on a single attack roll, saving throw, or skill check. It must choose to use the bonus before making the roll to which it applies.</p></h4></div>"},
new Spell(){ Name = "Gust Of Wind", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Gust Of Wind</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [air]; <b>Level </b>druid 2, sorcerer/wizard 2, magus 2, bloodrager 2, occultist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>60 ft.</h5><h5><b>Effect </b>line-shaped gust of severe wind emanating out from you to the extreme of the range</h5><h5><b>Duration </b>1 round</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates a severe blast of air (approximately 50 mph) that originates from you, affecting all creatures in its path. All flying creatures in this area take a -4 penalty on Fly skill checks. Tiny or smaller flying creatures must make a DC 25 Fly skill check or be blown back 2d6 x 10 feet and take 2d6 points of damage. Small or smaller flying creatures must make a DC 20 Fly skill check to move against the force of the wind.</p><p>A Tiny or smaller creature on the ground is knocked down and rolled 1d4 x 10 feet, taking 1d4 points of nonlethal damage per 10 feet.</p><p>Small creatures are knocked prone by the force of the wind.</p><p>Medium or smaller creatures are unable to move forward against the force of the wind unless they succeed at a DC 15 Strength check.</p><p>Large or larger creatures may move normally within a <i>gust of wind</i> effect.</p><p>This spell can't move a creature beyond the limit of it's range.</p><p>Any creature, regardless of size, takes a -4 penalty on ranged attacks and Perception checks in the area of a <i>gust of wind</i>.</p><p>The force of the gust automatically extinguishes candles, torches, and similar unprotected flames. It causes protected flames, such as those in lanterns, to dance wildly and has a 50% chance to extinguish those lights.</p><p>In addition to the effects noted, a <i>gust of wind</i> can do anything that a sudden blast of wind would be expected to do. It can create a stinging spray of sand or dust, fan a large fire, overturn delicate awnings or hangings, heel over a small boat, and blow gases or vapors to the edge of its range.</p><p><i>Gust of wind</i> can be made permanent with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>The force of the wind increases to windstorm (approximately 70 mph; Core Rulebook 439). All effects described in the spell affect creatures one size larger. Ranged attacks can't be attempted in the area of the spell. The wind automatically blows out unprotected flames and has a 75% chance of blowing out protected flames.</h5><h5><b>Augmented (2nd)</b>: If you expend two uses of mythic power, the spell lasts for 1 additional round and the range increases by 30 feet. As a swift action at the start of your turn, you can expend another use of mythic power to extend the duration by 1 round and the range by another 30 feet.</h5></div>"},
new Spell(){ Name = "Hallow", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hallow</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [good]; <b>Level </b>cleric/oracle 5, druid 5, inquisitor 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>24 hours</h5><h5><b>Components </b>V, S, M (herbs, oils, and incense worth at least 1,000 gp, plus 1,000 gp per level of the spell to be included in the hallowed area), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Area </b>40-ft. radius emanating from the touched point</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Hallow</i> makes a particular site, building, or structure a holy site.</p><p>This has four major effects.</p><p>First, the site is warded by a <i>magic circle against evil</i> effect.</p><p>Second, the DC to resist positive channeled energy within this area gains a +4 sacred bonus and the DC to resist negative energy is reduced by 4. Spell resistance does not apply to this effect. This provision does not apply to the druid version of the spell.</p><p>Third, any dead body interred in a <i><i>hallow</i>ed</i> site cannot be turned into an undead creature.</p><p>Finally, you can fix a single spell effect to the <i><i>hallow</i>ed</i> site.</p><p>The spell effect lasts for 1 year and functions throughout the entire site, regardless of the normal duration and area or effect.</p><p>You may designate whether the effect applies to all creatures, creatures who share your faith or alignment, or creatures who adhere to another faith or alignment. At the end of the year, the chosen effect lapses, but it can be renewed or replaced simply by casting <i>hallow</i> again.</p><p>Spell effects that may be tied to a <i><i>hallow</i>ed</i> site include <i>aid, bane, bless, cause fear, darkness, daylight, death ward, deeper darkness, detect evil, detect magic, dimensional anchor, discern lies, dispel magic, endure elements, freedom of movement, invisibility purge, protection from energy, remove fear, resist energy, silence, tongues,</i> and <i>zone of truth</i>. Saving throws and spell resistance might apply to these spells' effects. (See the individual spell descriptions for details.) An area can receive only one <i>hallow</i> spell (and its associated spell effect) at a time. <i>Hallow</i> counters but does not dispel un<i>hallow</i>.</p></h4></div>"},
new Spell(){ Name = "Hallucinatory Terrain", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hallucinatory Terrain</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 4, sorcerer/wizard 4, psychic 4, mesmerist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (a stone, a twig, and a green leaf)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>one 30-ft. cube/level  (S)</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You make natural terrain look, sound, and smell like some other sort of natural terrain. Structures, equipment, and creatures within the area are not hidden or changed in appearance.</p></h4></div>"},
new Spell(){ Name = "Halt Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Halt Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 3, inquisitor 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a pinch of sulfur and powdered garlic)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>up to three undead creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates (see text); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell renders as many as three undead creatures immobile. A nonintelligent undead creature gets no saving throw; an intelligent undead creature does. If the spell is successful, it renders the undead creature immobile for the duration of the spell (similar to the effect of <i>hold person on</i> a living creature). The effect is broken if the <i>halted</i> creatures are attacked or take damage.</p></h4></div>"},
new Spell(){ Name = "Harm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Harm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 6, witch 7, inquisitor 6, shaman 7, occultist 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Harm</i> charges a subject with negative energy that deals 10 points of damage per caster level (to a maximum of 150 points at 15th level). If the creature successfully saves, <i>harm</i> deals half this amount. <i>Harm</i> cannot reduce the target's hit points to less than 1.</p><p>If used on an undead creature, <i>harm</i> acts like <i>heal</i>.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 15 points of damage per caster level (maximum 225 points).</h5><h5><b>Augmented</b>: If you expend two uses of mythic power, the spell also acts as poison.</h5></div>"},
new Spell(){ Name = "Haste", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Haste</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>alchemist 3, bard 3, magus 3, sorcerer/wizard 3, summoner 2, bloodrager 3, occultist 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a shaving of licorice root)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Fortitude negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The transmuted creatures move and act more quickly than normal. This extra <i>speed</i> has several effects.</p><p>When making a full attack action, a <i>haste</i>d creature may make one extra attack with one natural or manufactured weapon. The attack is made using the creature's full base attack bonus, plus any modifiers appropriate to the situation. (This effect is not cumulative with similar effects, such as that provided by a <i>speed</i> weapon, nor does it actually grant an extra action, so you can't use it to cast a second spell or otherwise take an extra action in the round.) A <i>haste</i>d creature gains a +1 bonus on attack rolls and a +1 dodge bonus to AC and Reflex saves. Any condition that makes you lose your Dexterity bonus to Armor Class (if any) also makes you lose dodge bonuses.</p><p>All of the <i>haste</i>d creature's modes of movement (including land movement, burrow, climb, fly, and swim) increase by 30 feet, to a maximum of twice the subject's normal <i>speed</i> using that form of movement. This increase counts as an enhancement bonus, and it affects the creature's jumping distance as normal for increased <i>speed</i>. Multiple <i>haste</i> effects don't stack. <i>Haste</i> dispels and counters <i>slow</i>.</p></h4><h5><b>Mythic: </b>Affected creatures gain an additional move action each round. The movement speed increase changes to 50 feet, to a maximum of three times the creature's normal speed for that movement type.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the movement speed increase changes to 70 feet, with no limit based on the creature's normal speed. If an affected creature moves at least 30 feet on its turn, it can travel across liquid as if the liquid were solid. If the liquid deals damage on contact, the creature takes only half damage from moving across it.</h5></div>"},
new Spell(){ Name = "Heal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 6, druid 7, alchemist 6, witch 7, inquisitor 6, shaman 7, occultist 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Heal</i> enables you to channel positive energy into a creature to wipe away injury and afflictions. It immediately ends any and all of the following adverse conditions affecting the target: ability damage, blinded, confused, dazed, dazzled, deafened, diseased, exhausted, fatigued, feebleminded, insanity, nauseated, poisoned, sickened, and stunned. It also cures 10 hit points of damage per level of the caster, to a maximum of 150 points at 15th level.</p><p><i>Heal</i> does not remove negative levels or restore permanently drained ability score points.</p><p>If used against an undead creature, <i>heal</i> instead acts like <i>harm</i>.</p></h4><h5><b>Mythic: </b>The damage cured increases to 15 points of damage per caster level (maximum 225 points).</h5><h5><b>Augmented</b>: If you expend two uses of mythic power, the spell also acts as restoration.</h5></div>"},
new Spell(){ Name = "Heal, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heal, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 9, shaman 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one or more creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>heal,</i> except as noted above. The maximum number of hit points restored to each creature is 250.</p></h4></div>"},
new Spell(){ Name = "Heal Mount", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heal Mount</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>paladin 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>your mount touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>heal,</i> but it affects only the paladin's special mount (typically a horse).</p></h4></div>"},
new Spell(){ Name = "Heat Metal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heat Metal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [fire]; <b>Level </b>druid 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>metal equipment of one creature per two levels, no two of which can be more than 30 ft. apart; or 25 lbs. of metal/level, all of which must be within a 30-ft. circle</h5><h5><b>Duration </b>7 rounds</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Heat metal</i> causes metal objects to become red-hot.</p><p>Unattended, nonmagical metal gets no saving throw. Magical metal is allowed a saving throw against the spell. An item in a creature's possession uses the creature's saving throw bonus unless its own is higher.</p><p>A creature takes fire damage if its equipment is heated. It takes full damage if its armor, shield, or weapon is affected. The creature takes minimum damage (1 point or 2 points; see the table) if it's not wearing or wielding such an item.</p><p>On the first round of the spell, the metal becomes warm and uncomfortable to touch but deals no damage. The same effect also occurs on the last round of the spell's duration. During the second (and also the next-to-last) round, intense heat causes pain and damage. In the third, fourth, and fifth rounds, the metal is searing hot, and causes more damage, as shown on the table presented on the following page.</p> <table border ='1'><tr><th>Round</th><th>Metal Temperature</th><th>Damage</th></tr><tr><td>1</td><td>Warm</td><td>None</td></tr><tr><td>2</td><td>Hot</td><td>1d4 points</td></tr><tr><td>3-5</td><td>Searing</td><td>2d4 points</td></tr><tr><td>6</td><td>Hot</td><td>1d4 points</td></tr><tr><td>7</td><td>Warm</td><td>None</td></tr></table>  Any cold intense enough to damage the creature negates fire damage from the spell (and vice versa) on a point-for-point basis.</p><p>If cast underwater, <i>heat metal</i> deals half damage and boils the surrounding water.</p><p><i>Heat metal</i> counters and dispels <i>chill metal</i>.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d8 points of fire damage on rounds 2 and 6 and to 2d8 points of fire damage per round on rounds 3 through 5. On any round in which a creature takes fire damage from this spell, that creature must succeed at a Reflex save or catch on fire (Core Rulebook 444).</h5></div>"},
new Spell(){ Name = "Helping Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Helping Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>5 miles</h5><h5><b>Effect </b>ghostly hand</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You create the ghostly image of a hand, which you can send to find a creature within 5 miles. The hand then beckons to that creature and leads it to you if the creature is willing to follow.</p><p>When the spell is cast, you specify a person (or any creature) by physical description, which can include race, gender, and appearance but not ambiguous factors such as level, alignment, or class. When the description is done, the hand streaks off in search of a subject that fits the description. The amount of time it takes to find the subject depends on how far away he is, as detailed on the following table.</p><p> <table><tr><th>Distance</th><th>Time to Locate</th></tr><tr><td>100 ft. or less</td><td>1 round</td></tr><tr><td>1,000 ft.</td><td>1 minute</td></tr><tr><td>1 mile</td><td>10 minutes</td></tr><tr><td>2 miles</td><td>1 hour</td></tr><tr><td>3 miles</td><td>2 hours</td></tr><tr><td>4 miles</td><td>3 hours</td></tr><tr><td>5 miles</td><td>4 hours</td></tr></table>  Once the hand locates the subject, it beckons the creature to follow it. If the subject does so, the hand points in your direction, indicating the most direct, feasible route. The hand hovers 10 feet in front of the subject, moving before it at a speed of as much as 240 feet per round. Once the hand leads the subject back to you, it disappears.</p><p>The subject is not compelled to follow the hand or act in any particular way toward you. If the subject chooses not to follow, the hand continues to beckon for the duration of the spell, then disappears. If the spell expires while the subject is en route to you, the hand disappears; the subject must then rely on its own devices to locate you.</p><p>If more than one subject in a 5-mile radius meets the description, the hand locates the closest creature. If that creature refuses to follow the hand, the hand does not seek out a second subject.</p><p>If, at the end of 4 hours of searching, the hand has found no subject that matches the description within 5 miles, it returns to you, displays an outstretched palm (indicating that no such creature was found), and disappears.</p><p>The ghostly hand has no physical form. It is invisible to anyone except you and a potential subject. It cannot engage in combat or execute any other task aside from locating a subject and leading it back to you. The hand can't pass through solid objects but can ooze through small cracks and slits. The hand cannot travel more than 5 miles from the spot it appeared when you cast the spell.</p></h4></div>"},
new Spell(){ Name = "Heroes' Feast", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heroes' Feast</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>bard 6, cleric/oracle 6, inquisitor 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>feast for one creature/level</h5><h5><b>Duration </b>1 hour plus 12 hours; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You bring forth a great feast, including a magnificent table, chairs, service, and food and drink. The feast takes 1 hour to consume, and the beneficial effects do not set in until this hour is over. Every creature partaking of the feast is cured of all sickness and nausea, receives the benefits of both neutralize poison and remove disease, and gains 1d8 temporary hit points + 1 point per two caster levels (maximum +10) after imbibing the nectar-like beverage that is part of the feast. The ambrosial food grants each creature that partakes a +1 morale bonus on attack rolls and Will saves and a +4 morale bonus on saving throws against poison and fear effects for 12 hours.</p><p> If the feast is interrupted for any reason, the spell is ruined and all effects of the spell are negated.</p></h4></div>"},
new Spell(){ Name = "Heroism", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heroism</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 2, sorcerer/wizard 3, alchemist 3, summoner 3, witch 3, inquisitor 3, bloodrager 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell imbues a single creature with great bravery and morale in battle. The target gains a +2 morale bonus on attack rolls, saves, and skill checks.</p></h4><h5><b>Mythic: </b>The morale bonus increases to +4 and applies on all checks, attack rolls, saves, and weapon damage rolls. Allies adjacent to the target gain a +4 morale bonus on saves against fear effects.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the target can attempt one additional attack per round when taking a full attack action (as if using a haste spell). The target can use this additional attack ability a number of times equal to your tier.</h5></div>"},
new Spell(){ Name = "Heroism, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Heroism, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 5, sorcerer/wizard 6, summoner 5, witch 6, psychic 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>heroism,</i> except the creature gains a +4 morale bonus on attack rolls, saves, and skill checks, immunity to fear effects, and temporary hit points equal to your caster level (maximum 20).</p></h4></div>"},
new Spell(){ Name = "Hide from Animals", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hide from Animals</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>druid 1, ranger 1, shaman 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one creature touched/level</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Animals cannot sense the warded creatures. Even extraordinary or supernatural sensory capabilities, such as blindsense, blindsight, scent, and tremorsense, cannot detect or locate warded creatures.</p><p>Animals simply act as though the warded creatures are not there.</p><p>If a warded character touches an animal or attacks any creature, even with a spell, the spell ends for all recipients.</p></h4></div>"},
new Spell(){ Name = "Hide from Undead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hide from Undead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 1, inquisitor 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched creature/level</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Undead cannot see, hear, or smell creatures warded by this spell.</p><p>Even extraordinary or supernatural sensory capabilities, such as blindsense, blindsight, scent, and tremorsense, cannot detect or locate warded creatures. Nonintelligent undead creatures (such as skeletons or zombies) are automatically affected and act as though the warded creatures are not there. An intelligent undead creature gets a single Will saving throw. If it fails, the subject can't see any of the warded creatures. If it has reason to believe unseen opponents are present, however, it can attempt to find or strike them. If a warded creature attempts to channel positive energy, turn or command undead, touches an undead creature, or attacks any creature (even with a spell), the spell ends for all recipients.</p></h4></div>"},
new Spell(){ Name = "Hideous Laughter", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hideous Laughter</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 1, sorcerer/wizard 2, psychic 2, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (tiny fruit tarts and a feather)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature; see text</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell afflicts the subject with uncontrollable laughter. It collapses into gales of manic laughter, falling prone. The subject can take no actions while laughing, but is not considered helpless.</p><p>After the spell ends, it can act normally. On the creature's next turn, it may attempt a new saving throw to end the effect. This is a full round action that does not provoke attacks of opportunity.</p><p>If this save is successful, the effect ends. If not, the creature continues laughing for the entire duration.</p><p>A creature with an Intelligence score of 2 or lower is not affected.</p><p>A creature whose type is different from the caster's receives a +4 bonus on its saving throw, because humor doesn't ""translate"" well.</p></h4></div>"},
new Spell(){ Name = "Hold Person", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hold Person</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 2, cleric/oracle 2, sorcerer/wizard 3, witch 2, inquisitor 2, antipaladin 2, bloodrager 3, shaman 2, occultist 3, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a small, straight piece of iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one humanoid creature</h5><h5><b>Duration </b>1 round/level ; see text (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject becomes paralyzed and freezes in place. It is aware and breathes normally but cannot take any actions, even speech. Each round on its turn, the subject may attempt a new saving throw to end the effect. This is a full-round action that does not provoke attacks of opportunity. A winged creature who is paralyzed cannot flap its wings and falls. A swimmer can't swim and may drown.</p></h4></div>"},
new Spell(){ Name = "Hold Monster", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hold Monster</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 4, sorcerer/wizard 5, summoner 4, witch 5, inquisitor 4, summoner 4, witch 5, inquisitor 4, occultist 4, psychic 5, mesmerist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (one hard metal bar or rod, which can be as small as a three-penny nail)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 round/level ; see text (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>hold person,</i> except that it affects any living creature that fails its Will save.</p></h4></div>"},
new Spell(){ Name = "Hold Monster, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hold Monster, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 9, witch 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (one hard metal bar or rod, which can be as small as a three-penny nail)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one or more creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level ; see text (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>hold person,</i> except that it affects multiple creatures and holds any living creature that fails its Will save.</p></h4></div>"},
new Spell(){ Name = "Hold Person, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hold Person, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 7, witch 7, psychic 7, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a small, straight piece of iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one or more humanoid creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level ; see text (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>hold person,</i> except as noted above.</p></h4></div>"},
new Spell(){ Name = "Hold Portal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hold Portal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 1, occultist 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one portal, up to 20 sq. ft./level</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell magically holds shut a door, gate, window, or shutter of wood, metal, or stone. The magic affects the portal just as if it were securely closed and normally locked. A <i>knock</i> spell or a successful <i>dispel magic</i> spell can negate a <i>hold portal</i> spell.</p><p>Add 5 to the normal DC for forcing open a portal affected by this spell.</p></h4><h5><b>Mythic: </b>The target portal can be as large as 30 square feet per caster level. A creature attempting to force open the portal must first succeed at a Will save against the spell's DC. This is a mind-affecting effect. Failure means the creature can't open the door while the spell is in effect.</h5></div>"},
new Spell(){ Name = "Holy Aura", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Holy Aura</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [good]; <b>Level </b>cleric/oracle 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a tiny reliquary worth 500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>20 ft.</h5><h5><b>Targets </b>one creature/level in a 20-ft.-radius burst centered on you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A brilliant divine radiance surrounds the subjects, protecting them from attacks, granting them resistance to spells cast by evil creatures, and causing evil creatures to become blinded when they strike the subjects. This abjuration has four effects.</p><p>First, each warded creature gains a +4 deflection bonus to AC and a +4 resistance bonus on saves. Unlike <i>protection from evil</i>, this benefit applies against all attacks, not just against attacks by evil creatures.</p><p>Second, each warded creature gains spell resistance 25 against evil spells and spells cast by evil creatures.</p><p>Third, the abjuration protects the recipient from possession and mental influence, just as <i>protection from evil</i> does.</p><p>Finally, if an evil creature succeeds on a melee attack against a creature warded by a <i>holy aura</i>, the offending attacker is blinded (Fortitude save negates, as <i>blindness/deafness,</i> but against <i>holy aura</i>'s save DC).</p></h4></div>"},
new Spell(){ Name = "Holy Smite", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Holy Smite</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [good]; <b>Level </b>cleric/oracle 4, inquisitor 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>20-ft.-radius burst</h5><h5><b>Duration </b>instantaneous (1 round); see text</h5><h5><b>Saving Throw </b>Will partial; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You draw down holy power to smite your enemies. Only evil and neutral creatures are harmed by the spell; good creatures are unaffected.</p><p>The spell deals 1d8 points of damage per two caster levels (maximum 5d8) to each evil creature in the area (or 1d6 points of damage per caster level, maximum 10d6, to an evil outsider) and causes it to become blinded for 1 round. A successful Will saving throw reduces damage to half and negates the blinded effect.</p><p>The spell deals only half damage to creatures who are neither good nor evil, and they are not blinded. Such a creature can reduce that damage by half (down to one-quarter of the roll) with a successful Will save.</p></h4><h5><b>Mythic: </b>The damage dealt to evil outsiders increases to 1d10 points of damage per caster level (maximum 10d10) and the damage dealt to other evil creatures increases to 1d12 points of damage per 2 caster levels (maximum 5d12). The duration that creatures in the area are blinded increases to 2 rounds. On a successful save, an evil creature is blinded for 1 round.</h5></div>"},
new Spell(){ Name = "Holy Sword", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Holy Sword</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [good]; <b>Level </b>paladin 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>melee weapon touched</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell allows you to channel holy power into your sword, or any other melee weapon you choose. The weapon acts as a <i>+5 holy weapon</i> (+5 enhancement bonus on attack and damage rolls, extra 2d6 damage against evil opponents). It also emits a <i><i>magic circle</i> against evil</i> effect (as the spell). If the <i>magic circle</i> ends, the sword creates a new one on your turn as a free action. The spell is automatically canceled 1 round after the weapon leaves your hand.</p><p>You cannot have more than one <i>holy sword</i> at a time.</p><p>If this spell is cast on a magic weapon, the powers of the spell supercede any that the weapon normally has, rendering the normal enhancement bonus and powers of the weapon inoperative for the duration of the spell. This spell is not cumulative with <i>bless weapon</i> or any other spell that might modify the weapon in any way. This spell does not work on artifacts. A masterwork weapon's bonus to attack does not stack with an enhancement bonus to attack.</p></h4></div>"},
new Spell(){ Name = "Holy Word", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Holy Word</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [good, sonic]; <b>Level </b>cleric/oracle 7, inquisitor 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>40 ft.</h5><h5><b>Area </b>nongood creatures in a 40-ft.-radius spread centered on you</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any nongood creature within the area of a <i>holy word</i> spell suffers the following ill effects.</p>   <table border ='1'><tr><th><table><tr><th>HD</th><th>Effect</th></tr><tr><td>Equal to caster level</td><td>Deafened</td></tr><tr><td>Up to caster level -1</td><td>Blinded, deafened</td></tr><tr><td>Up to caster level -5</td><td>Paralyzed, blinded, deafened</td></tr><tr><td>Up to caster level -10</td><td>Killed, paralyzed, blinded, deafened</td></tr></table></td></tr></table>   The effects are cumulative and concurrent. A successful Will save reduces or eliminates these effects. Creatures affected by multiple effects make only one save and apply the result to all the effects.</p><p><i>Deafened</i>: The creature is deafened for 1d4 rounds. Save negates.</p><p><i>Blinded</i>: The creature is blinded for 2d4 rounds. Save reduces the blinded effect to 1d4 rounds.</p><p><i>Paralyzed</i>: The creature is paralyzed and helpless for 1d10 minutes. Save reduces the paralyzed effect to 1 round.</p><p><i>Killed</i>: Living creatures die. Undead creatures are destroyed. Save negates. If the save is successful, the creature instead takes 3d6 points of damage + 1 point per caster level (maximum +25).</p><p>Furthermore, if you are on your home plane when you cast this spell, nongood extraplanar creatures within the area are instantly banished back to their home planes. Creatures so banished cannot return for at least 24 hours. This effect takes place regardless of whether the creatures hear the <i>holy word</i> or not. The banishment effect allows a Will save (at a -4 penalty) to negate.</p><p>Creatures whose HD exceed your caster level are unaffected by <i>holy word</i>.</p></h4><h5><b>Mythic: </b>When determining the spell's effect on non-mythic creatures, add your tier to your caster level. Nongood creatures that fail their saves also take a -4 penalty on attack rolls and saving throws, and their spell resistance decreases by 5 for as long as the spell's other effects last.</h5></div>"},
new Spell(){ Name = "Horrid Wilting", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Horrid Wilting</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 8, witch 8, shaman 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a bit of sponge)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Targets </b>living creatures, no two of which can be more than 60 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell evaporates moisture from the body of each subject living creature, causing flesh to wither and crack and crumble to dust. This deals 1d6 points of damage per caster level (maximum 20d6). This spell is especially devastating to water elementals and plant creatures, which instead take 1d8 points of damage per caster level (maximum 20d8).</p></h4></div>"},
new Spell(){ Name = "Hypnotic Pattern", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hypnotic Pattern</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (pattern) [mind-affecting]; <b>Level </b>bard 2, sorcerer/wizard 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V (bard only), S, M (a stick of incense or a crystal rod); see text</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>colorful lights in a 10-ft.-radius spread</h5><h5><b>Duration </b>Concentration + 2 rounds</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A twisting pattern of subtle, shifting colors weaves through the air, fascinating creatures within it. Roll 2d4 and add your caster level (maximum 10) to determine the total number of HD of creatures affected. Creatures with the fewest HD are affected first; and, among creatures with equal HD, those who are closest to the spell's point of origin are affected first. HD that are not sufficient to affect a creature are wasted. Affected creatures become fascinated by the pattern of colors. Sightless creatures are not affected.</p><p>A wizard or sorcerer need not utter a sound to cast this spell, but a bard must perform as a verbal component.</p></h4></div>"},
new Spell(){ Name = "Hypnotism", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Hypnotism</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 1, sorcerer/wizard 1, witch 1, occultist 1, psychic 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>several living creatures, no two of which may be more than 30 ft. apart</h5><h5><b>Duration </b>2d4 rounds (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Your gestures and droning incantation fascinate nearby creatures, causing them to stop and stare blankly at you.</p><p>In addition, you can use their rapt attention to make your suggestions and requests seem more plausible. Roll 2d4 to see how many total HD of creatures you affect. Creatures with fewer HD are affected before creatures with more HD. Only creatures that can see or hear you are affected, but they do not need to understand you to be fascinated.</p><p>If you use this spell in combat, each target gains a +2 bonus on its saving throw. If the spell affects only a single creature not in combat at the time, the saving throw has a penalty of -2.</p><p>While the subject is fascinated by this spell, it reacts as though it were two steps more friendly in attitude. This allows you to make a single request of the affected creature (provided you can communicate with it). The request must be brief and reasonable. Even after the spell ends, the creature retains its new attitude toward you, but only with respect to that particular request.</p><p>A creature that fails its saving throw does not remember that you enspelled it.</p></h4></div>"},
new Spell(){ Name = "Ice Storm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ice Storm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [cold]; <b>Level </b>druid 4, sorcerer/wizard 4, witch 4, magus 4, bloodrager 4, shaman 4, occultist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (dust and water)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>cylinder (20-ft. radius, 40 ft. high)</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Great magical hailstones pound down upon casting this spell, dealing 3d6 points of bludgeoning damage and 2d6 points of cold damage to every creature in the area. This damage only occurs once, when the spell is cast. For the remaining duration of the spell, heavy snow and sleet rains down in the area. Creatures inside this area take a -4 penalty on Perception skill checks and the entire area is treated as difficult terrain. At the end of the duration, the snow and hail disappear, leaving no aftereffects (other than the damage dealt).</p></h4><h5><b>Mythic: </b>The bludgeoning damage increases to 4d8 points of damage and the cold damage increases to 3d6 points of damage. The ground in the area is covered in ice and hailstones, acting as though a grease spell were cast on it. Dealing 5 or more points of fire damage to a square melts the ice and hail, negating the grease effect.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, one creature in the area is paralyzed (as if by hold person) and gains vulnerability to fire as long as it's paralyzed.</h5></div>"},
new Spell(){ Name = "Identify", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Identify</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 1, sorcerer/wizard 1, alchemist 1, summoner 1, witch 1, alchemist 1, summoner 1, witch 1, occultist 1, psychic 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (wine stirred with an owl's feather)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped emanation</h5><h5><b>Duration </b>3 rounds/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>detect magic</i>, except that it gives you a +10 enhancement bonus on Spellcraft checks made to identify the properties and command words of magic items in your possession. This spell does not allow you to identify artifacts.</p></h4></div>"},
new Spell(){ Name = "Illusory Script", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Illusory Script</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (phantasm) [mind-affecting]; <b>Level </b>bard 3, sorcerer/wizard 3, occultist 3, psychic 3, mesmerist 3, witch 3, alchemist 2, witch 2, inquisitor 2, witch 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute per page</h5><h5><b>Components </b>V, S, M (lead-based ink worth 50 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched object weighing no more than 10 lbs.</h5><h5><b>Duration </b>one day/level (D)</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You write instructions or other information on parchment, paper, or any suitable writing material. The <i>illusory script</i> appears to be some form of foreign or magical writing. Only the person (or people) designated by you at the time of the casting can read the writing; it's unintelligible to any other character.</p><p>Any unauthorized creature attempting to read the script triggers a potent illusory effect and must make a saving throw. A successful saving throw means the creature can look away with only a mild sense of disorientation. Failure means the creature is subject to a <i>suggestion</i> implanted in the script by you at the time the <i>illusory script</i> spell was cast. The <i>suggestion</i> lasts only 30 minutes. Typical <i>suggestion</i>s include ""Close the book and leave,"" ""Forget the existence of this note,"" and so forth. If successfully dispelled by <i>dispel magic,</i> the <i>illusory script</i> and its secret message disappear.</p><p>The hidden message can be read by a combination of the <i>true seeing</i> spell with the <i>read magic</i> or <i>comprehend languages</i> spell.</p></h4></div>"},
new Spell(){ Name = "Illusory Wall", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Illusory Wall</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>sorcerer/wizard 4, occultist 4, psychic 4, mesmerist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>image 1 ft. by 10 ft. by 10 ft.</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates the illusion of a wall, floor, ceiling, or similar surface. It appears absolutely real when viewed, but physical objects can pass through it without difficulty. When the spell is used to hide pits, traps, or normal doors, any detection abilities that do not require sight work normally. Touch or a probing search reveals the true nature of the surface, though such measures do not cause the illusion to disappear. Although the caster can see through his <i>illusory wall</i>, other creatures cannot, even if they succeed at their will save (but they do learn that it is not real).</p></h4></div>"},
new Spell(){ Name = "Imbue with Spell Ability", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Imbue with Spell Ability</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 4, shaman 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched; see text</h5><h5><b>Duration </b>permanent until discharged (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You transfer some of your currently prepared spells, and the ability to cast them, to another creature. Only a creature with an Intelligence score of at least 5 and a Wisdom score of at least 9 can receive this boon. Only cleric spells from the schools of abjuration, divination, and conjuration (healing) can be transferred. The number and level of spells that the subject can be granted depends on its Hit Dice; even multiple castings of <i>imbue with spell ability</i> can't exceed this limit.</p><p> <table><tr><th>HD of recipient</th><th>Spells imbued</th></tr><tr><td>2 or lower</td><td>One 1st-level spell</td></tr><tr><td>3-4</td><td>One or two 1st-level spells</td></tr><tr><td>5 or higher</td><td>One or two 1st-level spells and one 2nd-level spell</td></tr></table>  The transferred spell's variable characteristics (range, duration, area, and the like) function according to your level, not the level of the recipient.</p><p>Once you cast <i>imbue with spell ability</i>, you cannot prepare a new 4th-level spell to replace it until the recipient uses the imbued spells or is slain, or until you dismiss the <i>imbue with spell ability</i> spell. In the meantime, you remain responsible to your deity or your principles for the use to which the spell is put. If the number of 4th-level spells you can cast decreases, and that number drops below your current number of active <i>imbue with spell ability</i> spells, the more recently cast imbued spells are dispelled.</p><p>To cast a spell with a verbal component, the subject must be able to speak. To cast a spell with a somatic component, it must be able to move freely. To cast a spell with a material component or focus, it must have the materials or focus.</p></h4></div>"},
new Spell(){ Name = "Implosion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Implosion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one corporeal creature/round</h5><h5><b>Duration </b>concentration (up to 1 round per 2 levels)</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes a destructive resonance in a corporeal creature's body. Each round you concentrate (including the first), you can cause one creature to collapse in on itself, inflicting 10 points of damage per caster level. If you break concentration, the spell immediately ends, though any implosions that have already happened remain in effect. You can target a particular creature only once with each casting of the spell. Implosion has no effect on creatures in gaseous form or on incorporeal creatures.</p></h4></div>"},
new Spell(){ Name = "Imprisonment", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Imprisonment</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you cast <i>imprisonment</i> and touch a creature, it is entombed in a state of suspended animation (see the <i>temporal stasis</i> spell) in a small sphere far beneath the surface of the ground. The subject remains there unless a <i>freedom</i> spell is cast at the locale where the <i>imprisonment</i> took place. Magical search by a <i>crystal ball, a locate object</i> spell, or some other similar divination does not reveal the fact that a creature is imprisoned, but <i>discern location</i> does. A <i>wish</i> or <i>miracle</i> spell will not free the recipient, but will reveal where it is entombed. If you know the target's name and some facts about its life, the target takes a -4 penalty on its save.</p></h4></div>"},
new Spell(){ Name = "Incendiary Cloud", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Incendiary Cloud</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [fire]; <b>Level </b>sorcerer/wizard 8, summoner 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>cloud spreads in 20-ft. radius, 20 ft. high</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Reflex half; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An <i>incendiary cloud</i> spell creates a cloud of roiling smoke shot through with white-hot embers. The smoke obscures all sight as a <i>fog cloud</i> does. In addition, the white-hot embers within the cloud deal 6d6 points of fire damage to everything within the cloud on your turn each round. All targets can make Reflex saves each round to take half damage.</p><p>As with a <i>cloudkill</i> spell, the smoke moves away from you at 10 feet per round. Figure out the smoke's new spread each round based on its new point of origin, which is 10 feet farther away from where you were when you cast the spell. By concentrating, you can make the cloud move as much as 60 feet each round. Any portion of the cloud that would extend beyond your maximum range dissipates harmlessly, reducing the remainder's spread thereafter.</p><p>As with <i>fog cloud</i>, wind disperses the smoke, and the spell can't be cast underwater.</p></h4></div>"},
new Spell(){ Name = "Inflict Light Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Light Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>antipaladin 1, cleric/oracle 1, inquisitor 1, witch 1, shaman 1, occultist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When laying your hand upon a creature, you channel negative energy that deals 1d8 points of damage + 1 point per caster level (maximum +5).</p><p>Since undead are powered by negative energy, this spell cures such a creature of a like amount of damage, rather than harming it.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 2d8 points of damage + 2 points per caster level (maximum +10). A living target is sickened for 1 round per caster level (maximum 5 rounds) if it fails its saving throw.</h5></div>"},
new Spell(){ Name = "Inflict Critical Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Critical Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 4, witch 5, inquisitor 4, shaman 4, occultist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>inflict light wounds</i>, except that you deal 4d8 points of damage + 1 point per caster level (maximum +20).</p></h4><h5><b>Mythic: </b>The damage dealt increases to 8d8 points of damage + 2 points per caster level (maximum +40). A living target is sickened for 1 round per caster level (maximum 20 rounds) if it fails its saving throw.</h5></div>"},
new Spell(){ Name = "Inflict Critical Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Critical Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 8, witch 9, shaman 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>mass inflict light wounds</i>, except that it deals 4d8 points of damage + 1 point per caster level (maximum +40).</p></h4></div>"},
new Spell(){ Name = "Inflict Light Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Light Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 5, witch 6, inquisitor 5, shaman 5, occultist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Negative energy spreads out in all directions from the point of origin, dealing 1d8 points of damage + 1 point per caster level (maximum +25) to nearby living enemies.</p><p>Like other <i>inflict</i> spells, <i>mass inflict light wounds</i> cures undead in its area rather than damaging them. A cleric capable of spontaneously casting <i>inflict</i> spells can also spontaneously cast mass <i>inflict</i> spells.</p></h4></div>"},
new Spell(){ Name = "Inflict Moderate Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Moderate Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>antipaladin 3, cleric/oracle 2, inquisitor 2, witch 2, shaman 2, occultist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The damage dealt increases to 4d8 points of damage + 2 points per caster level (maximum +20). A living target is sickened for 1 round per caster level (maximum 10 rounds) if it fails its saving throw.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 4d8 points of damage + 2 points per caster level (maximum +20). A living target is sickened for 1 round per caster level (maximum 10 rounds) if it fails its saving throw.</h5></div>"},
new Spell(){ Name = "Inflict Moderate Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Moderate Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 6, witch 7, inquisitor 6, shaman 6, occultist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>mass inflict light wounds</i>, except that it deals 2d8 points of damage + 1 point per caster level (maximum +30).</p></h4></div>"},
new Spell(){ Name = "Inflict Serious Wounds", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Serious Wounds</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 3, witch 4, inquisitor 3, antipaladin 4, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The damage dealt increases to 6d8 points of damage + 2 points per caster level (maximum +30). A living target is sickened for 1 round per caster level (maximum 15 rounds) if it fails its saving throw.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 6d8 points of damage + 2 points per caster level (maximum +30). A living target is sickened for 1 round per caster level (maximum 15 rounds) if it fails its saving throw.</h5></div>"},
new Spell(){ Name = "Inflict Serious Wounds, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Inflict Serious Wounds, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 7, witch 8, shaman 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>mass inflict light wounds</i>, except that it deals 3d8 points of damage + 1 point per caster level (maximum +35).</p></h4></div>"},
new Spell(){ Name = "Insanity", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Insanity</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 7, witch 7, psychic 7, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The affected creature suffers from a continuous <i>confusion</i> effect, as the spell.</p><p><i>Remove curse</i> does not remove <i>insanity. Greater restoration, heal, limited <i>wish</i>, miracle,</i> or <i>wish</i> can restore the creature.</p></h4></div>"},
new Spell(){ Name = "Interposing Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Interposing Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 5, magus 5, occultist 5, psychic 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a soft glove)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>10-ft. hand</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Interposing hand</i> creates a Large magic hand that appears between you and one opponent. This floating, disembodied hand then moves to remain between the two of you, regardless of where you move or how the opponent tries to get around it, providing cover (+4 AC) for you against that opponent. Nothing can fool the hand-it sticks with the selected opponent in spite of <i>darkness,</i> invisibility, polymorphing, or any other attempt at hiding or disguise. The hand does not pursue an opponent, however.</p><p>An <i>interposing hand</i> is 10 feet long and about that wide with its fingers outstretched. It has as many hit points as you do when you're undamaged, and is AC 20 (-1 size, +11 natural). It takes damage as a normal creature, but most magical effects that don't cause damage do not affect it.</p><p>The hand never provokes attacks of opportunity from opponents.</p><p>It cannot push through a <i>wall of force or</i> enter an <i>antimagic field</i>, but it suffers the full effect of a <i>prismatic wall</i> or <i>prismatic sphere.</i> The hand makes saving throws as its caster.</p><p><i>Disintegrate</i> or a successful <i>dispel magic</i> destroys it.</p><p>Any creature weighing 2,000 pounds or less that tries to push past the hand is slowed to half its normal speed. The hand cannot reduce the speed of a creature weighing more than 2,000 pounds, but it still affects the creature's attacks.</p><p>Directing the spell to a new target is a move action.</p></h4></div>"},
new Spell(){ Name = "Invisibility", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Invisibility</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 2, sorcerer/wizard 2, alchemist 2, summoner 2, inquisitor 2, antipaladin 2, magus 2, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (an eyelash encased in gum arabic)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal or touch</h5><h5><b>Targets </b>you or a creature or object weighing no more than 100 lbs./level</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless) or Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless) or yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The creature or object touched becomes invisible. If the recipient is a creature carrying gear, that vanishes, too. If you cast the spell on someone else, neither you nor your allies can see the subject, unless you can normally see invisible things or you employ magic to do so.</p><p>Items dropped or put down by an invisible creature become visible; items picked up disappear if tucked into the clothing or pouches worn by the creature. Light, however, never becomes invisible, although a source of light can become so (thus, the effect is that of a light with no visible source). Any part of an item that the subject carries but that extends more than 10 feet from it becomes visible.</p><p>Of course, the subject is not magically silenced, and certain other conditions can render the recipient detectable (such as swimming in water or stepping in a puddle). If a check is required, a stationary invisible creature has a +40 bonus on its Stealth checks. This bonus is reduced to +20 if the creature is moving. The spell ends if the subject attacks any creature. For purposes of this spell, an attack includes any spell targeting a foe or whose area or effect includes a foe. Exactly who is a foe depends on the invisible character's perceptions. Actions directed at unattended objects do not break the spell. Causing harm indirectly is not an attack. Thus, an invisible being can open doors, talk, eat, climb stairs, summon monsters and have them attack, cut the ropes holding a rope bridge while enemies are on the bridge, remotely trigger traps, open a portcullis to release attack dogs, and so forth. If the subject attacks directly, however, it immediately becomes visible along with all its gear.</p><p>Spells such as <i>bless</i> that specifically affect allies but not foes are not attacks for this purpose, even when they include foes in their area.</p><p><i>Invisibility</i> can be made permanent (on objects only) with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>The invisible target can't be detected with detect magic or other spells that detect magic auras. The invisibility can't be penetrated, revealed, or dispelled by spells of 2nd level or lower (such as see invisibility or glitterdust), though true seeing and dust of appearance can reveal the invisible target's presence.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the invisible target is undetectable by blindsense, blindsight, scent, and tremorsense.</h5></div>"},
new Spell(){ Name = "Invisibility, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Invisibility, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 4, sorcerer/wizard 4, alchemist 4, summoner 3, inquisitor 4, antipaladin 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal or touch</h5><h5><b>Targets </b>you or creature touched</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>invisibility,</i> except that it doesn't end if the subject attacks.</p></h4></div>"},
new Spell(){ Name = "Invisibility, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Invisibility, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>sorcerer/wizard 7, summoner 5, psychic 7, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Targets </b>any number of creatures, no two of which can be more than 180 ft. apart</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>invisibility,</i> except that the effect moves with the group and is broken when anyone in the group attacks.</p><p>Individuals in the group cannot see each other. The spell is broken for any individual who moves more than 180 feet from the nearest member of the group. If only two individuals are affected, the one moving away from the other one loses its invisibility. If both are moving away from each other, they both become visible when the distance between them exceeds 180 feet.</p></h4></div>"},
new Spell(){ Name = "Invisibility Purge", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Invisibility Purge</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 3, inquisitor 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You surround yourself with a sphere of power with a radius of 5 feet per caster level that negates all forms of invisibility.</p><p>Anything invisible becomes visible while in the area.</p></h4></div>"},
new Spell(){ Name = "Invisibility Sphere", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Invisibility Sphere</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 3, sorcerer/wizard 3, occultist 3, psychic 3, mesmerist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Area </b>10-ft.-radius emanation around the creature</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>invisibility</i>, except that this spell confers <i>invisibility</i> upon all creatures within 10 feet of the recipient at the time the spell is cast. The center of the effect is mobile with the recipient.</p><p>Those affected by this spell can see each other and themselves as if unaffected by the spell. Any affected creature moving out of the area becomes visible, but creatures moving into the area after the spell is cast do not become invisible.</p><p>Affected creatures (other than the recipient) who attack negate the <i>invisibility</i> only for themselves. If the spell recipient attacks, the <i>invisibility</i> sphere ends.</p></h4></div>"},
new Spell(){ Name = "Iron Body", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Iron Body</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a piece of iron from an iron golem, a hero's armor, or a war machine)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell transforms your body into living iron, which grants you several powerful resistances and abilities. You gain damage reduction 15/adamantine. You are immune to blindness, critical hits, ability score damage, deafness, disease, drowning, electricity, poison, stunning, and all spells or attacks that affect your physiology or respiration, because you have no physiology or respiration while this spell is in effect. You take only half damage from acid and fire.</p><p>However, you also become vulnerable to all special attacks that affect iron golems (see the <i>Pathfinder RPG Bestiary</i>).</p><p>You gain a +6 enhancement bonus to your Strength score, but you take a -6 penalty to Dexterity as well (to a minimum Dexterity score of 1), and your speed is reduced to half normal. You have an arcane spell failure chance of 35% and a -6 armor check penalty, just as if you were clad in full plate armor. You cannot drink (and thus can't use potions) or play wind instruments.</p><p>Your unarmed attack deals damage equal to a club sized for you (1d4 for Small characters or 1d6 for Medium characters), and you are considered armed when making unarmed attacks.</p><p>Your weight increases by a factor of 10, causing you to sink in water like a stone. However, you could survive the lack of air at the bottom of the ocean-at least until the spell duration expires.</p></h4></div>"},
new Spell(){ Name = "Ironwood", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ironwood</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 6, occultist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute/lb. created</h5><h5><b>Components </b>V, S, F (wood to be transformed)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>an ironwood object weighing up to 5 lbs./level</h5><h5><b>Duration </b>1 day/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Ironwood</i> is a magical substance created by druids from normal wood. While remaining natural wood in almost every way, <i>ironwood</i> is as strong, heavy, and resistant to fire as steel. Spells that affect metal or iron do not function on <i>ironwood</i>. Spells that affect wood do affect <i>ironwood</i>, although <i>ironwood</i> does not burn. Using this spell with <i>wood shape</i> or a wood-related Craft check, you can fashion wooden items that function as steel items. Thus, wooden plate armor and wooden swords can be created that are as durable as their normal steel counterparts. These items are freely usable by druids.</p><p>Further, if you make only half as much <i>ironwood</i> as the spell would normally allow, any weapon, shield, or suit of armor so created is treated as a magic item with a +1 enhancement bonus.</p></h4></div>"},
new Spell(){ Name = "Irresistible Dance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Irresistible Dance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 6, sorcerer/wizard 8, witch 8, shaman 8, psychic 8, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>1d4+1 rounds</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject feels an undeniable urge to dance and begins doing so, complete with foot shuffling and tapping. The spell effect makes it impossible for the subject to do anything other than caper and prance in place. The effect imposes a -4 penalty to Armor Class and a -10 penalty on Reflex saves, and it negates any AC bonus granted by a shield the target holds. The dancing subject provokes attacks of opportunity each round on its turn. A successful Will save reduces the duration of this effect to 1 round.</p></h4></div>"},
new Spell(){ Name = "Jump", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Jump</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1, ranger 1, sorcerer/wizard 1, alchemist 1, summoner 1, alchemist 1, summoner 1, magus 1, bloodrager 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a grasshopper's hind leg)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject gets a +10 enhancement bonus on Acrobatics checks made to attempt high jumps or long jumps. The enhancement bonus increases to +20 at caster level 5th, and to +30 (the maximum) at caster level 9th.</p></h4></div>"},
new Spell(){ Name = "Keen Edge", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Keen Edge</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 3, inquisitor 3, magus 3, bloodrager 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one weapon or 50 projectiles, all of which must be together at the time of casting</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell makes a weapon magically <i>keen</i>, improving its ability to deal telling blows. This transmutation doubles the threat range of the weapon. A threat range of 20 becomes 19-20, a threat range of 19-20 becomes 17-20, and a threat range of 18-20 becomes 15-20. The spell can be cast only on piercing or slashing weapons. If cast on arrows or crossbow bolts, the <i><i>keen</i> edge</i> on a particular projectile ends after one use, whether or not the missile strikes its intended target.</p><p>Treat shuriken as arrows, rather than as thrown weapons, for the purpose of this spell.</p><p>Multiple effects that increase a weapon's threat range (such as the <i>keen</i> special weapon property and the Improved Critical feat) don't stack. You can't cast this spell on a natural weapon, such as a claw.</p></h4></div>"},
new Spell(){ Name = "Knock", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Knock</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 2, inquisitor 2, occultist 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one door, box, or chest with an area of up to 10 sq. ft./level</h5><h5><b>Duration </b>instantaneous; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Knock</i> opens stuck, barred, or locked doors, as well as those subject to <i>hold portal or <i>arcane lock</i></i>. When you complete the casting of this spell, make a caster level check against the DC of the lock with a +10 bonus. If successful, <i>knock</i> opens up to two means of closure. This spell opens secret doors, as well as locked or trick-opening boxes or chests. It also loosens welds, shackles, or chains (provided they serve to hold something shut). If used to open an <i><i>arcane lock</i>ed</i> door, the spell does not remove the <i>arcane lock</i> but simply suspends its functioning for 10 minutes.</p><p>In all other cases, the door does not relock itself or become stuck again on its own. <i>Knock</i> does not raise barred gates or similar impediments (such as a portcullis), nor does it affect ropes, vines, and the like. The effect is limited by the area. Each casting can undo as many as two means of preventing access.</p></h4></div>"},
new Spell(){ Name = "Know Direction", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Know Direction</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 0, druid 0, shaman 0, occultist 0, psychic 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>instantaneous</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you cast this spell, you instantly know the direction of north from your current position. The spell is effective in any environment in which ""north"" exists, but it may not work in extraplanar settings. Your knowledge of north is correct at the moment of casting, but you can get lost again within moments if you don't find some external reference point to help you keep track of direction.</p></h4></div>"},
new Spell(){ Name = "Legend Lore", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Legend Lore</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 4, sorcerer/wizard 6, witch 6, inquisitor 6, occultist 6, psychic 6, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>see text</h5><h5><b>Components </b>V, S, M (incense worth 250 gp), F (four pieces of ivory worth 50 gp each)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Legend lore</i> brings to your mind legends about an important person, place, or thing. If the person or thing is at hand, or if you are in the place in question, the casting time is only 1d4 x 10 minutes. If you have only detailed information on the person, place, or thing, the casting time is 1d10 days, and the resulting lore is less complete and specific (though it often provides enough information to help you find the person, place, or thing, thus allowing a better <i>legend lore</i> result next time). If you know only rumors, the casting time is 2d6 weeks, and the resulting lore is vague and incomplete (though it often directs you to more detailed information, thus allowing a better <i>legend lore</i> result next time).</p><p>During the casting, you cannot engage in other than routine activities: eating, sleeping, and so forth. When completed, the divination brings legends (if any) about the person, place, or things to your mind. These may be legends that are still current, legends that have been forgotten, or even information that has never been generally known. If the person, place, or thing is not of legendary importance, you gain no information. As a rule of thumb, characters who are 11th level and higher are ""legendary,"" as are the sorts of creatures they contend with, the major magic items they wield, and the places where they perform their key deeds.</p></h4></div>"},
new Spell(){ Name = "Levitate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Levitate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 2, alchemist 2, summoner 2, witch 2, magus 2, shaman 2, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a leather loop or golden wire bent into a cup shape)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal or close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>you or one willing creature or one object (total weight up to 100 lbs./level)</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Levitate</i> allows you to move yourself, another creature, or an object up and down as you wish. A creature must be willing to be levitated, and an object must be unattended or possessed by a willing creature. You can mentally direct the recipient to move up or down as much as 20 feet each round; doing so is a move action.</p><p>You cannot move the recipient horizontally, but the recipient could clamber along the face of a cliff, for example, or push against a ceiling to move laterally (generally at half its base land speed).</p><p>A levitating creature that attacks with a melee or ranged weapon finds itself increasingly unstable; the first attack has a -1 penalty on attack rolls, the second -2, and so on, to a maximum penalty of -5. A full round spent stabilizing allows the creature to begin again at -1.</p></h4></div>"},
new Spell(){ Name = "Light", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Light</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [light]; <b>Level </b>bard 0, cleric/oracle 0, druid 0, inquisitor 0, magus 0, sorcerer/wizard 0, summoner 0, witch 0, shaman 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, M/DF (a firefly)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes a touched object to glow like a torch, shedding normal light in a 20-foot radius from the point touched, and increasing the light level for an additional 20 feet by one step, up to normal light (darkness becomes dim light, and dim light becomes normal light). In an area of normal or bright light, this spell has no effect. The effect is immobile, but it can be cast on a movable object.</p><p>You can only have one light spell active at any one time. If you cast this spell while another casting is still in effect, the previous casting is dispelled. If you make this spell permanent (through permanency or a similar effect), it does not count against this limit. Light can be used to counter or dispel any darkness spell of equal or lower spell level.</p></h4></div>"},
new Spell(){ Name = "Lightning Bolt", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Lightning Bolt</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [electricity]; <b>Level </b>sorcerer/wizard 3, witch 3, magus 3, bloodrager 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (fur and a glass rod)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>120 ft.</h5><h5><b>Area </b>120-ft. line</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Reflex half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You release a powerful stroke of electrical energy that deals 1d6 points of electricity damage per caster level (maximum 10d6) to each creature within its area. The bolt begins at your fingertips.</p><p>The <i>lightning bolt</i> sets fire to combustibles and damages objects in its path. It can melt metals with a low melting point, such as lead, gold, copper, silver, or bronze. If the damage caused to an interposing barrier shatters or breaks through it, the bolt may continue beyond the barrier if the spell's range permits; otherwise, it stops at the barrier just as any other spell effect does.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d8 points of electricity damage per caster level (maximum 10d8). Any creature that fails its save is staggered for 1 round.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, you can bend the line of the spell once up to 90 degrees. Any creature that fails its save is stunned for 1 round.</h5></div>"},
new Spell(){ Name = "Limited Wish", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Limited Wish</p><div style=""clear: both;""></div></div><div><h5><b>School </b>universal; <b>Level </b>sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (diamond worth 1,500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Area </b>see text</h5><h5><b>Effect </b>see text</h5><h5><b>Targets </b>see text</h5>   <h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none, see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>limited wish</i> lets you create nearly any type of effect. For example, a <i>limited wish</i> can do any of the following things.</p><p><ul><li> Duplicate any sorcerer/wizard spell of 6th level or lower, provided the spell does not belong to one of your opposition schools.</p><p><li> Duplicate any non-sorcerer/wizard spell of 5th level or lower, provided the spell does not belong to one of your opposition schools.</p><p><li> Duplicate any sorcerer/wizard spell of 5th level or lower, even if it belongs to one of your opposition schools.</p><p><li> Duplicate any non-sorcerer/wizard spell of 4th level or lower, even if it belongs to one of your opposition schools.</p><p><li> Undo the harmful effects of many spells, such as <i>geas/quest</i> or <i>insanity</i>.</p><p><li> Produce any other effect whose power level is in line with the above effects, such as a single creature automatically hitting on its next attack or taking a -7 penalty on its next saving throw.</p><p>A duplicated spell allows saving throws and spell resistance as normal, but the save DC is for a 7th-level spell. When a <i>limited wish</i> spell duplicates a spell with a material component that costs more than 1,000 gp, you must provide that component (in addition to the 1,500 gp diamond component for this spell).</ul></p></h4><h5><b>Mythic: </b>When using mythic limited wish to duplicate another spell, you can duplicate a mythic spell you know (if you're a spontaneous caster) or have prepared (if you're a caster who prepares spells). If you don't know or haven't prepared the mythic spell, you can expend a second use of mythic power duplicate the mythic version of the desired spell.</h5><h5><b>Augmented</b>: If you expend two uses of mythic power, you can cast a silent, stilled mythic limited wish, even if you're helpless or couldn't otherwise take actions (but not unconscious).</h5></div>"},
new Spell(){ Name = "Liveoak", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Liveoak</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 6, shaman 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>tree touched</h5><h5><b>Duration </b>1 day/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell turns an oak tree into a protector or guardian. The spell can only be cast on a single tree at a time; while <i>liveoak</i> is in effect, you can't cast it again on another tree. <i>Liveoak</i> must be cast on a healthy, Huge oak. A triggering phrase of up to one word per caster level is placed on the targeted oak. The <i>liveoak</i> spell triggers the tree into animating as a treant.</p><p>If <i>liveoak</i> is dispelled, the tree takes root immediately wherever it happens to be. If released by you, the tree tries to return to its original location before taking root.</p></h4></div>"},
new Spell(){ Name = "Locate Object", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Locate Object</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 2, cleric/oracle 3, sorcerer/wizard 2, witch 3, inquisitor 3, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a forked twig)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>circle, centered on you, with a radius of 400 ft. + 40 ft./level</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You sense the direction of a well-known or clearly visualized object.</p><p>You can search for general items, in which case you locate the nearest of its kind if more than one is within range. Attempting to find a certain item requires a specific and accurate mental image; if the image is not close enough to the actual object, the spell fails.</p><p>You cannot specify a unique item unless you have observed that particular item firsthand (not through divination).</p><p>The spell is blocked by even a thin sheet of lead. Creatures cannot be found by this spell. <i>Polymorph any object</i> and <i>nondetection</i> fool it.</p></h4></div>"},
new Spell(){ Name = "Locate Creature", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Locate Creature</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 4, sorcerer/wizard 4, summoner 3, witch 4, occultist 4, psychic 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (fur from a bloodhound)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>circle, centered on you, with a radius of 400 ft. + 40 ft./level</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>locate object,</i> except this spell locates a known creature. You slowly turn and sense when you are facing in the direction of the creature to be located, provided it is within range. You also know in which direction the creature is moving, if any.</p><p>The spell can locate a creature of a specific kind or a specific creature known to you. It cannot find a creature of a certain type. To find a kind of creature, you must have seen such a creature up close (within 30 feet) at least once.</p><p>Running water blocks the spell. It cannot detect objects. It can be fooled by <i>mislead, nondetection</i>, and <i>polymorph</i> spells.</p></h4></div>"},
new Spell(){ Name = "Longstrider", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Longstrider</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1, ranger 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a pinch of dirt)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 hour/level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell gives you a +10 foot enhancement bonus to your base speed. It has no effect on other modes of movement, such as burrow, climb, fly, or swim.</p></h4></div>"},
new Spell(){ Name = "Lullaby", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Lullaby</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 0, psychic 0, mesmerist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>living creatures within a 10-ft.-radius burst</h5><h5><b>Duration </b>concentration + 1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any creature within the area that fails a Will save becomes drowsy and inattentive, taking a -5 penalty on Perception checks and a -2 penalty on Will saves against <i>sleep</i> effects while the <i>lullaby</i> is in effect. <i>Lullaby</i> lasts for as long as the caster concentrates, plus up to 1 round per caster level thereafter.</p></h4></div>"},
new Spell(){ Name = "Mage Armor", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage Armor</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [force]; <b>Level </b>sorcerer/wizard 1, summoner 1, witch 1, bloodrager 1, occultist 1, psychic 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a piece of cured leather)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An invisible but tangible field of force surrounds the subject of a <i>mage armor</i> spell, providing a +4 armor bonus to AC.</p><p>Unlike mundane armor, <i>mage armor</i> entails no armor check penalty, arcane spell failure chance, or speed reduction. Since <i>mage armor</i> is made of force, incorporeal creatures can't bypass it the way they do normal armor.</p></h4><h5><b>Mythic: </b>The armor bonus increases to +6. There is a 50% chance that any critical hit or sneak attack made against the target is negated and treated as a normal hit, as if the target were wearing moderate fortification armor.</h5></div>"},
new Spell(){ Name = "Mage Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 0, sorcerer/wizard 0, summoner 0, magus 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one nonmagical, unattended object weighing up to 5 lbs.</h5><h5><b>Duration </b>concentration</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You point your finger at an object and can lift it and move it at will from a distance. As a move action, you can propel the object as far as 15 feet in any direction, though the spell ends if the distance between you and the object ever exceeds the spell's range.</p></h4></div>"},
new Spell(){ Name = "Mage's Disjunction", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage's Disjunction</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>all magical effects and magic items within a 40-ft.-radius burst, or one magic item (see text)</h5><h5><b>Duration </b>1 minute/level</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>All magical effects and magic items within the radius of the spell, except for those that you carry or touch, are disjoined.</p><p>That is, spells and spell-like effects are unraveled and destroyed completely (ending the effect as a <i>dispel magic</i> spell does), and each permanent magic item must make a successful Will save or be turned into a normal item for the duration of this spell.</p><p>An item in a creature's possession uses its own Will save bonus or its possessor's Will save bonus, whichever is higher. If an item's saving throw results in a natural 1 on the die, the item is destroyed instead of being suppressed.</p><p>You also have a 1% chance per caster level of destroying an <i>antimagic field</i>. If the <i>antimagic field</i> survives the <i>disjunction</i>, no items within it are disjoined.</p><p>You can also use this spell to target a single item. The item gets a Will save at a -5 penalty to avoid being permanently destroyed. Even artifacts are subject to mage's <i>disjunction</i>, though there is only a 1% chance per caster level of actually affecting such powerful items. If successful, the artifact's power unravels, and it is destroyed (with no save). If an artifact is destroyed, you must make a DC 25 Will save or permanently lose all spellcasting abilities. These abilities cannot be recovered by mortal magic, not even <i>miracle</i> or <i>wish</i>. Destroying artifacts is a dangerous business, and it is 95% likely to attract the attention of some powerful being who has an interest in or connection with the device.</p></h4><h5><b>Mythic: </b>The duration increases to 10 minutes per caster level. The chance to destroy an antimagic field increases to 2% per caster level.</h5></div>"},
new Spell(){ Name = "Mage's Faithful Hound", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage's Faithful Hound</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 5, summoner 4, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a tiny silver whistle, a piece of bone, and a thread)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>phantom watchdog</h5><h5><b>Duration </b>1 hour/caster level or until discharged, then 1 round/caster level; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You conjure up a phantom watchdog that is invisible to everyone but yourself. It then guards the area where it was conjured (it does not move). The hound immediately starts barking loudly if any Small or larger creature approaches within 30 feet of it. (Those within 30 feet of the hound when it is conjured may move about in the area, but if they leave and return, they activate the barking.) The hound sees invisible and ethereal creatures. It does not react to figments, but it does react to shadow illusions.</p><p>If an intruder approaches to within 5 feet of the hound, the dog stops barking and delivers a vicious bite (+10 attack bonus, 2d6+3 points of piercing damage) once per round. The dog also gets the bonuses appropriate to an invisible creature (see <i>invisibility).</i></p><p>The dog is considered ready to bite intruders, so it delivers its first bite on the intruder's turn. Its bite is the equivalent of a magic weapon for the purpose of damage reduction. The hound cannot be attacked, but it can be dispelled.</p><p>The spell lasts for 1 hour per caster level, but once the hound begins barking, it lasts only 1 round per caster level. If you are ever more than 100 feet distant from the hound, the spell ends.</p></h4></div>"},
new Spell(){ Name = "Mage's Lucubration", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage's Lucubration</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>wizard 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>instantaneous</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You instantly prepare any one spell of 5th level or lower that you have used during the past 24 hours. The spell must have been actually cast during that period. The chosen spell is stored in your mind as through prepared in the normal fashion.</p><p>If the recalled spell requires material components, you must provide them. The recovered spell is not usable until the material components are available.</p></h4></div>"},
new Spell(){ Name = "Mage's Magnificent Mansion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage's Magnificent Mansion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a miniature ivory door, a piece of polished marble, and a silver spoon, each worth 5 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>extradimensional mansion, up to three 10-ft. cubes/level (S)</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You conjure up an extradimensional dwelling that has a single entrance on the plane from which the spell was cast. The entry point looks like a faint shimmering in the air that is 4 feet wide and 8 feet high. Only those you designate may enter the mansion, and the portal is shut and made invisible behind you when you enter. You may open it again from your own side at will. Once observers have passed beyond the entrance, they are in a magnificent foyer with numerous chambers beyond. The atmosphere is clean, fresh, and warm.</p><p>You can create any floor plan you desire to the limit of the spell's effect. The place is furnished and contains sufficient foodstuffs to serve a nine-course banquet to a dozen people per caster level.</p><p>A staff of near-transparent servants (as many as two per caster level), liveried and obedient, wait upon all who enter. The servants function as <i>unseen servant</i> spells except that they are visible and can go anywhere in the mansion.</p><p>Since the place can be entered only through its special portal, outside conditions do not affect the mansion, nor do conditions inside it pass to the plane beyond.</p></h4></div>"},
new Spell(){ Name = "Mage's Private Sanctum", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage's Private Sanctum</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 5, psychic 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (a sheet of lead, a piece of glass, a wad of cotton, and powdered chrysolite)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>30-ft. cube/level  (S)</h5><h5><b>Duration </b>24 hours (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell ensures privacy. Anyone looking into the area from outside sees only a dark, foggy mass. Darkvision cannot penetrate it. No sounds, no matter how loud, can escape the area, so nobody can eavesdrop from outside. Those inside can see out normally.</p><p>Divination (scrying) spells cannot perceive anything within the area, and those within are immune to <i>detect thoughts</i>. The ward prevents speech between those inside and those outside (because it blocks sound), but it does not prevent other communication, such as a <i>sending</i> or <i>message</i> spell, or telepathic communication, such as that between a wizard and her familiar.</p><p>The spell does not prevent creatures or objects from moving into and out of the area.</p><p>Mage's private sanctum can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Mage's Sword", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mage's Sword</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a miniature platinum sword worth 250 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one sword</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell brings into being a shimmering, sword-like plane of force. The sword strikes at any opponent within its range, as you desire, starting in the round that you cast the spell. The sword attacks its designated target once each round on your turn. Its attack bonus is equal to your caster level + your Intelligence bonus or your Charisma bonus (for wizards or sorcerers, respectively) with an additional +3 enhancement bonus. As a force effect, it can strike ethereal and incorporeal creatures. It deals 4d6+3 points of force damage, with a threat range of 19-20 and a critical multiplier of x2.</p><p>The sword always strikes from your direction. It does not get a bonus for flanking or help a combatant get one. If the sword goes beyond the spell range from you, goes out of your sight, or you are not directing it, it returns to you and hovers.</p><p>Each round after the first, you can use a standard action to switch the sword to a new target. If you do not, the sword continues to attack the previous round's target.</p><p>The sword cannot be attacked or harmed by physical attacks, but dispel magic, disintegrate, a sphere of annihilation, or a <i>rod of cancellation</i> affects it. The sword's AC is 13 (10, +0 size bonus for Medium object, +3 deflection bonus).</p><p>If an attacked creature has spell resistance, the resistance is checked the first time mage's sword strikes it. If the sword is successfully resisted, the spell is dispelled. If not, the sword has its normal full effect on that creature for the duration of the spell.</p></h4></div>"},
new Spell(){ Name = "Magic Aura", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Aura</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 1, sorcerer/wizard 1, occultist 1, psychic 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a small square of silk that must be passed over the object that receives the aura)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched object weighing up to 5 lbs./level</h5><h5><b>Duration </b>1 day/level (D)</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You alter an item's aura so that it registers to <i>detect</i> spells (and spells with similar capabilities) as though it were nonmagical, or a magic item of a kind you specify, or the subject of a spell you specify. If the object bearing <i>magic aura</i> has <i>identify</i> cast on it or is similarly examined, the examiner recognizes that the aura is false and <i>detect</i>s the object's actual qualities if he succeeds on a Will save. Otherwise, he believes the aura and no amount of testing reveals what the true magic is.</p><p>If the targeted item's own aura is exceptionally powerful (if it is an artifact, for instance), <i>magic aura</i> doesn't work.</p><p><i>Note:</i> A magic weapon, shield, or suit of armor must be a masterwork item, so a sword of average make, for example, looks suspicious if it has a magical aura.</p></h4></div>"},
new Spell(){ Name = "Magic Circle against Evil", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Circle against Evil</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [good]; <b>Level </b>cleric/oracle 3, paladin 3, sorcerer/wizard 3, summoner 3, inquisitor 3, summoner 3, inquisitor 3, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a 3-ft.-diameter circle of powdered silver)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Area </b>10-ft.-radius emanation from touched creature</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>All creatures within the area gain the effects of a <i>protection from evil</i> spell, and evil summoned creatures cannot enter the area either. Creatures in the area, or who later enter the area, receive only one attempt to suppress effects that are controlling them.</p><p>If successful, such effects are suppressed as long as they remain in the area. Creatures that leave the area and come back are not protected. You must overcome a creature's spell resistance in order to keep it at bay (as in the third function of <i>protection from evil</i>), but the deflection and resistance bonuses and the protection from mental control apply regardless of enemies' spell resistance.</p><p>This spell has an alternative version that you may choose when casting it. A <i><i>magic circle</i> against evil</i> can be focused inward rather than outward. When focused inward, the spell binds a nongood called creature (such as those called by the <i><i>lesser planar binding</i>, planar binding,</i> and <i>greater planar binding</i> spells) for a maximum of 24 hours per caster level, provided that you cast the spell that calls the creature within 1 round of casting the <i>magic circle</i>. The creature cannot cross the circle's boundaries. If a creature too large to fit into the spell's area is the subject of the spell, the spell acts as a normal <i>protection from evil</i> spell for that creature only.</p><p>A <i>magic circle</i> leaves much to be desired as a trap. If the circle of powdered silver laid down in the process of spellcasting is broken, the effect immediately ends. The trapped creature can do nothing that disturbs the circle, directly or indirectly, but other creatures can. If the called creature has spell resistance, it can test the trap once a day. If you fail to overcome its spell resistance, the creature breaks free, destroying the circle.</p><p>A creature capable of any form of dimensional travel (astral projection, blink, dimension door, etherealness, gate, plane shift, shadow walk, teleport, and similar abilities) can simply leave the circle through such means. You can prevent the creature's extradimensional escape by casting a <i>dimensional <i>anchor</i></i> spell on it, but you must cast the spell before the creature acts. If you are successful, the <i>anchor</i> effect lasts as long as the <i>magic circle</i> does. The creature cannot reach across the <i>magic circle</i>, but its ranged attacks (ranged weapons, spells, magical abilities, and the like) can. The creature can attack any target it can reach with its ranged attacks except for the circle itself.</p><p>You can add a special diagram (a two-dimensional bounded figure with no gaps along its circumference, augmented with various magical sigils) to make the <i>magic circle</i> more secure. Drawing the diagram by hand takes 10 minutes and requires a DC 20 Spellcraft check. You do not know the result of this check. If the check fails, the diagram is ineffective. You can take 10 when drawing the diagram if you are under no particular time pressure to complete the task.</p><p>This task also takes 10 full minutes. If time is no factor at all, and you devote 3 hours and 20 minutes to the task, you can take 20.</p><p>A successful diagram allows you to cast a <i>dimensional <i>anchor</i></i> spell on the <i>magic circle</i> during the round before casting any summoning spell. The <i>anchor</i> holds any called creatures in the <i>magic circle</i> for 24 hours per caster level. A creature cannot use its spell resistance against a <i>magic circle</i> prepared with a diagram, and none of its abilities or attacks can cross the diagram. If the creature tries a Charisma check to break free of the trap (see the <i>lesser planar binding</i> spell), the DC increases by 5. The creature is immediately released if anything disturbs the diagram-even a straw laid across it. The creature itself cannot disturb the diagram either directly or indirectly, as noted above.</p><p>This spell is not cumulative with <i>protection from evil</i> and vice versa.</p></h4></div>"},
new Spell(){ Name = "Magic Circle against Chaos", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Circle against Chaos</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [lawful]; <b>Level </b>cleric/oracle 3, paladin 3, sorcerer/wizard 3, summoner 3, inquisitor 3, summoner 3, inquisitor 3, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a 3-ft.-diameter circle of powdered silver)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Area </b>10-ft.-radius emanation from touched creature</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>magic circle against evil,</i> except that it is similar to <i>protection from chaos</i> instead of <i>protection from evil</i>, and it can imprison a nonlawful called creature.</p></h4></div>"},
new Spell(){ Name = "Magic Circle against Good", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Circle against Good</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [evil]; <b>Level </b>cleric/oracle 3, sorcerer/wizard 3, summoner 3, inquisitor 3, antipaladin 3, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a 3-ft.-diameter circle of powdered silver)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Area </b>10-ft.-radius emanation from touched creature</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>magic circle against evil,</i> except that it is similar to <i>protection from good</i> instead of <i>protection from evil</i>, and it can imprison a nonevil called creature.</p></h4></div>"},
new Spell(){ Name = "Magic Circle against Law", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Circle against Law</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [chaotic]; <b>Level </b>cleric/oracle 3, sorcerer/wizard 3, summoner 3, inquisitor 3, antipaladin 3, shaman 3, occultist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a 3-ft.-diameter circle of powdered silver)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Area </b>10-ft.-radius emanation from touched creature</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>magic circle against evil,</i> except that it is similar to <i>protection from law</i> instead of <i>protection from evil</i>, and it can imprison a nonchaotic called creature.</p></h4></div>"},
new Spell(){ Name = "Magic Fang", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Fang</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1, ranger 1, summoner 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Magic fang</i> gives one natural weapon or unarmed strike of the subject a +1 enhancement bonus on attack and damage rolls. The spell can affect a slam attack, fist, bite, or other natural weapon.</p><p>The spell does not change an unarmed strike's damage from nonlethal damage to lethal damage.</p><p><i>Magic fang</i> can be made permanent with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>The spell affects an additional number of the target's natural weapons equal to your tier. For example, if you're 3rd tier, it could affect two claws and one bite).</h5></div>"},
new Spell(){ Name = "Magic Fang, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Fang, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 3, ranger 3, summoner 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>magic fang</i>, except that the enhancement bonus on attack and damage rolls is +1 per four caster levels (maximum +5). This bonus does not allow a natural weapon or unarmed strike to bypass damage reduction aside from magic.</p><p>Alternatively, you may imbue all of the creature's natural weapons with a +1 enhancement bonus (regardless of your caster level).</p><p>Greater <i>magic fang</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Magic Jar", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Jar</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 5, alchemist 5, summoner 4, witch 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a gem or crystal worth at least 100 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>1 hour/level or until you return to your body</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>By casting <i><i>magic jar</i>,</i> you place your soul in a gem or large crystal (known as the <i>magic jar</i>), leaving your body lifeless. Then you can attempt to take control of a nearby body, forcing its soul into the <i>magic jar</i>. You may move back to the jar (thereby returning the trapped soul to its body) and attempt to possess another body.</p><p>The spell ends when you send your soul back to your own body, leaving the receptacle empty. To cast the spell, the <i>magic jar</i> must be within spell range and you must know where it is, though you do not need line of sight or line of effect to it. When you transfer your soul upon casting, your body is, as near as anyone can tell, dead.</p><p>While in the <i><i>magic jar</i>,</i> you can sense and attack any life force within 10 feet per caster level (and on the same plane of existence).</p><p>You do need line of effect from the jar to the creatures. You cannot determine the exact creature types or positions of these creatures.</p><p>In a group of life forces, you can sense a difference of 4 or more HD between one creature and another and can determine whether a life force is powered by positive or negative energy. (Undead creatures are powered by negative energy. Only sentient undead creatures have, or are, souls.) You could choose to take over either a stronger or a weaker creature, but which particular stronger or weaker creature you attempt to possess is determined randomly.</p><p>Attempting to possess a body is a full-round action. It is blocked by <i>protection from evil</i> or a similar ward. You possess the body and force the creature's soul into the <i>magic jar</i> unless the subject succeeds on a Will save. Failure to take over the host leaves your life force in the <i><i>magic jar</i>,</i> and the target automatically succeeds on further saving throws if you attempt to possess its body again.</p><p>If you are successful, your life force occupies the host body, and the host's life force is imprisoned in the <i>magic jar</i>. You keep your Intelligence, Wisdom, Charisma, level, class, base attack bonus, base save bonuses, alignment, and mental abilities. The body retains its Strength, Dexterity, Constitution, hit points, natural abilities, and automatic abilities. A body with extra limbs does not allow you to make more attacks (or more advantageous two-weapon attacks) than normal. You can't choose to activate the body's extraordinary or supernatural abilities. The creature's spells and spell-like abilities do not stay with the body.</p><p>As a standard action, you can shift freely from a host to the <i>magic jar</i> if within range, sending the trapped soul back to its body. The spell ends when you shift from the jar to your own body.</p><p>If the host body is slain, you return to the <i><i>magic jar</i>,</i> if within range, and the life force of the host departs (it is dead). If the host body is slain beyond the range of the spell, both you and the host die. Any life force with nowhere to go is treated as slain.</p><p>If the spell ends while you are in the <i><i>magic jar</i>,</i> you return to your body (or die if your body is out of range or destroyed). If the spell ends while you are in a host, you return to your body (or die, if it is out of range of your current position), and the soul in the <i>magic jar</i> returns to its body (or dies if it is out of range). Destroying the receptacle ends the spell, and the spell can be dispelled at either the <i>magic jar</i> or the host's location.</p></h4></div>"},
new Spell(){ Name = "Magic Missile", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Missile</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 1, magus 1, bloodrager 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>up to five creatures, no two of which can be more than 15 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A missile of magical energy darts forth from your fingertip and strikes its target, dealing 1d4+1 points of force damage.</p><p>The missile strikes unerringly, even if the target is in melee combat, so long as it has less than total cover or total concealment.</p><p>Specific parts of a creature can't be singled out. Objects are not damaged by the spell.</p><p>For every two caster levels beyond 1st, you gain an additional missile-two at 3rd level, three at 5th, four at 7th, and the maximum of five missiles at 9th level or higher. If you shoot multiple missiles, you can have them strike a single creature or several creatures.</p><p>A single missile can strike only one creature. You must designate targets before you check for spell resistance or roll damage.</p></h4><h5><b>Mythic: </b>The damage dealt by each missile increases to 2d4+1. The missiles bypass the shield spell and similar effects that block the non-mythic version of this spell.</h5><h5><b>Augmented (4th)</b>: If you expend two uses of mythic power, the spell creates double the normal number of missiles (affecting up to 10 creatures), its range increases to line of sight, and it bypasses the targets' spell resistance and spell immunity.</h5></div>"},
new Spell(){ Name = "Magic Mouth", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Mouth</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 1, sorcerer/wizard 2, summoner 1, occultist 2, psychic 2, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a small bit of honeycomb and jade dust worth 10 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature or object</h5><h5><b>Duration </b>permanent until discharged</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell imbues the chosen object or creature with an enchanted mouth that suddenly appears and speaks its message the next time a specified event occurs. The message, which must be 25 or fewer words long, can be in any language known by you and can be delivered over a period of 10 minutes. The mouth cannot utter verbal components, use command words, or activate magical effects. It does, however, move according to the words articulated; if it were placed upon a statue, the mouth of the statue would move and appear to speak. <i>Magic mouth</i> can also be placed upon a tree, rock, or any other object or creature.</p><p>The spell functions when specific conditions are fulfilled according to your command as set in the spell. Commands can be as general or as detailed as desired, although only visual and audible triggers can be used. Triggers react to what appears to be the case.</p><p>Disguises and illusions can fool them. Normal <i>darkness</i> does not defeat a visual trigger, but magical <i>darkness</i> or <i>invisibility</i> does.</p><p>Silent movement or magical <i>silence</i> defeats audible triggers. Audible triggers can be keyed to general types of noises or to a specific noise or spoken word. Actions can serve as triggers if they are visible or audible. A <i>magic mouth</i> cannot distinguish alignment, level, Hit Dice, or class except by external garb.</p><p>The range limit of a trigger is 15 feet per caster level, so a 6thlevel caster can command a <i>magic mouth</i> to respond to triggers as far as 90 feet away. Regardless of range, the mouth can respond only to visible or audible triggers and actions in line of sight or within hearing distance.</p><p><i>Magic mouth</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Magic Stone", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Stone</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 1, druid 1, shaman 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>up to three pebbles touched</h5><h5><b>Duration </b>30 minutes or until discharged</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You transmute as many as three pebbles, which can be no larger than sling bullets, so that they strike with great force when thrown or slung. If hurled, they have a range increment of 20 feet. If slung, treat them as sling bullets (range increment 50 feet). The spell gives them a +1 enhancement bonus on attack and damage rolls. The user of the stones makes a normal ranged attack. Each stone that hits deals 1d6+1 points of damage (including the spell's enhancement bonus), or 2d6+2 points against undead.</p></h4></div>"},
new Spell(){ Name = "Magic Vestment", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Vestment</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 3, inquisitor 3, shaman 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>armor or shield touched</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You imbue a suit of armor or a shield with an enhancement bonus of +1 per four caster levels (maximum +5 at 20th level).</p><p>An outfit of regular clothing counts as armor that grants no AC bonus for the purpose of this spell.</p></h4><h5><b>Mythic: </b>You can add one armor special ability to the target if it's armor or one shield special ability if it's a shield. The special ability must have a base price modifier no greater than a +1 enhancement bonus or 4,000 gp.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, you can add any number of special abilities with a total base price modifier no greater than a +2 enhancement bonus or 15,000 gp.</h5></div>"},
new Spell(){ Name = "Magic Weapon", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Weapon</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 1, paladin 1, sorcerer/wizard 1, inquisitor 1, antipaladin 1, magus 1, bloodrager 1, shaman 1, occultist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>weapon touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Magic weapon</i> gives a weapon a +1 enhancement bonus on attack and damage rolls. An enhancement bonus does not stack with a masterwork weapon's +1 bonus on attack rolls.</p><p>You can't cast this spell on a natural weapon, such as an unarmed strike (instead, see magic fang). A monk's unarmed strike is considered a weapon, and thus it can be enhanced by this spell.</p></h4></div>"},
new Spell(){ Name = "Magic Weapon, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Magic Weapon, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 4, paladin 3, sorcerer/wizard 3, inquisitor 3, antipaladin 3, magus 3, bloodrager 3, shaman 4, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (powdered lime and carbon)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one weapon or 50 projectiles (all of which must be together at the time of casting)</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>magic weapon</i>, except that it gives a weapon an enhancement bonus on attack and damage rolls of +1 per four caster levels (maximum +5). This bonus does not allow a weapon to bypass damage reduction aside from magic.</p><p>Alternatively, you can affect as many as 50 arrows, bolts, or bullets.</p><p>The projectiles must be of the same kind, and they have to be together (in the same quiver or other container). Projectiles, but not thrown weapons, lose their transmutation after they are used.</p><p>Treat shuriken as projectiles, rather than as thrown weapons, for the purpose of this spell.</p></h4><h5><b>Mythic: </b>You can add one of the following weapon special abilities to the weapon: flaming, frost, keen, merciful, shock, or thundering.</h5><h5><b>Augmented (5th)</b>: If you expend two uses of mythic power, you can reduce the granted enhancement bonus by 1 to add two of the above abilities or one of the following abilities: anarchic, axiomatic, flaming burst, holy, icy burst, shocking burst, or unholy.</h5></div>"},
new Spell(){ Name = "Minor Creation", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Minor Creation</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 4, summoner 3, witch 4, occultist 3, psychic 4, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (a tiny piece of matter of the same sort of item you plan to create with minor creation)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>unattended, nonmagical object of nonliving plant matter, up to 1 cu. ft./level</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You create a nonmagical, unattended object of nonliving vegetable matter. The volume of the item created cannot exceed 1 cubic foot per caster level. You must succeed on an appropriate Craft skill check to make a complex item.</p><p>Attempting to use any created object as a material component causes the spell to fail.</p></h4></div>"},
new Spell(){ Name = "Major Creation", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Major Creation</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 5, summoner 4, witch 5, occultist 4, psychic 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (a tiny piece of matter of the same sort of item you plan to create with minor creation)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>unattended, nonmagical object of nonliving plant matter, up to 1 cu. ft./level</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>minor creation,</i> except that you can also create an object of mineral nature: stone, crystal, metal, or the like. The duration of the created item varies with its relative hardness and rarity, as indicated on the following table.</p><p> <table><tr><th>Hardness and Rarity Examples</th><th>Duration</th></tr><tr><td>Vegetable matter</td><td>2 hr./level</td></tr><tr><td>Stone, crystal, base metals</td><td>1 hr./level</td></tr><tr><td>Precious metals</td><td>20 min./level</td></tr><tr><td>Gems</td><td>10 min./level</td></tr><tr><td>Rare metal*</td><td>1 round/level</td></tr><tr><td colspan=""2"">*Includes adamantine, alchemical silver, and mithral. You can't use major creation to create a cold iron item.</td></tr></table> </p></h4></div>"},
new Spell(){ Name = "Major Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Major Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 3, sorcerer/wizard 3, magus 3, occultist 3, psychic 3, mesmerist 3, witch 3, alchemist 2, witch 2, inquisitor 2, witch 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a bit of fleece)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>visual figment that cannot extend beyond four 10-ft. cubes + one 10-ft. cube/level (S)</h5><h5><b>Duration </b>Concentration + 3 rounds</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>silent image,</i> except that sound, smell, and thermal illusions are included in the spell effect. While concentrating, you can move the image within the range.</p><p>The image disappears when struck by an opponent unless you cause the illusion to react appropriately.</p></h4></div>"},
new Spell(){ Name = "Make Whole", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Make Whole</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 2, sorcerer/wizard 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one object of up to 10 cu. ft./level or one construct creature of any size</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>mending</i>, except that it repairs 1d6 points of damage per level when cast on an object or construct creature (maximum 5d6).</p><p><i>Make whole</i> can fix destroyed magic items (at 0 hit points or less), and restores the magic properties of the item if your caster level is at least twice that of the item. Items with charges (such as wands) and single-use items (such as potions and scrolls) cannot be repaired in this way. When <i>make whole</i> is used on a construct creature, the spell bypasses any immunity to magic as if the spell did not allow spell resistance.</p></h4><h5><b>Mythic: </b>The damage the spell repairs increases to 2d6 points of damage per caster level when cast on a construct creature (maximum 10d6). A construct repaired with this spell gains a +2 enhancement bonus on an ability score of your choice for the next 24 hours. To repair a magic item, your caster level has to equal to only the caster level of the magic item, not double the caster level.</h5></div>"},
new Spell(){ Name = "Mark of Justice", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mark of Justice</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [curse]; <b>Level </b>cleric/oracle 5, paladin 4, witch 5, inquisitor 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>permanent; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You mark a subject and state some behavior on the part of the subject that will activate the mark. When activated, the mark curses the subject. Typically, you designate some sort of undesirable behavior that activates the mark, but you can pick any act you please.</p><p>The effect of the mark is identical with the effect of <i>bestow curse</i>.</p><p>Since this spell takes 10 minutes to cast and involves writing on the target, you can cast it only on a creature that is willing or restrained.</p><p>Like the effect of <i>bestow curse</i>, a <i>mark of justice</i> cannot be dispelled, but it can be removed with a break enchantment, limited <i>wish</i>, miracle, remove curse, or <i>wish</i> spell. <i>Remove curse</i> works only if its caster level is equal to or higher than your <i>mark of justice</i> caster level. These restrictions apply regardless of whether the mark has activated.</p></h4></div>"},
new Spell(){ Name = "Maze", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Maze</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (teleportation); <b>Level </b>sorcerer/wizard 8, summoner 6, witch 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You banish the subject into an extradimensional labyrinth. Each round on its turn, it may attempt a DC 20 Intelligence check to escape the labyrinth as a full-round action. If the subject doesn't escape, the <i>maze</i> disappears after 10 minutes, freeing the subject.</p><p>On escaping or leaving the <i>maze</i>, the subject reappears where it had been when the <i>maze</i> spell was cast. If this location is filled with a solid object, the subject appears in the nearest open space.</p><p>Spells and abilities that move a creature within a plane, such as <i>teleport</i> and <i>dimension door</i>, do not help a creature escape a <i>maze</i> spell, although a <i>plane shift</i> spell allows it to exit to whatever plane is designated in that spell. Minotaurs are not affected by this spell.</p></h4><h5><b>Mythic: </b>A spectral minotaur hunts the target trapped in the maze. Every round the creature fails to escape from the maze, it takes 2d8 + your tier points of piercing and slashing damage from this phantom opponent.</h5></div>"},
new Spell(){ Name = "Meld into Stone", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Meld into Stone</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [earth]; <b>Level </b>cleric/oracle 3, druid 3, psychic 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>10 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Meld into stone</i> enables you to meld your body and possessions into a single block of stone. The stone must be large enough to accommodate your body in all three dimensions. When the casting is complete, you and not more than 100 pounds of nonliving gear merge with the stone. If either condition is violated, the spell fails and is wasted.</p><p>While in the stone, you remain in contact, however tenuous, with the face of the stone through which you melded. You remain aware of the passage of time and can cast spells on yourself while hiding in the stone. Nothing that goes on outside the stone can be seen, but you can still hear what happens around you. Minor physical damage to the stone does not harm you, but its partial destruction (to the extent that you no longer fit within it) expels you and deals you 5d6 points of damage. The stone's complete destruction expels you and slays you instantly unless you make a DC 18 Fortitude save. Even if you make your save, you still take 5d6 points of damage.</p><p>Any time before the duration expires, you can step out of the stone through the surface that you entered. If the spell's duration expires or the effect is dispelled before you voluntarily exit the stone, you are violently expelled and take 5d6 points of damage.</p><p>The following spells harm you if cast upon the stone that you are occupying. <i>Stone to flesh</i> expels you and deals you 5d6 points of damage. Stone <i>shape</i> deals 3d6 points of damage but does not expel you. <i>Transmute rock to mud</i> expels you and then slays you instantly unless you make a DC 18 Fortitude save, in which case you are merely expelled. Finally, <i>passwall</i> expels you without damage.</p></h4></div>"},
new Spell(){ Name = "Mending", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mending</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 0, cleric/oracle 0, druid 0, sorcerer/wizard 0, summoner 0, witch 0, summoner 0, witch 0, shaman 0, occultist 0, psychic 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Targets </b>one object of up to 1 lb./level</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell repairs damaged objects, restoring 1d4 hit points to the object. If the object has the broken condition, this condition is removed if the object is restored to at least half its original hit points. All of the pieces of an object must be present for this spell to function. Magic items can be repaired by this spell, but you must have a caster level equal to or higher than that of the object. Magic items that are destroyed (at 0 hit points or less) can be repaired with this spell, but this spell does not restore their magic abilities. This spell does not affect creatures (including constructs). This spell has no effect on objects that have been warped or otherwise transmuted, but it can still repair damage done to such items.</p></h4></div>"},
new Spell(){ Name = "Message", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Message</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [language-dependent]; <b>Level </b>bard 0, sorcerer/wizard 0, summoner 0, witch 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a piece of copper wire)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one creature/level</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can whisper messages and receive whispered replies.</p><p>Those nearby can hear these messages with a DC 25 Perception check. You point your finger at each creature you want to receive the message. When you whisper, the whispered message is audible to all targeted creatures within range.</p><p>Magical <i>silence,</i> 1 foot of stone, 1 inch of common metal (or a thin sheet of lead), or 3 feet of wood or dirt blocks the spell.</p><p>The message does not have to travel in a straight line. It can circumvent a barrier if there is an open path between you and the subject, and the path's entire length lies within the spell's range. The creatures that receive the message can whisper a reply that you hear. The spell transmits sound, not meaning; it doesn't transcend language barriers. To speak a message, you must mouth the words and whisper.</p></h4></div>"},
new Spell(){ Name = "Meteor Swarm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Meteor Swarm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>sorcerer/wizard 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>four 40-ft.-radius spreads, see text</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none or Reflex half, see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Meteor swarm</i> is a very powerful and spectacular spell that is similar to <i>fireball</i> in many aspects. When you cast it, four 2-foot-diameter spheres spring from your outstretched hand and streak in straight lines to the spots you select. The meteor spheres leave a fiery trail of sparks.</p><p>If you aim a sphere at a specific creature, you may make a ranged touch attack to strike the target with the meteor. Any creature struck by a sphere takes 2d6 points of bludgeoning damage (no save) and takes a -4 penalty on the saving throw against the sphere's fire damage (see below). If a targeted sphere misses its target, it simply explodes at the nearest corner of the target's space. You may aim more than one sphere at the same target.</p><p>Once a sphere reaches its destination, it explodes in a 40-foot-radius spread, dealing 6d6 points of fire damage to each creature in the area. If a creature is within the area of more than one sphere, it must save separately against each. Despite stemming from separate spheres, all of the fire damage is added together after the saves have been made, and fire resistance is applied only once.</p></h4><h5><b>Mythic: </b>The meteors created by this spell deal bludgeoning damage in a line extending from you to the target destination where it explodes. Creatures in the path of a meteor take 2d10 points of bludgeoning damage and must succeed at a Fortitude save or be knocked prone. The damage from the exploding meteors increases to 6d10 points of fire damage. Creatures that fail their Reflex saves against the fire damage are also knocked prone and deafened for 1d4 rounds.</h5><h5><b>Augmented (10th)</b>: If you expend two uses of mythic power, creatures in the path of a meteor also take 4d10 points of fire damage and the damage from the exploding meteors increases to 10d10 points of fire damage; this fire damage bypasses fire resistance and fire immunity.</h5></div>"},
new Spell(){ Name = "Mind Blank", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mind Blank</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 8, witch 8, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>24 hours</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject is protected from all devices and spells that gather information about the target through divination magic (such as <i>detect evil, locate creature, scry,</i> and <i>see invisible</i>). This spell also grants a +8 resistance bonus on saving throws against all mindaffecting spells and effects. <i>Mind blank</i> even foils limited <i>wish</i>, miracle, and <i>wish</i> spells when they are used in such a way as to gain information about the target. In the case of scrying that scans an area the creature is in, such as <i>arcane eye,</i> the spell works but the creature simply isn't detected. Scrying attempts that are targeted specifically at the subject do not work at all.</p></h4></div>"},
new Spell(){ Name = "Mind Fog", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mind Fog</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 5, sorcerer/wizard 5, witch 5, occultist 5, psychic 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>fog spreads in 20-ft. radius, 20 ft. high</h5><h5><b>Duration </b>30 minutes and 2d6 rounds; see text</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Mind fog</i> produces a bank of thin mist that weakens the mental resistance of those caught in it. Creatures in the <i>mind fog</i> take a -10 penalty on Wisdom checks and Will saves. (A creature that successfully saves against the fog is not affected and need not make further saves even if it remains in the fog.) Affected creatures take the penalty as long as they remain in the fog and for 2d6 rounds thereafter. The fog is stationary and lasts for 30 minutes (or until dispersed by wind).</p><p>A moderate wind (11+ mph) disperses the fog in 4 rounds; a strong wind (21+ mph) disperses the fog in 1 round.</p><p>The fog is thin and does not significantly hamper vision.</p></h4></div>"},
new Spell(){ Name = "Minor Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Minor Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 2, sorcerer/wizard 2, magus 2, occultist 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a bit of fleece)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>visual figment that cannot extend beyond four 10-ft. cubes + one 10-ft. cube/level (S)</h5><h5><b>Duration </b>concentration + 2 rounds</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>silent image</i>, except that <i>minor image</i> includes some minor sounds but not understandable speech.</p></h4></div>"},
new Spell(){ Name = "Miracle", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Miracle</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S; see text</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Area </b>see text</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You don't so much cast a <i>miracle</i> as request one. You state what you would like to have happen and request that your deity (or the power you pray to for spells) intercede.</p><p>A <i>miracle</i> can do any of the following things.</p><p><ul><li> Duplicate any cleric spell of 8th level or lower.</p><p><li> Duplicate any other spell of 7th level or lower.</p><p><li> Undo the harmful effects of certain spells, such as <i>feeblemind</i> or <i>insanity.</i></p><p><li> Have any effect whose power level is in line with the above effects.</ul></p><p>Alternatively, a cleric can make a very powerful request. Casting such a <i>miracle</i> costs the cleric 25,000 gp in powdered diamond because of the powerful divine energies involved. Examples of especially powerful <i>miracle</i>s of this sort could include the following: <ul><li> Swinging the tide of a battle in your favor by raising fallen allies to continue fighting.</p><p><li> Moving you and your allies, with all your and their gear, from one plane to a specific locale through planar barriers with no chance of error.</p><p><li> Protecting a city from an earthquake, volcanic eruption, flood, or other major natural disaster.</ul></p><p>In any event, a request that is out of line with the deity's (or alignment's) nature is refused.</p><p>A duplicated spell allows saving throws and spell resistance as normal, but the save DCs are as for a 9th-level spell. When a <i>miracle</i> spell duplicates a spell with a material component that costs more than 100 gp, you must provide that component.</p></h4></div>"},
new Spell(){ Name = "Mirage Arcana", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mirage Arcana</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 5, sorcerer/wizard 5, psychic 5, mesmerist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>one 20-ft. cube/level  (S)</h5><h5><b>Duration </b>concentration +1 hour/ level (D)</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>hallucinatory terrain</i>, except that it enables you to make any area appear to be something other than it is. The illusion includes audible, visual, tactile, and olfactory elements.</p><p>Unlike <i>hallucinatory terrain</i>, the spell can alter the appearance of structures (or add them where none are present). Still, it can't disguise, conceal, or add creatures (though creatures within the area might hide themselves within the illusion just as they can hide themselves within a real location).</p></h4></div>"},
new Spell(){ Name = "Mirror Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mirror Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 2, sorcerer/wizard 2, magus 2, bloodrager 2, occultist 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates a number of illusory doubles of you that inhabit your square. These doubles make it difficult for enemies to precisely locate and attack you.</p><p>When <i>mirror image</i> is cast, 1d4 images plus one image per three caster levels (maximum eight images total) are created.</p><p>These images remain in your space and move with you, mimicking your movements, sounds, and actions exactly. Whenever you are attacked or are the target of a spell that requires an attack roll, there is a possibility that the attack targets one of your images instead. If the attack is a hit, roll randomly to see whether the selected target is real or a figment. If it is a figment, the figment is destroyed. If the attack misses by 5 or less, one of your figments is destroyed by the near miss. Area spells affect you normally and do not destroy any of your figments. Spells and effects that do not require an attack roll affect you normally and do not destroy any of your figments. Spells that require a touch attack are harmlessly discharged if used to destroy a figment.</p><p>An attacker must be able to see the figments to be fooled. If you are invisible or the attacker is blind, the spell has no effect (although the normal miss chances still apply).</p></h4><h5><b>Mythic: </b>The maximum number of images generated by this spell increases to 12. Destroying an image creates a flash of light. Any creature that destroys an image must succeed at a Will save or be dazzled for 1 round.</h5></div>"},
new Spell(){ Name = "Misdirection", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Misdirection</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 2, sorcerer/wizard 2, summoner 2, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature or object, up to a 10-ft. cube in size</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>none or Will negates; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>By means of this spell, you misdirect the information from divination spells that reveal auras (detect <i>evil, detect magic, discern lies,</i> and the like). On casting the spell, you choose another object within range. For the duration of the spell, the subject of <i>misdirection is</i> detected as if it were the other object. Neither the subject nor the other object gets a saving throw against this effect. Detection spells provide information based on the second object rather than on the actual target of the detection unless the caster of the detection succeeds on a Will save. For instance, you could make yourself detect as a tree if one were within range at casting: not evil, not lying, not magical, neutral in alignment, and so forth. This spell does not affect other types of divination magic (augury, <i>detect thoughts, clairaudience/clairvoyance,</i> and the like).</p></h4></div>"},
new Spell(){ Name = "Mislead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mislead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment, glamer); <b>Level </b>bard 5, sorcerer/wizard 6, alchemist 6, magus 6, occultist 5, psychic 6, mesmerist 5, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>Target/ you/one illusory double</h5><h5><b>Duration </b>1 round/level (D) and concentration + 3 rounds; see text</h5><h5><b>Saving Throw </b>none or Will disbelief (if interacted with); see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You become invisible (as <i>greater invisibility</i>, a glamer), and at the same time, an illusory double of you (as <i>major image</i>, a figment) appears. You are then free to go elsewhere while your double moves away. The double appears within range but thereafter moves as you direct it (which requires concentration beginning on the first round after the casting). You can make the figment appear superimposed perfectly over your own body so that observers don't notice an image appearing and you turning invisible. You and the figment can then move in different directions. The double moves at your speed and can talk and gesture as if it were real, but it cannot attack or cast spells, though it can pretend to do so.</p><p>The illusory double lasts as long as you concentrate upon it, plus 3 additional rounds. After you cease concentration, the illusory double continues to carry out the same activity until the duration expires. The <i>greater invisibility</i> lasts for 1 round per level, regardless of concentration.</p></h4><h5><b>Mythic: </b>You create one illusory double per 5 caster levels, each of which appears in your square or in an unoccupied square adjacent to yours. When you cast the spell, you can give each of these doubles a simple programmed task as a telepathic command, such a ""walk south,"" ""beg for mercy,"" or ""follow that troll."" You can change or modify a single illusory double's programmed task by spending a swift, move, or standard action; this requires concentration.</h5><h5><b>Augmented (4th)</b>: If you expend two uses of mythic power, this spell also grants you the benefits of augmented mythic invisibility instead of greater invisibility.</h5></div>"},
new Spell(){ Name = "Mnemonic Enhancer", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mnemonic Enhancer</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>wizard 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (a piece of string, and ink consisting of squid secretion mixed with black dragon's blood), F (an ivory plaque worth 50 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>instantaneous</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Casting this spell allows you to prepare additional spells or retain spells recently cast. Pick one of these two versions when the spell is cast.</p><p><i>Prepare:</i> You prepare up to three additional levels of spells. A cantrip counts as 1/2 level for this purpose. You prepare and cast these spells normally.</p><p><i>Retain:</i> You retain any spell of 3rd level or lower that you had cast up to 1 round before you started casting the <i>mnemonic enhancer</i>.</p><p>This restores the previously cast spell to your mind.</p><p>In either event, the spell or spells prepared or retained fade after 24 hours (if not cast).</p></h4></div>"},
new Spell(){ Name = "Modify Memory", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Modify Memory</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 4, psychic 5, mesmerist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round; see text</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You reach into the subject's mind and modify as many as 5 minutes of its memories in one of the following ways.</p><p><ul><li> Eliminate all memory of an event the subject actually experienced. This spell cannot negate <i>charm, geas/quest, suggestion,</i> or similar spells.</p><p><li> Allow the subject to recall with perfect clarity an event it actually experienced.</p><p><li> Change the details of an event the subject actually experienced.</p><p><li> Implant a memory of an event the subject never experienced.</ul></p><p>Casting the spell takes 1 round. If the subject fails to save, you proceed with the spell by spending as much as 5 minutes (a period of time equal to the amount of memory you want to modify) visualizing the memory you wish to modify in the subject. If your concentration is disturbed before the visualization is complete, or if the subject is ever beyond the spell's range during this time, the spell is lost.</p><p>A modified memory does not necessarily affect the subject's actions, particularly if it contradicts the creature's natural inclinations. An illogical modified memory is dismissed by the creature as a bad dream, too much wine, or another similar excuse.</p></h4></div>"},
new Spell(){ Name = "Moment of Prescience", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Moment of Prescience</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>sorcerer/wizard 8, witch 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 hour/level or until discharged</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell grants you a sixth sense. Once during the spell's duration, you may choose to use its effect. This spell grants you an insight bonus equal to your caster level (maximum +25) on any single attack roll, combat maneuver check, opposed ability or skill check, or saving throw. Alternatively, you can apply the insight bonus to your AC against a single attack (even if flatfooted).</p><p>Activating the effect doesn't take an action; you can even activate it on another character's turn. You must choose to use the <i>moment of prescience</i> before you make the roll it is to modify.</p><p>Once used, the spell ends. You can't have more than one <i>moment of prescience</i> active on you at the same time.</p></h4></div>"},
new Spell(){ Name = "Mount", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Mount</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>sorcerer/wizard 1, summoner 1, witch 1, magus 1, bloodrager 1, occultist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (a bit of horse hair)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one mount</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You summon a light horse or a pony (your choice) to serve you as a mount (see the <i>Pathfinder RPG Bestiary</i>). The steed serves willingly and well. The mount comes with a bit and bridle and a riding saddle.</p></h4></div>"},
new Spell(){ Name = "Move Earth", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Move Earth</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [earth]; <b>Level </b>druid 6, sorcerer/wizard 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>see text</h5><h5><b>Components </b>V, S, M (clay, loam, sand, and an iron blade)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>dirt in an area up to 750 ft. square and up to 10 ft. deep (S)</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Move earth</i> moves dirt (clay, loam, sand, and soil), possibly collapsing embankments, moving hillocks, shifting dunes, and so forth.</p><p>In no event can rock formations be collapsed or moved. The area to be affected determines the casting time. For every 150- foot square (up to 10 feet deep), casting takes 10 minutes. The maximum area, 750 feet by 750 feet, takes 4 hours and 10 minutes to move.</p><p>This spell does not violently break the surface of the ground.</p><p>Instead, it creates wavelike crests and troughs, with the earth reacting with glacial fluidity until the desired result is achieved.</p><p>Trees, structures, rock formations, and such are mostly unaffected except for changes in elevation and relative topography.</p><p>The spell cannot be used for tunneling and is generally too slow to trap or bury creatures. Its primary use is for digging or filling moats or for adjusting terrain contours before a battle.</p><p>This spell has no effect on earth creatures.</p></h4><h5><b>Mythic: </b>This spell can move earth much more quickly, with each full round of casting allowing you to reshape a 25-foot-square area up to 10 feet deep. All creatures on or under the ground within that area are knocked prone and moved to the closest square outside the area being shaped. A successful Reflex save prevents a creature from being knocked prone, but it's still moved.</h5></div>"},
new Spell(){ Name = "Neutralize Poison", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Neutralize Poison</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>bard 4, cleric/oracle 4, druid 3, paladin 4, ranger 3, alchemist 4, witch 4, inquisitor 4, shaman 4, spiritualist 4, alchemist 4, witch 4, inquisitor 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (charcoal)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature or object of up to 1 cu. ft./level touched</h5><h5><b>Duration </b>instantaneous or 10 min./level; see text</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You detoxify any sort of venom in the creature or object touched. If the target is a creature, you must make a caster level check (1d20 + caster level) against the DC of each poison affecting the target. Success means that the poison is neutralized. A cured creature suffers no additional effects from the poison, and any temporary effects are ended, but the spell does not reverse instantaneous effects, such as hit point damage, temporary ability damage, or effects that don't go away on their own.</p><p>This spell can instead neutralize the poison in a poisonous creature or object for 10 minutes per level, at the caster's option.</p><p>If cast on a creature, the creature receives a Will save to negate the effect.</p></h4></div>"},
new Spell(){ Name = "Nightmare", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Nightmare</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (phantasm) [mind-affecting, evil]; <b>Level </b>bard 5, sorcerer/wizard 5, alchemist 5, psychic 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>unlimited</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You send a hideous and unsettling phantasmal vision to a specific creature that you name or otherwise specifically designate.</p><p>The <i>nightmare</i> prevents restful sleep and causes 1d10 points of damage. The <i>nightmare</i> leaves the subject fatigued and unable to regain arcane spells for the next 24 hours.</p><p>The difficulty of the save depends on your knowledge the subject and the physical connection (if any) you have to that creature.</p>   <table border ='1'><tr><th>Knowledge</th><th>Will Save Modifier</th></tr><tr><td>None*</td><td>+10</td></tr><tr><td>Secondhand (you have heard of the subject)</td><td>+5</td></tr><tr><td>Firsthand (you have met the subject)</td><td>+0</td></tr><tr><td>Familiar (you know the subject well)</td><td>-5</td></tr><tr><td colspan='2'>*You must have some sort of connection to a creature of which you have no knowledge.</td></tr></table>   <table border ='1'><tr><th>Connection</th><th>Will Save Modifier</th></tr><tr><td>Likeness or picture</td><td>-2</td></tr><tr><td>Possession or garment</td><td>-4</td></tr><tr><td>Body part, lock of hair, bit of nail, etc.</td><td>-10</td></tr></table>    <i>Dispel evil</i> cast on the subject while you are casting the spell dispels the <i>nightmare</i> and causes you to be stunned for 10 minutes per caster level of the <i>dispel evil</i>.</p><p>If the recipient is awake when the spell begins, you can choose to cease casting (ending the spell) or to enter a trance until the recipient goes to sleep, whereupon you become alert again and complete the casting. If you are disturbed during the trance, you must succeed on a concentration check as if you were in the midst of casting a spell or the spell ends.</p><p>If you choose to enter a trance, you are not aware of your surroundings or the activities around you while in the trance.</p><p>You are defenseless, both physically and mentally, while in the trance. (You always fail Reflex and Will saving throws, for example.) Creatures who don't sleep (such as outsiders) or dream are immune to this spell.</p></h4><h5><b>Mythic: </b>The spell's damage increases to 4d6 points of damage, and the target is also shaken for 24 hours.</h5><h5><b>Augmented (6th)</b>: You can deliver a mind-affecting spell through a mythic nightmare. The maximum level of the delivered spell is equal to half your tier, and you must expend one use of mythic power for each spell level of the imbued spell in addition to the one use needed to cast mythic nightmare. You choose whether the delivered spell affects the creature immediately or when it awakens. The delivered spell affects only the target, even if it normally affects multiple creatures or an area.</h5></div>"},
new Spell(){ Name = "Nondetection", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Nondetection</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>ranger 4, sorcerer/wizard 3, alchemist 3, summoner 3, inquisitor 3, antipaladin 3, occultist 3, psychic 3, mesmerist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (diamond dust worth 50 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature or object touched</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates (harmless, object); <b>Spell Resistance </b>yes (harmless, object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The warded creature or object becomes difficult to <i>detect</i> by divination spells such as <i>clairaudience/clairvoyance, locate object,</i> and <i>detect</i> spells. <i>Nondetection</i> also prevents location by such magic items as <i>crystal ball</i>s. If a divination is attempted against the warded creature or item, the caster of the divination must succeed on a caster level check (1d20 + caster level) against a DC of 11 + the caster level of the spellcaster who cast <i>nondetection</i>. If you cast <i>nondetection</i> on yourself or on an item currently in your possession, the DC is 15 + your caster level.</p><p>If cast on a creature, <i>nondetection</i> wards the creature's gear as well as the creature itself.</p></h4></div>"},
new Spell(){ Name = "Obscure Object", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Obscure Object</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 1, cleric/oracle 3, sorcerer/wizard 2, inquisitor 3, occultist 2, psychic 2, mesmerist 1, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (chameleon skin)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one object touched of up to 100 lbs./level</h5><h5><b>Duration </b>8 hours (D)</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell hides an object from location by divination (<i>scrying</i>) effects, such as the <i>scrying</i> spell or a <i>crystal ball</i>. Such an attempt automatically fails (if the divination is targeted on the object) or fails to perceive the object (if the divination is targeted on a nearby location, object, or person).</p></h4></div>"},
new Spell(){ Name = "Obscuring Mist", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Obscuring Mist</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>cleric/oracle 1, druid 1, sorcerer/wizard 1, witch 1, magus 1, shaman 1, mesmerist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>20 ft.</h5><h5><b>Effect </b>cloud spreads in 20-ft. radius from you, 20 ft. high</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A misty vapor arises around you. It is stationary. The vapor obscures all sight, including darkvision, beyond 5 feet. A creature 5 feet away has concealment (attacks have a 20% miss chance). Creatures farther away have total concealment (50% miss chance, and the attacker cannot use sight to locate the target).</p><p>A moderate wind (11+ mph), such as from a <i>gust of wind</i> spell, disperses the fog in 4 rounds. A strong wind (21+ mph) disperses the fog in 1 round. A <i>fireball, flame strike</i>, or similar spell burns away the fog in the explosive or fiery spell's area. A <i>wall of fire</i> burns away the fog in the area into which it deals damage.</p><p>This spell does not function underwater.</p></h4></div>"},
new Spell(){ Name = "Open/Close", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Open/Close</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 0, sorcerer/wizard 0, summoner 0, magus 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a brass key)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>object weighing up to 30 lbs. or portal that can be opened or closed</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can open or close (your choice) a door, chest, box, window, bag, pouch, bottle, barrel, or other container. If anything resists this activity (such as a bar on a door or a lock on a chest), the spell fails. In addition, the spell can only open and close things weighing 30 pounds or less. Thus, doors, chests, and similar objects sized for enormous creatures may be beyond this spell's ability to affect.</p></h4></div>"},
new Spell(){ Name = "Order's Wrath", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Order's Wrath</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [lawful]; <b>Level </b>cleric/oracle 4, inquisitor 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>nonlawful creatures within a burst that fills a 30-ft. cube</h5><h5><b>Duration </b>instantaneous (1 round); see text</h5><h5><b>Saving Throw </b>Will partial; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You channel lawful power to smite enemies. The power takes the form of a three-dimensional grid of energy. Only chaotic and neutral (not lawful) creatures are harmed by the spell.</p><p>The spell deals 1d8 points of damage per two caster levels (maximum 5d8) to chaotic creatures (or 1d6 points of damage per caster level, maximum 10d6, to chaotic outsiders) and causes them to be dazed for 1 round. A successful Will save reduces the damage to half and negates the daze effect.</p><p>The spell deals only half damage to creatures who are neither chaotic nor lawful, and they are not dazed. They can reduce the damage in half again (down to one-quarter of the roll) with a successful Will save.</p></h4><h5><b>Mythic: </b>The damage dealt to chaotic outsiders increases to 1d10 points of damage per caster level (maximum 10d10) and the damage to other chaotic creatures increases to 1d12 points of damage per 2 caster levels (maximum 5d12). The duration that creatures in the area are dazed increases to 2 rounds. Chaotic creatures that succeed at their saves are dazed for 1 round.</h5></div>"},
new Spell(){ Name = "Overland Flight", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Overland Flight</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 5, alchemist 5, summoner 4, witch 5, magus 5, shaman 5, occultist 5, psychic 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 hour/level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like a <i>fly</i> spell, except you can <i>fly</i> at a speed of 40 feet (30 feet if wearing medium or heavy armor, or if carrying a medium or heavy load) with a bonus on Fly skill checks equal to half your caster level. When using this spell for long-distance movement, you can hustle without taking nonlethal damage (a forced march still requires Constitution checks). This means you can cover 64 miles in an 8-hour period of flight (or 48 miles at a speed of 30 feet).</p></h4></div>"},
new Spell(){ Name = "Owl's Wisdom", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Owl's Wisdom</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 2, druid 2, paladin 2, ranger 2, sorcerer/wizard 2, alchemist 2, summoner 2, shaman 2, psychic 2, alchemist 2, summoner 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (feathers or droppings from an owl)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The transmuted creature becomes wiser. The spell grants a +4 enhancement bonus to Wisdom, adding the usual benefit to Wisdom-related skills. Clerics, druids, and rangers (and other Wisdom-based spellcasters) who receive owl's wisdom do not gain any additional bonus spells for the increased Wisdom, but the save DCs for their spells increase.</p></h4></div>"},
new Spell(){ Name = "Owl's Wisdom, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Owl's Wisdom, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 6, druid 6, sorcerer/wizard 6, summoner 4, shaman 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (feathers or droppings from an owl)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>owl's wisdom</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Passwall", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Passwall</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 5, psychic 5, mesmerist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (sesame seeds)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Effect </b>5-ft.-by-8-ft. opening, 10 ft. deep plus 5 ft. deep per three additional levels</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You create a passage through wooden, plaster, or stone walls, but not through metal or other harder materials. The passage is 10 feet deep plus an additional 5 feet deep per three caster levels above 9th (15 feet at 12th, 20 feet at 15th, and a maximum of 25 feet deep at 18th level). If the wall's thickness is more than the depth of the passage created, then a single <i>passwall</i> simply makes a niche or short tunnel. Several <i>passwall</i> spells can then form a continuing passage to breach very thick walls. When <i>passwall</i> ends, creatures within the passage are ejected out the nearest exit. If someone dispels the <i>passwall</i> or you dismiss it, creatures in the passage are ejected out the far exit, if there is one, or out the sole exit if there is only one.</p></h4></div>"},
new Spell(){ Name = "Pass without Trace", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Pass without Trace</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1, ranger 1, shaman 1, occultist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one creature/level touched</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject or subjects of this spell do not leave footprints or a scent trail while moving. Tracking the subjects is impossible by nonmagical means.</p></h4></div>"},
new Spell(){ Name = "Permanency", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Permanency</p><div style=""clear: both;""></div></div><div><h5><b>School </b>universal; <b>Level </b>sorcerer/wizard 5, psychic 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>2 rounds</h5><h5><b>Components </b>V, S, M (see tables below)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Targets </b>see text</h5><h5><b>Duration </b>permanent; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell makes the duration of certain other spells permanent.</p><p>You first cast the desired spell and then follow it with the <i>permanency</i> spell.</p><p>Depending on the spell, you must be of a minimum caster level and must expend a specific gp value of diamond dust as a material component.</p><p>You can make the following spells permanent in regard to yourself.</p><p> <table><tr><th>Spell</th><th>Minimum Caster Level</th><th>GP Cost</th></tr><tr><td><i>Arcane sight</i></td><td>11th</td><td>7,500 gp</td></tr><tr><td><i>Comprehend languages</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Darkvision</i></td><td>10th</td><td>5,000 gp</td></tr><tr><td><i>Detect magic</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Read magic</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>See invisibility</i></td><td>10th</td><td>5,000 gp</td></tr><tr><td><i>Tongues</i></td><td>11th</td><td>7,500 gp</td></tr></table>  You cannot cast these spells on other creatures. This application of <i>permanency</i> can be dispelled only by a caster of higher level than you were when you cast the spell.</p><p>In addition to personal use, <i>permanency</i> can be used to make the following spells permanent on yourself, another creature, or an object (as appropriate).</p><p> <table><tr><th>Spell</th><th>Minimum Caster Level</th><th>GP Cost</th></tr><tr><td><i>Enlarge person</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Magic fang</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Magic fang, greater</i></td><td>11th</td><td>7,500 gp</td></tr><tr><td><i>Reduce person</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Resistance</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Telepathic bond</i>*</td><td>13th</td><td>12,500 gp</td></tr><tr><td colspan=""2"">*Only bonds two creatures per casting of <i>permanency</i>.</td></tr></table> </p><p>Additionally, the following spells can be cast upon objects or areas only and rendered permanent.</p><p> <table><tr><th>Spell</th><th>Minimum Caster Level</th><th>GP Cost</th></tr><tr><td><i>Alarm</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Animate objects</i></td><td>14th</td><td>15,000 gp</td></tr><tr><td><i>Dancing lights</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Ghost sound</i></td><td>9th</td><td>2,500 gp</td></tr><tr><td><i>Gust of wind</i></td><td>11th</td><td>7,500 gp</td></tr><tr><td><i>Invisibility</i></td><td>10th</td><td>5,000 gp</td></tr><tr><td><i>Mage's private sanctum</i></td><td>13th</td><td>12,500 gp</td></tr><tr><td><i>Magic mouth</i></td><td>10th</td><td>5,000 gp</td></tr><tr><td><i>Phase door</i></td><td>15th</td><td>17,500 gp</td></tr><tr><td><i>Prismatic sphere</i></td><td>17th</td><td>22,500 gp</td></tr><tr><td><i>Prismatic wall</i></td><td>16th</td><td>20,000 gp</td></tr><tr><td><i>Shrink item</i></td><td>11th</td><td>7,500 gp</td></tr><tr><td><i>Solid fog</i></td><td>12th</td><td>10,000 gp</td></tr><tr><td><i>Stinking cloud</i></td><td>11th</td><td>7,500 gp</td></tr><tr><td><i>Symbol of death</i></td><td>16th</td><td>20,000 gp</td></tr><tr><td><i>Symbol of fear</i></td><td>14th</td><td>15,000 gp</td></tr><tr><td><i>Symbol of insanity</i></td><td>16th</td><td>20,000 gp</td></tr><tr><td><i>Symbol of pain</i></td><td>13th</td><td>12,500 gp</td></tr><tr><td><i>Symbol of persuasion</i></td><td>14th</td><td>15,000 gp</td></tr><tr><td><i>Symbol of sleep</i></td><td>16th</td><td>20,000 gp</td></tr><tr><td><i>Symbol of stunning</i></td><td>15th</td><td>17,500 gp</td></tr><tr><td><i>Symbol of weakness</i></td><td>15th</td><td>17,500 gp</td></tr><tr><td><i>Teleportation circle</i></td><td>17th</td><td>22,500 gp</td></tr><tr><td><i>Wall of fire</i></td><td>12th</td><td>10,000 gp</td></tr><tr><td><i>Wall of force</i></td><td>13th</td><td>12,500 gp</td></tr><tr><td><i>Web</i></td><td>10th</td><td>5,000 gp</td></tr></table>  Spells cast on other targets are vulnerable to <i>dispel magic</i> as normal. The GM may allow other spells to be made permanent.</p></h4></div>"},
new Spell(){ Name = "Permanent Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Permanent Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 6, sorcerer/wizard 6, occultist 6, psychic 6, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a bit of fleece)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>figment that cannot extend beyond a 20-ft. cube + one 10-ft. cube/level (S)</h5><h5><b>Duration </b>permanent (D)</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>silent image,</i> except that the figment includes visual, auditory, olfactory, and thermal elements, and the spell is permanent. By concentrating, you can move the image within the limits of the range, but it is static while you are not concentrating.</p></h4></div>"},
new Spell(){ Name = "Persistent Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Persistent Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 5, sorcerer/wizard 5, occultist 5, psychic 5, mesmerist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a bit of fleece)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>visual figment that cannot extend beyond four 10-ft. cubes + one 10-ft. cube/level (S)</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>silent image,</i> except that the figment includes visual, auditory, olfactory, and thermal components, and the figment follows a script determined by you. The figment follows that script without your having to concentrate on it. The illusion can include intelligible speech if you wish.</p></h4></div>"},
new Spell(){ Name = "Phantasmal Killer", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Phantasmal Killer</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (phantasm) [fear, mind-affecting]; <b>Level </b>sorcerer/wizard 4, witch 4, magus 4, bloodrager 4, psychic 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will disbelief, then Fortitude partial; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You create a phantasmal image of the most fearsome creature imaginable to the subject simply by forming the fears of the subject's subconscious mind into something that its conscious mind can visualize: this most horrible beast. Only the spell's subject can see the <i>phantasmal killer</i>. You see only a vague shape. The target first gets a Will save to recognize the image as unreal. If that save fails, the phantasm touches the subject, and the subject must succeed on a Fortitude save or die from fear.</p><p>Even if the Fortitude save is successful, the subject takes 3d6 points of damage.</p><p>If the subject of a <i>phantasmal killer</i> attack succeeds in disbelieving and possesses telepathy or is wearing a <i>helm of telepathy</i>, the beast can be turned upon you. You must then disbelieve it or become subject to its deadly fear attack.</p></h4><h5><b>Mythic: </b>If the target fails its Will save but succeeds at its Fortitude save, the phantasmal killer persists in the target's mind, giving it the dazed condition until your next turn. On your next turn, the target must attempt another Fortitude save against the phantasm. Success means it takes 3d6 points of damage; failure means it dies from fear.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the spell can affect a living target that is immune to fear, illusions, or mind-affecting effects.</h5></div>"},
new Spell(){ Name = "Phantom Steed", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Phantom Steed</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>bard 3, sorcerer/wizard 3, summoner 2, magus 3, bloodrager 3, occultist 3, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>one quasi-real, horselike creature</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You conjure a Large, quasi-real, horselike creature (the exact coloration can be customized as you wish). It can be ridden only by you or by the one person for whom you specifically created the mount. A phantom steed has a black head and body, gray mane and tail, and smoke-colored, insubstantial hooves that make no sound.</p><p>It has what seems to be a saddle, bit, and bridle. It does not fight, but animals shun it and refuse to attack it.</p><p>The mount is AC 18 (-1 size, +4 natural armor, +5 Dex) and 7 hit points + 1 hit point per caster level. If it loses all its hit points, the phantom steed disappears. A phantom steed has a speed of 20 feet per two caster levels, to a maximum of 100 feet at 10th level. It can bear its rider's weight plus up to 10 pounds per caster level.</p><p>These mounts gain certain powers according to caster level. A mount's abilities include those of mounts of lower caster levels.</p><p><i>8th Level</i>: The mount can ride over sandy, muddy, or even swampy ground without difficulty or decrease in speed.</p><p><i>10th Level</i>: The mount can use <i>water walk</i> at will (as the spell, no action required to activate this ability).</p><p><i>12th Level</i>: The mount can use <i>air walk</i> at will (as the spell, no action required to activate this ability) for up to 1 round at a time, after which it falls to the ground.</p><p><i>14th Level</i>: The mount can fly at its speed with a bonus on Fly skill checks equal to your caster level.</p></h4><h5><b>Mythic: </b>The steed's rider automatically succeeds at Ride checks to remain mounted. The mount's hit points increase to 10 plus 2 per caster level, and its speed increases to 30 feet per 2 caster levels (maximum 150 feet).</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the phantom steed is incorporeal, but its rider can interact with it normally.</h5></div>"},
new Spell(){ Name = "Phantom Trap", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Phantom Trap</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>sorcerer/wizard 2, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (special dust worth 50 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>permanent (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell makes a lock or other small mechanism seem to be trapped to anyone who can detect traps. You place the spell upon any small mechanism or device, such as a lock, hinge, hasp, cork, cap, or ratchet. Any character able to detect traps, or who uses any spell or device enabling trap detection, is certain a real trap exists.</p><p>Of course, the effect is illusory and nothing happens if the trap is ""sprung""; its primary purpose is to frighten away thieves or make them waste precious time.</p><p>If another <i>phantom trap</i> is active within 50 feet when the spell is cast, the casting fails.</p></h4></div>"},
new Spell(){ Name = "Phase Door", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Phase Door</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 7, witch 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Effect </b>ethereal 5-ft.-by-8-ft. opening, 10 ft. deep + 5 ft. deep per three levels</h5><h5><b>Duration </b>one usage per two levels</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates an ethereal passage through wooden, plaster, or stone walls, but not other materials. The <i>phase door</i> is invisible and inaccessible to all creatures except you, and only you can use the passage. You disappear when you enter the <i>phase door</i> and appear when you exit. If you desire, you can take one other creature (Medium or smaller) through the door. This counts as two uses of the door. The door does not allow light, sound, or spell effects through it, nor can you see through it without using it. Thus, the spell can provide an escape route, though certain creatures, such as phase spiders, can follow with ease. A <i>gem of true seeing</i> or similar magic reveals the presence of a <i>phase door</i> but does not allow its use.</p><p>A <i>phase door</i> is subject to <i>dispel magic.</i> If anyone is within the passage when it is dispelled, he is harmlessly ejected just as if he were inside a <i>passwall</i> effect.</p><p>You can allow other creatures to use the <i>phase door</i> by setting some triggering condition for the door. Such conditions can be as simple or elaborate as you desire. They can be based on a creature's name, identity, or alignment, but otherwise must be based on observable actions or qualities. Intangibles such as level, class, HD, and hit points don't qualify.</p><p><i>Phase door</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Planar Ally, Lesser", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Planar Ally, Lesser</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (calling) [see text]; <b>Level </b>cleric/oracle 4, shaman 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (offerings worth 500 gp plus payment, see text), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one called outsider of 6 HD or less</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>By casting this spell, you request your deity to send you an outsider (of 6 HD or less) of the deity's choice. If you serve no particular deity, the spell is a general plea answered by a creature sharing your philosophical alignment. If you know an individual creature's name, you may request that individual by speaking the name during the spell (though you might get a different creature anyway).</p><p>You may ask the creature to perform one task in exchange for a payment from you. Tasks might range from the simple to the complex. You must be able to communicate with the creature called in order to bargain for its services.</p><p>The creature called requires a payment for its services. This payment can take a variety of forms, from donating gold or magic items to an allied temple, to a gift given directly to the creature, to some other action on your part that matches the creature's alignment and goals.</p><p>Regardless, this payment must be made before the creature agrees to perform any services. The bargaining takes at least 1 round, so any actions by the creature begin in the round after it arrives.</p><p>A task taking up to 1 minute per caster level requires a payment of 100 gp per HD of the creature called. For a task taking up to 1 hour per caster level, the creature requires a payment of 500 gp per HD. A long-term task, one requiring up to 1 day per caster level, requires a payment of 1,000 gp per HD.</p><p>A nonhazardous task requires only half the indicated payment, while an especially hazardous task might require a greater gift. Few if any creatures will accept a task that seems suicidal (remember, a called creature actually dies when it is killed, unlike a summoned creature). However, if the task is strongly aligned with the creature's ethos, it may halve or even waive the payment.</p><p>At the end of its task, or when the duration bargained for expires, the creature returns to its home plane (after reporting back to you, if appropriate and possible).</p><p><i>Note:</i> When you use a calling spell that calls an air, chaotic, earth, evil, fire, good, lawful, or water creature, it is a spell of that type.</p></h4></div>"},
new Spell(){ Name = "Planar Ally", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Planar Ally</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (calling) [see text for lesser planar ally]; <b>Level </b>cleric/oracle 6, shaman 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (offerings worth 1,250 gp plus payment), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one or two called outsiders, totaling no more than 12 HD, which cannot be more than 30 ft. apart when they appear</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser planar ally</i>, except you may call a single creature of 12 HD or less, or two creatures of the same kind whose HD total no more than 12. The creatures agree to help you and request your return payment together.</p></h4></div>"},
new Spell(){ Name = "Planar Ally, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Planar Ally, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (calling) [see text for lesser planar ally]; <b>Level </b>cleric/oracle 8, shaman 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (offerings worth 2,500 gp plus payment), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>up to three called outsiders, totaling no more than 18 HD, no two of which can be more than 30 ft. apart when they appear.</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser planar ally</i>, except that you may call a single creature of 18 HD or less, or up to three creatures of the same kind whose Hit Dice total no more than 18. The creatures agree to help you and request your return payment together.</p></h4></div>"},
new Spell(){ Name = "Planar Binding, Lesser", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Planar Binding, Lesser</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (calling) [see text]; <b>Level </b>sorcerer/wizard 5, summoner 4, occultist 5, psychic 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels); see text</h5><h5><b>Targets </b>one elemental or outsider with 6 HD or less</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no and yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Casting this spell attempts a dangerous act: to lure a creature from another plane to a specifically prepared trap, which must lie within the spell's range. The called creature is held in the trap until it agrees to perform one service in return for its freedom.</p><p>To create the trap, you must use a <i>magic circle</i> spell, focused inward. The kind of creature to be bound must be known and stated. If you wish to call a specific individual, you must use that individual's proper name in casting the spell.</p><p>The target creature is allowed a Will saving throw. If the saving throw succeeds, the creature resists the spell. If the saving throw fails, the creature is immediately drawn to the trap (spell resistance does not keep it from being called). The creature can escape from the trap by successfully pitting its spell resistance against your caster level check, by dimensional travel, or with a successful Charisma check (DC 15 + 1/2 your caster level + your Charisma modifier). It can try each method once per day. If it breaks loose, it can flee or attack you. A <i>dimensional anchor</i> cast on the creature prevents its escape via dimensional travel. You can also employ a calling diagram (see <i>magic circle</i> against evil) to make the trap more secure.</p><p>If the creature does not break free of the trap, you can keep it bound for as long as you dare. You can attempt to compel the creature to perform a service by describing the service and perhaps offering some sort of reward. You make a Charisma check opposed by the creature's Charisma check. The check is assigned a bonus of +0 to +6 based on the nature of the service and the reward. If the creature wins the opposed check, it refuses service. New offers, bribes, and the like can be made or the old ones reoffered every 24 hours. This process can be repeated until the creature promises to serve, until it breaks free, or until you decide to get rid of it by means of some other spell. Impossible demands or unreasonable commands are never agreed to. If you ever roll a natural 1 on the Charisma check, the creature breaks free of the spell's effect and can escape or attack you.</p><p>Once the requested service is completed, the creature need only to inform you to be instantly sent back whence it came. The creature might later seek revenge. If you assign some open-ended task that the creature cannot complete through its own actions, the spell remains in effect for a maximum of 1 day per caster level, and the creature gains an immediate chance to break free (with the same chance to resist as when it was trapped). Note that a clever recipient can subvert some instructions.</p><p>When you use a calling spell to call an air, chaotic, earth, evil, fire, good, lawful, or water creature, it is a spell of that type.</p></h4></div>"},
new Spell(){ Name = "Planar Binding", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Planar Binding</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (calling) [see text for lesser planar binding]; <b>Level </b>sorcerer/wizard 6, summoner 5, occultist 6, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels); see text</h5><h5><b>Targets </b>up to three elementals or outsiders, totaling no more than 12 HD, no two of which can be more than 30 ft. apart when they appear</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no and yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser planar binding</i>, except that you may call a single creature of 12 HD or less, or up to three creatures of the same kind whose Hit Dice total no more than 12. Each creature gets a saving throw, makes an independent attempt to escape, and must be individually persuaded to aid you.</p></h4></div>"},
new Spell(){ Name = "Planar Binding, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Planar Binding, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (calling) [see text for lesser planar binding]; <b>Level </b>sorcerer/wizard 8, summoner 6, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels); see text</h5><h5><b>Targets </b>up to three elementals or outsiders, totaling no more than 18 HD, no two of which can be more than 30 ft. apart when they appear.</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no and yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser planar binding</i>, except that you may call a single creature of 18 HD or less, or up to three creatures of the same kind whose Hit Dice total no more than 18. Each creature gets a saving throw, makes an independent attempt to escape, and must be individually persuaded to aid you.</p></h4></div>"},
new Spell(){ Name = "Plane Shift", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Plane Shift</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (teleportation); <b>Level </b>cleric/oracle 5, sorcerer/wizard 7, summoner 5, witch 7, shaman 7, psychic 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a forked metal rod attuned to the plane of travel)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched, or up to eight willing creatures joining hands</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You move yourself or some other creature to another plane of existence or alternate dimension. If several willing persons link hands in a circle, as many as eight can be affected by the <i>plane shift</i> at the same time. Precise accuracy as to a particular arrival location on the intended plane is nigh impossible. From the Material Plane, you can reach any other plane, though you appear 5 to 500 miles (5d%) from your intended destination. <i>Plane shift</i> transports creatures instantaneously and then ends. The creatures need to find other means if they are to travel back (including casting <i>plane shift</i> again).</p></h4><h5><b>Mythic: </b>If you've been to the destination plane and are familiar with a location there, you may choose that location and arrive on target at that location with anyone else you transport with the spell.</h5><h5><b>Augmented</b>: If you expend two uses of mythic power, you grant transported creatures the effects of a planar adaptation spell (Advanced Player's Guide 236) upon arrival, lasting for 1 hour.</h5></div>"},
new Spell(){ Name = "Plant Growth", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Plant Growth</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 3, ranger 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Area </b>see text</h5><h5><b>Targets </b>see text</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Plant growth</i> has different effects depending on the version chosen.</p><p><i>Overgrowth:</i> This effect causes normal vegetation (grasses, briars, bushes, creepers, thistles, trees, vines, and so on) within long range (400 feet + 40 feet per caster level) to become thick and overgrown.</p><p>The plants entwine to form a thicket or jungle that creatures must hack or force a way through. Speed drops to 5 feet, or 10 feet for Large or larger creatures. The area must have brush and trees in it for this spell to take effect. If this spell is cast on an area that is already affected by any spell or effect that enhances plants, such as <i>entangle</i> or <i>wall of thorns,</i> any DC involved with these spells is increased by 4.</p><p>This bonus is granted for 1 day after the casting of <i>plant growth.</i></p><p>At your option, the area can be a 100-foot-radius circle, a 150-foot-radius semicircle, or a 200-foot-radius quarter circle.</p><p>You may designate places within the area that are not affected.</p><p><i>Enrichment:</i> This effect targets plants within a range of a halfmile, raising their potential productivity over the course of the next year to one-third above normal.</p><p><i>Plant growth</i> counters <i>diminish plants</i>.</p><p>This spell has no effect on plant creatures.</p></h4></div>"},
new Spell(){ Name = "Plant Shape I", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Plant Shape I</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 5, alchemist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you cast this spell you can assume the form of any Small or Medium creature of the plant type (see the <i>Pathfinder RPG Bestiary</i>).</p><p>If the form you assume has any of the following abilities, you gain the listed ability: darkvision 60 feet, low-light vision, constrict, grab, and poison. If the form you assume does not possess the ability to move, your speed is reduced to 5 feet and you lose all other forms of movement. If the creature has vulnerability to an element, you gain that vulnerability.</p><p><i>Small plant</i>: If the form you take is that of a <i>Small plant</i>, you gain a +2 size bonus to your Constitution and a +2 natural armor bonus.</p><p><i>Medium plant</i>: If the form you take is that of a <i>Medium plant</i>, you gain a +2 size bonus to your Strength, a +2 enhancement bonus to your Constitution, and a +2 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Plant Shape II", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Plant Shape II</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 6, alchemist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>plant shape I</i> except that it also allows you to assume the form of a Large creature of the plant type. If the creature has immunity or resistance to any elements, you gain resistance 20 to those elements. If the creature has vulnerability to an element, you gain that vulnerability.</p><p><i>Large plant</i>: If the form you take is that of a <i>Large plant</i>, you gain a +4 size bonus to your Strength, a +2 size bonus to your Constitution, and a +4 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Plant Shape III", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Plant Shape III</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you plan to assume)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>plant shape II</i> except that it also allows you to assume the form of a Huge creature of the plant type. If the form you assume has any of the following abilities, you gain the listed ability: DR, regeneration 5, and trample.</p><p><i>Huge plant</i>: If the form you take is that of a <i>Huge plant</i>, you gain a +8 size bonus to your Strength, a -2 penalty to your Dexterity, a +4 size bonus to your Constitution, and a +6 natural armor bonus.</p></h4></div>"},
new Spell(){ Name = "Poison", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Poison</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [poison]; <b>Level </b>cleric/oracle 4, druid 3, witch 4, antipaladin 4, shaman 4, occultist 4, mesmerist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>instantaneous; see text</h5><h5><b>Saving Throw </b>Fortitude negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Calling upon the venomous powers of natural predators, you infect the subject with a horrible poison by making a successful melee touch attack. This poison deals 1d3 Constitution damage per round for 6 rounds. Poisoned creatures can make a Fortitude save each round to negate the damage and end the affliction.</p></h4></div>"},
new Spell(){ Name = "Polar Ray", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Polar Ray</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [cold]; <b>Level </b>sorcerer/wizard 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a white ceramic cone or prism)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A blue-white ray of freezing air and ice springs from your hand.</p><p>You must succeed on a ranged touch attack with the ray to deal damage to a target. The ray deals 1d6 points of cold damage per caster level (maximum 25d6) and 1d4 points of Dexterity drain.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 1d8 points of cold damage per caster level (maximum 25d8) and 1d6 points of Dexterity drain. If the target takes damage from this spell, it becomes partially encased in ice, reducing its speed by half. Creatures with the cold or incorporeal subtypes or immunity to being grappled are immune to this effect. The reduced movement lasts until the ice is destroyed. Destroying the ice requires a successful Strength or dispel check against the spell's DC or dealing 1 point of bludgeoning or fire damage per caster level to the target.</h5></div>"},
new Spell(){ Name = "Polymorph", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Polymorph</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 5, alchemist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you choose)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>1 min/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell transforms a willing creature into an animal, humanoid or elemental of your choosing; the spell has no effect on unwilling creatures, nor can the creature being targeted by this spell influence the new form assumed (apart from conveying its wishes, if any, to you verbally).</p><p>If you use this spell to cause the target to take on the form of an animal or magical beast, the spell functions as <i>beast shape II</i>. If the form is that of an elemental, the spell functions as <i>elemental body I.</i> If the form is that of a humanoid, the spell functions as <i>alter self</i>.</p><p>The subject may choose to resume its normal form as a full-round action; doing so ends the spell for that subject.</p></h4></div>"},
new Spell(){ Name = "Polymorph, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Polymorph, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a piece of the creature whose form you choose)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>1 min/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions as <i>polymorph</i> except that it allows the creature to take on the form of a dragon or plant creature. If you use this spell to cause the target to take on the form of an animal or magical beast, it functions as <i>beast shape IV</i>. If the form is that of an elemental, the spell functions as <i>elemental body III.</i> If the form is that of a humanoid, the spell functions as <i>alter self</i>. If the form is that of a plant, the spell functions as <i>plant shape II.</i> If the form is that of a dragon, the spell functions as <i>form of the dragon I.</i> The subject may choose to resume its normal form as a full-round action; doing so ends the spell.</p></h4></div>"},
new Spell(){ Name = "Polymorph Any Object", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Polymorph Any Object</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>sorcerer/wizard 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (mercury, gum arabic, and smoke)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature, or one nonmagical object of up to 100 cu. ft./level</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>Fortitude negates (object); see text; <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>greater polymorph,</i> except that it changes one object or creature into another. You can use this spell to transform all manner of objects and creatures into new forms-you aren't limited to transforming a living creature into another living form. The duration of the spell depends on how radical a change is made from the original state to its transmuted state. The duration is determined by using the following guidelines.</p><p> <table><tr><th>Changed Subject is...</th><th>Increase to duration Factor*</th></tr><tr><td>Same kingdom (animal, vegetable, mineral)</td><td>+5</td></tr><tr><td>Same class (mammals, fungi, metals, etc.)</td><td>+2</td></tr><tr><td>Same size</td><td>+2</td></tr><tr><td>Related (twig is to tree, wolf fur is to wolf, etc.)</td><td>+2</td></tr><tr><td>Same or lower Intelligence</td><td>+2</td></tr><tr><td colspan=""2"">*Add all that apply. Look up the total on the next table.</td></tr></table> </p><p> <table><tr><th>Duration Factor</th><th>Duration</th><th>Example</th></tr><tr><td>0</td><td>20 minutes</td><td>Pebble to human</td></tr><tr><td>2</td><td>1 hour</td><td>Marionette to human</td></tr><tr><td>4</td><td>3 hours</td><td>Human to marionette</td></tr><tr><td>5</td><td>12 hours</td><td>Lizard to manticore</td></tr><tr><td>6</td><td>2 days</td><td>Sheep to wool coat</td></tr><tr><td>7</td><td>1 week</td><td>Shrew to manticore</td></tr><tr><td>9+</td><td>Permanent</td><td>Manticore to shrew</td></tr></table>  If the target of the spell does not have physical ability scores (Strength, Dexterity, or Constitution), this spell grants a base score of 10 to each missing ability score. If the target of the spell does not have mental ability scores (Intelligence, Wisdom, or Charisma), this spell grants a score of 5 to such scores. Damage taken by the new form can result in the injury or death of the polymorphed creature. In general, damage occurs when the new form is changed through physical force. A nonmagical object cannot be made into a magic item with this spell. Magic items aren't affected by this spell.</p><p>This spell cannot create material of great intrinsic value, such as copper, silver, gems, silk, gold, platinum, mithral, or adamantine. It also cannot reproduce the special properties of cold iron in order to overcome the damage reduction of certain creatures.</p><p>This spell can also be used to duplicate the effects of baleful polymorph, <i>greater polymorph,</i> flesh to stone, stone to flesh, transmute mud to rock, transmute metal to wood, or transmute rock to mud.</p></h4></div>"},
new Spell(){ Name = "Power Word Blind", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Power Word Blind</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 7, witch 7, psychic 7, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature with 200 hp or less</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You utter a single word of power that causes a creature to become blinded, whether the creature can hear the word or not. The duration of the spell depends on the target's current hit point total.</p><p>Any creature that currently has 201 or more hit points is unaffected.</p><p> <table><tr><th>Hit points</th><th>Duration</th></tr><tr><td>50 or less</td><td>Permanent</td></tr><tr><td>51-100</td><td>1d4+1 minutes</td></tr><tr><td>101-200</td><td>1d4+1 rounds</td></tr></table> </p></h4></div>"},
new Spell(){ Name = "Power Word Kill", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Power Word Kill</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [death, mind-affecting]; <b>Level </b>sorcerer/wizard 9, witch 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature with 100 hp or less</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You utter a single word of power that instantly kills one creature of your choice, whether the creature can hear the word or not. Any creature that currently has 101 or more hit points is unaffected by <i>power word kill</i>.</p></h4><h5><b>Mythic: </b>Treat the target's current hit point total as though it were lowered by 5 times your tier. For example, a 3rd-tier archmage casting mythic power word kill would instantly kill a creature with 85 hit points or fewer. You can target a creature with more hit points than this spell could normally affect, causing it to become exhausted.</h5><h5><b>Augmented (6th)</b>: If you expend four uses of mythic power, the magic of the word lingers around the target. Until the beginning of your next turn, if the target's hit point total drops to the point that this spell would have killed it upon casting, it's instantly slain.</h5></div>"},
new Spell(){ Name = "Power Word Stun", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Power Word Stun</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>sorcerer/wizard 8, witch 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature with 150 hp or less</h5><h5><b>Duration </b>See text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You utter a single word of power that instantly causes one creature of your choice to become stunned, whether the creature can hear the word or not. The duration of the spell depends on the target's current hit point total. Any creature that currently has 151 or more hit points is unaffected by <i>power word stun</i>.</p><p> <table><tr><th>Hit Points</th><th>Duration</th></tr><tr><td>50 or less</td><td>4d4 rounds</td></tr><tr><td>51-100</td><td>2d4 rounds</td></tr><tr><td>101-150</td><td>1d4 rounds</td></tr></table> </p></h4></div>"},
new Spell(){ Name = "Prayer", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Prayer</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>cleric/oracle 3, paladin 3, inquisitor 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>40 ft.</h5><h5><b>Area </b>all allies and foes within a 40-ft.-radius burst centered on you</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You bring special favor upon yourself and your allies while bringing disfavor to your enemies. You and each of your allies gain a +1 luck bonus on attack rolls, weapon damage rolls, saves, and skill checks, while each of your foes takes a -1 penalty on such rolls.</p></h4><h5><b>Mythic: </b>The spell's luck bonus increases to +2, and the penalty for enemies changes to -2. When you cast the spell, your affected allies heal an amount of hit point damage equal to double your tier, and affected enemies take an amount of damage equal to double your tier.</h5></div>"},
new Spell(){ Name = "Prestidigitation", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Prestidigitation</p><div style=""clear: both;""></div></div><div><h5><b>School </b>universal; <b>Level </b>bard 0, magus 0, sorcerer/wizard 0, psychic 0, mesmerist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Target, Effect, or Area </b>see text<h5><b>Duration </b>1 hour</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Prestidigitations are minor tricks that novice spellcasters use for practice. Once cast, a prestidigitation spell enables you to perform simple magical effects for 1 hour. The effects are minor and have severe limitations. A prestidigitation can slowly lift 1 pound of material. It can color, clean, or soil items in a 1-foot cube each round. It can chill, warm, or flavor 1 pound of nonliving material. It cannot deal damage or affect the concentration of spellcasters. Prestidigitation can create small objects, but they look crude and artificial. The materials created by a prestidigitation spell are extremely fragile, and they cannot be used as tools, weapons, or spell components. Finally, prestidigitation lacks the power to duplicate any other spell effects. Any actual change to an object (beyond just moving, cleaning, or soiling it) persists only 1 hour.</p></h4></div>"},
new Spell(){ Name = "Prismatic Sphere", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Prismatic Sphere</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Effect </b>10-ft.-radius sphere centered on you</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>prismatic wall</i>, except you conjure up an immobile, opaque globe of shimmering, multicolored light that surrounds you and protects you from all forms of attack. The sphere flashes in all colors of the visible spectrum.</p><p>The sphere's <i>blindness</i> effect on creatures with less than 8 HD lasts 2d4 x 10 minutes.</p><p>You can pass into and out of the <i>prismatic sphere</i> and remain near it without harm. When you're inside it, however, the sphere blocks any attempt to project something through the sphere (including spells). Other creatures that attempt to attack you or pass through suffer the effects of each color, one at a time.</p><p>Typically, only the upper hemisphere of the globe exists, since you are at the center of the sphere, so the lower half is usually occluded by the floor surface you are standing on.</p><p>The colors of the sphere have the same effects as the colors of a <i>prismatic wall</i>.</p><p><i>Prismatic sphere</i> can be made permanent with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>When you cast the spell, you simultaneously create a prismatic spray that originates from one point on the sphere and radiates directly away from you. The save DC of this prismatic spray is the same as that of your mythic prismatic sphere.</h5></div>"},
new Spell(){ Name = "Prismatic Spray", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Prismatic Spray</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>sorcerer/wizard 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes seven shimmering, multicolored beams of light to spray from your hand. Each beam has a different power. Creatures in the area of the spell with 8 HD or less are automatically blinded for 2d4 rounds. Every creature in the area is randomly struck by one or more beams, which have additional effects.</p>   <table border ='1'><tr><th><table><tr><th>1d8</th><th>Color of Beam</th><th>Effect</th></tr><tr><td>1</td><td>Red</td><td>20 points fire damage (Reflex half)</td></tr><tr><td>2</td><td>Orange</td><td>40 points acid damage (Reflex half</td></tr><tr><td>3</td><td>Yellow</td><td>80 points electricity damage (Reflex half )</td></tr><tr><td>4</td><td>Green</td><td>Poison (Frequency 1/rd. for 6 rd.; Init. effect death; Sec. effect 1 Con/rd.; Cure 2 consecutive Fort saves)*</td></tr><tr><td>5</td><td>Blue</td><td><i>Flesh to stone</i> (Fortitude negates)</td></tr><tr><td>6</td><td>Indigo</td><td>Insane, as <i>insanity</i> spell (Will negates)</td></tr><tr><td>7</td><td>Violet</td><td>Sent to another plane (Will negates)</td></tr><tr><td>8</td><td>Struck by two rays</td><td>Roll twice more, ignoring any ""8""results</td></tr><tr><td colspan=""3"">* See poisons on page 557.</td></tr></table></td></tr></table>   </h4><h5><b>Mythic: </b>The spell automatically blinds creatures with up to 12 Hit Dice instead of 8 Hit Dice. Attempt one bull rush combat maneuver and apply its results to each creature in the area. Your CMB for this bull rush is equal to your caster level plus your Intelligence, Wisdom, or Charisma modifier, whichever is highest. This bull rush doesn't provoke an attack of opportunity.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the area changes from a cone into a line. Creatures in the line are struck by two beams (or three, if the creature rolls an 8 on the table). The spell bypasses energy resistance and immunity.</h5></div>"},
new Spell(){ Name = "Prismatic Wall", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Prismatic Wall</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>wall 4 ft./level wide, 2 ft./level high</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Prismatic wall</i> creates a vertical, opaque wall-a shimmering, multicolored plane of light that protects you from all forms of attack. The wall flashes with seven colors, each of which has a distinct power and purpose. The wall is immobile, and you can pass through and remain near the wall without harm. Any other creature with less than 8 HD that is within 20 feet of the wall is blinded by the colors for 2d4 rounds if it looks at the wall.</p><p>The wall's maximum proportions are 4 feet wide per caster level and 2 feet high per caster level. A <i>prismatic wall</i> spell cast to materialize in a space occupied by a creature is disrupted, and the spell is wasted.</p><p>Each color in the wall has a special effect. The accompanying table shows the seven colors of the wall, the order in which they appear, their effects on creatures trying to attack you or pass through the wall, and the magic needed to negate each color.</p><p>The wall can be destroyed, color by color, in consecutive order, by casting the specified spells on the wall; however, the first color must be brought down before the second can be affected, and so on. A <i>rod of cancellation</i> or a mage's disjunction spell destroys a <i>prismatic wall</i>, but an <i>antimagic field</i> fails to penetrate it. <i>Dispel magic</i> and <i>greater dispel magic</i> can only be used on the wall once all the other colors have been destroyed. Spell resistance is effective against a <i>prismatic wall</i>, but the caster level check must be repeated for each color present.</p><p><i>Prismatic wall</i> can be made permanent with a <i>permanency</i> spell.</p><p> <table><tr><th>Order</th><th>Color</th><th>Effect of Color</th><th>Negated by</th></tr><tr><td>1st</td><td>Red</td><td>Stops nonmagical ranged weapons. Deals 20 points of fire damage (Reflex half ).</td><td><i>Cone of cold</i></td></tr><tr><td>2nd</td><td>Orange</td><td>Stops magical ranged weapons. Deals 40 points of acid damage (Reflex half).</td><td><i>Gust of wind</i></td></tr><tr><td>3rd</td><td>Yellow</td><td>Stops poisons, gases, and petrification. Deals 80 points of electricity damage (Reflex half).</td><td><i>Disintegrate</i></td></tr><tr><td>4th</td><td>Green</td><td>Stops breath weapons. Poison (frequency: 1/rd. for 6 rd.; init. effect: death, sec. effect: 1 Con/rd.; cure 2 consecutive Fort saves).</td><td><i>Passwall</i></td></tr><tr><td>5th</td><td>Blue</td><td>Stops divination and mental attacks. Turned to stone (Fortitude negates).</td><td><i>Magic missile</i></td></tr><tr><td>6th</td><td>Indigo</td><td>Stops all spells. Will save or become insane (as insanity spell).</td><td><i>Daylight</i></td></tr><tr><td>7th</td><td>Violet</td><td>Energy field destroys all objects and effects.* Creatures sent to another plane (Will negates).</td><td><i>Dispel magic</i> or <i>greater dispel magic</i></td></tr><tr><td colspan=""4"">*The violet effect makes the special effects of the other six colors redundant, but these six effects are included here because certain magic items can create prismatic effects one color at a time, and spell resistance might render some colors ineffective (see above).</td></tr></table> </p></h4></div>"},
new Spell(){ Name = "Prying Eyes, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Prying Eyes, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>sorcerer/wizard 8, witch 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (a handful of crystal marbles)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>1 mile</h5><h5><b>Effect </b>10 or more levitating eyes</h5><h5><b>Duration </b>1 hour/level; see text (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>prying eyes,</i> except that the eyes can see all things as they actually are, just as if they had <i>true seeing</i> with a range of 120 feet. Thus, they can navigate darkened areas at normal speed. Also, a <i>greater prying eye</i>'s maximum Perception modifier is +25 instead of +15.</p></h4></div>"},
new Spell(){ Name = "Produce Flame", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Produce Flame</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>druid 1, shaman 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>flame in your palm</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Flames as bright as a torch appear in your open hand. The flames harm neither you nor your equipment.</p><p>In addition to providing illumination, the flames can be hurled or used to touch enemies. You can strike an opponent with a melee touch attack, dealing fire damage equal to 1d6 + 1 point per caster level (maximum +5). Alternatively, you can hurl the flames up to 120 feet as a thrown weapon. When doing so, you attack with a ranged touch attack (with no range penalty) and deal the same damage as with the melee attack. No sooner do you hurl the flames than a new set appears in your hand. Each attack you make reduces the remaining duration by 1 minute. If an attack reduces the remaining duration to 0 minutes or less, the spell ends after the attack resolves.</p><p>This spell does not function underwater.</p></h4></div>"},
new Spell(){ Name = "Programmed Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Programmed Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 6, sorcerer/wizard 6, psychic 6, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (fleece and jade dust worth 25 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>visual figment that cannot extend beyond a 20-ft. cube + one 10-ft. cube/level (S)</h5><h5><b>Duration </b>permanent until triggered, then 1 round/level</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>silent image,</i> except that this spell's figment activates when a specific condition occurs. The figment includes visual, auditory, olfactory, and thermal elements, including intelligible speech.</p><p>You set the triggering condition (which may be a special word) when casting the spell. The event that triggers the illusion can be as general or as specific and detailed as desired but must be based on an audible, tactile, olfactory, or visual trigger. The trigger cannot be based on some quality not normally obvious to the senses, such as alignment. See <i>magic mouth</i> for more details about such triggers.</p></h4></div>"},
new Spell(){ Name = "Project Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Project Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>bard 6, sorcerer/wizard 7, occultist 6, psychic 7, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a small replica of you worth 5 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>one shadow duplicate</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You tap energy from the Plane of Shadow to create a quasi-real, illusory version of yourself. The projected image looks, sounds, and smells like you but is intangible. The projected image mimics your actions (including speech) unless you direct it to act differently (which is a move action).</p><p>You can see through its eyes and hear through its ears as if you were standing where it is, and during your turn you can switch from using its senses to using your own, or back again, as a free action. While you are using its senses, your body is considered blinded and deafened.</p><p>If you desire, any spell you cast whose range is touch or greater can originate from the projected image instead of from you. The projected image can't cast any spells on itself except for illusion spells. The spells affect other targets normally, despite originating from the projected image.</p><p>Objects are affected by the projected image as if they had succeeded on their Will save. You must maintain line of effect to the projected image at all times. If your line of effect is obstructed, the spell ends. If you use <i>dimension door, teleport, plane shift,</i> or a similar spell that breaks your line of effect, even momentarily, the spell ends.</p></h4></div>"},
new Spell(){ Name = "Protection From Arrows", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Protection From Arrows</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 2, alchemist 2, summoner 2, bloodrager 2, psychic 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a piece of tortoiseshell or turtle shell)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 hour/level or until discharged</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The warded creature gains resistance to ranged weapons.</p><p>The subject gains damage reduction 10/magic against ranged weapons. This spell doesn't grant you the ability to damage creatures with similar damage reduction. Once the spell has prevented a total of 10 points of damage per caster level (maximum 100 points), it is discharged.</p></h4><h5><b>Mythic: </b>The damage reduction increases to 15/magic. The maximum damage prevented by the spell increases to 15 points per caster level (maximum 150 points). If the spell prevents all damage from a ranged weapon attack, it turns the projectile or weapon back on the attacker, using the same attack roll result. If the attacker is also protected by mythic protection from arrows and its spell completely prevents the damage from the rebounded attack, the weapon or projectile is destroyed.</h5></div>"},
new Spell(){ Name = "Protection From Chaos", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Protection From Chaos</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [lawful]; <b>Level </b>cleric/oracle 1, paladin 1, sorcerer/wizard 1, inquisitor 1, summoner 1, bloodrager 1, shaman 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>protection from evil</i>, except that the deflection and resistance bonuses apply to attacks made by chaotic creatures. The target receives a new saving throw against control by chaotic creatures and chaotic summoned creatures cannot touch the target.</p></h4><h5><b>Mythic: </b>The bonuses to AC and on saves increase to +4. Any chaotic creature attempting to possess or exercise mental control over the target must attempt a Will save against this spell. If it fails, the creature takes 1d6 points of damage per 2 caster levels (maximum 5d6) from mental feedback. A chaotic creature that uses spell resistance to bypass the spell's protection against contact must successfully save or take this damage once each round it attacks the protected target.</h5></div>"},
new Spell(){ Name = "Protection From Evil", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Protection From Evil</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [good]; <b>Level </b>cleric/oracle 1, inquisitor 1, paladin 1, sorcerer/wizard 1, summoner 1, bloodrager 1, shaman 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell wards a creature from attacks by evil creatures, from mental control, and from summoned creatures. It creates a <i>magic</i>al barrier around the subject at a distance of 1 foot. The barrier moves with the subject and has three major effects.</p><p>First, the subject gains a +2 deflection bonus to AC and a +2 resistance bonus on saves. Both these bonuses apply against attacks made or effects created by evil creatures.</p><p>Second, the subject immediately receives another saving throw (if one was allowed to begin with) against any spells or effects that possess or exercise mental control over the creature (including enchantment [charm] effects and enchantment [compulsion] effects such as <i>charm person</i>, <i>command</i>, and <i>dominate</i> person). This saving throw is made with a +2 morale bonus, using the same DC as the original effect.</p><p>If successful, such effects are suppressed for the duration of this spell. The effects resume when the duration of this spell expires.</p><p>While under the effects of this spell, the target is immune to any new attempts to possess or exercise mental control over the target.</p><p>This spell does not expel a controlling life force (such as a ghost or spellcaster using <i>magic</i> jar), but it does prevent them from controlling the target. This second effect only functions against spells and effects created by evil creatures or objects, subject to GM discretion.</p><p>Third, the spell prevents bodily contact by evil summoned creatures. This causes the natural weapon attacks of such creatures to fail and the creatures to recoil if such attacks require touching the warded creature. Summoned creatures that are not evil are immune to this effect. The protection against contact by summoned creatures ends if the warded creature makes an attack against or tries to force the barrier against the blocked creature. Spell resistance can allow a creature to overcome this protection and touch the warded creature.</p></h4><h5><b>Mythic: </b>The bonuses to AC and on saves increase to +4. Any evil creature attempting to possess or exercise mental control over the target must attempt a Will save against this spell. If it fails, the creature takes 1d6 points of damage per 2 caster levels (maximum 5d6) from mental feedback. An evil creature that uses spell resistance to bypass the spell's protection against contact must successfully save or take this damage once each round it attacks the protected target.</h5></div>"},
new Spell(){ Name = "Protection From Good", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Protection From Good</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [evil]; <b>Level </b>cleric/oracle 1, sorcerer/wizard 1, inquisitor 1, summoner 1, antipaladin 1, bloodrager 1, shaman 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>protection from evil</i>, except that the deflection and resistance bonuses apply to attacks made by good creatures. The target receives a new saving throw against control by good creatures and good summoned creatures cannot touch the target.</p></h4><h5><b>Mythic: </b>The bonuses to AC and on saves increase to +4. Any good creature attempting to possess or exercise mental control over the target must attempt a Will save against this spell. If it fails, the creature takes 1d6 points of damage per 2 caster levels (maximum 5d6) from mental feedback. A good creature that uses spell resistance to bypass the spell's protection against contact must successfully save or take this damage once each round it attacks the protected target.</h5></div>"},
new Spell(){ Name = "Protection From Law", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Protection From Law</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [chaotic]; <b>Level </b>cleric/oracle 1, sorcerer/wizard 1, inquisitor 1, summoner 1, antipaladin 1, bloodrager 1, shaman 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>protection from evil</i>, except that the deflection and resistance bonuses apply to attacks made by lawful creatures.</p><p>The target receives a new saving throw against control by lawful creatures and lawful summoned creatures cannot touch the target.</p></h4><h5><b>Mythic: </b>The bonuses to AC and on saves increase to +4. Any lawful creature attempting to possess or exercise mental control over the target must attempt a Will save against this spell. If it fails, the creature takes 1d6 points of damage per 2 caster levels (maximum 5d6) from mental feedback. A lawful creature that uses spell resistance to bypass the spell's protection against contact must successfully save or take this damage once each round it attacks the protected target.</h5></div>"},
new Spell(){ Name = "Protection from Spells", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Protection from Spells</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 8, summoner 6, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (diamond worth 500 gp), F (One 1,000 gp diamond per target. Each subject must carry the gem for the duration of the spell. If a subject loses the gem, the spell ceases to affect him.)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>up to one creature touched per four levels</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject gains a +8 resistance bonus on saving throws against spells and spell-like abilities (but not against supernatural and extraordinary abilities).</p></h4></div>"},
new Spell(){ Name = "Purify Food and Drink", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Purify Food and Drink</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 0, druid 0, shaman 0, occultist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Targets </b>1 cu. ft./level of contaminated food and water</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell makes spoiled, rotten, diseased, poisonous, or otherwise contaminated food and water pure and suitable for eating and drinking. This spell does not prevent subsequent natural decay or spoilage. Unholy water and similar food and drink of significance is spoiled by <i>purify food and drink,</i> but the spell has no effect on creatures of any type nor upon magic potions. Water weighs about 8 pounds per gallon. One cubic foot of water contains roughly 8 gallons and weighs about 60 pounds.</p></h4></div>"},
new Spell(){ Name = "Pyrotechnics", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Pyrotechnics</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 2, sorcerer/wizard 2, magus 2, bloodrager 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (one fire source)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Targets </b>one fire source, up to a 20-ft. cube</h5><h5><b>Duration </b>1d4+1 rounds, or 1d4+1 rounds after creatures leave the smoke cloud; see text</h5><h5><b>Saving Throw </b>Will negates or Fortitude negates; see text; <b>Spell Resistance </b>yes or no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Pyrotechnics</i> turns a fire into a burst of blinding fireworks or a thick cloud of choking smoke, depending on your choice. The spell uses one fire source, which is immediately extinguished. A fire so large that it exceeds a 20-foot cube is only partly extinguished. Magical fires are not extinguished, although a fire-based creature used as a source takes 1 point of damage per caster level.</p><p><i>Fireworks:</i> The fireworks are a flashing, fiery, momentary burst of glowing, colored aerial lights. This effect causes creatures within 120 feet of the fire source to become blinded for 1d4+1 rounds (Will negates). These creatures must have line of sight to the fire to be affected. Spell resistance can prevent blindness.</p><p><i>Smoke Cloud</i>: A stream of smoke billows out from the fire, forming a choking cloud that spreads 20 feet in all directions and lasts for 1 round per caster level. All sight, even darkvision, is ineffective in or through the cloud. All within the cloud take -4 penalties to Strength and Dexterity (Fortitude negates). These effects last for 1d4+1 rounds after the cloud dissipates or after the creature leaves the area of the cloud. Spell resistance does not apply.</p></h4></div>"},
new Spell(){ Name = "Quench", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Quench</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>one 20-ft. cube/level (S)</h5><h5><b>Targets </b>one fire-based magic item</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none or Will negates (object); <b>Spell Resistance </b>no or yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Quench</i> is often used to put out forest fires and other conflagrations. It extinguishes all nonmagical fires in its area.</p><p>The spell also dispels any fire spells in its area, though you must succeed on a dispel check (1d20 +1 per caster level, maximum +15) against each spell to dispel it. The DC to dispel such spells is 11 + the caster level of the fire spell.</p><p>Each creature with the fire subtype within the area of a <i>quench</i> spell takes 1d6 points of damage per caster level (maximum 10d6, no save allowed).</p><p>Alternatively, you can target the spell on a single magic item that creates or controls flame. The item loses all its fire-based magical abilities for 1d4 hours unless it succeeds on a Will save. Artifacts are immune to this effect.</p></h4></div>"},
new Spell(){ Name = "Rage", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Rage</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, emotion]; <b>Level </b>bard 2, sorcerer/wizard 3, alchemist 3, summoner 3, witch 3, alchemist 3, summoner 3, witch 3, bloodrager 3, psychic 3, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one willing living creature per three levels, no two of which may be more than 30 ft. apart</h5><h5><b>Duration </b>concentration + 1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Each affected creature gains a +2 morale bonus to Strength and Constitution, a +1 morale bonus on Will saves, and a -2 penalty to AC. The effect is otherwise identical with a barbarian's rage except that the subjects aren't fatigued at the end of the rage.</p></h4></div>"},
new Spell(){ Name = "Rainbow Pattern", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Rainbow Pattern</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (pattern) [mind-affecting]; <b>Level </b>bard 4, sorcerer/wizard 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V (bard only), S, M (a piece of phosphor), F (a crystal prism); see text</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>colorful lights with a 20-ft.-radius spread</h5><h5><b>Duration </b>Concentration +1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A glowing, rainbow-hued pattern of interweaving colors fascinates those within it. <i>Rainbow pattern</i> fascinates a maximum of 24 HD of creatures. Creatures with the fewest HD are affected first. Among creatures with equal HD, those who are closest to the spell's point of origin are affected first. An affected creature that fails its saves is fascinated by the pattern.</p><p>With a simple gesture (a free action), you can make the <i>rainbow pattern</i> move up to 30 feet per round (moving its effective point of origin). All fascinated creatures follow the moving rainbow of light, trying to remain within the effect. Fascinated creatures who are restrained and removed from the pattern still try to follow it. If the pattern leads its subjects into a dangerous area, each fascinated creature gets a second save. If the view of the lights is completely blocked, creatures who can't see them are no longer affected.</p><p>The spell does not affect sightless creatures.</p></h4></div>"},
new Spell(){ Name = "Raise Dead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Raise Dead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 5, witch 6, shaman 6, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (diamond worth 5,000 gp), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>dead creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You restore life to a deceased creature. You can raise a creature that has been dead for no longer than 1 day per caster level. In addition, the subject's soul must be free and willing to return.</p><p>If the subject's soul is not willing to return, the spell does not work; therefore, a subject that wants to return receives no saving throw.</p><p>Coming back from the dead is an ordeal. The subject of the spell gains two permanent negative levels when it is raised, just as if it had been hit by an energy-draining creature. If the subject is 1st level, it takes 2 points of Constitution drain instead (if this would reduce its Con to 0 or less, it can't be raised). A character who died with spells prepared has a 50% chance of losing any given spell upon being raised. A spellcasting creature that doesn't prepare spells (such as a sorcerer) has a 50% chance of losing any given unused spell slot as if it had been used to cast a spell.</p><p>A raised creature has a number of hit points equal to its current HD. Any ability scores damaged to 0 are raised to 1. Normal poison and normal disease are cured in the process of raising the subject, but magical diseases and curses are not undone. While the spell closes mortal wounds and repairs lethal damage of most kinds, the body of the creature to be raised must be whole.</p><p>Otherwise, missing parts are still missing when the creature is brought back to life. None of the dead creature's equipment or possessions are affected in any way by this spell.</p><p>A creature who has been turned into an undead creature or killed by a death effect can't be raised by this spell. Constructs, elementals, outsiders, and undead creatures can't be raised. The spell cannot bring back a creature that has died of old age.</p></h4></div>"},
new Spell(){ Name = "Ray Of Enfeeblement", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ray Of Enfeeblement</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 1, witch 1, magus 1, bloodrager 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Fortitude half; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A coruscating ray springs from your hand. You must succeed on a ranged touch attack to strike a target. The subject takes a penalty to Strength equal to 1d6+1 per two caster levels (maximum 1d6+5).</p><p>The subject's Strength score cannot drop below 1. A successful Fortitude save reduces this penalty by half. This penalty does not stack with itself. Apply the highest penalty instead.</p></h4><h5><b>Mythic: </b>The Strength penalty increases to 1d8 + 1 per 2 caster levels (maximum +10). If the target fails its save, it's fatigued.</h5></div>"},
new Spell(){ Name = "Ray of Exhaustion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ray of Exhaustion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 3, witch 3, magus 3, bloodrager 3, mesmerist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a drop of sweat)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Fortitude partial; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A black ray projects from your pointing finger. You must succeed on a ranged touch attack with the ray to strike a target.</p><p>The subject is immediately exhausted for the spell's duration. A successful Fortitude save means the creature is only fatigued.</p><p>A character that is already fatigued instead becomes exhausted.</p><p>This spell has no effect on a creature that is already exhausted.</p><p>Unlike normal exhaustion or fatigue, the effect ends as soon as the spell's duration expires.</p></h4></div>"},
new Spell(){ Name = "Ray of Frost", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Ray of Frost</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [cold]; <b>Level </b>sorcerer/wizard 0, magus 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A ray of freezing air and ice projects from your pointing finger.</p><p>You must succeed on a ranged touch attack with the ray to deal damage to a target. The ray deals 1d3 points of cold damage.</p></h4></div>"},
new Spell(){ Name = "Read Magic", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Read Magic</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 0, cleric/oracle 0, druid 0, paladin 1, ranger 1, sorcerer/wizard 0, summoner 0, witch 0, inquisitor 0, magus 0, shaman 0, occultist 0, psychic 0, mesmerist 0, spiritualist 0, antipaladin 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a clear crystal or mineral prism)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>10 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can decipher magical inscriptions on objects-books, scrolls, weapons, and the like-that would otherwise be unintelligible. This deciphering does not normally invoke the magic contained in the writing, although it may do so in the case of a cursed or trapped scroll. Furthermore, once the spell is cast and you have read the magical inscription, you are thereafter able to read that particular writing without recourse to the use of <i>read magic.</i> You can read at the rate of one page (250 words) per minute. The spell allows you to identify a <i>glyph of warding</i> with a DC 13 Spellcraft check, a greater <i>glyph of warding</i> with a DC 16 Spellcraft check, or any <i>symbol</i> spell with a Spellcraft check (DC 10 + spell level).</p><p><i>Read magic</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Reduce Person", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Reduce Person</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 1, alchemist 1, summoner 1, witch 1, magus 1, bloodrager 1, occultist 1, psychic 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (a pinch of powdered iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one humanoid creature</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes instant diminution of a humanoid creature, halving its height, length, and width and dividing its weight by 8. This decrease changes the creature's size category to the next smaller one. The target gains a +2 size bonus to Dexterity, a -2 size penalty to Strength (to a minimum of 1), and a +1 bonus on attack rolls and AC due to its <i>reduced</i> size.</p><p>A Small humanoid creature whose size decreases to Tiny has a space of 2-1/2 feet and a natural reach of 0 feet (meaning that it must enter an opponent's square to attack). A Large humanoid creature whose size decreases to Medium has a space of 5 feet and a natural reach of 5 feet. This spell doesn't change the target's speed.</p><p>All equipment worn or carried by a creature is similarly <i>reduced</i> by the spell.</p><p>Melee and projectile weapons deal less damage. Other magical properties are not affected by this spell. Any <i>reduced</i> item that leaves the <i>reduced</i> creature's possession (including a projectile or thrown weapon) instantly returns to its normal size. This means that thrown weapons deal their normal damage (projectiles deal damage based on the size of the weapon that fired them).</p><p>Multiple magical effects that reduce size do not stack. <i>Reduce person</i> counters and dispels <i>enlarge person</i>.</p><p><i>Reduce person</i> can be made permanent with a <i>permanency</i> spell.</p></h4><h5><b>Mythic: </b>You can decrease the target's size by up to two categories, to a minimum of Tiny. If the target shrinks two sizes, its weight decreases by a factor of 16 and it gains a +4 size bonus to Dexterity, a -4 size penalty to Strength, and a +2 bonus on attack rolls and to AC because of its decreased size. A Medium or Small humanoid creature whose size decreases to Tiny has a space of 2-1/2 feet and a natural reach of 0 feet. Mythic reduce person counters and dispels mythic enlarge person.</h5></div>"},
new Spell(){ Name = "Reduce Animal", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Reduce Animal</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 2, ranger 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one willing animal of Small, Medium, Large, or Huge size</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>reduce person,</i> except that it affects a single willing animal. Reduce the damage dealt by the animal's natural attacks as appropriate for its new size (see Chapter 6 how to adjust damage for size).</p></h4></div>"},
new Spell(){ Name = "Reduce Person, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Reduce Person, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 4, summoner 3, bloodrager 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (a pinch of powdered iron)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one humanoid creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Fortitude negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>reduce person</i>, except that it affects multiple creatures.</p></h4></div>"},
new Spell(){ Name = "Refuge", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Refuge</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (teleportation); <b>Level </b>cleric/oracle 7, sorcerer/wizard 9, witch 9, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a prepared object worth 1,500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>object touched</h5><h5><b>Duration </b>permanent until discharged</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you cast this spell, you create powerful magic in a specially prepared object. This object contains the power to instantly transport its possessor across any distance within the same plane to your abode. Once the item is so enhanced, you must give it willingly to a creature and at the same time inform it of a command word to be spoken when the item is used. To make use of the item, the subject speaks the command word at the same time that it rends or breaks the item (a standard action). When this is done, the individual and all objects it is wearing and carrying (to a maximum of the character's heavy load) are instantly transported to your abode. No other creatures are affected (aside from a familiar or animal companion that is touching the subject).</p><p>You can alter the spell when casting it so that it transports you to within 10 feet of the possessor of the item when it is broken and the command word spoken. You will have a general idea of the location and situation of the item possessor at the time the <i>refuge</i> spell is discharged, but once you decide to alter the spell in this fashion, you have no choice whether or not to be transported.</p></h4></div>"},
new Spell(){ Name = "Regenerate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Regenerate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 7, druid 9, witch 7, shaman 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>3 full rounds</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject's severed body members (fingers, toes, hands, feet, arms, legs, tails, or even heads of multiheaded creatures), broken bones, and ruined organs grow back. After the spell is cast, the physical regeneration is complete in 1 round if the severed members are present and touching the creature. It takes 2d10 rounds otherwise.</p><p><i>Regenerate</i> also cures 4d8 points of damage + 1 point per caster level (maximum +35), rids the subject of exhaustion and fatigue, and eliminates all nonlethal damage the subject has taken. It has no effect on nonliving creatures (including undead).</p></h4><h5><b>Mythic: </b>The casting time changes to 1 standard action and the target changes to 1 living creature or corpse touched. The spell removes the blinded, dazzled, and deafened conditions. The target gains regeneration equal to your tier (overcome by acid and fire) for 1 round per caster level. If the target was killed by amputation or decapitation (such as from a vorpal weapon) within the past 1 round, the spell regenerates lost body parts and brings the creature back to life (as breath of life), treating it as if it had negative hp equal to its Constitution score.</h5></div>"},
new Spell(){ Name = "Reincarnate", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Reincarnate</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 4, witch 5, shaman 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M, DF (oils worth 1,000 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>dead creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>With this spell, you bring back a dead creature in another body, provided that its death occurred no more than 1 week before the casting of the spell and the subject's soul is free and willing to return. If the subject's soul is not willing to return, the spell does not work; therefore, a subject that wants to return receives no saving throw.</p><p>Since the dead creature is returning in a new body, all physical ills and afflictions are repaired. The condition of the remains is not a factor. So long as some small portion of the creature's body still exists, it can be reincarnated, but the portion receiving the spell must have been part of the creature's body at the time of death.</p><p>The magic of the spell creates an entirely new young adult body for the soul to inhabit from the natural elements at hand. This process takes 1 hour to complete. When the body is ready, the subject is reincarnated.</p><p>A reincarnated creature recalls the majority of its former life and form. It retains any class abilities, feats, or skill ranks it formerly possessed. Its class, base attack bonus, base save bonuses, and hit points are unchanged. Strength, Dexterity, and Constitution scores depend partly on the new body. First eliminate the subject's racial adjustments (since it is no longer necessarily of his previous race) and then apply the adjustments found below to its remaining ability scores. The subject of the spell gains two permanent negative levels when it is reincarnated. If the subject is 1st level, it takes 2 points of Constitution drain instead (if this would reduce its Con to 0 or less, it can't be reincarnated). A character who died with spells prepared has a 50% chance of losing any given spell upon being reincarnated. A spellcreature that doesn't prepare spells (such as a sorcerer) has a 50% chance of losing any given unused spell slot as if it had been used to cast a spell.</p><p>It's possible for the change in the subject's ability scores to make it difficult for it to pursue its previous character class. If this is the case, the subject is advised to become a multiclass character.</p><p>For a humanoid creature, the new incarnation is determined using the table on the next page. For nonhumanoid creatures, a similar table of creatures of the same type should be created.</p><p>A creature that has been turned into an undead creature or killed by a death effect can't be returned to life by this spell.</p><p>Constructs, elementals, outsiders, and undead creatures can't be reincarnated. The spell can bring back a creature that has died of old age.</p><p> <table><tr><th>d%</th><th>Incarnation</th><th>Str</th><th>Dex</th><th>Con</th></tr><tr><td>01</td><td>Bugbear</td><td>+4</td><td>+2</td><td>+2</td></tr><tr><td>02-13</td><td>Dwarf</td><td>+0</td><td>+0</td><td>+2</td></tr><tr><td>14-25</td><td>Elf</td><td>+0</td><td>+2</td><td>-2</td></tr><tr><td>26</td><td>Gnoll</td><td>+4</td><td>+0</td><td>+2</td></tr><tr><td>27-38</td><td>Gnome</td><td>-2</td><td>+0</td><td>+2</td></tr><tr><td>39-42</td><td>Goblin</td><td>-2</td><td>+2</td><td>+0</td></tr><tr><td>43-52</td><td>Half-elf</td><td>+0</td><td>+2</td><td>+0</td></tr><tr><td>53-62</td><td>Half-orc</td><td>+2</td><td>+0</td><td>+0</td></tr><tr><td>63-74</td><td>Halfling</td><td>-2</td><td>+2</td><td>+0</td></tr><tr><td>75-89</td><td>Human</td><td>+0</td><td>+0</td><td>+2</td></tr><tr><td>90-93</td><td>Kobold</td><td>-4</td><td>+2</td><td>-2</td></tr><tr><td>94</td><td>Lizardfolk</td><td>+2</td><td>+0</td><td>+2</td></tr><tr><td>95-98</td><td>Orc</td><td>+4</td><td>+0</td><td>+0</td></tr><tr><td>99</td><td>Troglodyte</td><td>+0</td><td>-2</td><td>+4</td></tr><tr><td>100</td><td>Other (GM's choice)</td><td>?</td><td>?</td><td>?</td></tr></table>  The reincarnated creature gains all abilities associated with its new form, including forms of movement and speeds, natural armor, natural attacks, extraordinary abilities, and the like, but it doesn't automatically speak the language of the new form.</p><p>A <i>wish</i> or a <i>miracle</i> spell can restore a reincarnated character to his or her original form.</p></h4></div>"},
new Spell(){ Name = "Remove Blindness/Deafness", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Remove Blindness/Deafness</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 3, paladin 3, alchemist 3, witch 3, shaman 3, mesmerist 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Remove blindness/deafness</i> cures blindness or deafness (your choice), whether the effect is normal or magical in nature. The spell does not restore ears or eyes that have been lost, but it repairs them if they are damaged.</p><p><i>Remove blindness/deafness</i> counters and dispels blindness/deafness.</p></h4></div>"},
new Spell(){ Name = "Remove Curse", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Remove Curse</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 3, cleric/oracle 3, paladin 3, sorcerer/wizard 4, alchemist 3, witch 3, inquisitor 3, shaman 3, mesmerist 3, spiritualist 3, alchemist 3, witch 3, inquisitor 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature or object touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Remove <i>curse</i></i> can remove all <i>curse</i>s on an object or a creature. If the target is a creature, you must make a caster level check (1d20 + caster level) against the DC of each <i>curse</i> affecting the target.</p><p>Success means that the <i>curse</i> is removed. <i>Remove <i>curse</i></i> does not remove the <i>curse</i> from a <i>curse</i>d shield, weapon, or suit of armor, although a successful caster level check enables the creature afflicted with any such <i>curse</i>d item to remove and get rid of it.</p><p><i>Remove <i>curse</i></i> counters and dispels bestow <i>curse</i>.</p></h4></div>"},
new Spell(){ Name = "Remove Disease", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Remove Disease</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 3, druid 3, ranger 3, alchemist 3, witch 3, inquisitor 3, alchemist 3, witch 3, inquisitor 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Remove disease</i> can cure all diseases from which the subject is suffering. You must make a caster level check (1d20 + caster level) against the DC of each disease affecting the target. Success means that the disease is cured. The spell also kills some hazards and parasites, including green slime and others.</p><p>Since the spell's duration is instantaneous, it does not prevent reinfection after a new exposure to the same disease at a later date.</p></h4></div>"},
new Spell(){ Name = "Remove Fear", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Remove Fear</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 1, cleric/oracle 1, inquisitor 1, shaman 1, psychic 1, mesmerist 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature plus one additional creature per four levels, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>10 minutes; see text</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You instill courage in the subject, granting it a +4 morale bonus against fear effects for 10 minutes. If the subject is under the influence of a fear effect when receiving the spell, that effect is suppressed for the duration of the spell.</p><p><i>Remove fear</i> counters and dispels cause fear.</p></h4></div>"},
new Spell(){ Name = "Remove Paralysis", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Remove Paralysis</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 2, paladin 2, inquisitor 2, shaman 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>up to four creatures, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can free one or more creatures from the effects of temporary paralysis or related magic, including spells and effects that cause a creature to gain the staggered condition. If the spell is cast on one creature, the paralysis is negated. If cast on two creatures, each receives another save with a +4 resistance bonus against the effect that afflicts it. If cast on three or four creatures, each receives another save with a +2 resistance bonus.</p><p>The spell does not restore ability scores reduced by penalties, damage, or drain.</p></h4></div>"},
new Spell(){ Name = "Repel Metal or Stone", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Repel Metal or Stone</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [earth]; <b>Level </b>druid 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>60-ft. line from you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates waves of invisible energy that roll forth from you. All metal or stone objects in the path of the spell are pushed away from you to the limit of the range. Fixed metal or stone objects larger than 3 inches in diameter and loose objects weighing more than 500 pounds are not affected. Anything else, including animated objects, small boulders, and creatures in metal armor, moves back. Fixed objects 3 inches in diameter or smaller bend or break, and the pieces move with the wave of energy. Objects affected by the spell are repelled at the rate of 40 feet per round.</p><p>Objects such as metal armor, swords, and the like are pushed back, dragging their bearers with them. Even magic items with metal components are repelled, although an <i>antimagic field</i> blocks the effects. A creature being dragged by an item it is carrying can let go. A creature being dragged by a shield can loose it as a move action and drop it as a free action.</p><p>The waves of energy continue to sweep down the set path for the spell's duration. After you cast the spell, the path is set, and you can then do other things or go elsewhere without affecting the spell's power.</p></h4></div>"},
new Spell(){ Name = "Repel Vermin", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Repel Vermin</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [pain]; <b>Level </b>bard 4, cleric/oracle 4, druid 4, ranger 3, shaman 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Area </b>10-ft.-radius emanation centered on you</h5><h5><b>Duration </b>10 min./level (D)</h5><h5><b>Saving Throw </b>none or Will negates; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An invisible barrier holds back vermin. A vermin with HD of less than one-third your level cannot penetrate the barrier.</p><p>A vermin with HD of one-third your level or more can penetrate the barrier if it succeeds on a Will save. Even so, crossing the barrier deals the vermin 2d6 points of damage, and pressing against the barrier causes pain, which deters most vermin.</p></h4></div>"},
new Spell(){ Name = "Repel Wood", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Repel Wood</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>60-ft. line-shaped emanation from you</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Waves of energy roll forth from you, moving in the direction that you determine, causing all wooden objects in the path of the spell to be pushed away from you to the limit of the range. Wooden objects larger than 3 inches in diameter that are fixed firmly are not affected, but loose objects are. Objects 3 inches in diameter or smaller that are fixed in place splinter and break, and the pieces move with the wave of energy. Objects affected by the spell are repelled at the rate of 40 feet per round.</p><p>Objects such as wooden shields, spears, wooden weapon shafts and hafts, and arrows and bolts are pushed back, dragging those carrying them along. A creature being dragged by an item it is carrying can let go. A creature being dragged by a shield can loose it as a move action and drop it as a free action. If a spear is planted (set) in a way that prevents this forced movement, it splinters.</p><p>Even magic items with wooden sections are repelled, although an <i>antimagic field</i> blocks the effects.</p><p>The waves of energy continue to sweep down the set path for the spell's duration. After you cast the spell, the path is set, and you can then do other things or go elsewhere without affecting the spell's power.</p></h4><h5><b>Mythic: </b>The casting time changes to 1 immediate action. You may cause one affected wooden object up to 5 inches in diameter to splinter and break.</h5><h5><b>Augmented (3rd)</b>: If you expend two uses of mythic power, the area changes to a 60-foot, cone-shaped emanation from you.</h5></div>"},
new Spell(){ Name = "Repulsion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Repulsion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 7, sorcerer/wizard 6, summoner 5, inquisitor 6, occultist 6, psychic 7, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a pair of canine statuettes worth 50 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>up to 10 ft./level</h5><h5><b>Area </b>up to 10-ft.-radius/level emanation centered on you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An invisible, mobile field surrounds you and prevents creatures from approaching you. You decide how big the field is at the time of casting (to the limit your level allows). Any creature within or entering the field must attempt a save. If it fails, it becomes unable to move toward you for the duration of the spell. Repelled creatures' actions are not otherwise restricted. They can fight other creatures and can cast spells and attack you with ranged weapons.</p><p>If you move closer to an affected creature, nothing happens. The creature is not forced back. The creature is free to make melee attacks against you if you come within reach. If a repelled creature moves away from you and then tries to turn back toward you, it cannot move any closer if it is still within the spell's area.</p></h4></div>"},
new Spell(){ Name = "Resilient Sphere", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Resilient Sphere</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>sorcerer/wizard 4, occultist 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a crystal sphere)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>1-ft.-diameter/level sphere, centered around a creature</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>Reflex negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A globe of shimmering force encloses a creature, provided the creature is small enough to fit within the diameter of the sphere.</p><p>The sphere contains its subject for the spell's duration. The sphere functions as a <i>wall of force</i>, except that it can be negated by dispel magic. A subject inside the sphere can breathe normally.</p><p>The sphere cannot be physically moved either by people outside it or by the struggles of those within.</p></h4></div>"},
new Spell(){ Name = "Resistance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Resistance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>bard 0, cleric/oracle 0, druid 0, paladin 1, sorcerer/wizard 0, summoner 0, witch 0, inquisitor 0, shaman 0, occultist 0, psychic 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a miniature cloak)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 minute</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You imbue the subject with magical energy that protects it from harm, granting it a +1 resistance bonus on saves.</p><p><i>Resistance</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Resist Energy", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Resist Energy</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>alchemist 2, cleric/oracle 2, druid 2, inquisitor 2, paladin 2, ranger 1, sorcerer/wizard 2, summoner 2, bloodrager 2, shaman 2, occultist 2, psychic 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Fortitude negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This abjuration grants a creature limited <i>protection</i> from damage of whichever one of five energy types you select: acid, cold, electricity, fire, or sonic. The subject gains <i>resist energy</i> 10 against the energy type chosen, meaning that each time the creature is subjected to such damage (whether from a natural or magical source), that damage is reduced by 10 points before being applied to the creature's hit points. The value of the energy resistance granted increases to 20 points at 7th level and to a maximum of 30 points at 11th level. The spell protects the recipient's equipment as well.</p><p><i>Resist energy</i> absorbs only damage. The subject could still suffer unfortunate side effects.</p><p><i>Resist energy</i> overlaps (and does not stack with) <i><i>protection</i> from energy</i>. If a character is warded by <i><i>protection</i> from energy</i> and <i>resist energy</i>, the <i>protection</i> spell absorbs damage until its power is exhausted.</p></h4><h5><b>Mythic: </b>The target gains a +4 bonus on saves against effects that deal damage of the selected energy type. Once each round as a free action, the target can choose one adjacent ally to gain energy resistance 5 against the chosen energy type for 1 round. This increases to energy resistance 10 at caster level 7 and energy resistance 15 at caster level 11.</h5></div>"},
new Spell(){ Name = "Restoration", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Restoration</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 4, paladin 4, shaman 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>3 rounds</h5><h5><b>Components </b>V, S, M (diamond dust worth 100 gp or 1,000 gp, see text)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser restoration</i>, except that it also dispels temporary negative levels or one permanent negative level. If this spell is used to dispel a permanent negative level, it has a material component of diamond dust worth 1,000 gp. This spell cannot be used to dispel more than one permanent negative level possessed by a target in a 1-week period.</p><p><i>Restoration</i> cures all temporary ability damage, and it restores all points permanently drained from a single ability score (your choice if more than one is drained). It also eliminates any fatigue or exhaustion suffered by the target.</p></h4></div>"},
new Spell(){ Name = "Restoration, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Restoration, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 7, shaman 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (diamond dust 5,000 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>lesser restoration,</i> except that it dispels all permanent and temporary negative levels afflicting the healed creature. <i>Greater restoration</i> also dispels all magical effects penalizing the creature's abilities, cures all temporary ability damage, and restores all points permanently drained from all ability scores. It also eliminates fatigue and exhaustion, and removes all forms of insanity, confusion, and similar mental effects.</p></h4></div>"},
new Spell(){ Name = "Restoration, Lesser", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Restoration, Lesser</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 2, druid 2, paladin 1, inquisitor 2, alchemist 2, shaman 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>3 rounds</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Lesser restoration</i> dispels any magical effects reducing one of the subject's ability scores or cures 1d4 points of temporary ability damage to one of the subject's ability scores. It also eliminates any fatigue suffered by the character, and improves an exhausted condition to fatigued. It does not restore permanent ability drain.</p></h4></div>"},
new Spell(){ Name = "Resurrection", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Resurrection</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 7, witch 8, shaman 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 minute</h5><h5><b>Components </b>V, S, M (diamond worth 10,000 gp), DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>dead creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>raise dead,</i> except that you are able to restore life and complete strength to any deceased creature.</p><p>The condition of the remains is not a factor. So long as some small portion of the creature's body still exists, it can be resurrected, but the portion receiving the spell must have been part of the creature's body at the time of death. (The remains of a creature hit by a <i>disintegrate</i> spell count as a small portion of its body.) The creature can have been dead no longer than 10 years per caster level.</p><p>Upon completion of the spell, the creature is immediately restored to full hit points, vigor, and health, with no loss of prepared spells.</p><p>The subject of the spell gains one permanent negative level when it is raised, just as if it had been hit by an energy-draining creature. If the subject is 1st level, it takes 2 points of Constitution drain instead (if this would reduce its Con to 0 or less, it can't be resurrected).</p><p>You can resurrect someone killed by a death effect or someone who has been turned into an undead creature and then destroyed.</p><p>You cannot resurrect someone who has died of old age. Constructs, elementals, outsiders, and undead creatures can't be resurrected.</p></h4></div>"},
new Spell(){ Name = "Reverse Gravity", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Reverse Gravity</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 8, sorcerer/wizard 7, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (lodestone and iron filings)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>up to one 10-ft. cube/level (S)</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell reverses gravity in an area, causing unattached objects and creatures in the area to fall upward and reach the top of the area in 1 round. If a solid object (such as a ceiling) is encountered in this fall, falling objects and creatures strike it in the same manner as they would during a normal downward fall. If an object or creature reaches the top of the area without striking anything, it remains there, oscillating slightly, until the spell ends. At the end of the spell duration, affected objects and creatures fall downward.</p><p>Provided it has something to hold onto, a creature caught in the area can attempt a Reflex save to secure itself when the spell strikes.</p><p>Creatures who can fly or levitate can keep themselves from falling.</p></h4><h5><b>Mythic: </b>Creatures in the area or that enter the area must succeed at a Fortitude save or be nauseated.</h5><h5><b>Augmented (8th)</b>: If you expend three uses of mythic power, once per round as a move action you may select one secured creature (one that succeeded at its Reflex save) or attached object (such as a tree or cottage) and force it to attempt a Fortitude save against the spell. The selected creature or object can weigh no more than 100 pounds per caster level. If it fails the save, it's pulled free and falls upward.</h5></div>"},
new Spell(){ Name = "Righteous Might", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Righteous Might</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>cleric/oracle 5, inquisitor 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 round/level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Your height immediately doubles, and your weight increases by a factor of eight. This increase changes your size category to the next larger one. You gain a +4 size bonus to Strength and Constitution and take a -2 penalty to your Dexterity. You gain a +2 enhancement bonus to your natural armor. You gain DR 5/ evil (if you normally channel positive energy) or DR 5/good (if you normally channel negative energy). At 15th level, this DR becomes 10/evil or 10/good (the maximum). Your size modifier for AC and attacks changes as appropriate to your new size category. This spell doesn't change your speed. Determine space and reach as appropriate to your new size.</p><p>If insufficient room is available for the desired growth, you attain the maximum possible size and may make a Strength check (using your increased Strength) to burst any enclosures in the process (see Chapter 7 for rules on breaking objects). If you fail, you are constrained without harm by the materials enclosing you-the spell cannot crush you by increasing your size.</p><p>All equipment you wear or carry is similarly enlarged by the spell.</p><p>Melee weapons deal more damage. Other magical properties are not affected by this spell. Any enlarged item that leaves your possession (including a projectile or thrown weapon) instantly returns to its normal size. This means that thrown and projectile weapons deal their normal damage. Magical effects that increase size do not stack.</p></h4></div>"},
new Spell(){ Name = "Rope Trick", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Rope Trick</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (powdered corn and a twisted loop of parchment)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched piece of rope from 5 ft. to 30 ft. long</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When this spell is cast upon a piece of rope from 5 to 30 feet long, one end of the rope rises into the air until the whole rope hangs perpendicular to the ground, as if affixed at the upper end.</p><p>The upper end is, in fact, fastened to an extradimensional space that is outside the usual multiverse of extradimensional spaces.</p><p>Creatures in the extradimensional space are hidden, beyond the reach of spells (including divinations), unless those spells work across planes. The space holds as many as eight creatures (of any size). The rope cannot be removed or hidden. The rope can support up to 16,000 pounds. A weight greater than that can pull the rope free.</p><p>Spells cannot be cast across the extradimensional interface, nor can area effects cross it. Those in the extradimensional space can see out of it as if a 3-foot-by-5-foot window were centered on the rope. The window is invisible, and even creatures that can see the window can't see through it. Anything inside the extradimensional space drops out when the spell ends. The rope can be climbed by only one person at a time. The <i>rope trick</i> spell enables climbers to reach a normal place if they do not climb all the way to the extradimensional space.</p></h4></div>"},
new Spell(){ Name = "Rusting Grasp", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Rusting Grasp</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 4, occultist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one nonmagical ferrous object (or the volume of the object within 3 ft. of the touched point) or one ferrous creature</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any iron or iron alloy item you touch crumbles into rust. If the item is so large that it cannot fit within a 3-foot radius, a 3-footradius volume of the metal is rusted and destroyed. Magic items made of metal are immune to this spell.</p><p>You may employ <i>rusting grasp</i> in combat with a successful melee touch attack. <i>Rusting grasp</i> used in this way instantaneously destroys 1d6 points of AC gained from metal armor (to the maximum amount of protection the armor offers) through corrosion.</p><p>Weapons in use by an opponent targeted by the spell are more difficult to grasp. You must succeed on a melee touch attack against the weapon. A metal weapon that is hit is destroyed. Striking at an opponent's weapon provokes an attack of opportunity. Also, you must touch the weapon and not the other way around.</p><p>Against a ferrous creature, <i>rusting grasp</i> instantaneously deals 3d6 points of damage + 1 per caster level (maximum +15) per successful attack. The spell lasts for 1 round per level, and you can make one melee touch attack per round.</p></h4></div>"},
new Spell(){ Name = "Sanctuary", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sanctuary</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 1, inquisitor 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any opponent attempting to directly attack the warded creature, even with a targeted spell, must attempt a Will save. If the save succeeds, the opponent can attack normally and is unaffected by that casting of the spell. If the save fails, the opponent can't follow through with the attack, that part of its action is lost, and it can't directly attack the warded creature for the duration of the spell.</p><p>Those not attempting to attack the subject remain unaffected. This spell does not prevent the warded creature from being attacked or affected by area of effect spells. The subject cannot attack without breaking the spell but may use nonattack spells or otherwise act.</p></h4><h5><b>Mythic: </b>If an attacker succeeds at its Will save to attack the protected target, add your tier to the target's AC against the attack. If the target uses a healing spell or ability on another creature, that creature gains the benefit of non-mythic sanctuary for 1 round. If the creature breaks the non-mythic sanctuary by attacking, subsequent healing by the target doesn't create another non-mythic sanctuary effect on the creature.</h5><h5><b>Augmented (2nd)</b>: If you expend two uses of mythic power, select a number of additional creatures equal to half your tier that are within reach. These creatures gain the benefit of non-mythic sanctuary.</h5></div>"},
new Spell(){ Name = "Scare", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Scare</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [fear, mind-affecting, emotion]; <b>Level </b>bard 2, sorcerer/wizard 2, witch 2, antipaladin 2, shaman 2, occultist 2, psychic 2, mesmerist 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a bone from an undead creature)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one living creature per three levels, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level or 1 round; see text for cause fear</h5><h5><b>Saving Throw </b>Will partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>cause fear</i>, except that it causes all targeted creatures of less than 6 HD to become frightened.</p></h4></div>"},
new Spell(){ Name = "Scintillating Pattern", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Scintillating Pattern</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (pattern) [mind-affecting]; <b>Level </b>sorcerer/wizard 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a crystal prism)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>colorful lights in a 20-ft.-radius spread</h5><h5><b>Duration </b>concentration + 2 rounds</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A twisting pattern of coruscating colors weaves through the air, affecting creatures within. The spell affects a total number of HD of creatures equal to your caster level (maximum 20).</p><p>Creatures with the fewest HD are affected first, and among creatures with equal HD, those who are closest to the spell's point of origin are affected first. HD that are not sufficient to affect a creature are wasted. The spell affects each subject according to its HD.</p><p><i>6 or less</i>: Unconscious for 1d4 rounds, then stunned for 1d4 rounds, and then <i>confused</i> for 1d4 rounds. (Treat an unconscious result as stunned for nonliving creatures.) <i>7 to 12</i>: Stunned for 1d4 rounds, then <i>confused</i> for an additional 1d4 rounds.</p><p>13 or more: Confused for 1d4 rounds.</p><p>Sightless creatures are not affected by scintillating pattern.</p></h4></div>"},
new Spell(){ Name = "Scorching Ray", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Scorching Ray</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [fire]; <b>Level </b>sorcerer/wizard 2, magus 2, bloodrager 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one or more rays</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You blast your enemies with a searing beam of fire. You may fire one ray, plus one additional ray for every four levels beyond 3rd (to a maximum of three rays at 11th level). Each ray requires a ranged touch attack to hit and deals 4d6 points of fire damage. The rays may be fired at the same or different targets, but all rays must be aimed at targets within 30 feet of each other and fired simultaneously.</p></h4><h5><b>Mythic: </b>Each ray's damage increases to 6d6 points of fire damage. The first ray bypasses fire resistance and immunity.</h5></div>"},
new Spell(){ Name = "Screen", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Screen</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>sorcerer/wizard 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>30-ft. cube/level  (S)</h5><h5><b>Duration </b>24 hours</h5><h5><b>Saving Throw </b>none or Will disbelief (if interacted with); see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates a powerful protection from scrying and observation. When casting the spell, you dictate what will and will not be observed in the spell's area. The illusion created must be stated in general terms. Once the conditions are set, they cannot be changed. Attempts to scry the area automatically detect the image stated by you with no save allowed. Sight and sound are appropriate to the illusion created. Direct observation may allow a save (as per a normal illusion), if there is cause to disbelieve what is seen. Even entering the area does not cancel the illusion or necessarily allow a save, assuming that hidden beings take care to stay out of the way of those affected by the illusion.</p></h4></div>"},
new Spell(){ Name = "Scrying", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Scrying</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination (scrying); <b>Level </b>bard 3, cleric/oracle 5, druid 4, sorcerer/wizard 4, witch 4, shaman 4, occultist 4, psychic 4, mesmerist 3, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 hour</h5><h5><b>Components </b>V, S, M/DF (a pool of water), F (a silver mirror worth 1,000 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>magical sensor</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can observe a creature at any distance. If the subject succeeds on a Will save, the spell fails. The difficulty of the save depends on how well your knowledge of the subject and what sort of physical connection (if any) you have to that creature.</p><p>Furthermore, if the subject is on another plane, it gets a +5 bonus on its Will save.</p><p> <table><tr><th>Knowledge</th><th>Will Save Modifier</th></tr><tr><td>None*</td><td>+10</td></tr><tr><td>Secondhand (you have heard of the subject)</td><td>+5</td></tr><tr><td>Firsthand (you have met the subject)</td><td>+0</td></tr><tr><td>Familiar (you know the subject well)</td><td>-5</td></tr><tr><td colspan=""2"">*You must have some sort of connection (see below) to a creature of which you have no knowledge.</td></tr></table> </p><p> <table><tr><th>Connection</th><th>Will Save Modifier</th></tr><tr><td>Likeness or picture</td><td>-2</td></tr><tr><td>Possession or garment</td><td>-4</td></tr><tr><td>Body part, lock of hair, bit of nail, etc.</td><td>-10</td></tr></table>  If the save fails, you can see and hear the subject and its surroundings (approximately 10 feet in all directions of the subject).</p><p>If the subject moves, the sensor follows at a speed of up to 150 feet.</p><p>As with all divination (scrying) spells, the sensor has your full visual acuity, including any magical effects. In addition, the following spells have a 5% chance per caster level of operating through the sensor: <i>detect chaos</i>, <i>detect evil</i>, <i>detect good</i>, <i>detect law</i>, <i>detect magic</i>, and <i>message.</i></p><p>If the save succeeds, you can't attempt to scry on that subject again for at least 24 hours.</p></h4></div>"},
new Spell(){ Name = "Scrying, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Scrying, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination (scrying); <b>Level </b>bard 6, cleric/oracle 7, druid 7, sorcerer/wizard 7, witch 7, shaman 7, occultist 6, psychic 7, mesmerist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>magical sensor</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>scrying,</i> except as noted above.</p><p>Additionally, all of the following spells function reliably through the sensor: <i>detect chaos</i>, <i>detect evil</i>, <i>detect good</i>, <i>detect law</i>, <i>detect magic</i>, <i>message</i>, <i>read magic,</i> and <i>tongues</i>.</p></h4></div>"},
new Spell(){ Name = "Sculpt Sound", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sculpt Sound</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature or object/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can change the sounds that creatures or objects make. You can create sounds where none exist, deaden sounds, or transform sounds into other sounds. All affected creatures or objects must be transmuted in the same way. Once the transmutation is made, you cannot change it. You can change the qualities of sounds but cannot create words with which you are unfamiliar yourself.</p><p>A spellcaster whose voice is changed dramatically is unable to cast spells with verbal components.</p></h4></div>"},
new Spell(){ Name = "Searing Light", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Searing Light</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 3, inquisitor 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>ray</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Focusing divine power like a ray of the sun, you project a blast of light from your open palm. You must succeed on a ranged touch attack to strike your target. A creature struck by this ray of light takes 1d8 points of damage per two caster levels (maximum 5d8). An undead creature takes 1d6 points of damage per caster level (maximum 10d6), and an undead creature particularly vulnerable to bright light takes 1d8 points of damage per caster level (maximum 10d8). A construct or inanimate object takes only 1d6 points of damage per two caster levels (maximum 5d6).</p></h4><h5><b>Mythic: </b>The spell's damage increases to 1d6 per caster level (maximum 10d6). The damage dealt to an undead creature increases to 1d8 points of damage per caster level (maximum 10d8), or 2d6 per caster level (maximum 20d6) if the undead is vulnerable to bright light. The damage dealt to constructs increases to 1d8 points of damage per 2 caster levels (maximum 5d8). If a creature struck has light sensitivity or light blindness, it takes the penalty from that weakness for 1 round after being struck by the light.</h5></div>"},
new Spell(){ Name = "Secret Chest", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Secret Chest</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>sorcerer/wizard 5, witch 5, occultist 5, psychic 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, F (the chest and its replica)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Targets </b>one chest and up to 1 cu. ft. of goods/caster level</h5><h5><b>Duration </b>60 days or until discharged</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You hide a chest on the Ethereal Plane for as long as 60 days and can retrieve it at will. The chest can contain up to 1 cubic foot of material per caster level (regardless of the chest's actual size, which is about 3 feet by 2 feet by 2 feet). If any living creatures are in the chest, there is a 75% chance that the spell simply fails.</p><p>Once the chest is hidden, you can retrieve it by concentrating (a standard action), and it appears next to you.</p><p>The chest must be exceptionally well crafted and expensive, constructed for you by master crafters. The cost of such a chest is never less than 5,000 gp. Once it is constructed, you must make a tiny replica (of the same materials and perfect in every detail) so that the miniature of the chest appears to be a perfect copy. (The replica costs 50 gp.) The chests are nonmagical and can be fitted with locks, wards, and so on, just as any normal chest can be.</p><p>To hide the chest, you cast the spell while touching both the chest and the replica. The chest vanishes into the Ethereal Plane.</p><p>You need the replica to recall the chest. After 60 days, there is a cumulative chance of 5% per day that the chest is irretrievably lost.</p><p>If the miniature of the chest is lost or destroyed, there is no way, even with a <i>wish</i> spell, that the large chest can be summoned back, although an extraplanar expedition might be mounted to find it.</p><p>Living things in the chest eat, sleep, and age normally, and they die if they run out of food, air, water, or whatever they need to survive.</p></h4></div>"},
new Spell(){ Name = "Secret Page", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Secret Page</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 3, sorcerer/wizard 3, occultist 3, mesmerist 3, witch 3, alchemist 2, witch 2, inquisitor 2, witch 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (powdered herring scales and a vial of will-o'-wisp essence)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>page touched, up to 3 sq. ft. in size</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Secret page</i> alters the contents of a <i>page</i> so that it appears to be something entirely different. The text of a spell can be changed to show another spell of equal or lower level known by the caster.</p><p>This spell cannot be used to change a spell contained on a scroll, but it can be used to hide a scroll. <i>Explosive runes</i> or <i>sepia snake sigil</i> can be cast upon the <i>secret page</i>.</p><p>A comprehend languages spell alone cannot reveal a <i>secret page</i>'s contents. You are able to reveal the original contents by speaking a special word. You can then peruse the actual <i>page</i> and return it to its <i>secret page</i> form at will. You can also remove the spell by double repetition of the special word. A <i>detect magic</i> spell reveals dim magic on the <i>page</i> in question but does not reveal its true contents. <i>True seeing</i> reveals the presence of the hidden material but does not reveal the contents unless cast in combination with <i>comprehend languages</i>. A <i>secret page</i> spell can be dispelled, and the hidden writings can be destroyed by means of an <i>erase</i> spell.</p></h4></div>"},
new Spell(){ Name = "Secure Shelter", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Secure Shelter</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>bard 4, sorcerer/wizard 4, witch 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (a chip of stone, sand, a drop of water, and a wood splinter)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>20-ft.-square structure</h5><h5><b>Duration </b>2 hours/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You conjure a sturdy cottage or lodge made of material that is common in the area where the spell is cast. The floor is level, clean, and dry. The lodging resembles a normal cottage, with a sturdy door, two shuttered windows, and a small fireplace.</p><p>The shelter must be heated as a normal dwelling, and extreme heat adversely affects it and its occupants. The dwelling does, however, provide considerable security otherwise-it is as strong as a normal stone building, regardless of its material composition. The dwelling resists flames and fire as if it were stone. It is impervious to normal missiles (but not the sort cast by siege engines or giants).</p><p>The door, shutters, and even chimney are secure against intrusion, the former two being secured with <i>arcane lock</i> and the latter by an iron grate at the top and a narrow flue. In addition, these three areas are protected by an <i>alarm</i> spell. Finally, an <i>unseen servant</i> is conjured to provide service to you for the duration of the shelter.</p><p>The <i>secure shelter</i> contains crude furnishings-eight bunks, a trestle table, eight stools, and a writing desk.</p></h4></div>"},
new Spell(){ Name = "See Invisibility", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">See Invisibility</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 3, sorcerer/wizard 2, alchemist 2, summoner 2, witch 2, inquisitor 2, bloodrager 2, occultist 2, psychic 2, mesmerist 3, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (talc and powdered silver)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>10 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can see any objects or beings that are invisible within your range of vision, as well as any that are ethereal, as if they were normally visible. Such creatures are visible to you as translucent shapes, allowing you easily to discern the difference between visible, invisible, and ethereal creatures.</p><p>The spell does not reveal the method used to obtain <i>invisibility</i>.</p><p>It does not reveal illusions or enable you to see through opaque objects. It does not reveal creatures who are simply hiding, concealed, or otherwise hard to see.</p><p>See <i>invisibility</i> can be made permanent with a <i>permanency</i> spell.</p></h4></div>"},
new Spell(){ Name = "Seeming", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Seeming</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 5, sorcerer/wizard 5, occultist 5, psychic 5, mesmerist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature per two levels, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>12 hours (D)</h5><h5><b>Saving Throw </b>Will negates or Will disbelief (if interacted with); <b>Spell Resistance </b>yes or no; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>disguise self,</i> except that you can change the appearance of other people as well. Affected creatures resume their normal appearances if slain. Unwilling targets can negate the spell's effect on them by making Will saves or with spell resistance.</p></h4></div>"},
new Spell(){ Name = "Sending", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sending</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation; <b>Level </b>cleric/oracle 4, sorcerer/wizard 5, alchemist 5, summoner 4, inquisitor 4, shaman 4, occultist 4, psychic 4, mesmerist 3, spiritualist 5, alchemist 5, summoner 4, inquisitor 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M/DF (fine copper wire)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>1 round; see text</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You contact a particular creature with which you are familiar and send a short message of 25 words or less to the subject. The subject recognizes you if it knows you. It can answer in like manner immediately. A creature with an Intelligence score as low as 1 can understand the <i><i>sending</i>,</i> though the subject's ability to react is limited as normal by its Intelligence. Even if the <i>sending</i> is received, the subject is not obligated to act upon it in any manner.</p><p>If the creature in question is not on the same plane of existence as you are, there is a 5% chance that the <i>sending</i> does not arrive. (Local conditions on other planes may worsen this chance considerably.)</p></h4></div>"},
new Spell(){ Name = "Sepia Snake Sigil", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sepia Snake Sigil</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [force]; <b>Level </b>bard 3, sorcerer/wizard 3, witch 3, occultist 3, witch 3, alchemist 2, witch 2, inquisitor 2, witch 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, M (powdered amber worth 500 gp and a snake scale)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched book or written work</h5><h5><b>Duration </b>permanent or until discharged; until released or 1d4 days + 1 day/level; see text</h5><h5><b>Saving Throw </b>Reflex negates; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You cause a small symbol to appear in the text of a written work.</p><p>The text containing the symbol must be at least 25 words long.</p><p>When anyone reads the text containing the symbol, the <i><i>sepia snake</i> sigil</i> springs into being, transforming into a large sepia serpent that strikes at the reader, provided there is line of between the symbol and the reader.</p><p>Simply seeing the enspelled text is not sufficient to trigger the spell; the subject must deliberately read it. The target is entitled to a save to evade the snake's strike. If it succeeds, the <i>sepia snake</i> dissipates in a flash of brown light accompanied by a puff of duncolored smoke and a loud noise. If the target fails its save, it is engulfed in a shimmering amber field of force and immobilized until released, either at your command or when 1d4 days + 1 day per caster level have elapsed.</p><p>While trapped in the amber field of force, the subject does not age, breathe, grow hungry, sleep, or regain spells. It is preserved in a state of suspended animation, unaware of its surroundings. It can be damaged by outside forces (and perhaps even killed), since the field provides no protection against physical injury. However, a dying subject does not lose hit points or become stable until the spell ends.</p><p>The hidden sigil cannot be detected by normal observation, and <i>detect magic</i> reveals only that the entire text is magical.</p><p>A <i>dispel magic</i> can remove the sigil. An <i>erase</i> spell destroys the entire page of text.</p><p><i>Sepia snake sigil</i> can be cast in combination with other spells that hide or garble text, such as secret page.</p></h4></div>"},
new Spell(){ Name = "Sequester", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sequester</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 7, summoner 5, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a basilisk eyelash and gum arabic)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one willing creature or object (up to a 2-ft. cube/level) touched</h5><h5><b>Duration </b>1 day/level (D)</h5><h5><b>Saving Throw </b>none or Will negates (object); <b>Spell Resistance </b>no or yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When cast, this spell prevents divination spells from detecting or locating the target and also renders the affected target invisible (as the <i>invisibility</i> spell). The spell does not prevent the subject from being discovered through tactile means or through the use of devices. Creatures affected by <i>sequester</i> become comatose and are effectively in a state of suspended animation until the spell ends.</p><p><i>Note:</i> The Will save prevents an attended or magical object from being <i>sequester</i>ed. There is no save to see the <i>sequester</i>ed creature or object or to detect it with a divination spell.</p></h4></div>"},
new Spell(){ Name = "Shadow Conjuration", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shadow Conjuration</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>bard 4, sorcerer/wizard 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>see text</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); varies; see text; <b>Spell Resistance </b>yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You use material from the Plane of Shadow to shape quasi-real illusions of one or more creatures, objects, or forces. <i>Shadow conjuration</i> can mimic any sorcerer or wizard conjuration (summoning) or conjuration (creation) spell of 3rd level or lower.</p><p><i>Shadow conjuration</i>s are only one-fifth (20%) as strong as the real things, though creatures who believe the <i><i>shadow conjuration</i>s</i> to be real are affected by them at full strength. Any creature that interacts with the spell can make a Will save to recognize its true nature.</p><p>Spells that deal damage have normal effects unless the affected creature succeeds on a Will save. Each disbelieving creature takes only one-fifth (20%) damage from the attack. If the disbelieved attack has a special effect other than damage, that effect is only 20% likely to occur. Regardless of the result of the save to disbelieve, an affected creature is also allowed any save that the spell being simulated allows, but the save DC is set according to <i>shadow conjuration</i>'s level (4th) rather than the spell's normal level. In addition, any effect created by <i>shadow conjuration</i> allows spell resistance, even if the spell it is simulating does not. Shadow objects or substances have normal effects except against those who disbelieve them. Against disbelievers, they are 20% likely to work.</p><p>A shadow creature has one-fifth the hit points of a normal creature of its kind (regardless of whether it's recognized as shadowy). It deals normal damage and has all normal abilities and weaknesses. Against a creature that recognizes it as a shadow creature, however, the shadow creature's damage is one-fifth (20%) normal, and all special abilities that do not deal lethal damage are only 20% likely to work. (Roll for each use and each affected character separately.) Furthermore, the shadow creature's AC bonuses are just one-fifth as large.</p><p>A creature that succeeds on its save sees the <i><i>shadow conjuration</i>s</i> as transparent images superimposed on vague, shadowy forms.</p><p>Objects automatically succeed on their Will saves against this spell.</p></h4></div>"},
new Spell(){ Name = "Shades", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shades</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>sorcerer/wizard 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>see text</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); varies; see text; <b>Spell Resistance </b>yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>shadow conjuration,</i> except that it mimics conjuration spells of 8th level or lower. The illusory conjurations created deal four-fifths (80%) damage to nonbelievers, and nondamaging effects are 80% likely to work against nonbelievers.</p></h4></div>"},
new Spell(){ Name = "Shadow Conjuration, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shadow Conjuration, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>sorcerer/wizard 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>see text</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); varies; see text; <b>Spell Resistance </b>yes; see text</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>shadow conjuration,</i> except that it duplicates any sorcerer or wizard conjuration (summoning) or conjuration (creation) spell of 6th level or lower. The illusory conjurations created deal three-fifths (60%) damage to nonbelievers, and nondamaging effects are 60% likely to work against nonbelievers.</p></h4></div>"},
new Spell(){ Name = "Shadow Evocation", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shadow Evocation</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>bard 5, sorcerer/wizard 5, mesmerist 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>see text</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You tap energy from the Plane of Shadow to cast a quasi-real, illusory version of a sorcerer or wizard evocation spell of 4th level or lower. Spells that deal damage have normal effects unless an affected creature succeeds on a Will save. Each disbelieving creature takes only one-fifth damage from the attack. If the disbelieved attack has a special effect other than damage, that effect is one-fifth as strong (if applicable) or only 20% likely to occur. If recognized as a <i>shadow evocation, a</i> damaging spell deals only one-fifth (20%) damage. Regardless of the result of the save to disbelieve, an affected creature is also allowed any save (or spell resistance) that the spell being simulated allows, but the save DC is set according to shadow evocation's level (5th) rather than the spell's normal level.</p><p>Nondamaging effects have normal effects except against those who disbelieve them. Against disbelievers, they have no effect.</p><p>Objects automatically succeed on their Will saves against this spell.</p></h4></div>"},
new Spell(){ Name = "Shadow Evocation, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shadow Evocation, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>sorcerer/wizard 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>see text</h5><h5><b>Effect </b>see text</h5><h5><b>Duration </b>see text</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>sh<i>a</i>dow evoc<i>a</i>tion,</i> except th<i>a</i>t it en<i>a</i>bles you to cre<i>a</i>te p<i>a</i>rti<i>a</i>lly re<i>a</i>l, illusory versions of sorcerer or wiz<i>a</i>rd evoc<i>a</i>tion spells of 7th level or lower. If recognized <i>a</i>s <i>a</i> gre<i>a</i>ter <i>sh<i>a</i>dow evoc<i>a</i>tion,</i> <i>a</i> d<i>a</i>m<i>a</i>ging spell de<i>a</i>ls only three-fifths (60%) d<i>a</i>m<i>a</i>ge.</p></h4></div>"},
new Spell(){ Name = "Shadow Walk", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shadow Walk</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow) [shadow]; <b>Level </b>bard 5, sorcerer/wizard 6, alchemist 6, mesmerist 5, spiritualist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>up to one touched creature/level</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>To use the <i>shadow walk</i> spell, you must be in an area of dim light. You and any creature you touch are then transported along a coiling path of shadowstuff to the edge of the Material Plane where it borders the Plane of Shadow. The effect is largely illusory, but the path is quasi-real. You can take more than one creature along with you (subject to your level limit), but all must be touching each other.</p><p>In the region of shadow, you move at a rate of 50 miles per hour, moving normally on the borders of the Plane of Shadow but much more rapidly relative to the Material Plane. Thus, you can use this spell to travel rapidly by stepping onto the Plane of Shadow, moving the desired distance, and then stepping back onto the Material Plane.</p><p>Because of the blurring of reality between the Plane of Shadow and the Material Plane, you can't make out details of the terrain or areas you pass over during transit, nor can you predict perfectly where your travel will end. It's impossible to judge distances accurately, making the spell virtually useless for scouting or spying.</p><p>Furthermore, when the spell effect ends, you are shunted 1d10 x 100 feet in a random horizontal direction from your desired endpoint. If this would place you within a solid object, you are shunted 1d10 x 1,000 feet in the same direction. If this would still place you within a solid object, you (and any creatures with you) are shunted to the nearest empty space available, but the strain of this activity renders each creature fatigued (no save).</p><p><i>Shadow walk</i> can also be used to travel to other planes that border on the Plane of Shadow, but this usage requires the transit of the Plane of Shadow to arrive at a border with another plane of reality. The transit of the Plane of Shadow requires 1d4 hours.</p><p>Any creatures touched by you when <i>shadow walk</i> is cast also make the transition to the borders of the Plane of Shadow.</p><p>They may opt to follow you, wander off through the plane, or stumble back into the Material Plane (50% chance for either of the latter results if they are lost or abandoned by you). Creatures unwilling to accompany you into the Plane of Shadow receive a Will saving throw, negating the effect if successful.</p></h4></div>"},
new Spell(){ Name = "Shambler", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shambler</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>druid 9, shaman 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>three or more shambling mounds, no two of which can be more than 30 ft. apart; see text</h5><h5><b>Duration </b>7 days or 7 months ; see text (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The <i>shambler</i> spell creates 1d4+2 shambling mounds with the advanced template (see the <i>Pathfinder RPG Bestiary</i>). The creatures willingly aid you in combat or battle, perform a specific mission, or serve as bodyguards. The creatures remain with you for 7 days unless you dismiss them. If the <i>shambler</i>s are created only for guard duty, however, the duration of the spell is 7 months. In this case, the <i>shambler</i>s can only be ordered to guard a specific site or location. Shamblers summoned to guard duty cannot move outside the spell's range, which is measured from the point where each first appeared. You can only have one <i>shambler</i> spell in at one time. If you cast this spell while another casting is still in effect, the previous casting is dispelled. The <i>shambler</i>s have resistance to fire as normal shambling mounds do only if the terrain where they are summoned is rainy, marshy, or damp.</p></h4></div>"},
new Spell(){ Name = "Shapechange", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shapechange</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation (polymorph); <b>Level </b>druid 9, sorcerer/wizard 9, shaman 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (jade circlet worth 1,500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>10 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell allows you to take the form of a wide variety of creatures. This spell can function as <i>alter self, beast form IV, elemental body IV</i>, <i>form of the dragon III, giant form II,</i> and <i>plant shape III</i> depending on what form you take. You can change form once each round as a free action. The change takes place either immediately before your regular action or immediately after it, but not during the action.</p></h4></div>"},
new Spell(){ Name = "Shatter", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shatter</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [sonic]; <b>Level </b>bard 2, cleric/oracle 2, sorcerer/wizard 2, magus 2, bloodrager 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a chip of mica)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Target or Area </b>5-ft.-radius spread; or one solid object or one crystalline creature<h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (object); Will negates (object) or Fortitude half; see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Shatter</i> creates a loud, ringing noise that breaks brittle, nonmagical objects; sunders a single solid, nonmagical object; or damages a crystalline creature.</p><p>Used as an area attack, <i>shatter</i> destroys nonmagical objects of crystal, glass, ceramic, or porcelain. All such unattended objects within a 5-foot radius of the point of origin are smashed into dozens of pieces by the spell. Objects weighing more than 1 pound per your level are not affected, but all other objects of the appropriate composition are <i>shatter</i>ed.</p><p>Alternatively, you can target <i>shatter</i> against a single solid nonmagical object, regardless of composition, weighing up to 10 pounds per caster level. Targeted against a crystalline creature (of any weight), <i>shatter</i> deals 1d6 points of sonic damage per caster level (maximum 10d6), with a Fortitude save for half damage.</p></h4><h5><b>Mythic: </b>Add your tier to your caster level when determining the maximum object weight you can affect. When targeting a crystalline creature, the spell's damage increases to 1d8 points of sonic damage per caster level (maximum 10d8). You may damage a single magical object as if it were a crystalline creature. If the spell destroys an object or damages a crystalline creature, broken shards litter the spell area or creature's space and act as caltrops.</h5></div>"},
new Spell(){ Name = "Shield", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shield</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [force]; <b>Level </b>sorcerer/wizard 1, alchemist 1, summoner 1, magus 1, bloodrager 1, occultist 1, psychic 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level (D)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Shield</i> creates an invisible <i>shield</i> of force that hovers in front of you. It negates <i>magic missile</i> attacks directed at you. The disk also provides a +4 <i>shield</i> bonus to AC. This bonus applies against incorporeal touch attacks, since it is a force effect. The <i>shield</i> has no armor check penalty or arcane spell failure chance.</p></h4></div>"},
new Spell(){ Name = "Shield Of Faith", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shield Of Faith</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 1, inquisitor 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (parchment with a holy text written on it)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates a shimmering, magical field around the target that averts and deflects attacks. The spell grants the subject a +2 deflection bonus to AC, with an additional +1 to the bonus for every six levels you have (maximum +5 deflection bonus at 18th level).</p></h4><h5><b>Mythic: </b>Add half your tier to the spell's deflection bonus.</h5></div>"},
new Spell(){ Name = "Shield of Law", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shield of Law</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration [lawful]; <b>Level </b>cleric/oracle 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a reliquary worth 500 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>20 ft.</h5><h5><b>Targets </b>one creature/level in a 20-ft.-radius burst centered on you</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A dim, blue glow surrounds the subjects, protecting them from attacks, granting them resistance to spells cast by chaotic creatures, and <i><i>slow</i>ing</i> chaotic creatures when they strike the subjects. This abjuration has four effects.</p><p>First, each warded creature gains a +4 deflection bonus to AC and a +4 resistance bonus on saves. Unlike <i>protection from chaos</i>, this benefit applies against all attacks, not just against attacks by chaotic creatures.</p><p>Second, a warded creature gains spell resistance 25 against chaotic spells and spells cast by chaotic creatures.</p><p>Third, the abjuration protects you from possession and mental influence, just as <i>protection from chaos</i> does.</p><p>Finally, if a chaotic creature succeeds on a melee attack against a warded creature, the attacker is <i><i>slow</i>ed</i> (Will save negates, as the <i>slow</i> spell, but against <i>shield of law</i>'s save DC).</p></h4></div>"},
new Spell(){ Name = "Shield Other", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shield Other</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 2, paladin 2, inquisitor 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a pair of platinum rings worth 50 gp worn by both you and the target)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell wards the subject and creates a mystic connection between you and the subject so that some of its wounds are transferred to you. The subject gains a +1 deflection bonus to AC and a +1 resistance bonus on saves. Additionally, the subject takes only half damage from all wounds and attacks (including those dealt by special abilities) that deal hit point damage. The amount of damage not taken by the warded creature is taken by you. Forms of harm that do not involve hit points, such as <i>charm</i> effects, temporary ability damage, level draining, and death effects, are not affected. If the subject suffers a reduction of hit points from a lowered Constitution score, the reduction is not split with you because it is not hit point damage. When the spell ends, subsequent damage is no longer divided between the subject and you, but damage already split is not reassigned to the subject.</p><p>If you and the subject of the spell move out of range of each other, the spell ends.</p></h4><h5><b>Mythic: </b>Increase the range to long (400 feet + 40 feet per caster level). Subtract your tier from any damage transmitted to you through the mystical connection before applying the damage (minimum 1 hp of damage). When you use your surge ability to modify a saving throw against an effect that also affects your linked ally, the ally also applies your surge bonus die result to the ally's saving throw. If the ally also uses its own surge ability, it takes either your result or its result, whichever is higher.</h5></div>"},
new Spell(){ Name = "Shillelagh", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shillelagh</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched nonmagical oak club or quarterstaff</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Your own nonmagical club or quarterstaff becomes a weapon with a +1 enhancement bonus on attack and damage rolls. A quarterstaff gains this enhancement for both ends of the weapon. It deals damage as if it were two size categories larger (a Small club or quarterstaff so transmuted deals 1d8 points of damage, a Medium 2d6, and a Large 3d6), +1 for its enhancement bonus. These effects only occur when the weapon is wielded by you. If you do not wield it, the weapon behaves as if unaffected by this spell.</p></h4></div>"},
new Spell(){ Name = "Shrink Item", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shrink Item</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 3, occultist 3, psychic 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one touched object of up to 2 cu. ft./level</h5><h5><b>Duration </b>1 day/level; see text</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You are able to shrink one nonmagical item (if it is within the size limit) to 1/16 of its normal size in each dimension (to about 1/4,000 the original volume and mass). This change effectively reduces the object's size by four categories. Optionally, you can also change its now shrunken composition to a clothlike one.</p><p>Objects changed by a <i>shrink item</i> spell can be returned to normal composition and size merely by tossing them onto any solid surface or by a word of command from the original caster. Even a burning fire and its fuel can be shrunk by this spell. Restoring the shrunken object to its normal size and composition ends the spell.</p><p><i>Shrink item</i> can be made permanent with a <i>permanency</i> spell, in which case the affected object can be shrunk and expanded an indefinite number of times, but only by the original caster.</p></h4></div>"},
new Spell(){ Name = "Shocking Grasp", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shocking Grasp</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [electricity]; <b>Level </b>sorcerer/wizard 1, magus 1, bloodrager 1, occultist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature or object touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Your successful melee touch attack deals 1d6 points of electricity damage per caster level (maximum 5d6). When delivering the jolt, you gain a +3 bonus on attack rolls if the opponent is wearing metal armor (or is carrying a metal weapon or is made of metal).</p></h4><h5><b>Mythic: </b>The spell's damage increases to 1d8 points of electricity damage per caster level (maximum 5d8). If the target is wearing metal armor or is made of metal, you may attempt a free grapple combat maneuver against the target. Alternatively, if the target is carrying a metal weapon, you may attempt a free disarm combat maneuver against that weapon. This free combat maneuver doesn't provoke attacks of opportunity.</h5><h5><b>Augmented</b>: If you expend two uses of mythic power and the free grapple or disarm combat maneuver is successful, the target is staggered until your next turn. This is an electricity effect.</h5></div>"},
new Spell(){ Name = "Shout", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shout</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [sonic]; <b>Level </b>bard 4, sorcerer/wizard 4, magus 4, bloodrager 4, occultist 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>30 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial or Reflex negates (object); see text; <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You emit an ear-splitting yell that deafens and damages creatures in its path. Any creature within the area is deafened for 2d6 rounds and takes 5d6 points of sonic damage. A successful save negates the deafness and reduces the damage by half. Any exposed brittle or crystalline object or crystalline creature takes 1d6 points of sonic damage per caster level (maximum 15d6). An affected creature is allowed a Fortitude save to reduce the damage by half, and a creature holding fragile objects can negate damage to them with a successful Reflex save. A <i>shout</i> spell cannot penetrate a <i>silence</i> spell.</p></h4><h5><b>Mythic: </b>The duration that creatures in the area are deafened increases to 4d6 rounds, and the damage dealt increases to 5d8 points of sonic damage. The damage dealt to crystalline creatures and exposed brittle or crystalline objects increases to 1d8 points of sonic damage per caster level (maximum 15d8). Crystalline creatures reverberate with sonic energy, taking 4 points of sonic damage on your turn for a number of rounds equal to your tier.</h5></div>"},
new Spell(){ Name = "Shout, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Shout, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [sonic]; <b>Level </b>bard 6, sorcerer/wizard 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a metal or ivory horn)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>60 ft.</h5><h5><b>Area </b>cone-shaped burst</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial or Reflex negates (object); see text; <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>shout,</i> except that the cone deals 10d6 points of sonic damage (or 1d6 points of sonic damage per caster level, maximum 20d6, against exposed brittle or crystalline objects or crystalline creatures). It also causes creatures to be stunned for 1 round and deafened for 4d6 rounds. A creature in the area of the cone can negate the stunning and halve both the damage and the duration of the deafness with a successful Fortitude save. A creature holding vulnerable objects can attempt a Reflex save to negate the damage to those objects.</p></h4></div>"},
new Spell(){ Name = "Silence", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Silence</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (glamer); <b>Level </b>bard 2, cleric/oracle 2, inquisitor 2, antipaladin 2, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>20-ft.-radius emanation centered on a creature, object, or point in space</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>Will negates; see text or none (object); <b>Spell Resistance </b>yes; see text or no (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Upon the casting of this spell, complete <i>silence</i> prevails in the affected area. All sound is stopped: Conversation is impossible, spells with verbal components cannot be cast, and no noise whatsoever issues from, enters, or passes through the area. The spell can be cast on a point in space, but the effect is stationary unless cast on a mobile object. The spell can be centered on a creature, and the effect then radiates from the creature and moves as it moves. An unwilling creature can attempt a Will save to negate the spell and can use spell resistance, if any. Items in a creature's possession or magic items that emit sound receive the benefits of saves and spell resistance, but unattended objects and points in space do not. Creatures in an area of a <i>silence</i> spell are immune to sonic or language-based attacks, spells, and effects.</p></h4><h5><b>Mythic: </b>This spell also applies to telepathy, cutting off any mental communication within the area. At the time of casting, you can choose to be immune to your own mythic silence effect and can designate up to one creature per tier to also gain this immunity. Creatures immune to the mythic silence are subject to sonic effects as normal.</h5></div>"},
new Spell(){ Name = "Silent Image", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Silent Image</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (figment); <b>Level </b>bard 1, sorcerer/wizard 1, magus 1, occultist 1, psychic 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (a bit of fleece)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>visual figment that cannot extend beyond four 10-ft. Cubes + one 10-ft. cube/level (S)</h5><h5><b>Duration </b>concentration</h5><h5><b>Saving Throw </b>Will disbelief (if interacted with); <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell creates the visual illusion of an object, creature, or force, as visualized by you. The illusion does not create sound, smell, texture, or temperature. You can move the image within the limits of the size of the effect.</p></h4><h5><b>Mythic: </b>The duration increases to concentration + 1 round per caster level, and the effect increases to a visual figment that can't extend beyond eight 10-foot cubes + 1 10-foot cube per caster level.</h5><h5><b>Augmented (6th)</b>: If you expend two uses of mythic power, the duration changes to concentration + 1 minute per caster level and the effect changes to a visual figment no larger than a 50-foot emanation. An augmented mythic silent image can't move.</h5></div>"},
new Spell(){ Name = "Simulacrum", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Simulacrum</p><div style=""clear: both;""></div></div><div><h5><b>School </b>illusion (shadow); <b>Level </b>sorcerer/wizard 7, summoner 5, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>12 hours</h5><h5><b>Components </b>V, S, M (ice sculpture of the target plus powdered rubies worth 500 gp per HD of the simulacrum)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>one duplicate creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Simulacrum</i> creates an illusory duplicate of any creature. The duplicate creature is partially real and formed from ice or snow. It appears to be the same as the original, but it has only half of the real creature's levels or HD (and the appropriate hit points, feats, skill ranks, and special abilities for a creature of that level or HD).</p><p>You can't create a simulacrum of a creature whose HD or levels exceed twice your caster level. You must make a Disguise check when you cast the spell to determine how good the likeness is.</p><p>A creature familiar with the original might detect the ruse with a successful Perception check (opposed by the caster's Disguise check) or a DC 20 Sense Motive check.</p><p>At all times, the simulacrum remains under your absolute command. No special telepathic link exists, so command must be exercised in some other manner. A simulacrum has no ability to become more powerful. It cannot increase its level or abilities. If reduced to 0 hit points or otherwise destroyed, it reverts to snow and melts instantly into nothingness. A complex process requiring at least 24 hours, 100 gp per hit point, and a fully equipped magical laboratory can repair damage to a simulacrum.</p></h4></div>"},
new Spell(){ Name = "Slay Living", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Slay Living</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [death]; <b>Level </b>cleric/oracle 5, witch 6, antipaladin 4, shaman 6, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>living creature touched</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can attempt to slay any one living creature. When you cast this spell, your hand seethes with eerie dark fire. You must succeed on a melee touch attack to touch the target. The target takes 12d6 points of damage + 1 point per caster level. If the target's Fortitude saving throw succeeds, it instead takes 3d6 points of damage + 1 point per caster level. The subject might die from damage even if it succeeds on its saving throw.</p></h4></div>"},
new Spell(){ Name = "Sleep", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sleep</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting]; <b>Level </b>bard 1, sorcerer/wizard 1, witch 1, shaman 1, occultist 1, psychic 1, mesmerist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (fine sand, rose petals, or a live cricket)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>one or more living creatures within a 10-ft.-radius burst</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>sleep</i> spell causes a magical slumber to come upon 4 HD of creatures. Creatures with the fewest HD are affected first. Among creatures with equal HD, those who are closest to the spell's point of origin are affected first. HD that are not sufficient to affect a creature are wasted. <i>Sleep</i>ing creatures are helpless. Slapping or wounding awakens an affected creature, but normal noise does not. Awakening a creature is a standard action (an application of the aid another action). <i>Sleep</i> does not target unconscious creatures, constructs, or undead creatures.</p></h4><h5><b>Mythic: </b>The spell affects up to 8 Hit Dice of creatures, and you can choose the order in which creatures are affected by the spell. Any effect that would automatically awaken a sleeping creature instead allows it to attempt a new Will saving throw to awaken.</h5><h5><b>Augmented (8th)</b>: If you expend three uses of mythic power, you can target all living creatures other than you that have 8 Hit Dice or fewer in a 1-mile radius centered on you. Creatures that fail their saving throws fall asleep for a number of days equal to your tier. You can select a number of creatures up to your tier to not be affected by the spell.</h5></div>"},
new Spell(){ Name = "Sleet Storm", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sleet Storm</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation) [cold]; <b>Level </b>druid 3, sorcerer/wizard 3, witch 3, magus 3, bloodrager 3, shaman 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (dust and water)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Area </b>cylinder (40-ft. radius, 20 ft. high)</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Driving sleet blocks all sight (even darkvision) within it and causes the ground in the area to be icy. A creature can walk within or through the area of sleet at half normal speed with a DC 10 Acrobatics check. Failure means it can't move in that round, while failure by 5 or more means it falls (see the Acrobatics skill for details).</p><p>The sleet extinguishes torches and small fires.</p></h4></div>"},
new Spell(){ Name = "Slow", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Slow</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>bard 3, sorcerer/wizard 3, summoner 2, magus 3, bloodrager 3, occultist 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a drop of molasses)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>An affected creature moves and attacks at a drastically <i><i>slow</i>ed</i> rate. Creatures affected by this spell are staggered and can take only a single move action or standard action each turn, but not both (nor may it take full-round actions). Additionally, it takes a -1 penalty on attack rolls, AC, and Reflex saves. A <i><i>slow</i>ed</i> creature moves at half its normal speed (round down to the next 5-foot increment), which affects the creature's jumping distance as normal for decreased speed.</p><p>Multiple <i>slow</i> effects don't stack. <i>Slow</i> counters and dispels <i>haste</i>.</p></h4><h5><b>Mythic: </b>The penalty on attack rolls, on Reflex saves, and to AC increases to -2, and affected creatures move at one-fourth speed. Select one affected creature; this creature is denied its Dexterity bonus to AC.</h5></div>"},
new Spell(){ Name = "Snare", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Snare</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 3, ranger 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>3 rounds</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>touched nonmagical circle of vine, rope, or thong with a 2 ft. diameter + 2 ft./level</h5><h5><b>Duration </b>Until triggered or broken</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell enables you to make a <i>snare</i> that functions as a magic trap. The <i>snare</i> can be made from any supple vine, a thong, or a rope. When you cast <i>snare</i> upon it, the cordlike object blends with its surroundings (DC 23 Perception check for a character with the trapfinding ability to locate). One end of the <i>snare</i> is tied in a loop that contracts around one or more of the limbs of any creature stepping inside the circle.</p><p>If a strong and supple tree is nearby, the <i>snare</i> can be fastened to it. The spell causes the tree to bend, straightening when the loop is triggered, dealing 1d6 points of damage to the creature trapped and lifting it off the ground by the trapped limb or limbs. If no such tree is available, the cordlike object tightens around the creature, dealing no damage but causing it to be entangled.</p><p>The <i>snare</i> is magical. To escape, a trapped creature must make a DC 23 Escape Artist check or a DC 23 Strength check that is a full-round action. The <i>snare</i> has AC 7 and 5 hit points. A successful escape from the <i>snare</i> breaks the loop and ends the spell.</p></h4></div>"},
new Spell(){ Name = "Soften Earth and Stone", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Soften Earth and Stone</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [earth]; <b>Level </b>druid 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>10-ft. square/level; see text</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When this spell is cast, all natural, undressed earth or stone in the spell's area is softened. Wet earth becomes thick mud, dry earth becomes loose sand or dirt, and stone becomes soft clay that is easily molded or chopped. You affect a 10-foot square area to a depth of 1 to 4 feet, depending on the toughness or resilience of the ground at that spot. Magical, enchanted, dressed, or worked stone cannot be affected. Earth or stone creatures are not affected.</p><p>A creature in mud must succeed on a Reflex save or be caught for 1d2 rounds and unable to move, attack, or cast spells. A creature that succeeds on its save can move through the mud at half speed, and it can't run or charge. Loose dirt is not as troublesome as mud, but all creatures in the area can move at only half their normal speed and can't run or charge over the surface. Stone softened into clay does not hinder movement, but it does allow characters to cut, shape, or excavate areas they may not have been able to affect before.</p><p>While this spell does not affect dressed or worked stone, cavern ceilings or vertical surfaces such as cliff faces can be affected.</p><p>Usually, this causes a moderate collapse or landslide as the loosened material peels away from the face of the wall or roof and falls (treat as a cave-in with no bury zone, see Chapter 13).</p><p>A moderate amount of structural damage can be dealt to a manufactured structure by softening the ground beneath it, causing it to settle. However, most well-built structures will only be damaged by this spell, not destroyed.</p></h4></div>"},
new Spell(){ Name = "Solid Fog", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Solid Fog</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>sorcerer/wizard 4, witch 4, magus 4, shaman 4, mesmerist 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (powdered peas and an animal hoof)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft. level)</h5><h5><b>Effect </b>fog spreads in 20-ft. radius</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>fog cloud</i>, but in addition to obscuring sight, the <i>solid fog</i> is so thick that it impedes movement.</p><p>Creatures moving through a <i>solid fog</i> move at half their normal speed and take a -2 penalty on all melee attack and melee damage rolls. The vapors prevent effective ranged weapon attacks (except for magic rays and the like). A creature or object that falls into <i>solid fog</i> is slowed so that each 10 feet of vapor that it passes through reduces the falling damage by 1d6. A creature cannot take a 5-foot-step while in <i>solid fog</i>. <i>Solid fog</i>, and effects that work like <i>solid fog</i>, do not stack with each other in terms of slowed movement and attack penalties.</p><p>Unlike normal fog, only a severe wind (31+ mph) disperses these vapors, and it does so in 1 round.</p><p><i>Solid fog</i> can be made permanent with a <i>permanency</i> spell. A permanent <i>solid fog</i> dispersed by wind reforms in 10 minutes.</p></h4><h5><b>Mythic: </b>The penalty on melee attack and damage rolls increases to -4. The cloud's radius and height both increase to 50 feet.</h5><h5><b>Augmented</b>: The augmented version of mythic solid fog has the same additional benefits as the augmented version of mythic fog cloud.</h5></div>"},
new Spell(){ Name = "Song of Discord", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Song of Discord</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [mind-affecting, sonic]; <b>Level </b>bard 5, psychic 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>creatures within a 20-ft.-radius spread</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell causes those within the area to turn on each other rather than attack their foes. Each affected creature has a 50% chance to attack the nearest target each round. (Roll to determine each creature's behavior every round at the beginning of its turn.) A creature that does not attack its nearest neighbor is free to act normally for that round. Creatures forced by a <i>song of discord</i> to attack their fellows employ all methods at their disposal, choosing their deadliest spells and most advantageous combat tactics. They do not, however, harm targets that have fallen unconscious.</p></h4></div>"},
new Spell(){ Name = "Soul Bind", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Soul Bind</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>cleric/oracle 9, sorcerer/wizard 9, witch 9, shaman 9, psychic 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F (see text)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>corpse</h5><h5><b>Duration </b>permanent</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You draw the soul from a newly dead body and imprison it in a black sapphire gem. The subject must have been dead no more than 1 round per caster level. The soul, once trapped in the gem, cannot be returned through <i>clone, raise dead, reincarnation, resurrection, true resurrection,</i> or even a <i>miracle</i> or a <i>wish.</i> Only by destroying the gem or dispelling the spell on the gem can one free the soul (which is then still dead).</p><p>The focus for this spell is a black sapphire of at least 1,000 gp value for every HD possessed by the creature whose soul is to be bound. If the gem is not valuable enough, it shatters when the binding is attempted. (While creatures have no concept of level or HD as such, the value of the gem needed to trap an individual can be researched.)</p></h4></div>"},
new Spell(){ Name = "Sound Burst", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Sound Burst</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [sonic]; <b>Level </b>bard 2, cleric/oracle 2, occultist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, F/DF (a musical instrument)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Area </b>10-ft.-radius spread</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You blast an area with a tremendous cacophony. Every creature in the area takes 1d8 points of sonic damage and must succeed on a Fortitude save to avoid being stunned for 1 round. Creatures that cannot hear are not stunned but are still damaged.</p></h4></div>"},
new Spell(){ Name = "Speak with Animals", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Speak with Animals</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 3, druid 1, ranger 1, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can ask questions of and receive answers from animals, but the spell doesn't make them any more friendly than normal. Wary and cunning animals are likely to be terse and evasive, while the more stupid ones make inane comments. If an animal is friendly toward you, it may do some favor or service for you.</p></h4></div>"},
new Spell(){ Name = "Speak with Dead", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Speak with Dead</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy [language-dependent]; <b>Level </b>cleric/oracle 3, witch 3, inquisitor 3, shaman 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>10 ft.</h5><h5><b>Targets </b>one dead creature</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You grant the semblance of life to a corpse, allowing it to answer questions. You may ask one question per two caster levels. The corpse's knowledge is limited to what it knew during life, including the languages it spoke. Answers are brief, cryptic, or repetitive, especially if the creature would have opposed you in life.</p><p>If the dead creature's alignment was different from yours, the corpse gets a Will save to resist the spell as if it were alive.</p><p>If successful, the corpse can refuse to answer your questions or attempt to deceive you, using Bluff. The soul can only speak about what it knew in life. It cannot answer any questions that pertain to events that occurred after its death.</p><p>If the corpse has been subject to <i>speak with dead</i> within the past week, the new spell fails. You can cast this spell on a corpse that has been deceased for any amount of time, but the body must be mostly intact to be able to respond. A damaged corpse may be able to give partial answers or partially correct answers, but it must at least have a mouth in order to speak at all. This spell does not affect a corpse that has been turned into an undead creature.</p></h4></div>"},
new Spell(){ Name = "Speak with Plants", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Speak with Plants</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>bard 4, druid 3, ranger 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can communicate with normal plants and plant creatures, and can ask questions of and receive answers from them. A normal plant's sense of its surroundings is limited, so it won't be able to give (or recognize) detailed descriptions of creatures or answer questions about events outside its immediate vicinity. The spell doesn't make plant creatures any more friendly or cooperative than normal. Furthermore, wary and cunning plant creatures are likely to be terse and evasive, while the more stupid ones may make inane comments. If a plant creature is friendly, it may do some favor or service for you.</p></h4></div>"},
new Spell(){ Name = "Spectral Hand", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spectral Hand</p><div style=""clear: both;""></div></div><div><h5><b>School </b>necromancy; <b>Level </b>sorcerer/wizard 2, witch 2, occultist 2, psychic 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>one spectral hand</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A ghostly hand shaped from your life force materializes and moves as you desire, allowing you to deliver low-level, touch range spells at a distance. On casting the spell, you lose 1d4 hit points that return when the spell ends (even if it is dispelled), but not if the hand is destroyed. (The hit points can be healed as normal.) For as long as the spell lasts, any touch range spell of 4th level or lower that you cast can be delivered by the <i>spectral hand</i>. The spell gives you a +2 bonus on your melee touch attack roll, and attacking with the hand counts normally as an attack. The hand always strikes from your direction. The hand cannot flank targets like a creature can. After it delivers a spell, or if it goes beyond the spell range or goes out of your sight, the hand returns to you and hovers.</p><p>The hand is incorporeal and thus cannot be harmed by normal weapons. It has improved evasion (half damage on a failed Reflex save and no damage on a successful save), your save bonuses, and an AC of 22 (+8 size, +4 natural armor). Your Intelligence modifier applies to the hand's AC as if it were the hand's Dexterity modifier. The hand has 1 to 4 hit points, the same number that you lost in creating it.</p></h4></div>"},
new Spell(){ Name = "Spell Immunity", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spell Immunity</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 4, alchemist 4, inquisitor 4, occultist 4, psychic 4, spiritualist 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The warded creature is immune to the effects of one specified spell for every four levels you have. The spells must be of 4th level or lower. The warded creature effectively has unbeatable spell resistance regarding the specified spell or spells. Naturally, that immunity doesn't protect a creature from spells for which spell resistance doesn't apply. <i>Spell immunity</i> protects against spells, spell-like effects of magic items, and innate spell-like abilities of creatures. It does not protect against supernatural or extraordinary abilities, such as breath weapons or gaze attacks.</p><p>Only a particular spell can be protected against, not a certain domain or school of spells or a group of spells that are similar in effect. A creature can have only one <i>spell immunity</i> or greater <i>spell immunity</i> spell in effect on it at a time.</p></h4></div>"},
new Spell(){ Name = "Spell Immunity, Greater", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spell Immunity, Greater</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 8, psychic 8</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>spell immunity</i>, except the immunity applies to spells of 8th level or lower. A creature can have only one <i>spell immunity</i> or <i>greater spell immunity</i> spell in effect on it at a time.</p></h4></div>"},
new Spell(){ Name = "Spell Resistance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spell Resistance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>cleric/oracle 5, alchemist 5, inquisitor 5, occultist 5, psychic 5, spiritualist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The target gains spell resistance equal to 12 + your caster level.</p></h4></div>"},
new Spell(){ Name = "Spellstaff", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spellstaff</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, F (the staff that stores the spell)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>wooden quarterstaff touched</h5><h5><b>Duration </b>permanent until discharged (D)</h5><h5><b>Saving Throw </b>Will negates (object); <b>Spell Resistance </b>yes (object)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You store one spell that you can normally cast in a wooden quarterstaff. Only one such spell can be stored in a staff at a given time, and you cannot have more than one <i>spellstaff</i> at any given time. You can cast a spell stored within a staff just as though it were among those you had prepared, but it does not count against your normal allotment for a given day. You use up any applicable material components required to cast the spell when you store it in the <i>spellstaff</i>.</p></h4></div>"},
new Spell(){ Name = "Spell Turning", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spell Turning</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>sorcerer/wizard 7, summoner 5, psychic 7</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (a small silver mirror)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>until expended or 10 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Spells and spell-like effects targeted on you are turned back upon the original caster. The abjuration turns only spells that have you as a target. Effect and area spells are not affected. <i>Spell turning</i> also fails to stop touch range spells. From seven to ten (1d4+6) spell levels are affected by the turning. The exact number is rolled secretly.</p><p>When you are targeted by a spell of higher level than the amount of <i>spell turning</i> you have left, that spell is partially turned. Subtract the amount of <i>spell turning</i> left from the spell level of the incoming spell, then divide the result by the spell level of the incoming spell to see what fraction of the effect gets through. For damaging spells, you and the caster each take a fraction of the damage. For nondamaging spells, each of you has a proportional chance to be the one who is affected. If you and a spellcasting attacker are both warded by <i>spell turning</i> effects in operation, a resonating field is created. Roll randomly to determine the result.</p><p> <table><tr><th>d%</th><th>Effect</th></tr><tr><td>01-70</td><td>Spell drains away without effect.</td></tr><tr><td>71-80</td><td>Spell affects both of you equally at full effect.</td></tr><tr><td>81-97</td><td>Both turning effects are rendered nonfunctional for 1d4 minutes.</td></tr><tr><td>98-100</td><td>Both of you go through a rift into another plane.</td></tr></table> </p></h4></div>"},
new Spell(){ Name = "Spider Climb", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spider Climb</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 2, sorcerer/wizard 2, alchemist 2, summoner 2, magus 2, bloodrager 2, occultist 2, psychic 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a live spider)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>10 min./level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The subject can climb and travel on vertical surfaces or even traverse ceilings as well as a spider does. The affected creature must have its hands free to climb in this manner. The subject gains a climb speed of 20 feet and a +8 racial bonus on Climb skill checks; furthermore, it need not make Climb checks to traverse a vertical or horizontal surface (even upside down). A <i>spider climbing</i> creature retains its Dexterity bonus to Armor Class (if any) while climbing, and opponents get no special bonus to their attacks against it. It cannot, however, use the run action while climbing.</p></h4><h5><b>Mythic: </b>The target gains a climb speed equal to its base land speed or 30 feet, whichever is higher. The target can take the run action while climbing and needs only one hand free to climb. The target can move freely across spider webs (including giant spider webs), web spells, and similar adhesives of 2nd level or less (including tanglefoot bags) as if it were using freedom of movement.</h5></div>"},
new Spell(){ Name = "Spike Growth", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spike Growth</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>druid 3, ranger 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>one 20-ft. square/level</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Reflex partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Any ground-covering vegetation in the spell's area becomes very hard and sharply pointed without changing its appearance.</p><p>In areas of bare earth, roots and rootlets act in the same way.</p><p>Typically, <i>spike growth</i> can be cast in any outdoor setting except open water, ice, heavy snow, sandy desert, or bare stone. Any creature moving on foot into or through the spell's area takes 1d4 points of piercing damage for each 5 feet of movement through the spiked area.</p><p>Any creature that takes damage from this spell must also succeed on a Reflex save or suffer injuries to its feet and legs that slow its land speed by half. This speed penalty lasts for 24 hours or until the injured creature receives a <i>cure</i> spell (which also restores lost hit points). Another character can remove the penalty by taking 10 minutes to dress the injuries and succeeding on a Heal check against the spell's save DC.</p><p>Magic traps are hard to detect. A rogue (only) can use the Perception skill to find a <i>spike growth</i>. The DC is 25 + spell level, or DC 28 for <i>spike growth</i> (or DC 27 for <i>spike growth</i> cast by a ranger).</p><p><i>Spike growth</i> can't be disabled with the Disable Device skill.</p></h4></div>"},
new Spell(){ Name = "Spike Stones", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spike Stones</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [earth]; <b>Level </b>druid 4, shaman 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Area </b>one 20-ft. square/level</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Reflex partial; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Rocky ground, stone floors, and similar surfaces shape themselves into long, sharp points that blend into the background.</p><p><i>Spike stones</i> impede progress through an area and deal damage.</p><p>Any creature moving on foot into or through the spell's area moves at half speed. In addition, each creature moving through the area takes 1d8 points of piercing damage for each 5 feet of movement through the spiked area.</p><p>Any creature that takes damage from this spell must also succeed on a Reflex save to avoid injuries to its feet and legs. A failed save causes the creature's speed to be reduced to half normal for 24 hours or until the injured creature receives a <i>cure</i> spell (which also restores lost hit points). Another character can remove the penalty by taking 10 minutes to dress the injuries and succeeding on a Heal check against the spell's save DC.</p><p>Magic traps such as <i>spike stones</i> are hard to detect. A character with trapfinding can use the Perception skill to find <i>spike stones</i>.</p><p>The DC is 25 + spell level, or DC 29 for <i>spike stones</i>. <i>Spike stones</i> is a magic trap that can't be disabled with the Disable Device skill.</p></h4><h5><b>Mythic: </b>The damage dealt increases to 2d6 points of piercing damage plus 1 point of bleed damage. Add your tier to the DC to detect the magical trap.</h5></div>"},
new Spell(){ Name = "Spiritual Weapon", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Spiritual Weapon</p><div style=""clear: both;""></div></div><div><h5><b>School </b>evocation [force]; <b>Level </b>cleric/oracle 2, inquisitor 2, shaman 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>magic weapon of force</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A weapon made of force appears and attacks foes at a distance, as you direct it, dealing 1d8 force damage per hit, + 1 point per three caster levels (maximum +5 at 15th level). The weapon takes the shape of a weapon favored by your deity or a weapon with some <i>spiritual</i> significance or symbolism to you (see below) and has the same threat range and critical multipliers as a real weapon of its form. It strikes the opponent you designate, starting with one attack in the round the spell is cast and continuing each round thereafter on your turn. It uses your base attack bonus (possibly allowing it multiple attacks per round in subsequent rounds) plus your Wisdom modifier as its attack bonus. It strikes as a spell, not as a weapon, so for example, it can damage creatures that have damage reduction. As a force effect, it can strike incorporeal creatures without the reduction in damage associated with incorporeality. The weapon always strikes from your direction. It does not get a flanking bonus or help a combatant get one. Your feats or combat actions do not affect the weapon. If the weapon goes beyond the spell range, if it goes out of your sight, or if you are not directing it, the weapon returns to you and hovers.</p><p>Each round after the first, you can use a move action to redirect the weapon to a new target. If you do not, the weapon continues to attack the previous round's target. On any round that the weapon switches targets, it gets one attack. Subsequent rounds of attacking that target allow the weapon to make multiple attacks if your base attack bonus would allow it to. Even if the <i><i>spiritual</i> weapon</i> is a ranged weapon, use the spell's range, not the weapon's normal range increment, and switching targets still is a move action.</p><p>A <i><i>spiritual</i> weapon</i> cannot be attacked or harmed by physical attacks, but <i>dispel magic</i>, <i>disintegrate</i>, a <i>sphere of annihilation</i>, or a <i>rod of cancellation</i> affects it. A <i><i>spiritual</i> weapon</i>'s AC against touch attacks is 12 (10 + size bonus for Tiny object).</p><p>If an attacked creature has spell resistance, you make a caster level check (1d20 + caster level) against that spell resistance the first time the <i><i>spiritual</i> weapon</i> strikes it. If the weapon is successfully resisted, the spell is dispelled. If not, the weapon has its normal full effect on that creature for the duration of the spell.</p><p>The weapon that you get is often a force replica of your deity's own personal weapon. A cleric without a deity gets a weapon based on his alignment. A neutral cleric without a deity can create a <i><i>spiritual</i> weapon</i> of any alignment, provided he is acting at least generally in accord with that alignment at the time. The weapons associated with each alignment are as follows: chaos (battleaxe), evil (light flail), good (warhammer), law (longsword).</p></h4><h5><b>Mythic: </b>The weapon gains your choice of one of the following magic weapon special abilities: flaming, frost, keen, merciful, shock, or thundering. Before or after the weapon attempts all of its attacks for the round, you can redirect it to a different target as a swift action instead of a move action.</h5></div>"},
new Spell(){ Name = "Stabilize", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Stabilize</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (healing); <b>Level </b>cleric/oracle 0, druid 0, witch 0, inquisitor 0, shaman 0, occultist 0, psychic 0, spiritualist 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>Upon casting this spell, you target a living creature that has -1 or fewer hit points. That creature is automatically stabilized and does not lose any further hit points. If the creature later takes damage, it continues dying normally.</p></h4></div>"},
new Spell(){ Name = "Statue", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Statue</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 7, alchemist 6, occultist 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, M (lime, sand, and a drop of water stirred by an iron spike)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>1 hour/level (D)</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>A <i>statue</i> spell turns the subject to solid stone, along with any garments and equipment worn or carried. In <i>statue</i> form, the subject gains hardness 8. The subject retains its own hit points.</p><p>The subject can see, hear, and smell normally, but it does not need to eat or breathe. Feeling is limited to those sensations that can affect the granite-hard substance of the individual's body.</p><p>Chipping is equal to a mere scratch, but breaking off one of the <i>statue</i>'s arms constitutes serious damage. The subject of a <i>statue</i> spell can return to its normal state, act, and then return instantly to the <i>statue</i> state (a free action) if it so desires as long as the spell duration is in effect.</p></h4></div>"},
new Spell(){ Name = "Status", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Status</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>cleric/oracle 2, witch 2, occultist 2, psychic 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>one living creature touched per three levels</h5><h5><b>Duration </b>1 hour/level</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>When you need to keep track of comrades who may get separated, <i>status</i> allows you to mentally monitor their relative positions and general condition. You are aware of direction and distance to the creatures and any conditions affecting them: unharmed, wounded, disabled, staggered, unconscious, dying, nauseated, panicked, stunned, poisoned, diseased, confused, or the like. Once the spell has been cast upon the subjects, the distance between them and the caster does not affect the spell as long as they are on the same plane of existence. If a subject leaves the plane, or if it dies, the spell ceases to function for it.</p></h4></div>"},
new Spell(){ Name = "Stinking Cloud", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Stinking Cloud</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (creation); <b>Level </b>magus 3, sorcerer/wizard 3, witch 3, bloodrager 3, shaman 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a rotten egg or cabbage leaves)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Effect </b>cloud spreads in 20-ft. radius, 20 ft. high</h5><h5><b>Duration </b>1 round/level</h5><h5><b>Saving Throw </b>Fortitude negates; see text; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p><i>Stinking cloud</i> creates a bank of fog like that created by <i>fog cloud</i>, except that the vapors are nauseating. Living creatures in the cloud become nauseated. This condition lasts as long as the creature is in the cloud and for 1d4+1 rounds after it leaves.</p><p>(Roll separately for each nauseated character.) Any creature that succeeds on its save but remains in the cloud must continue to save each round on your turn. This is a poison effect.</p><p><i>Stinking cloud</i> can be made permanent with a <i>permanency</i> spell. A permanent <i>stinking cloud</i> dispersed by wind reforms in 10 minutes.</p></h4><h5><b>Mythic: </b>Creatures that succeed at their saves are sickened while they remain in the cloud and for 1d4+1 rounds after they leave. Creatures that fail their saves continue to be sickened for 1 hour after leaving the cloud.</h5></div>"},
new Spell(){ Name = "Stone Shape", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Stone Shape</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation [earth]; <b>Level </b>cleric/oracle 3, druid 3, sorcerer/wizard 4, shaman 3, occultist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M/DF (soft clay)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>stone or stone object touched, up to 10 cu. ft. + 1 cu. ft./level</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You can form an existing piece of stone into any shape that suits your purpose. While it's possible to make crude coffers, doors, and so forth with <i>stone shape</i>, fine detail isn't possible. There is a 30% chance that any shape including moving parts simply doesn't work.</p></h4><h5><b>Mythic: </b>The duration changes to 1 round/level and instantaneous (see text). Each round after the round you cast the spell, you can spend a standard action to shape up to 5 additional cubic feet of stone you touch. Shaped stone has an instantaneous duration (meaning it does not revert to its previous form when the spell ends).</h5></div>"},
new Spell(){ Name = "Stoneskin", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Stoneskin</p><div style=""clear: both;""></div></div><div><h5><b>School </b>abjuration; <b>Level </b>druid 5, sorcerer/wizard 4, alchemist 4, summoner 3, inquisitor 4, magus 4, bloodrager 4, shaman 5, occultist 4, psychic 4</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (granite and diamond dust worth 250 gp)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>touch</h5><h5><b>Targets </b>creature touched</h5><h5><b>Duration </b>10 min./level or until discharged</h5><h5><b>Saving Throw </b>Will negates (harmless); <b>Spell Resistance </b>yes (harmless)</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>The warded creature gains resistance to blows, cuts, stabs, and slashes. The subject gains DR 10/adamantine. It ignores the first 10 points of damage each time it takes damage from a weapon, though an adamantine weapon bypasses the reduction. Once the spell has prevented a total of 10 points of damage per caster level (maximum 150 points), it is discharged.</p></h4><h5><b>Mythic: </b>The target gains a +4 bonus on saving throws against disease, poison, and stun effects. There is a 50% chance any critical hit or sneak attack against the target is treated as a normal hit, as if the target were wearing medium fortification armor.</h5></div>"},
new Spell(){ Name = "Stone Tell", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Stone Tell</p><div style=""clear: both;""></div></div><div><h5><b>School </b>divination; <b>Level </b>druid 6, shaman 7, psychic 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>10 minutes</h5><h5><b>Components </b>V, S, DF</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>personal</h5><h5><b>Targets </b>you</h5><h5><b>Duration </b>1 min./level</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You gain the ability to speak with stones, which relate to you who or what has touched them as well as revealing what is covered or concealed behind or under them. The stones relate complete descriptions if asked. A stone's perspective, perception, and knowledge may prevent the stone from providing the details you are looking for. You can speak with natural or worked stone.</p></h4></div>"},
new Spell(){ Name = "Stone to Flesh", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Stone to Flesh</p><div style=""clear: both;""></div></div><div><h5><b>School </b>transmutation; <b>Level </b>sorcerer/wizard 6, witch 6, magus 6, shaman 6</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, S, M (a drop of blood mixed with earth)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one petrified creature or a cylinder of stone from 1 ft. to 3 ft. in diameter and up to 10 ft. long</h5><h5><b>Duration </b>instantaneous</h5><h5><b>Saving Throw </b>Fortitude negates (object); see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell restores a petrified creature to its normal state, restoring life and goods. The creature must make a DC 15 Fortitude save to survive the process. Any petrified creature, regardless of size, can be restored. The spell also can convert a mass of stone into a fleshy substance. Such flesh is inert and lacking a vital life force unless a life force or magical energy is available. For example, this spell would turn an animated stone statue into an animated flesh statue, but an ordinary statue would become a mass of inert flesh in the shape of the statue. You can affect an object that fits within a cylinder from 1 foot to 3 feet in diameter and up to 10 feet long or a cylinder of up to those dimensions in a larger mass of stone.</p></h4></div>"},
new Spell(){ Name = "Storm Of Vengeance", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Storm Of Vengeance</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>cleric/oracle 9, druid 9, witch 9, shaman 9</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>long (400 ft. + 40 ft./level)</h5><h5><b>Effect </b>360-ft.-radius storm cloud</h5><h5><b>Duration </b>concentration (maximum 10 rounds) (D)</h5><h5><b>Saving Throw </b>see text; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You create a huge black storm cloud in the air. Each creature under the cloud must succeed on a Fortitude save or be deafened for 1d4 x 10 minutes. Each round you continue to concentrate, the spell generates additional effects as noted below. Each effect occurs on your turn.</p><p><i>2nd Round</i>: Acid rains down in the area, dealing 1d6 points of acid damage (no save).</p><p><i>3rd Round</i>: You call six bolts of lightning down from the cloud.</p><p>You decide where the bolts strike. No two bolts may be directed at the same target. Each bolt deals 10d6 points of electricity damage. A creature struck can attempt a Reflex save for half damage.</p><p><i>4th Round</i>: Hailstones rain down in the area, dealing 5d6 points of bludgeoning damage (no save).</p><p><i>5th through 10th Rounds</i>: Violent rain and wind gusts reduce visibility. The rain obscures all sight, including darkvision, beyond 5 feet. A creature 5 feet away has concealment (attacks have a 20% miss chance). Creatures farther away have total concealment (50% miss chance, and the attacker cannot use sight to locate the target). Speed is reduced by three-quarters.</p><p>Ranged attacks within the area of the storm are impossible. Spells cast within the area are disrupted unless the caster succeeds on a Concentration check against a DC equal to the <i>storm of</i> vengeance's save DC + the level of the spell the caster is trying to cast.</p></h4><h5><b>Mythic: </b>The acid damage dealt increases to 1d8 points of acid damage. The lightning damage dealt increases to 10d8 points of electricity damage. You can call down a bolt of lightning on the 2nd and 4th rounds (equivalent to those on the 3rd round). You can strike the same target with a bolt more than once during the casting of the spell, but not more than once per round.</h5><h5><b>Augmented (7th)</b>: If you expend three uses of mythic power, the storm cloud's radius increases to 1,000 feet. Structures in the storm take 1d10 points of damage per round (this damage bypasses hardness). You can call down one additional bolt in the 2nd and 4th rounds, and three additional bolts in the 3rd round.</h5></div>"},
new Spell(){ Name = "Suggestion", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Suggestion</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [language-dependent, mind-affecting]; <b>Level </b>bard 2, sorcerer/wizard 3, witch 3, occultist 3, psychic 2, mesmerist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, M (a snake's tongue and a honeycomb)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Targets </b>one living creature</h5><h5><b>Duration </b>1 hour/level or until completed</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>You influence the actions of the target creature by suggesting a course of activity (limited to a sentence or two). The <i>suggestion</i> must be worded in such a manner as to make the activity sound reasonable. Asking the creature to do some obviously harmful act automatically negates the effect of the spell.</p><p>The suggested course of activity can continue for the entire duration. If the suggested activity can be completed in a shorter time, the spell ends when the subject finishes what it was asked to do. You can instead specify conditions that will trigger a special activity during the duration. If the condition is not met before the spell duration expires, the activity is not performed.</p><p>A very reasonable <i>suggestion</i> causes the save to be made with a penalty (such as -1 or -2).</p></h4><h5><b>Mythic: </b>Mythic suggestion loses the language-dependent descriptor, and can target one or more living creatures in a 10-foot-radius burst.</h5></div>"},
new Spell(){ Name = "Suggestion, Mass", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Suggestion, Mass</p><div style=""clear: both;""></div></div><div><h5><b>School </b>enchantment (compulsion) [language-dependent, mind-affecting]; <b>Level </b>bard 5, sorcerer/wizard 6, witch 6, occultist 5, psychic 5, mesmerist 5</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 standard action</h5><h5><b>Components </b>V, M (a snake's tongue and a honeycomb)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>medium (100 ft. + 10 ft./level)</h5><h5><b>Targets </b>one creature/level, no two of which can be more than 30 ft. apart</h5><h5><b>Duration </b>1 hour/level or until completed</h5><h5><b>Saving Throw </b>Will negates; <b>Spell Resistance </b>yes</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i><i>suggestion</i>,</i> except that it can affect more creatures. The same <i>suggestion</i> applies to all these creatures.</p></h4></div>"},
new Spell(){ Name = "Summon Instrument", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Summon Instrument</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>bard 0</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>0 ft.</h5><h5><b>Effect </b>one summoned handheld musical instrument</h5><h5><b>Duration </b>1 min./level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell summons one handheld musical instrument of your choice. This instrument appears in your hands or at your feet (your choice). The instrument is typical for its type. Only one instrument appears per casting, and it will play only for you. You can't summon an instrument too large to be held in two hands.</p><p>The summoned instrument disappears at the end of this spell.</p></h4></div>"},
new Spell(){ Name = "Summon Monster I", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Summon Monster I</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning) [see text]; <b>Level </b>bard 1, cleric/oracle 1, sorcerer/wizard 1, summoner 1, witch 1, antipaladin 1, psychic 1, spiritualist 1</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, F/DF (a tiny bag and a small candle)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one summoned creature</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell summons an extraplanar creature (typically an outsider, elemental, or magical beast native to another plane). It appears where you designate and acts immediately, on your turn. It attacks your opponents to the best of its ability. If you can communicate with the creature, you can direct it not to attack, to attack particular enemies, or to perform other actions. The spell conjures one of the creatures from the 1st <b>Level</b> list on Table 10-1. You choose which kind of creature to summon, and you can choose a different one each time you cast the spell.</p><p>A summoned monster cannot summon or otherwise conjure another creature, nor can it use any teleportation or planar travel abilities. Creatures cannot be summoned into an environment that cannot support them. Creatures summoned using this spell cannot use spells or spell-like abilities that duplicate spells with expensive material components (such as <i>wish).</i></p><p>When you use a summoning spell to summon a creature with an alignment or elemental subtype, it is a spell of that type. Creatures on Table 10-1 marked with an ""*"" are summoned with the celestial template, if you are good, and the fiendish template, if you are evil. If you are neutral, you may choose which template to apply to the creature. Creatures marked with an ""*"" always have an alignment that matches yours, regardless of their usual alignment.</p><p>Summoning these creatures makes the summoning spell's type match your alignment.</p></h4></div>"},
new Spell(){ Name = "Summon Monster II", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Summon Monster II</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>bard 2, cleric/oracle 2, sorcerer/wizard 2, summoner 2, witch 2, antipaladin 2, psychic 2, spiritualist 2</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, F/DF (a tiny bag and a small candle)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one summoned creature</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>summon monster I,</i> except that you can summon one creature from the 2nd-level list or 1d3 creatures of the same kind from the 1st-level list.</p></h4></div>"},
new Spell(){ Name = "Summon Monster III", Description = @"<link rel=""stylesheet""href=""PF.css""><div class=""heading""><p class=""alignleft"">Summon Monster III</p><div style=""clear: both;""></div></div><div><h5><b>School </b>conjuration (summoning); <b>Level </b>bard 3, cleric/oracle 3, sorcerer/wizard 3, witch 3, antipaladin 3, psychic 3, spiritualist 3</h5></div><hr/><div><h5><b>CASTING</b></h5></div><hr/><div><h5><b>Casting Time </b>1 round</h5><h5><b>Components </b>V, S, F/DF (a tiny bag and a small candle)</h5></div><hr/><div><h5><b>EFFECT</b></h5></div><hr/><div><h5><b>Range </b>close (25 ft. + 5 ft./2 levels)</h5><h5><b>Effect </b>one summoned creature</h5><h5><b>Duration </b>1 round/level (D)</h5><h5><b>Saving Throw </b>none; <b>Spell Resistance </b>no</h5></div><hr/><div><h5><b>DESCRIPTION</b></h5></div><hr/><div><h4><p>This spell functions like <i>summon monster I,</i> except that you can summon one creature from the 3rd-level list, 1d3 creatures of the same kind from the 2nd-level list, or 1d4+1 creatures of the same kind from the 1st-level list.</p></h4></div>"},
